=\"scope\" v-if=\"scope.row.${c.javaFieldId}\">\n            <a :href=\"item\" target=\"_blank\" :key=\"index\" v-for=\"(item, index) in scope.row.${c.javaFieldId}.split(\'|\')\">\n                {{decodeURIComponent(item.substring(item.lastIndexOf(\"/\")+1))}}\n            </a>\n        </template>\n      </el-table-column>\n              <#elseif c.showType == \"imageselect\">\n    <el-table-column\n        prop=\"${c.javaFieldId}\"\n        header-align=\"center\"\n        align=\"center\"\n        show-overflow-tooltip\n        label=\"${c.comments}\">\n        <template slot-scope=\"scope\" v-if=\"scope.row.${c.javaFieldId}\">\n          <el-image\n            style=\"height: 50px;width:50px;margin-right:10px;\"\n            :src=\"src\" v-for=\"(src, index) in scope.row.${c.javaFieldId}.split(\'|\')\" :key=\"index\"\n            :preview-src-list=\"scope.row.${c.javaFieldId}.split(\'|\')\">\n          </el-image>\n        </template>\n      </el-table-column>\n         <#elseif c.showType == \"wangeditor\" || c.showType == \"quilleditor\">\n                  <el-table-column\n                    prop=\"${c.javaFieldId}\"\n                    header-align=\"center\"\n                    align=\"center\"\n                    show-overflow-tooltip\n                    label=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n            <p v-html=\"$utils.unescapeHTML(scope.row.${c.javaFieldId})\"></p>\n        </template>\n                  </el-table-column>\n      <#else>\n                <el-table-column\n                    prop=\"${c.javaFieldId}\"\n                    header-align=\"center\"\n                    align=\"center\"\n                    show-overflow-tooltip\n                    label=\"${c.comments}\">\n                  </el-table-column>\n          </#if>\n                    </#if>\n                    </#if>\n                </#list>\n                  <el-table-column\n                    fixed=\"right\"\n                    label=\"操作\"\n                    width=\"150\">\n                    <template slot-scope=\"scope\">\n                      <el-button @click=\"view${child.className}Row(scope.row)\" type=\"text\" size=\"small\">查看</el-button>\n                      <el-button  @click=\"edit${child.className}Row(scope.row)\" type=\"text\" size=\"small\">编辑</el-button>\n                      <el-button  @click=\"del${child.className}Row(scope.row)\" type=\"text\" size=\"small\">删除</el-button>\n                    </template>\n                  </el-table-column>\n                </el-table>\n              </el-tab-pane>\n         </#list>\n            </el-tabs>\n           </el-form-item>\n          </el-col>\n        </el-row>\n    </el-form>\n    <span slot=\"footer\" class=\"dialog-footer\">\n      <el-button size=\"small\" @click=\"visible = false\">关闭</el-button>\n      <el-button size=\"small\" type=\"primary\" v-if=\"method != \'view\'\" @click=\"doSubmit()\" v-noMoreClick>确定</el-button>\n    </span>\n  </el-dialog>\n<#list table.childList as child>\n  <${child.className}Form ref=\"${child.className?uncap_first}Form\" @addRow=\"save${child.className}Row(arguments)\"></${child.className}Form>\n</#list>\n</div>\n</template>\n\n<script>\n<#list table.childList as child>\n  import ${child.className}Form from \'./${child.className}Form\'\n</#list>\n<#list table.importFormList as c>\n  ${c}\n</#list>\n  export default {\n    data () {\n      return {\n        title: \'\',\n        method: \'\',\n        visible: false,\n        loading: false,\n        ${className?uncap_first}Tab: \'0\',\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"fileselect\" || c.showType == \"imageselect\">\n        ${c.javaFieldId}Arra: [],\n  </#if>\n  </#if>\n</#list>\n        inputForm: {\n          id: \'\'<#if (table.formList?size>0)>,</#if>\n<#list table.childList as child>\n          ${child.className?uncap_first}List: [],\n</#list>\n        <#list table.formList as c>\n          <#if c.javaFieldId?contains(\".\")>\n          ${c.javaFieldId?split(\".\")[0]}: {\n            ${c.javaFieldId?split(\".\")[1]}: \'\'\n          }<#if c_has_next>,</#if>\n          <#else>\n          ${c.javaFieldId}: \'\'<#if c_has_next>,</#if>\n          </#if>\n    </#list>\n        }\n      }\n    },\n    components: {\n<#list table.formComponents as c>\n      ${c},\n</#list>\n<#list table.childList as child>\n      ${child.className}Form<#if child_has_next>,</#if>\n</#list>\n    },\n    <#assign cks=0/>\n    <#list table.formList as c>\n     <#if c.showType == \"checkbox\">\n     <#assign cks= cks + 1/>\n    </#if>\n    </#list>\n    <#if cks gt 0>\n    computed: {\n    <#assign ckIndex=0/>\n    <#list table.formList as c>\n     <#if c.showType == \"checkbox\">\n     <#assign ckIndex= ckIndex + 1/>\n      ${c.javaFieldId}List: {\n        get: function () {\n          return this.inputForm.${c.javaFieldId} !== \'\' ? this.inputForm.${c.javaFieldId}.split(\',\') : []\n        },\n        set: function (val) {\n          this.inputForm.${c.javaFieldId} = val.join(\',\')\n        }\n      }<#if ckIndex lt cks >,</#if>\n    </#if>\n    </#list>\n    },\n    </#if>\n    methods: {\n      init (method, id) {\n        this.method = method\n        if (method === \'add\') {\n          this.title = `新建${functionNameSimple}`\n        } else if (method === \'edit\') {\n          this.title = \'修改${functionNameSimple}\'\n        } else if (method === \'view\') {\n          this.title = \'查看${functionNameSimple}\'\n        }\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"imageselect\" || c.showType == \"fileselect\">\n        this.${c.javaFieldId}Arra = []\n  </#if>\n  </#if>\n</#list>\n        this.visible = true\n        this.loading = false\n        this.$nextTick(() => {\n          this.$refs.inputForm.resetFields()\n          this.inputForm.id = id\n          this.${className?uncap_first}Tab = \'0\'\n<#list table.childList as child>\n          this.inputForm.${child.className?uncap_first}List = []\n</#list>\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"wangeditor\">\n          this.$refs.${c.javaFieldId}Editor.init(\'\')\n  </#if>\n  </#if>\n</#list>\n          if (method === \'edit\' || method === \'view\') { // 修改或者查看\n            this.loading = true\n            this.$http({\n              url: `/${urlPrefix}/queryById?id=${\"$\"}{this.inputForm.id}`,\n              method: \'get\'\n            }).then(({data}) => {\n              this.inputForm = this.recover(this.inputForm, data.${className})\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"wangeditor\">\n              this.$refs.${c.javaFieldId}Editor.init(this.inputForm.${c.javaFieldId})\n  </#if>\n  </#if>\n</#list>\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"fileselect\" || c.showType == \"imageselect\">\n              this.inputForm.${c.javaFieldId}.split(\'|\').forEach((item) => {\n                if (item.trim().length > 0) {\n                  this.${c.javaFieldId}Arra.push({name: decodeURIComponent(item.substring(item.lastIndexOf(\'/\') + 1)), url: item})\n                }\n              })\n  </#if>\n  </#if>\n</#list>\n              this.loading = false\n            })\n          }\n        })\n      },\n<#list table.childList as child>\n      save${child.className}Row (child) {\n        if (child[0] === \'\') {\n          this.inputForm.${child.className?uncap_first}List.push(child[1])\n        } else {\n          this.inputForm.${child.className?uncap_first}List.forEach((item, index) => {\n            if (item === child[0]) {\n              this.inputForm.${child.className?uncap_first}List.splice(index, 1, child[1])\n            }\n          })\n        }\n      },\n      add${child.className}Row (child) {\n        this.$refs.${child.className?uncap_first}Form.init(\'add\')\n      },\n      view${child.className}Row (child) {\n        this.$refs.${child.className?uncap_first}Form.init(\'view\', child)\n      },\n      edit${child.className}Row (child) {\n        this.$refs.${child.className?uncap_first}Form.init(\'edit\', child)\n      },\n      del${child.className}Row (child) {\n        this.inputForm.${child.className?uncap_first}List.forEach((item, index) => {\n          if (item === child && item.id === \'\') {\n            this.inputForm.${child.className?uncap_first}List.splice(index, 1)\n          } else if (item === child) {\n            item.delFlag = \'1\'\n            this.inputForm.${child.className?uncap_first}List.splice(index, 1, item)\n          }\n        })\n      },\n</#list>\n      // 表单提交\n      doSubmit () {\n        this.$refs[\'inputForm\'].validate((valid) => {\n          if (valid) {\n            this.loading = true\n            this.$http({\n              url: `/${urlPrefix}/save`,\n              method: \'post\',\n              data: this.inputForm\n            }).then(({data}) => {\n              this.loading = false\n              if (data && data.success) {\n                this.visible = false\n                this.$message.success(data.msg)\n                this.$emit(\'refreshDataList\')\n              }\n            })\n          }\n        })\n      }\n    }\n  }\n</script>\n\n  ]]>\n    </content>\n</template>', '', 0, 0, '0', NULL, '1', '2021-10-04 21:55:37', '1', '2021-10-04 21:55:37');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1445024813075689473', 'mapper.xml', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>mapper</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/mapper/xml</filePath>\n    <fileName>${ClassName}Mapper.xml</fileName>\n    <content><![CDATA[\n<?xml version=\"1.0\" encoding=\"UTF-8\" ?>\n<!DOCTYPE mapper PUBLIC \"-//mybatis.org//DTD Mapper 3.0//EN\" \"http://mybatis.org/dtd/mybatis-3-mapper.dtd\">\n<mapper namespace=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper.${ClassName}Mapper\">\n\n    <#-- 输出字段列 -->\n	<sql id=\"${className}Columns\">\n		<#assign columnField>\n			<#list table.columnList as c>\n		a.${c.name} AS \"${c.javaFieldId}\",\n			</#list>\n			<#list table.columnList as c>\n			<#-- 父表关联字段 -->\n				<#if table.parentExists && table.parentTableFk == c.name>\n					<#list c.javaFieldAttrs as a>\n		b.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"userselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"officeselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"areaselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"treeselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"gridselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n				</#list>\n				</#if>\n\n			</#list>\n		</#assign>\n${columnField?substring(0, columnField?last_index_of(\",\"))}\n	</sql>\n\n	<#-- 输出字段关联表 -->\n	<sql id=\"${className}Joins\">\n\n		<#-- 关联系统表 -->\n		<#list table.columnList as c>\n		<#-- 关联父表 -->\n		<#if table.parentExists && table.parentTableFk == c.name>\n			LEFT JOIN ${table.parent.name} b ON b.id = a.${table.parentTableFk}\n			<#elseif c.showType?? && c.showType == \"userselect\">\n		LEFT JOIN sys_user ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"officeselect\">\n		LEFT JOIN sys_office ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"areaselect\">\n		LEFT JOIN sys_area ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"treeselect\" && c.javaType != \"This\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"gridselect\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			</#if>\n		</#list>\n	</sql>\n\n    <#assign x=0 />\n    <#list table.columnList as c>\n			<#if c.showType?? && c.showType == \"umeditor\">\n			<#assign x=1 />\n    <resultMap type=\"${ClassName}\" id=\"${ClassName}Result\" autoMapping=\"true\">\n			<#break/>\n			</#if>\n	</#list>\n	<#list table.columnList as c>\n			<#if c.showType?? && c.showType == \"umeditor\">\n			<#assign x=1 />\n    	<result column=\"${c.name}\" property=\"${c.simpleJavaField}\"   typeHandler=\"cn.weegoo.core.mapper.ConvertBlobTypeHandler\"/>\n			</#if>\n	</#list>\n	<#list table.columnList as c>\n			<#if c.showType?? && c.showType == \"umeditor\">\n			<#assign x=1 />\n    </resultMap>\n			<#break/>\n			</#if>\n	</#list>\n\n	<select id=\"get\" <#if x==0>resultType=\"${ClassName}\"</#if><#if x==1>resultMap=\"${ClassName}Result\"</#if> >\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		WHERE a.id = ${\"#\"}{id}\n	</select>\n\n	<select id=\"findList\" <#if x==0>resultType=\"${ClassName}\"</#if><#if x==1>resultMap=\"${ClassName}Result\"</#if> >\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		<where>\n			<#if table.delFlagExists>a.del_flag = ${\"#\"}{DEL_FLAG_NORMAL}</#if>\n			${\"$\"}{dataScope}\n			<#list table.columnList as c>\n				<#if (c.isQuery?? && c.isQuery == \"1\") || (table.parentExists && table.parentTableFk == c.name)>\n					<#if c.queryType ?? && c.queryType == \'between\'>\n			<if test=\"begin${c.simpleJavaField?cap_first} != null and end${c.simpleJavaField?cap_first} != null <#if c.simpleJavaField != c.javaFieldId>and begin${c.javaFieldId?cap_first} != null and end${c.javaFieldId?cap_first} != null </#if>\">\n					<#else>\n			<if test=\"${c.simpleJavaField} != null<#if c.simpleJavaField != c.javaFieldId> and ${c.javaFieldId} != null</#if> and ${c.javaFieldId} != \'\'\">\n					</#if>\n					<#if c.queryType ?? && c.queryType == \'between\'>\n				AND a.${c.name} BETWEEN ${\"#\"}{begin${c.simpleJavaField?cap_first}} AND ${\"#\"}{end${c.simpleJavaField?cap_first}}\n					<#elseif c.queryType ?? && c.queryType == \'like\'>\n				AND a.${c.name} LIKE\n				    <if test=\"_databaseId == \'postgre\'\">\'%\'||${\"#\"}{${c.javaFieldId}}||\'%\'</if>\n					<if test=\"_databaseId == \'oracle\'\">\'%\'||${\"#\"}{${c.javaFieldId}}||\'%\'</if>\n					<if test=\"_databaseId == \'mssql\'\">\'%\'+${\"#\"}{${c.javaFieldId}}+\'%\'</if>\n					<if test=\"_databaseId == \'mysql\'\">concat(\'%\',${\"#\"}{${c.javaFieldId}},\'%\')</if>\n					<#elseif c.queryType ?? && c.queryType == \'left_like\'>\n				AND a.${c.name} LIKE\n				    <if test=\"_databaseId == \'postgre\'\">\'%\'||${\"#\"}{${c.javaFieldId}}</if>\n					<if test=\"_databaseId == \'oracle\'\">\'%\'||${\"#\"}{${c.javaFieldId}}</if>\n					<if test=\"_databaseId == \'mssql\'\">\'%\'+${\"#\"}{${c.javaFieldId}}</if>\n					<if test=\"_databaseId == \'mysql\'\">concat(\'%\',${\"#\"}{${c.javaFieldId}})</if>\n					<#elseif c.queryType ?? && c.queryType == \'right_like\'>\n				AND a.${c.name} LIKE\n				    <if test=\"_databaseId == \'postgre\'\">${\"#\"}{${c.javaFieldId}}||\'%\'</if>\n					<if test=\"_databaseId == \'oracle\'\">${\"#\"}{${c.javaFieldId}}||\'%\'</if>\n					<if test=\"_databaseId == \'mssql\'\">${\"#\"}{${c.javaFieldId}}+\'%\'</if>\n					<if test=\"_databaseId == \'mysql\'\">concat(${\"#\"}{${c.javaFieldId}},\'%\')</if>\n					<#else>\n				AND a.${c.name} ${c.queryType} ${\"#\"}{${c.javaFieldId}}\n					</#if>\n			</if>\n				</#if>\n			</#list>\n		</where>\n		<choose>\n			<when test=\"page !=null and page.orderBy != null and page.orderBy != \'\'\">\n				ORDER BY ${\"$\"}{page.orderBy}\n			</when>\n			<otherwise>\n				<#if table.parentExists>\n					<#if table.createDateExists>\n				ORDER BY a.create_date ASC\n					</#if>\n				<#else>\n					<#if table.updateDateExists>\n				ORDER BY a.update_date DESC\n					</#if>\n				</#if>\n			</otherwise>\n		</choose>\n	</select>\n\n	<select id=\"findAllList\" <#if x==0>resultType=\"${ClassName}\"</#if><#if x==1>resultMap=\"${ClassName}Result\"</#if> >\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		<where>\n			<#if table.delFlagExists>a.del_flag = ${\"#\"}{DEL_FLAG_NORMAL}</#if>\n			${\"$\"}{dataScope}\n		</where>\n		<choose>\n			<when test=\"page !=null and page.orderBy != null and page.orderBy != \'\'\">\n				ORDER BY ${\"$\"}{page.orderBy}\n			</when>\n			<otherwise>\n				<#if table.parentExists>\n					<#if table.createDateExists>\n				ORDER BY a.create_date ASC\n					</#if>\n				<#else>\n					<#if table.updateDateExists>\n				ORDER BY a.update_date DESC\n					</#if>\n				</#if>\n			</otherwise>\n		</choose>\n	</select>\n\n	<insert id=\"insert\">\n		INSERT INTO ${table.name}(\n		<#assign insertField>\n			<#list table.columnList as c>\n				<#if c.isInsert?? && c.isInsert == \"1\">\n				<#if !(table.genIdType ==\"2\" && c.name==\'id\') >\n			${c.name},\n				</#if>\n				</#if>\n			</#list>\n		</#assign>\n${insertField?substring(0, insertField?last_index_of(\",\"))}\n		) VALUES (\n		<#assign insertJavaField>\n			<#list table.columnList as c>\n				<#if c.isInsert?? && c.isInsert == \"1\">\n				<#if !(table.genIdType ==\"2\" && c.name==\'id\') >\n			   <#if c.showType == \"umeditor\">\n			${\"#\"}{${c.javaFieldId}, jdbcType=BLOB},\n			    <#else>\n			${\"#\"}{${c.javaFieldId}},\n				</#if>\n				</#if>\n				</#if>\n			</#list>\n		</#assign>\n${insertJavaField?substring(0, insertJavaField?last_index_of(\",\"))}\n		)\n	</insert>\n\n	<update id=\"update\">\n		UPDATE ${table.name} SET\n			<#assign updateField>\n				<#list table.columnList as c>\n					<#if c.isEdit?? && c.isEdit == \"1\">\n			     <#if c.showType == \"umeditor\">\n			${c.name} = ${\"#\"}{${c.javaFieldId}, jdbcType=BLOB},\n			    <#else>\n			${c.name} = ${\"#\"}{${c.javaFieldId}},\n				</#if>\n					</#if>\n				</#list>\n			</#assign>\n${updateField?substring(0, updateField?last_index_of(\",\"))}\n		WHERE id = ${\"#\"}{id}\n	</update>\n\n\n	<!--物理删除-->\n	<update id=\"delete\">\n		DELETE FROM ${table.name}\n	<#if table.parentExists>\n		<#list table.columnList as c>\n			<#if table.parentTableFk == c.name>\n		<choose>\n			<when test=\"id !=null and id != \'\'\">\n				WHERE id = ${\"#\"}{id}\n			</when>\n			<otherwise>\n				WHERE ${table.parentTableFk} = ${\"#\"}{${c.javaFieldId}}\n			</otherwise>\n		</choose>\n			</#if>\n		</#list>\n	<#else>\n		WHERE id = ${\"#\"}{id}\n	</#if>\n	</update>\n\n	<!--逻辑删除-->\n	<update id=\"deleteByLogic\">\n		UPDATE ${table.name} SET\n			del_flag = ${\"#\"}{DEL_FLAG_DELETE}\n	<#if table.parentExists>\n		<#list table.columnList as c>\n			<#if table.parentTableFk == c.name>\n		<choose>\n			<when test=\"id !=null and id != \'\'\">\n				WHERE id = ${\"#\"}{id}\n			</when>\n			<otherwise>\n				WHERE ${table.parentTableFk} = ${\"#\"}{${c.javaFieldId}}\n			</otherwise>\n		</choose>\n			</#if>\n		</#list>\n	<#else>\n		WHERE id = ${\"#\"}{id}\n	</#if>\n	</update>\n\n\n	<!-- 根据实体名称和字段名称和字段值获取唯一记录 -->\n	<select id=\"findUniqueByProperty\" resultType=\"${ClassName}\">\n		select * FROM ${table.name}  where ${\"$\"}{propertyName} = ${\"#\"}{value}\n	</select>\n\n</mapper>]]>\n    </content>\n</template>', '', 0, 0, '1', NULL, '1', '2021-10-04 21:55:37', '1', '2021-10-04 21:55:37');
