INSERT INTO `plugin_gencode_template_obj` VALUES ('1445024813268627458', 'entity.java', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<template>\r\n	<name>entity</name>\r\n	<filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/entity</filePath>\r\n	<fileName>${ClassName}.java</fileName>\r\n	<content><![CDATA[\r\n/**\r\n * Copyright © 2015-2020 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\r\n */\r\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.entity;\r\n\r\n<#list table.importList as i>\r\nimport ${i};\r\n</#list>\r\n\r\nimport cn.weegoo.core.persistence.DataEntity;\r\nimport cn.weegoo.common.utils.excel.annotation.ExcelField;\r\nimport lombok.Data;\r\n\r\n/**\r\n * ${functionName}Entity\r\n * @author ${functionAuthor}\r\n * @version ${functionVersion}\r\n */\r\n@Data\r\npublic class ${ClassName} extends DataEntity<${ClassName}> {\r\n	\r\n	private static final long serialVersionUID = 1L;\r\n	<#-- 生成字段属性 -->\r\n	<#list table.columnList as c>\r\n		<#-- 如果不是基类属性 -->\r\n		<#if c.isNotBaseField>\r\n			<#list c.simpleAnnotationList as a>\r\n	@${a}\r\n			</#list>\r\n			<#-- 父类对象 -->\r\n			<#if table.parentExists && table.parentTableFk == c.name>\r\n	private ${table.parent.className?cap_first} ${c.simpleJavaField};		<#if c.comments??>// ${c.comments} 父类</#if>\r\n			<#-- 其它字段 -->\r\n			<#else>\r\n    <#if c.showType == \"select\" || c.showType == \"checkbox\" || c.showType == \"radiobox\">\r\n	@ExcelField(title=\"<#if c.comments??>${c.comments}<#else>${c.simpleJavaField}</#if>\", dictType=\"${c.dictType}\", align=2, sort=${c_index})\r\n	<#elseif c.showType == \"userselect\" || c.showType == \"officeselect\" || c.showType == \"areaselect\"|| c.showType == \"treeselect\"|| c.showType == \"gridselect\">\r\n	@ExcelField(title=\"<#if c.comments??>${c.comments}<#else>${c.simpleJavaField}</#if>\", fieldType=${c.simpleJavaType}.class, value=\"${c.javaFieldName}\", align=2, sort=${c_index})\r\n	<#else>