INSERT INTO `plugin_gencode_template_obj` VALUES ('1445024813289598978', 'viewList.vue', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>viewList</name>\n    <filePath>src/views/${lastPackageName}/${moduleName}/${subModuleName}</filePath>\n    <fileName>${ClassName}List.vue</fileName>\n    <content><![CDATA[\n<template>\n  <div class=\"page\">\n      <el-form size=\"small\" :inline=\"true\" class=\"query-form\" ref=\"searchForm\" :model=\"searchForm\" @keyup.enter.native=\"refreshList()\" @submit.native.prevent>\n            <!-- 搜索框-->\n<#list table.columnList as c>\n            <#if c.isQuery?? && c.isQuery == \"1\">\n         <el-form-item prop=\"${c.javaFieldId}\">\n      <#if c.showType == \"input\" || c.showType == \"textarea\">\n                <el-input size=\"small\" v-model=\"searchForm.${c.javaFieldId}\" placeholder=\"${c.comments}\" clearable></el-input>\n      <#elseif c.showType == \"select\">\n                  <el-select size=\"small\" v-model=\"searchForm.${c.javaFieldId}\" placeholder=\"请选择${c.comments}\"  style=\"width: 100%;\">\n                    <el-option\n                      v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\"\n                      :key=\"item.value\"\n                      :label=\"item.label\"\n                      :value=\"item.value\">\n                    </el-option>\n                  </el-select>\n        <#elseif c.showType == \"checkbox\">\n                <el-checkbox-group\n                        v-model=\"${c.javaFieldId}List\">\n                       <el-checkbox v-for=\"${c.javaFieldId} in $dictUtils.getDictList(\'${c.dictType}\')\" :label=\"${c.javaFieldId}.value\" :key=\"${c.javaFieldId}.value\">{{${c.javaFieldId}.label}}</el-checkbox>\n                    </el-checkbox-group>\n        <#elseif c.showType == \"radiobox\">\n                  <el-radio-group v-model=\"searchForm.${c.javaFieldId}\">\n                    <el-radio v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\" :label=\"item.value\" :key=\"item.id\">{{item.label}}</el-radio>\n                  </el-radio-group>\n            <#elseif c.showType == \"dateselect\" && c.queryType == \"between\">\n               <el-date-picker\n                    v-model=\"searchForm.${c.javaFieldId}\"\n                    type=\"daterange\"\n                    size=\"small\"\n                    align=\"right\"\n                    value-format=\"yyyy-MM-dd hh:mm:ss\"\n                    unlink-panels\n                    range-separator=\"至\"\n                    start-placeholder=\"开始日期\"\n                    end-placeholder=\"结束日期\">\n                 </el-date-picker>\n        <#elseif c.showType == \"dateselect\">\n                <el-date-picker\n                  v-model=\"searchForm.${c.javaFieldId}\"\n                  type=\"datetime\"\n                  size=\"small\" \n                  value-format=\"yyyy-MM-dd HH:mm:ss\"\n                  placeholder=\"请选择${c.comments}\">\n                </el-date-picker>\n        <#elseif c.showType == \"userselect\">\n            <user-select :limit=\'1\' size=\"small\" placeholder=\"请选择${c.comments}\" :value=\"searchForm.${c.javaFieldId}\" @getValue=\'(value) => {searchForm.${c.javaFieldId}=value}\'></user-select>\n        <#elseif c.showType == \"officeselect\">\n            <SelectTree\n                  ref=\"${c.javaFieldId}\"\n                  :props=\"{\n                      value: \'id\',             // ID字段名\n                      label: \'name\',         // 显示名称\n                      children: \'children\'    // 子级字段名\n                    }\"\n                  size=\"small\" \n                  placeholder=\"请选择${c.comments}\"\n                  :url=\"`/sys/office/treeData?type=2`\"\n                  :value=\"searchForm.${c.javaFieldId}\"\n                  :clearable=\"true\"\n                  :accordion=\"true\"\n                  @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"areaselect\">\n                <SelectTree\n                  ref=\"${c.javaFieldId}\"\n                  :props=\"{\n                      value: \'id\',             // ID字段名\n                      label: \'name\',         // 显示名称\n                      children: \'children\'    // 子级字段名\n                    }\"\n                  size=\"small\"\n                  placeholder=\"请选择${c.comments}\"\n                  :url=\"/sys/area/treeData\"\n                  :value=\"searchForm.${c.javaFieldId}\"\n                  :clearable=\"true\"\n                  :accordion=\"true\"\n                  @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"cityselect\">\n                  <CityPicker\n                     style=\"width:100%\"\n                     size=\"small\" \n                     placeholder=\"请选择${c.comments}\"\n                    :value=\"searchForm.${c.javaFieldId}\"\n                    :clearable=\"true\"\n                    :accordion=\"true\"\n                    @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"treeselect\">\n            <SelectTree\n                  ref=\"${c.javaFieldId}\"\n                  :props=\"{\n                      value: \'id\',             // ID字段名\n                      label: \'name\',         // 显示名称\n                      children: \'children\'    // 子级字段名\n                    }\"\n                  size=\"small\" \n                  placeholder=\"请选择${c.comments}\"\n                  url=\"${c.dataUrl}/treeData\"\n                  :value=\"searchForm.${c.javaFieldId}\"\n                  :clearable=\"true\"\n                  :accordion=\"true\"\n                  @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"gridselect\">\n            <GridSelect\n                    title=\"选择${c.comments}\"\n                    placeholder=\"请选择${c.comments}\"\n                    labelName = \'${c.javaFieldName?split(\".\")[1]}\'\n                    labelValue = \'${c.javaFieldId?split(\".\")[1]}\'\n                    :value = \"searchForm.${c.javaFieldId}\"\n                    :limit=\"1\"\n                    size=\"small\" \n                    @getValue=\'(value) => {searchForm.${c.javaFieldId}=value}\'\n                    :columns=\"[\n            <#list c.fieldLabels?split(\"|\") as f>\n                    {\n                      prop: \'${c.fieldKeys?split(\"|\")[f_index]}\',\n                      label: \'${c.fieldLabels?split(\"|\")[f_index]}\'\n                    }<#if f_has_next>,</#if>\n            </#list>\n                    ]\"\n                    :searchs=\"[\n           <#list c.searchLabel?split(\"|\") as f>\n                    {\n                      prop: \'${c.searchKey?split(\"|\")[f_index]}\',\n                      label: \'${c.searchLabel?split(\"|\")[f_index]}\'\n                    }<#if f_has_next>,</#if>\n            </#list>\n                    ]\"\n                    dataListUrl=\"${c.dataUrl}/list\"\n                    entityBeanName=\"${c.dataUrl?split(\"/\")[c.dataUrl?split(\"/\")?size-1]}\"\n                    queryEntityUrl=\"${c.dataUrl}/queryById\">\n                  </GridSelect>\n        </#if>\n         </el-form-item>\n        </#if>\n    </#list>\n          <el-form-item>\n            <el-button type=\"primary\" @click=\"refreshList()\" size=\"small\" icon=\"el-icon-search\">查询</el-button>\n            <el-button @click=\"resetSearch()\" size=\"small\" icon=\"el-icon-refresh-right\">重置</el-button>\n          </el-form-item>\n      </el-form>\n        <!-- 导入导出-->\n        <el-dialog  title=\"导入Excel\" :visible.sync=\"isImportCollapse\">\n          <el-form :inline=\"true\" v-show=\"isImportCollapse\"  class=\"query-form\" ref=\"importForm\">\n             <el-form-item>\n              <el-button type=\"default\" @click=\"downloadTpl()\" size=\"small\">下载模板</el-button>\n             </el-form-item>\n             <el-form-item prop=\"loginName\">\n                <el-upload\n                  class=\"upload-demo\"\n                  :action=\"`${\"$\"}{this.$http.BASE_URL}/${urlPrefix}/import`\"\n                  :on-success=\"uploadSuccess\"\n                   :show-file-list=\"true\">\n                  <el-button size=\"small\" type=\"primary\">点击上传</el-button>\n                  <div slot=\"tip\" class=\"el-upload__tip\">只允许导入“xls”或“xlsx”格式文件！</div>\n                </el-upload>\n            </el-form-item>\n          </el-form>\n      </el-dialog>\n      <div class=\"bg-white top\">\n      <el-row>\n        <el-button v-if=\"hasPermission(\'${permissionPrefix}:add\')\" type=\"primary\" size=\"small\" icon=\"el-icon-plus\" @click=\"add()\">新建</el-button>\n        <el-button v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" type=\"warning\" size=\"small\" icon=\"el-icon-edit-outline\" @click=\"edit()\"\n         :disabled=\"dataListSelections.length != 1\" plain>修改</el-button>\n        <el-button v-if=\"hasPermission(\'${permissionPrefix}:del\')\" type=\"danger\"   size=\"small\" icon=\"el-icon-delete\" @click=\"del()\"\n                  :disabled=\"dataListSelections.length <= 0\" plain>删除\n        </el-button>\n        <el-button-group class=\"pull-right\">\n            <el-button v-if=\"hasPermission(\'${permissionPrefix}:import\')\" type=\"default\" size=\"small\" icon=\"el-icon-upload2\" title=\"导入\" @click=\"isImportCollapse = !isImportCollapse, isSearchCollapse=false\"></el-button>\n            <el-button v-if=\"hasPermission(\'${permissionPrefix}:export\')\" type=\"default\" size=\"small\" icon=\"el-icon-download\" title=\"导出\" @click=\"exportExcel()\"></el-button>\n            <el-button\n              type=\"default\"\n              size=\"small\"\n              icon=\"el-icon-refresh\"\n              @click=\"refreshList\">\n            </el-button>\n        </el-button-group>\n      </el-row>\n    <el-table\n      :data=\"dataList\"\n      @selection-change=\"selectionChangeHandle\"\n      @sort-change=\"sortChangeHandle\"\n      v-loading=\"loading\"\n      size=\"small\"\n      height=\"calc(100% - 80px)\"\n      @expand-change=\"detail\"\n      class=\"table\">\n      <el-table-column\n        type=\"selection\"\n        header-align=\"center\"\n        align=\"center\"\n        width=\"50\">\n      </el-table-column>\n      <el-table-column type=\"expand\">\n      <template slot-scope=\"scope\">\n      <el-tabs>\n        <#list table.childList as child>\n            <el-tab-pane label=\"${child.comments}\">\n                  <el-table\n                  size=\"small\"\n                  :data=\"scope.row.${child.className?uncap_first}List\"\n                  style=\"width: 100%\">\n                  <#assign childColumnCount = 0 />\n                <#list child.columnList as c>\n                    <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\') && c.name != c.genTable.parentTableFk>\n              <#if c.isList?? && c.isList == \"1\">\n          <#if c.showType == \"userselect\" || c.showType == \"officeselect\" || c.showType == \"areaselect\" || c.showType == \"treeselect\"|| c.showType == \"gridselect\">\n                  <el-table-column\n                    prop=\"${c.javaFieldName}\"\n                    label=\"${c.comments}\">\n                  </el-table-column>\n        <#elseif c.showType == \"select\">\n                <el-table-column\n                    prop=\"${c.javaFieldId}\"\n                    label=\"${c.comments}\">\n                    <template slot-scope=\"scope\">\n                        {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}\n                    </template>\n                  </el-table-column>\n      <#elseif c.showType == \"checkbox\">\n                <el-table-column\n                    prop=\"${c.javaFieldId}\"\n                    show-overflow-tooltip\n                    label=\"${c.comments}\">\n                    <template slot-scope=\"scope\">\n                        {{scope.row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}\n                    </template>\n                  </el-table-column>\n      <#elseif c.showType == \"radiobox\">\n                <el-table-column\n                    prop=\"${c.javaFieldId}\"\n                    show-overflow-tooltip\n                    label=\"${c.comments}\">\n                    <template slot-scope=\"scope\">\n                        {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}\n                    </template>\n                  </el-table-column>\n        <#elseif c.showType == \"fileselect\">\n      <el-table-column\n            prop=\"${c.javaFieldId}\"\n            show-overflow-tooltip\n            sortable=\"custom\"\n            label=\"${c.comments}\">\n            <template slot-scope=\"scope\" v-if=\"scope.row.${c.javaFieldId}\">\n                <a :href=\"item\" target=\"_blank\" :key=\"index\" v-for=\"(item, index) in scope.row.${c.javaFieldId}.split(\'|\')\">\n                    {{decodeURIComponent(item.substring(item.lastIndexOf(\"/\")+1))}}\n                </a>\n            </template>\n      </el-table-column>\n    <#elseif c.showType == \"imageselect\">\n      <el-table-column\n            prop=\"${c.javaFieldId}\"\n            show-overflow-tooltip\n            sortable=\"custom\"\n            label=\"${c.comments}\">\n            <template slot-scope=\"scope\" v-if=\"scope.row.${c.javaFieldId}\">\n              <el-image\n                style=\"height: 50px;width:50px;margin-right:10px;\"\n                :src=\"src\" v-for=\"(src, index) in scope.row.${c.javaFieldId}.split(\'|\')\" :key=\"index\"\n                :preview-src-list=\"scope.row.${c.javaFieldId}.split(\'|\')\">\n              </el-image>\n            </template>\n          </el-table-column>\n         <#elseif c.showType == \"wangeditor\" || c.showType == \"quilleditor\">\n                  <el-table-column\n                    prop=\"${c.javaFieldId}\"\n                    show-overflow-tooltip\n                    label=\"${c.comments}\">\n                    <template slot-scope=\"scope\">\n                      <p v-html=\"$utils.unescapeHTML(scope.row.${c.javaFieldId})\"></p>\n                    </template>\n                  </el-table-column>\n      <#else>\n                <el-table-column\n                    prop=\"${c.javaFieldId}\"\n                    show-overflow-tooltip\n                    label=\"${c.comments}\">\n                  </el-table-column>\n          </#if>\n                    </#if>\n                    </#if>\n                </#list>\n                </el-table>\n              </el-tab-pane>\n         </#list>\n            </el-tabs>\n      </template>\n      </el-table-column>\n      <#assign firstListField = true>\n      <#list table.columnList as c>\n        <#if c.isList?? && c.isList == \"1\">\n          <#if c.showType == \"userselect\" || c.showType == \"officeselect\" || c.showType == \"areaselect\" || c.showType == \"treeselect\"|| c.showType == \"gridselect\">\n      <el-table-column\n        prop=\"${c.javaFieldName}\"\n        show-overflow-tooltip\n        sortable=\"custom\"\n        label=\"${c.comments}\">\n      <#if firstListField>\n            <template slot-scope=\"scope\">\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\">{{scope.row.${c.javaFieldName}}}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\">{{scope.row.${c.javaFieldName}}}</el-link>\n              <span v-else>{{scope.row.${c.javaFieldName}}}</span>\n            </template>\n        </#if>\n      </el-table-column>\n        <#elseif c.showType == \"select\">\n    <el-table-column\n        prop=\"${c.javaFieldId}\"\n        show-overflow-tooltip\n        sortable=\"custom\"\n        label=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n        <#if firstListField>\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\"> {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\"> {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</el-link>\n              <span v-else>{{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</span>\n        <#else>\n              {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}\n        </#if>\n        </template>\n      </el-table-column>\n      <#elseif c.showType == \"checkbox\">\n    <el-table-column\n        prop=\"${c.javaFieldId}\"\n        show-overflow-tooltip\n        sortable=\"custom\"\n        label=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n       <#if firstListField>\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\">{{scope.row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\">{{scope.row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}</el-link>\n              <span v-else>{{scope.row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}</span>\n        <#else>\n              {{scope.row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}\n        </#if>\n        </template>\n      </el-table-column>\n      <#elseif c.showType == \"radiobox\">\n    <el-table-column\n        prop=\"${c.javaFieldId}\"\n        show-overflow-tooltip\n        sortable=\"custom\"\n        label=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n       <#if firstListField>\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\">{{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\">{{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</el-link>\n              <span v-else> {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</span>\n        <#else>\n             {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}\n        </#if>\n        </template>\n      </el-table-column>\n        <#elseif c.showType == \"fileselect\">\n    <el-table-column\n        prop=\"${c.javaFieldId}\"\n        show-overflow-tooltip\n        sortable=\"custom\"\n        label=\"${c.comments}\">\n        <template slot-scope=\"scope\" v-if=\"scope.row.${c.javaFieldId}\">\n            <a :href=\"item\" target=\"_blank\" :key=\"index\" v-for=\"(item, index) in scope.row.${c.javaFieldId}.split(\'|\')\">\n                {{decodeURIComponent(item.substring(item.lastIndexOf(\"/\")+1))}}\n            </a>\n        </template>\n      </el-table-column>\n              <#elseif c.showType == \"imageselect\">\n    <el-table-column\n        prop=\"${c.javaFieldId}\"\n        show-overflow-tooltip\n        sortable=\"custom\"\n        label=\"${c.comments}\">\n        <template slot-scope=\"scope\" v-if=\"scope.row.${c.javaFieldId}\">\n          <el-image\n            style=\"height: 50px;width:50px;margin-right:10px;\"\n            :src=\"src\" v-for=\"(src, index) in scope.row.${c.javaFieldId}.split(\'|\')\" :key=\"index\"\n            :preview-src-list=\"scope.row.${c.javaFieldId}.split(\'|\')\">\n          </el-image>\n        </template>\n      </el-table-column>\n        <#elseif c.showType == \"wangeditor\" || c.showType == \"quilleditor\">\n      <el-table-column\n        prop=\"${c.javaFieldId}\"\n        show-overflow-tooltip\n        sortable=\"custom\"\n        label=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n            <p v-html=\"$utils.unescapeHTML(scope.row.${c.javaFieldId})\"></p>\n        </template>\n      </el-table-column>\n      <#else>\n    <el-table-column\n        prop=\"${c.javaFieldId}\"\n        show-overflow-tooltip\n        sortable=\"custom\"\n        label=\"${c.comments}\">\n        <#if firstListField>\n            <template slot-scope=\"scope\">\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\">{{scope.row.${c.javaFieldId}}}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\">{{scope.row.${c.javaFieldId}}}</el-link>\n              <span v-else>{{scope.row.${c.javaFieldId}}}</span>\n            </template>\n        </#if>\n      </el-table-column>\n          </#if>\n        <#assign firstListField = false>\n      </#if>\n      </#list>\n      <el-table-column\n        header-align=\"center\"\n        align=\"center\"\n        fixed=\"right\"\n        :key=\"Math.random()\"\n        width=\"200\"\n        label=\"操作\">\n        <template  slot-scope=\"scope\">\n          <el-button v-if=\"hasPermission(\'${permissionPrefix}:view\')\" type=\"text\" icon=\"el-icon-view\" size=\"small\" @click=\"view(scope.row.id)\">查看</el-button>\n          <el-button v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" type=\"text\" icon=\"el-icon-edit\" size=\"small\" @click=\"edit(scope.row.id)\">修改</el-button>\n          <el-button v-if=\"hasPermission(\'${permissionPrefix}:del\')\" type=\"text\"  icon=\"el-icon-delete\" size=\"small\" @click=\"del(scope.row.id)\">删除</el-button>\n        </template>\n      </el-table-column>\n    </el-table>\n    <el-pagination\n      @size-change=\"sizeChangeHandle\"\n      @current-change=\"currentChangeHandle\"\n      :current-page=\"pageNo\"\n      :page-sizes=\"[10, 20, 50, 100]\"\n      :page-size=\"pageSize\"\n      :total=\"total\"\n      background\n      layout=\"total, sizes, prev, pager, next, jumper\">\n    </el-pagination>\n    </div>\n        <!-- 弹窗, 新增 / 修改 -->\n    <${ClassName}Form  ref=\"${className}Form\" @refreshDataList=\"refreshList\"></${ClassName}Form>\n  </div>\n</template>\n\n<script>\n  import ${ClassName}Form from \'./${ClassName}Form\'\n<#list table.importQueryList as c>\n  ${c}\n</#list>\n  export default {\n    data () {\n      return {\n        searchForm: {\n        <#list table.queryList as c>\n          <#if c.javaFieldId?contains(\".\")>\n          ${c.javaFieldId?split(\".\")[0]}: {\n            ${c.javaFieldId?split(\".\")[1]}: \'\'\n          }<#if c_has_next>,</#if>\n          <#else>\n          ${c.javaFieldId}: \'\'<#if c_has_next>,</#if>\n          </#if>\n    </#list>\n        },\n        dataList: [],\n        pageNo: 1,\n        pageSize: 10,\n        total: 0,\n        orderBy: \'\',\n        dataListSelections: [],\n        isImportCollapse: false,\n        loading: false\n      }\n    },\n    components: {\n      <#list table.queryComponents as c>\n      ${c},\n    </#list>\n      ${ClassName}Form\n    },\n    activated () {\n      this.refreshList()\n    },\n    <#assign cks=0/>\n    <#list table.columnList as c>\n    <#if c.isQuery?? && c.isQuery == \"1\">\n     <#if c.showType == \"checkbox\">\n     <#assign cks= cks + 1/>\n    </#if>\n    </#if>\n    </#list>\n    <#if cks gt 0>\n    computed: {\n    <#assign ckIndex=0/>\n    <#list table.columnList as c>\n    <#if c.isQuery?? && c.isQuery == \"1\">\n     <#if c.showType == \"checkbox\">\n     <#assign ckIndex= ckIndex + 1/>\n      ${c.javaFieldId}List: {\n        get: function () {\n          return this.searchForm.${c.javaFieldId} !== \'\' ? this.searchForm.${c.javaFieldId}.split(\',\') : []\n        },\n        set: function (val) {\n          this.searchForm.${c.javaFieldId} = val.join(\',\')\n        }\n      }<#if ckIndex lt cks >,</#if>\n    </#if>\n    </#if>\n    </#list>\n    },\n    </#if>\n    methods: {\n      // 获取数据列表\n      refreshList () {\n        this.loading = true\n        this.$http({\n          url: \'/${urlPrefix}/list\',\n          method: \'get\',\n          params: {\n            \'pageNo\': this.pageNo,\n            \'pageSize\': this.pageSize,\n            \'orderBy\': this.orderBy,\n            <#if (table.queryBetwinDate?size > 0)>\n            <#list table.queryBetwinDate as c>\n            begin${c.javaFieldId?cap_first}: this.searchForm.${c.javaFieldId}[0],\n            end${c.javaFieldId?cap_first}: this.searchForm.${c.javaFieldId}[1],\n            </#list>\n            ...this.lodash.omit(this.searchForm, <#list table.queryBetwinDate as c>\'${c.javaFieldId}\'<#if c_has_next>, </#if></#list>)\n            <#else>\n            ...this.searchForm\n            </#if>\n          }\n        }).then(({data}) => {\n          if (data && data.success) {\n            this.dataList = data.page.list\n            this.total = data.page.count\n            this.loading = false\n          }\n        })\n      },\n      // 每页数\n      sizeChangeHandle (val) {\n        this.pageSize = val\n        this.pageNo = 1\n        this.refreshList()\n      },\n      // 当前页\n      currentChangeHandle (val) {\n        this.pageNo = val\n        this.refreshList()\n      },\n      // 多选\n      selectionChangeHandle (val) {\n        this.dataListSelections = val\n      },\n\n    // 排序\n      sortChangeHandle (obj) {\n        if (obj.order === \'ascending\') {\n          this.orderBy = obj.prop + \' asc\'\n        } else if (obj.order === \'descending\') {\n          this.orderBy = obj.prop + \' desc\'\n        } else {\n          this.orderBy = \'\'\n        }\n        this.refreshList()\n      },\n      // 新增\n      add () {\n        this.$refs.${className}Form.init(\'add\', \'\')\n      },\n      // 修改\n      edit (id) {\n        id = id || this.dataListSelections.map(item => {\n          return item.id\n        })[0]\n        this.$refs.${className}Form.init(\'edit\', id)\n      },\n      // 查看\n      view (id) {\n        this.$refs.${className}Form.init(\'view\', id)\n      },\n      // 删除\n      del (id) {\n        let ids = id || this.dataListSelections.map(item => {\n          return item.id\n        }).join(\',\')\n        this.$confirm(`确定删除所选项吗?`, \'提示\', {\n          confirmButtonText: \'确定\',\n          cancelButtonText: \'取消\',\n          type: \'warning\'\n        }).then(() => {\n          this.loading = true\n          this.$http({\n            url: \'/${urlPrefix}/delete\',\n            method: \'delete\',\n            params: {\'ids\': ids}\n          }).then(({data}) => {\n            if (data && data.success) {\n              this.$message.success(data.msg)\n              this.refreshList()\n            }\n            this.loading = false\n          })\n        })\n      },\n     // 查看详情\n      detail (row) {\n        this.$http.get(`/${urlPrefix}/queryById?id=${\"$\"}{row.id}`).then(({data}) => {\n          this.dataList.forEach((item, index) => {\n            if (item.id === row.id) {\n              <#list table.childList as child>\n              item.${child.className?uncap_first}List = data.${className}.${child.className?uncap_first}List\n              </#list>\n            }\n          })\n        })\n      },\n      // 导入成功\n      uploadSuccess (res, file) {\n        if (res.success) {\n          this.$message.success({dangerouslyUseHTMLString: true,\n            message: res.msg})\n        } else {\n          this.$message.error(res.msg)\n        }\n      },\n      // 下载模板\n      downloadTpl () {\n        this.$utils.download(\'/${urlPrefix}/import/template\')\n      },\n      exportExcel () {\n        let params = {\n            <#if (table.queryBetwinDate?size > 0)>\n            <#list table.queryBetwinDate as c>\n          begin${c.javaFieldId?cap_first}: this.searchForm.${c.javaFieldId}[0],\n          end${c.javaFieldId?cap_first}: this.searchForm.${c.javaFieldId}[1],\n            </#list>\n          ...this.lodash.omit(this.searchForm, <#list table.queryBetwinDate as c>\'${c.javaFieldId}\'<#if c_has_next>, </#if></#list>)\n            <#else>\n          ...this.searchForm\n            </#if>\n        }\n        this.$utils.download(\'/${urlPrefix}/export\', params)\n      },\n      resetSearch () {\n        this.$refs.searchForm.resetFields()\n        this.refreshList()\n      }\n    }\n  }\n</script>\n]]>\n    </content>\n</template>', '', 0, 0, '0', NULL, '1', '2021-10-04 21:55:37', '1', '2021-10-04 21:55:37');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1445024813339930625', 'mapper.xml', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>mapper</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/mapper/xml</filePath>\n    <fileName>${ClassName}Mapper.xml</fileName>\n    <content><![CDATA[\n<?xml version=\"1.0\" encoding=\"UTF-8\" ?>\n<!DOCTYPE mapper PUBLIC \"-//mybatis.org//DTD Mapper 3.0//EN\" \"http://mybatis.org/dtd/mybatis-3-mapper.dtd\">\n<mapper namespace=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper.${ClassName}Mapper\">\n\n    <#-- 输出字段列 -->\n	<sql id=\"${className}Columns\">\n		<#assign columnField>\n			<#list table.columnList as c>\n		a.${c.name} AS \"${c.javaFieldId}\",\n			</#list>\n			<#list table.columnList as c>\n			<#-- 父表关联字段 -->\n				<#if table.parentExists && table.parentTableFk == c.name>\n					<#list c.javaFieldAttrs as a>\n		b.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"userselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"officeselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"areaselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"treeselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"gridselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n				</#list>\n				</#if>\n\n			</#list>\n		</#assign>\n${columnField?substring(0, columnField?last_index_of(\",\"))}\n	</sql>\n\n	<#-- 输出字段关联表 -->\n	<sql id=\"${className}Joins\">\n\n		<#-- 关联系统表 -->\n		<#list table.columnList as c>\n		<#-- 关联父表 -->\n		<#if table.parentExists && table.parentTableFk == c.name>\n			LEFT JOIN ${table.parent.name} b ON b.id = a.${table.parentTableFk}\n			<#elseif c.showType?? && c.showType == \"userselect\">\n		LEFT JOIN sys_user ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"officeselect\">\n		LEFT JOIN sys_office ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"areaselect\">\n		LEFT JOIN sys_area ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"treeselect\" && c.javaType != \"This\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"gridselect\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			</#if>\n		</#list>\n	</sql>\n\n    <#assign x=0 />\n    <#list table.columnList as c>\n			<#if c.showType?? && c.showType == \"umeditor\">\n			<#assign x=1 />\n    <resultMap type=\"${ClassName}\" id=\"${ClassName}Result\" autoMapping=\"true\">\n			<#break/>\n			</#if>\n	</#list>\n	<#list table.columnList as c>\n			<#if c.showType?? && c.showType == \"umeditor\">\n			<#assign x=1 />\n    	<result column=\"${c.name}\" property=\"${c.simpleJavaField}\"   typeHandler=\"cn.weegoo.core.mapper.ConvertBlobTypeHandler\"/>\n			</#if>\n	</#list>\n	<#list table.columnList as c>\n			<#if c.showType?? && c.showType == \"umeditor\">\n			<#assign x=1 />\n    </resultMap>\n			<#break/>\n			</#if>\n	</#list>\n\n	<select id=\"get\" <#if x==0>resultType=\"${ClassName}\"</#if><#if x==1>resultMap=\"${ClassName}Result\"</#if> >\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		WHERE a.id = ${\"#\"}{id}\n	</select>\n\n	<select id=\"findList\" <#if x==0>resultType=\"${ClassName}\"</#if><#if x==1>resultMap=\"${ClassName}Result\"</#if> >\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		<where>\n			<#if table.delFlagExists>a.del_flag = ${\"#\"}{DEL_FLAG_NORMAL}</#if>\n			${\"$\"}{dataScope}\n			<#list table.columnList as c>\n				<#if (c.isQuery?? && c.isQuery == \"1\") || (table.parentExists && table.parentTableFk == c.name)>\n					<#if c.queryType ?? && c.queryType == \'between\'>\n			<if test=\"begin${c.simpleJavaField?cap_first} != null and end${c.simpleJavaField?cap_first} != null <#if c.simpleJavaField != c.javaFieldId>and begin${c.javaFieldId?cap_first} != null and end${c.javaFieldId?cap_first} != null </#if>\">\n					<#else>\n			<if test=\"${c.simpleJavaField} != null<#if c.simpleJavaField != c.javaFieldId> and ${c.javaFieldId} != null</#if> and ${c.javaFieldId} != \'\'\">\n					</#if>\n					<#if c.queryType ?? && c.queryType == \'between\'>\n				AND a.${c.name} BETWEEN ${\"#\"}{begin${c.simpleJavaField?cap_first}} AND ${\"#\"}{end${c.simpleJavaField?cap_first}}\n					<#elseif c.queryType ?? && c.queryType == \'like\'>\n				AND a.${c.name} LIKE\n				    <if test=\"_databaseId == \'postgre\'\">\'%\'||${\"#\"}{${c.javaFieldId}}||\'%\'</if>\n					<if test=\"_databaseId == \'oracle\'\">\'%\'||${\"#\"}{${c.javaFieldId}}||\'%\'</if>\n					<if test=\"_databaseId == \'mssql\'\">\'%\'+${\"#\"}{${c.javaFieldId}}+\'%\'</if>\n					<if test=\"_databaseId == \'mysql\'\">concat(\'%\',${\"#\"}{${c.javaFieldId}},\'%\')</if>\n					<#elseif c.queryType ?? && c.queryType == \'left_like\'>\n				AND a.${c.name} LIKE\n				    <if test=\"_databaseId == \'postgre\'\">\'%\'||${\"#\"}{${c.javaFieldId}}</if>\n					<if test=\"_databaseId == \'oracle\'\">\'%\'||${\"#\"}{${c.javaFieldId}}</if>\n					<if test=\"_databaseId == \'mssql\'\">\'%\'+${\"#\"}{${c.javaFieldId}}</if>\n					<if test=\"_databaseId == \'mysql\'\">concat(\'%\',${\"#\"}{${c.javaFieldId}})</if>\n					<#elseif c.queryType ?? && c.queryType == \'right_like\'>\n				AND a.${c.name} LIKE\n				    <if test=\"_databaseId == \'postgre\'\">${\"#\"}{${c.javaFieldId}}||\'%\'</if>\n					<if test=\"_databaseId == \'oracle\'\">${\"#\"}{${c.javaFieldId}}||\'%\'</if>\n					<if test=\"_databaseId == \'mssql\'\">${\"#\"}{${c.javaFieldId}}+\'%\'</if>\n					<if test=\"_databaseId == \'mysql\'\">concat(${\"#\"}{${c.javaFieldId}},\'%\')</if>\n					<#else>\n				AND a.${c.name} ${c.queryType} ${\"#\"}{${c.javaFieldId}}\n					</#if>\n			</if>\n				</#if>\n			</#list>\n		</where>\n		<choose>\n			<when test=\"page !=null and page.orderBy != null and page.orderBy != \'\'\">\n				ORDER BY ${\"$\"}{page.orderBy}\n			</when>\n			<otherwise>\n				<#if table.parentExists>\n					<#if table.createDateExists>\n				ORDER BY a.create_date ASC\n					</#if>\n				<#else>\n					<#if table.updateDateExists>\n				ORDER BY a.update_date DESC\n					</#if>\n				</#if>\n			</otherwise>\n		</choose>\n	</select>\n\n	<select id=\"findAllList\" <#if x==0>resultType=\"${ClassName}\"</#if><#if x==1>resultMap=\"${ClassName}Result\"</#if> >\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		<where>\n			<#if table.delFlagExists>a.del_flag = ${\"#\"}{DEL_FLAG_NORMAL}</#if>\n			${\"$\"}{dataScope}\n		</where>\n		<choose>\n			<when test=\"page !=null and page.orderBy != null and page.orderBy != \'\'\">\n				ORDER BY ${\"$\"}{page.orderBy}\n			</when>\n			<otherwise>\n				<#if table.parentExists>\n					<#if table.createDateExists>\n				ORDER BY a.create_date ASC\n					</#if>\n				<#else>\n					<#if table.updateDateExists>\n				ORDER BY a.update_date DESC\n					</#if>\n				</#if>\n			</otherwise>\n		</choose>\n	</select>\n\n	<insert id=\"insert\">\n		INSERT INTO ${table.name}(\n		<#assign insertField>\n			<#list table.columnList as c>\n				<#if c.isInsert?? && c.isInsert == \"1\">\n				<#if !(table.genIdType ==\"2\" && c.name==\'id\') >\n			${c.name},\n				</#if>\n				</#if>\n			</#list>\n		</#assign>\n${insertField?substring(0, insertField?last_index_of(\",\"))}\n		) VALUES (\n		<#assign insertJavaField>\n			<#list table.columnList as c>\n				<#if c.isInsert?? && c.isInsert == \"1\">\n				<#if !(table.genIdType ==\"2\" && c.name==\'id\') >\n			   <#if c.showType == \"umeditor\">\n			${\"#\"}{${c.javaFieldId}, jdbcType=BLOB},\n			    <#else>\n			${\"#\"}{${c.javaFieldId}},\n				</#if>\n				</#if>\n				</#if>\n			</#list>\n		</#assign>\n${insertJavaField?substring(0, insertJavaField?last_index_of(\",\"))}\n		)\n	</insert>\n\n	<update id=\"update\">\n		UPDATE ${table.name} SET\n			<#assign updateField>\n				<#list table.columnList as c>\n					<#if c.isEdit?? && c.isEdit == \"1\">\n			     <#if c.showType == \"umeditor\">\n			${c.name} = ${\"#\"}{${c.javaFieldId}, jdbcType=BLOB},\n			    <#else>\n			${c.name} = ${\"#\"}{${c.javaFieldId}},\n				</#if>\n					</#if>\n				</#list>\n			</#assign>\n${updateField?substring(0, updateField?last_index_of(\",\"))}\n		WHERE id = ${\"#\"}{id}\n	</update>\n\n\n	<!--物理删除-->\n	<update id=\"delete\">\n		DELETE FROM ${table.name}\n	<#if table.parentExists>\n		<#list table.columnList as c>\n			<#if table.parentTableFk == c.name>\n		<choose>\n			<when test=\"id !=null and id != \'\'\">\n				WHERE id = ${\"#\"}{id}\n			</when>\n			<otherwise>\n				WHERE ${table.parentTableFk} = ${\"#\"}{${c.javaFieldId}}\n			</otherwise>\n		</choose>\n			</#if>\n		</#list>\n	<#else>\n		WHERE id = ${\"#\"}{id}\n	</#if>\n	</update>\n\n	<!--逻辑删除-->\n	<update id=\"deleteByLogic\">\n		UPDATE ${table.name} SET\n			del_flag = ${\"#\"}{DEL_FLAG_DELETE}\n	<#if table.parentExists>\n		<#list table.columnList as c>\n			<#if table.parentTableFk == c.name>\n		<choose>\n			<when test=\"id !=null and id != \'\'\">\n				WHERE id = ${\"#\"}{id}\n			</when>\n			<otherwise>\n				WHERE ${table.parentTableFk} = ${\"#\"}{${c.javaFieldId}}\n			</otherwise>\n		</choose>\n			</#if>\n		</#list>\n	<#else>\n		WHERE id = ${\"#\"}{id}\n	</#if>\n	</update>\n\n\n	<!-- 根据实体名称和字段名称和字段值获取唯一记录 -->\n	<select id=\"findUniqueByProperty\" resultType=\"${ClassName}\">\n		select * FROM ${table.name}  where ${\"$\"}{propertyName} = ${\"#\"}{value}\n	</select>\n\n</mapper>]]>\n    </content>\n</template>', '', 0, 0, '0', NULL, '1', '2021-10-04 21:55:37', '1', '2021-10-04 21:55:37');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1445024996291276802', 'viewList.vue', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>viewList</name>\n    <filePath>src/views/${lastPackageName}/${moduleName}/${subModuleName}</filePath>\n    <fileName>${ClassName}List.vue</fileName>\n    <content><![CDATA[\n<template>\n    <div class=\"page\">\n      <el-form size=\"small\" :inline=\"true\" class=\"query-form\" ref=\"searchForm\" :model=\"searchForm\" @keyup.enter.native=\"refreshList()\" @submit.native.prevent>\n            <!-- 搜索框-->\n<#list table.columnList as c>\n            <#if c.isQuery?? && c.isQuery == \"1\">\n         <el-form-item prop=\"${c.javaFieldId}\">\n      <#if c.showType == \"input\" || c.showType == \"textarea\">\n                <el-input size=\"small\" v-model=\"searchForm.${c.javaFieldId}\" placeholder=\"${c.comments}\" clearable></el-input>\n      <#elseif c.showType == \"select\">\n                  <el-select v-model=\"searchForm.${c.javaFieldId}\" placeholder=\"请选择${c.comments}\" size=\"small\" style=\"width: 100%;\">\n                    <el-option\n                      v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\"\n                      :key=\"item.value\"\n                      :title=\"item.label\"\n                      :value=\"item.value\">\n                    </el-option>\n                  </el-select>\n        <#elseif c.showType == \"checkbox\">\n                <el-checkbox-group\n                        v-model=\"${c.javaFieldId}List\">\n                       <el-checkbox v-for=\"${c.javaFieldId} in $dictUtils.getDictList(\'${c.dictType}\')\" :title=\"${c.javaFieldId}.value\" :key=\"${c.javaFieldId}.value\">{{${c.javaFieldId}.label}}</el-checkbox>\n                    </el-checkbox-group>\n        <#elseif c.showType == \"radiobox\">\n                  <el-radio-group v-model=\"searchForm.${c.javaFieldId}\">\n                    <el-radio v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\" :title=\"item.value\" :key=\"item.value\">{{item.label}}</el-radio>\n                  </el-radio-group>\n            <#elseif c.showType == \"dateselect\" && c.queryType == \"between\">\n               <el-date-picker\n                    v-model=\"searchForm.${c.javaFieldId}\"\n                    type=\"daterange\"\n                    size=\"small\"\n                    align=\"right\"\n                    value-format=\"yyyy-MM-dd hh:mm:ss\"\n                    unlink-panels\n                    range-separator=\"至\"\n                    start-placeholder=\"开始日期\"\n                    end-placeholder=\"结束日期\">\n                 </el-date-picker>\n        <#elseif c.showType == \"dateselect\">\n                <el-date-picker\n                  v-model=\"searchForm.${c.javaFieldId}\"\n                  type=\"datetime\"\n                  size=\"small\"\n                  value-format=\"yyyy-MM-dd HH:mm:ss\"\n                  placeholder=\"请选择${c.comments}\">\n                </el-date-picker>\n        <#elseif c.showType == \"userselect\">\n            <user-select :limit=\'1\' size=\"small\" placeholder=\"请选择${c.comments}\" :value=\"searchForm.${c.javaFieldId}\" @getValue=\'(value) => {searchForm.${c.javaFieldId}=value}\'></user-select>\n        <#elseif c.showType == \"officeselect\">\n            <SelectTree\n                  ref=\"${c.javaFieldId}\"\n                  :props=\"{\n                      value: \'id\',             // ID字段名\n                      label: \'name\',         // 显示名称\n                      children: \'children\'    // 子级字段名\n                    }\"\n                  placeholder=\"请选择${c.comments}\"\n                  size=\"small\"\n                  :url=\"`/sys/office/treeData?type=2`\"\n                  :value=\"searchForm.${c.javaFieldId}\"\n                  :clearable=\"true\"\n                  :accordion=\"true\"\n                  @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"areaselect\">\n                <SelectTree\n                  ref=\"${c.javaFieldId}\"\n                  :props=\"{\n                      value: \'id\',             // ID字段名\n                      label: \'name\',         // 显示名称\n                      children: \'children\'    // 子级字段名\n                    }\"\n                  placeholder=\"请选择${c.comments}\"\n                  size=\"small\"\n                  :url=\"/sys/area/treeData\"\n                  :value=\"searchForm.${c.javaFieldId}\"\n                  :clearable=\"true\"\n                  :accordion=\"true\"\n                  @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"cityselect\">\n                  <CityPicker \n                    style=\"width:100%\"\n                    size=\"small\"\n                    placeholder=\"选择${c.comments}\"\n                    :value=\"searchForm.${c.javaFieldId}\"\n                    :clearable=\"true\" \n                    :accordion=\"true\"\n                    @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"treeselect\">\n            <SelectTree\n                  ref=\"${c.javaFieldId}\"\n                  :props=\"{\n                      value: \'id\',             // ID字段名\n                      label: \'name\',         // 显示名称\n                      children: \'children\'    // 子级字段名\n                    }\"\n                  placeholder=\"请选择${c.comments}\"\n                  size=\"small\"\n                  url=\"${c.dataUrl}/treeData\"\n                  :value=\"searchForm.${c.javaFieldId}\"\n                  :clearable=\"true\"\n                  :accordion=\"true\"\n                  @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"gridselect\">\n            <GridSelect\n                    title=\"选择${c.comments}\"\n                    placeholder=\"请选择${c.comments}\"\n