n                 ]\">\n        <#if c.simpleJavaField == \'parent\'>\n                <SelectTree\n                      ref=\"${c.simpleJavaField}\"\n                      :props=\"{\n                          value: \'id\',             // ID字段名\n                          label: \'name\',         // 显示名称\n                          children: \'children\'    // 子级字段名\n                        }\"\n                      v-if=\"visible\"\n                      :url=\"`/${urlPrefix}/treeData?extId=${\"$\"}{inputForm.id}`\"\n                      :value=\"inputForm.${c.javaFieldId}\"\n                      :clearable=\"true\"\n                      :accordion=\"true\"\n                      @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"input\">\n              <el-input v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"请填写${c.comments}\" <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> <#if  c.minLength?? && c.minLength != \"\"> minlength=\"${c.minLength}\"</#if> <#if c.maxValue?? && c.maxValue != \"\"> max=\"${c.maxValue}\"</#if> <#if c.minValue?? && c.minValue != \"\"> min=\"${c.minValue}\"</#if> ></el-input>\n    <#elseif c.showType == \"textarea\">\n          <el-input type=\"textarea\" v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"请填写${c.comments}\" <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> <#if  c.minLength?? && c.minLength != \"\"> minlength=\"${c.minLength}\"</#if> <#if c.maxValue?? && c.maxValue != \"\"> max=\"${c.maxValue}\"</#if> <#if c.minValue?? && c.minValue != \"\"> min=\"${c.minValue}\"</#if> ></el-input>\n    <#elseif c.showType == \"select\">\n                <el-select v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"请选择\"  style=\"width: 100%;\">\n                          <el-option\n                            v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\"\n                            :key=\"item.value\"\n                            :label=\"item.label\"\n                            :value=\"item.value\">\n                          </el-option>\n                      </el-select>\n    <#elseif c.showType == \"checkbox\">\n                <el-checkbox-group\n                        v-model=\"${c.javaFieldId}List\">\n                       <el-checkbox v-for=\"${c.javaFieldId} in $dictUtils.getDictList(\'${c.dictType}\')\" :label=\"${c.javaFieldId}.value\" :key=\"${c.javaFieldId}.value\">{{${c.javaFieldId}.label}}</el-checkbox>\n                    </el-checkbox-group>\n    <#elseif c.showType == \"radiobox\">\n                    <el-radio-group v-model=\"inputForm.${c.javaFieldId}\">\n                        <el-radio v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\" :label=\"item.value\" :key=\"item.value\">{{item.label}}</el-radio>\n                    </el-radio-group>\n    <#elseif c.showType == \"dateselect\">\n                <el-date-picker\n                      v-model=\"inputForm.${c.javaFieldId}\"\n                      type=\"datetime\"\n                      style=\"width: 100%;\"\n                      value-format=\"yyyy-MM-dd HH:mm:ss\"\n                      placeholder=\"选择日期时间\">\n                    </el-date-picker>\n    <#elseif c.showType == \"userselect\">\n                <user-select :limit=\'1\' :value=\"inputForm.${c.javaFieldId}\" @getValue=\'(value) => {inputForm.${c.javaFieldId}=value}\'></user-select>\n    <#elseif c.showType == \"officeselect\">\n          <SelectTree\n                      ref=\"${c.simpleJavaField}\"\n                      :props=\"{\n                          value: \'id\',             // ID字段名\n                          label: \'name\',         // 显示名称\n                          children: \'children\'    // 子级字段名\n                        }\"\n\n                      url=\"/sys/office/treeData?type=2\"\n                      :value=\"inputForm.${c.javaFieldId}\"\n                      :clearable=\"true\"\n                      :accordion=\"true\"\n                      @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"areaselect\">\n              <SelectTree\n                      ref=\"${c.simpleJavaField}\"\n                      :props=\"{\n                          value: \'id\',             // ID字段名\n                          label: \'name\',         // 显示名称\n                          children: \'children\'    // 子级字段名\n                        }\"\n\n                      url=\"/sys/area/treeData\"\n                      :value=\"inputForm.${c.javaFieldId}\"\n                      :clearable=\"true\"\n                      :accordion=\"true\"\n                      @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"cityselect\">\n                      <CityPicker\n                         style=\"width:100%\"\n                        :value=\"inputForm.${c.javaFieldId}\"\n                        :clearable=\"true\"\n                        :accordion=\"true\"\n                        @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"fileselect\">\n              <el-upload ref=\"${c.javaFieldId}\"\n                    v-if=\"visible\"\n                    :action=\"`${\"$\"}{this.${\"$\"}http.BASE_URL}/sys/file/webupload/upload?uploadPath=/${moduleName}<#if subModuleName != \"\">/${subModuleName}</#if>/${className}`\"\n                    :headers=\"{token: ${\"$\"}cookie.get(\'token\')}\"\n                    :on-preview=\"(file, fileList) => {$window.location.href = (file.response && file.response.url) || file.url}\"\n                    :on-success=\"(response, file, fileList) => {\n                       inputForm.${c.javaFieldId} = fileList.map(item => (item.response && item.response.url) || item.url).join(\'|\')\n                    }\"\n                    :on-remove=\"(file, fileList) => {\n                      ${\"$\"}http.post(`/sys/file/webupload/deleteByUrl?url=${\"$\"}{(file.response && file.response.url) || file.url}`).then(({data}) => {\n                        ${\"$\"}message.success(data.msg)\n                      })\n                      inputForm.${c.javaFieldId} = fileList.map(item => item.url).join(\'|\')\n                    }\"\n                    :before-remove=\"(file, fileList) => {\n                      return ${\"$\"}confirm(`确定移除 ${\"$\"}{file.name}？`)\n                    }\"\n                    multiple\n                    :limit=\"5\"\n                    :on-exceed=\"(files, fileList) =>{\n                      $message.warning(`当前限制选择 5 个文件，本次选择了 ${\"$\"}{files.length} 个文件，共选择了 ${\"$\"}{files.length + fileList.length} 个文件`)\n                    }\"\n                    :file-list=\"${c.javaFieldId}Arra\">\n                    <el-button size=\"small\" type=\"primary\">点击上传</el-button>\n                    <div slot=\"tip\" class=\"el-upload__tip\">添加相关附件</div>\n                  </el-upload>\n<#elseif c.showType == \"imageselect\">\n              <el-upload ref=\"${c.javaFieldId}\"\n              v-if=\"visible\"\n              list-type=\"picture-card\"\n                    :action=\"`${\"$\"}{this.${\"$\"}http.BASE_URL}/sys/file/webupload/upload?uploadPath=/${moduleName}<#if subModuleName != \"\">/${subModuleName}</#if>/${className}`\"\n                    :headers=\"{token: ${\"$\"}cookie.get(\'token\')}\"\n                    :on-preview=\"(file, fileList) => {\n                        $alert(`<img style=\'width:100%\' src=\' ${\"$\"}{(file.response && file.response.url) || file.url}\'/>`,  {\n                          dangerouslyUseHTMLString: true,\n                          showConfirmButton: false,\n                          closeOnClickModal: true,\n                          customClass: \'showPic\'\n                        });\n                    }\"\n                    :on-success=\"(response, file, fileList) => {\n                       inputForm.${c.javaFieldId} = fileList.map(item => (item.response && item.response.url) || item.url).join(\'|\')\n                    }\"\n                    :on-remove=\"(file, fileList) => {\n                      ${\"$\"}http.post(`/sys/file/webupload/deleteByUrl?url=${\"$\"}{(file.response && file.response.url) || file.url}`).then(({data}) => {\n                        ${\"$\"}message.success(data.msg)\n                      })\n                      inputForm.${c.javaFieldId} = fileList.map(item => item.url).join(\'|\')\n                    }\"\n                    :before-remove=\"(file, fileList) => {\n                      return ${\"$\"}confirm(`确定移除 ${\"$\"}{file.name}？`)\n                    }\"\n                    multiple\n                    :limit=\"5\"\n                    :on-exceed=\"(files, fileList) =>{\n                      $message.warning(`当前限制选择 5 个文件，本次选择了 ${\"$\"}{files.length} 个文件，共选择了 ${\"$\"}{files.length + fileList.length} 个文件`)\n                    }\"\n                    :file-list=\"${c.javaFieldId}Arra\">\n                    <i class=\"el-icon-plus\"></i>\n                  </el-upload>\n    <#elseif c.showType == \"treeselect\">\n          <SelectTree\n                      ref=\"${c.simpleJavaField}\"\n                      :props=\"{\n                          value: \'id\',             // ID字段名\n                          label: \'name\',         // 显示名称\n                          children: \'children\'    // 子级字段名\n                        }\"\n                      url=\"${c.dataUrl}/treeData\"\n                      :value=\"inputForm.${c.javaFieldId}\"\n                      :clearable=\"true\"\n                      :accordion=\"true\"\n                      @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"gridselect\">\n          <GridSelect\n            title=\"选择${c.comments}\"\n            labelName = \'${c.javaFieldName?split(\".\")[1]}\'\n            labelValue = \'${c.javaFieldId?split(\".\")[1]}\'\n            :value = \"inputForm.${c.javaFieldId}\"\n            :limit=\"1\"\n            @getValue=\'(value) => {inputForm.${c.javaFieldId}=value}\'\n            :columns=\"[\n    <#list c.fieldLabels?split(\"|\") as f>\n            {\n              prop: \'${c.fieldKeys?split(\"|\")[f_index]}\',\n              label: \'${c.fieldLabels?split(\"|\")[f_index]}\'\n            }<#if f_has_next>,</#if>\n    </#list>\n            ]\"\n            :searchs=\"[\n   <#list c.searchLabel?split(\"|\") as f>\n            {\n              prop: \'${c.searchKey?split(\"|\")[f_index]}\',\n              label: \'${c.searchLabel?split(\"|\")[f_index]}\'\n            }<#if f_has_next>,</#if>\n    </#list>\n            ]\"\n            dataListUrl=\"${c.dataUrl}/list\"\n            entityBeanName=\"${c.dataUrl?split(\"/\")[c.dataUrl?split(\"/\")?size-1]}\"\n            queryEntityUrl=\"${c.dataUrl}/queryById\">\n          </GridSelect>\n    <#elseif c.showType == \"wangeditor\">\n            <WangEditor ref=\"${c.javaFieldId}Editor\" v-model=\"inputForm.${c.javaFieldId}\"/>\n    <#elseif c.showType == \"quilleditor\">\n            <vue-editor useCustomImageHandler @image-added=\"$utils.handleImageAdded\" v-model=\"inputForm.${c.javaFieldId}\"/>\n    </#if>\n           </el-form-item>\n        </el-col>\n      </#if>\n    </#list>\n        </el-row>\n    </el-form>\n    <span slot=\"footer\" class=\"dialog-footer\">\n      <el-button size=\"small\" @click=\"visible = false\">关闭</el-button>\n      <el-button size=\"small\" type=\"primary\" v-if=\"method != \'view\'\" @click=\"doSubmit()\" v-noMoreClick>确定</el-button>\n    </span>\n  </el-dialog>\n</div>\n</template>\n\n<script>\n<#list table.importFormList as c>\n  ${c}\n</#list>\n  export default {\n    data () {\n      return {\n        title: \'\',\n        method: \'\',\n        visible: false,\n        loading: false,\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"fileselect\" || c.showType == \"imageselect\">\n        ${c.javaFieldId}Arra: [],\n  </#if>\n  </#if>\n</#list>\n        inputForm: {\n          id: \'\'<#if (table.formList?size>0)>,</#if>\n        <#list table.formList as c>\n          <#if c.javaFieldId?contains(\".\")>\n          ${c.javaFieldId?split(\".\")[0]}: {\n            ${c.javaFieldId?split(\".\")[1]}: \'\'\n          }<#if c_has_next>,</#if>\n          <#else>\n          ${c.javaFieldId}: \'\'<#if c_has_next>,</#if>\n          </#if>\n    </#list>\n        }\n      }\n    },\n    components: {\n<#list table.formComponents as c>\n      ${c}<#if c_has_next>,</#if>\n</#list>\n    },\n    <#assign cks=0/>\n    <#list table.formList as c>\n     <#if c.showType == \"checkbox\">\n     <#assign cks= cks + 1/>\n    </#if>\n    </#list>\n    <#if cks gt 0>\n    computed: {\n    <#assign ckIndex=0/>\n    <#list table.formList as c>\n     <#if c.showType == \"checkbox\">\n     <#assign ckIndex= ckIndex + 1/>\n      ${c.javaFieldId}List: {\n        get: function () {\n          return this.inputForm.${c.javaFieldId} !== \'\' ? this.inputForm.${c.javaFieldId}.split(\',\') : []\n        },\n        set: function (val) {\n          this.inputForm.${c.javaFieldId} = val.join(\',\')\n        }\n      }<#if ckIndex lt cks >,</#if>\n    </#if>\n    </#list>\n    },\n    </#if>\n    methods: {\n      init (method, obj) {\n        this.method = method\n        this.inputForm.id = obj.id\n        if (method === \'add\') {\n          this.title = \'新建${functionNameSimple}\'\n        } else if (method === \'addChild\') {\n          this.title = \'添加下级${functionNameSimple}\'\n        } else if (method === \'edit\') {\n          this.title = \'修改${functionNameSimple}\'\n        } else if (method === \'view\') {\n          this.title = \'查看${functionNameSimple}\'\n        }\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"imageselect\" || c.showType == \"fileselect\">\n        this.${c.javaFieldId}Arra = []\n  </#if>\n  </#if>\n</#list>\n        this.visible = true\n        this.loading = false\n        this.$nextTick(() => {\n          this.$refs.inputForm.resetFields()\n          this.inputForm.parent.id = obj.parent.id\n          this.inputForm.parent.name = obj.parent.name\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"wangeditor\">\n          this.$refs.${c.javaFieldId}Editor.init(\'\')\n  </#if>\n  </#if>\n</#list>\n          if (method === \'edit\' || method === \'view\') { // 修改或者查看\n            this.loading = true\n            this.$http({\n              url: `/${urlPrefix}/queryById?id=${\"$\"}{this.inputForm.id}`,\n              method: \'get\'\n            }).then(({data}) => {\n              this.inputForm = this.recover(this.inputForm, data.${className})\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"wangeditor\">\n              this.$refs.${c.javaFieldId}Editor.init(this.inputForm.${c.javaFieldId})\n  </#if>\n  </#if>\n</#list>\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"fileselect\" || c.showType == \"imageselect\">\n              this.inputForm.${c.javaFieldId}.split(\'|\').forEach((item) => {\n                if (item.trim().length > 0) {\n                  this.${c.javaFieldId}Arra.push({name: decodeURIComponent(item.substring(item.lastIndexOf(\'/\') + 1)), url: item})\n                }\n              })\n  </#if>\n  </#if>\n</#list>\n              this.loading = false\n            })\n          }\n        })\n      },\n      // 表单提交\n      doSubmit () {\n        this.$refs[\'inputForm\'].validate((valid) => {\n          if (valid) {\n            this.loading = true\n            this.$http({\n              url: `/${urlPrefix}/save`,\n              method: \'post\',\n              data: this.inputForm\n            }).then(({data}) => {\n              this.loading = false\n              if (data && data.success) {\n                this.visible = false\n                this.$message.success(data.msg)\n                this.$emit(\'refreshDataList\')\n              }\n            })\n          }\n        })\n      }\n    }\n  }\n</script>\n\n  ]]>\n    </content>\n</template>', '', 0, 0, '0', NULL, '1', '2021-10-04 21:56:55', '1', '2021-10-04 21:56:55');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1445025140202041345', 'mapper.java', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<template>\r\n    <name>mapper</name>\r\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/mapper</filePath>\r\n    <fileName>${ClassName}Mapper.java</fileName>\r\n    <content><![CDATA[\r\n/**\r\n * Copyright © 2015-2020 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\r\n */\r\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper;\r\n\r\nimport org.springframework.stereotype.Repository;\r\nimport cn.weegoo.core.persistence.TreeMapper;\r\nimport org.apache.ibatis.annotations.Mapper;\r\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.entity.${ClassName};\r\n\r\n/**\r\n * ${functionName}MAPPER接口\r\n * @author ${functionAuthor}\r\n * @version ${functionVersion}\r\n */\r\n@Mapper\r\n@Repository\r\npublic interface ${ClassName}Mapper extends TreeMapper<${ClassName}> {\r\n	\r\n}]]>\r\n    </content>\r\n</template>', '', 0, 0, '0', NULL, '1', '2021-10-04 21:56:55', '1', '2021-10-04 21:56:55');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1445025140227207169', 'service.java', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>service</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/service</filePath>\n    <fileName>${ClassName}Service.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2015-2020 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service;\n\nimport java.util.List;\n\nimport org.springframework.stereotype.Service;\nimport org.springframework.transaction.annotation.Transactional;\n\nimport cn.weegoo.core.service.TreeService;\nimport cn.weegoo.common.utils.StringUtils;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.entity.${ClassName};\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper.${ClassName}Mapper;\n<#if table.dataSource?? && table.dataSource.enName?? && table.dataSource.enName!=\"\" && table.dataSource.enName!=\"master\">\nimport cn.weegoo.database.datasource.annotation.DS;\n</#if>\n\n/**\n * ${functionName}Service\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n<#if table.dataSource?? && table.dataSource.enName?? && table.dataSource.enName!=\"\" && table.dataSource.enName!=\"master\">\n@DS(\"${table.dataSource.enName}\")\n</#if>\n@Service\n@Transactional(readOnly = true)\npublic class ${ClassName}Service extends TreeService<${ClassName}Mapper, ${ClassName}> {\n\n	public ${ClassName} get(String id) {\n		return super.get(id);\n	}\n\n	public List<${ClassName}> findList(${ClassName} ${className}) {\n		if (StringUtils.isNotBlank(${className}.getParentIds())){\n			${className}.setParentIds(\",\"+${className}.getParentIds()+\",\");\n		}\n		return super.findList(${className});\n	}\n\n	@Transactional(readOnly = false)\n	public void save(${ClassName} ${className}) {\n		super.save(${className});\n	}\n\n	@Transactional(readOnly = false)\n	public void delete(${ClassName} ${className}) {\n		super.delete(${className});\n	}\n\n}]]>\n    </content>\n</template>', '', 0, 0, '0', NULL, '1', '2021-10-04 21:56:55', '1', '2021-10-04 21:56:55');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1445025140243984386', 'entity.java', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<template>\r\n    <name>entity</name>\r\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/entity</filePath>\r\n    <fileName>${ClassName}.java</fileName>\r\n    <content><![CDATA[\r\n/**\r\n * Copyright © 2015-2020 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\r\n */\r\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.entity;\r\n\r\n<#list table.importList as i>\r\nimport ${i};\r\n</#list>\r\nimport com.fasterxml.jackson.annotation.JsonIgnoreProperties;\r\n\r\nimport cn.weegoo.core.persistence.TreeEntity;\r\nimport lombok.Data;\r\n\r\n/**\r\n * ${functionName}Entity\r\n * @author ${functionAuthor}\r\n * @version ${functionVersion}\r\n */\r\n@Data \r\n@JsonIgnoreProperties(value={\"hibernateLazyInitializer\",\"handler\"})\r\npublic class ${ClassName} extends TreeEntity<${ClassName}> {\r\n	\r\n	private static final long serialVersionUID = 1L;\r\n	<#-- 生成字段属性 -->\r\n	<#list table.columnList as c>\r\n		<#-- 如果不是基类属性 -->\r\n		<#if c.isNotTreeBaseField>\r\n			<#list c.simpleAnnotationList as a>\r\n	@${a}\r\n			</#list>\r\n			<#-- 父类对象 -->\r\n			<#if table.parentExists && table.parentTableFk == c.name>\r\n	private ${table.parent.className?cap_first} ${c.simpleJavaField};		<#if c.comments??>// ${c.comments} 父类</#if>\r\n			<#-- 其它字段 -->\r\n			<#else>\r\n	private ${c.simpleJavaType} ${c.simpleJavaField};		<#if c.comments??>// ${c.comments}</#if>\r\n			</#if>\r\n		</#if>\r\n	</#list>\r\n	\r\n	<#-- 范围条件字段 -->\r\n	<#list table.columnList as c>\r\n		<#if c.isQuery?? && c.isQuery == \"1\" && c.queryType == \"between\">\r\n	private ${c.simpleJavaType} begin${c.simpleJavaField?cap_first};		<#if c.comments??>// 开始 ${c.comments}</#if>\r\n	private ${c.simpleJavaType} end${c.simpleJavaField?cap_first};		<#if c.comments??>// 结束 ${c.comments}</#if>\r\n		</#if>\r\n	</#list>\r\n	<#-- 子表列表字段 -->\r\n	<#list table.childList as c>\r\n	private List<${c.className?cap_first}> ${c.className?uncap_first}List = Lists.newArrayList();		// 子表列表\r\n	</#list>\r\n	\r\n	<#-- 构造方法 -->\r\n	public ${ClassName}() {\r\n		super();\r\n		<#if table.genIdType ==\"2\">\r\n		this.setIdType(IDTYPE_AUTO);\r\n		</#if>\r\n	}\r\n\r\n	public ${ClassName}(String id){\r\n		super(id);\r\n		<#if table.genIdType ==\"2\">\r\n		this.setIdType(IDTYPE_AUTO);\r\n		</#if>\r\n	}\r\n	<#list table.columnList as c>\r\n		<#if table.parentExists && table.parentTableFk == c.name>\r\n\r\n	public ${ClassName}(${table.parent.className?cap_first} ${c.simpleJavaField}){\r\n		this.${c.simpleJavaField} = ${c.simpleJavaField};\r\n	}\r\n		</#if>\r\n	</#list>\r\n\r\n	public  ${ClassName} getParent() {\r\n			return parent;\r\n	}\r\n	\r\n	@Override\r\n	public void setParent(${ClassName} parent) {\r\n		this.parent = parent;\r\n		\r\n	}\r\n	\r\n	<#-- JSON忽略了parent对象，添加此方法获取父类Id -->\r\n	<#list table.columnList as c>\r\n		<#if c.javaFieldId == \'parent.id\'>\r\n	public String getParentId() {\r\n		return parent != null && parent.getId() != null ? parent.getId() : \"0\";\r\n	}\r\n		</#if>\r\n	</#list>\r\n}]]>\r\n    </content>\r\n</template>', '', 0, 0, '0', NULL, '1', '2021-10-04 21:56:55', '1', '2021-10-04 21:56:55');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1445025140264955906', 'controller.java', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>controller</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/web</filePath>\n    <fileName>${ClassName}Controller.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2015-2020 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.web;\n\n<#list table.columnList as c>\n		<#-- 如果存在自定义java对象 -->\n		<#if c.tableName?? && c.tableName!=\"\">\nimport java.io.UnsupportedEncodingException;\nimport java.net.URLDecoder;\n		<#break>\n		</#if>\n</#list>\nimport java.util.List;\n\nimport org.apache.shiro.authz.annotation.Logical;\nimport org.apache.shiro.authz.annotation.RequiresPermissions;\nimport org.springframework.beans.factory.annotation.Autowired;\nimport org.springframework.ui.Model;\nimport org.springframework.web.bind.annotation.ModelAttribute;\nimport org.springframework.web.bind.annotation.RequestMapping;\nimport org.springframework.web.bind.annotation.GetMapping;\nimport org.springframework.web.bind.annotation.DeleteMapping;\nimport org.springframework.web.bind.annotation.PostMapping;\nimport org.springframework.web.bind.annotation.RequestParam;\nimport org.springframework.web.bind.annotation.RestController;\n\nimport com.google.common.collect.Lists;\nimport cn.weegoo.common.json.AjaxJson;\nimport cn.weegoo.core.web.BaseController;\nimport cn.weegoo.common.utils.StringUtils;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.entity.${ClassName};\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.${ClassName}Service;\n\n/**\n * ${functionName}Controller\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n@RestController\n@RequestMapping(value = \"/${urlPrefix}\")\npublic class ${ClassName}Controller extends BaseController {\n\n	@Autowired\n	private ${ClassName}Service ${className}Service;\n\n	@ModelAttribute\n	public ${ClassName} get(@RequestParam(required=false) String id) {\n		${ClassName} entity = null;\n		if (StringUtils.isNotBlank(id)){\n			entity = ${className}Service.get(id);\n		}\n		if (entity == null){\n			entity = new ${ClassName}();\n		}\n		return entity;\n	}\n\n\n	/**\n	 * ${functionNameSimple}树表数据\n	 */\n	@RequiresPermissions(\"${permissionPrefix}:list\")\n	@GetMapping(\"list\")\n	public AjaxJson list(${ClassName} ${className}) {\n		return AjaxJson.success().put(\"list\", ${className}Service.findList(${className}));\n	}\n\n	/**\n	 * 根据Id获取${functionNameSimple}数据\n	 */\n	@RequiresPermissions(value={\"${permissionPrefix}:view\",\"${permissionPrefix}:add\",\"${permissionPrefix}:edit\"},logical=Logical.OR)\n	@GetMapping(\"queryById\")\n	public AjaxJson queryById(${ClassName} ${className}) {\n		return AjaxJson.success().put(\"${className}\", ${className});\n	}\n\n	/**\n	 * 保存${functionNameSimple}\n	 */\n	@RequiresPermissions(value={\"${permissionPrefix}:add\",\"${permissionPrefix}:edit\"},logical=Logical.OR)\n	@PostMapping(\"save\")\n	public AjaxJson save(${ClassName} ${className}, Model model) throws Exception{\n		/**\n		 * 后台hibernate-validation插件校验\n		 */\n		String errMsg = beanValidator(${className});\n		if (StringUtils.isNotBlank(errMsg)){\n			return AjaxJson.error(errMsg);\n		}\n		//新增或编辑表单保存\n		${className}Service.save(${className});//保存\n		return AjaxJson.success(\"保存${functionNameSimple}成功\");\n	}\n\n	/**\n	 * 删除${functionNameSimple}\n	 */\n	@RequiresPermissions(\"${permissionPrefix}:del\")\n	@DeleteMapping(\"delete\")\n	public AjaxJson delete(String ids) {\n		String idArray[] =ids.split(\",\");\n		for(String id : idArray){\n			${className}Service.delete(new ${ClassName}(id));\n		}\n		return AjaxJson.success(\"删除${functionNameSimple}成功\");\n	}\n\n	/**\n     * 获取JSON树形数据。\n     * @param extId 排除的ID\n     * @return\n	*/\n	@RequiresPermissions(\"user\")\n	@GetMapping(\"treeData\")\n	public AjaxJson treeData(@RequestParam(required = false) String extId) {\n		List<${ClassName}> list = ${className}Service.findList(new ${ClassName}());\n		List rootTree =  ${className}Service.formatListToTree (new ${ClassName} (\"0\"),list, extId );\n		return AjaxJson.success().put(\"treeData\", rootTree);\n	}\n\n}]]>\n    </content>\n</template>', '', 0, 0, '0', NULL, '1', '2021-10-04 21:56:55', '1', '2021-10-04 21:56:55');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1445025140285927426', 'mapper.xml', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>mapper</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/mapper/xml</filePath>\n    <fileName>${ClassName}Mapper.xml</fileName>\n    <content><![CDATA[\n<?xml version=\"1.0\" encoding=\"UTF-8\" ?>\n<!DOCTYPE mapper PUBLIC \"-//mybatis.org//DTD Mapper 3.0//EN\" \"http://mybatis.org/dtd/mybatis-3-mapper.dtd\">\n<mapper namespace=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper.${ClassName}Mapper\">\n\n    <resultMap id=\"${className}Result\" type=\"${ClassName}\">\n   		<result property=\"id\" column=\"id\" />\n		<result property=\"name\" column=\"name\" />\n		<result property=\"sort\" column=\"sort\" />\n		<result property=\"parentIds\" column=\"parentIds\" />\n		<#list table.columnList as c>\n		<#if c.isNotTreeBaseField>\n		<result property=\"${c.simpleJavaField}\" column=\"${c.simpleJavaField}\" />\n		</#if>\n		</#list>\n    </resultMap>\n\n    <#-- 输出字段列 -->\n	<sql id=\"${className}Columns\">\n		<#assign columnField>\n			<#list table.columnList as c>\n		a.${c.name} AS \"${c.javaFieldId}\",\n			</#list>\n			<#list table.columnList as c>\n			<#-- 父表关联字段 -->\n				<#if table.parentExists && table.parentTableFk == c.name && c.javaType != \"This\">\n					<#list c.javaFieldAttrs as a>\n		b.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"userselect\" && c.javaType != \"This\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"officeselect\" && c.javaType != \"This\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"areaselect\" && c.javaType != \"This\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"treeselect\" && c.javaType != \"This\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"gridselect\" && c.javaType != \"This\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n				</#list>\n				</#if>\n\n			</#list>\n		</#assign>\n${columnField?substring(0, columnField?last_index_of(\",\"))}\n	</sql>\n\n\n\n\n	<#-- 输出字段关联表 -->\n	<sql id=\"${className}Joins\">\n\n		<#-- 关联系统表 -->\n		<#list table.columnList as c>\n		<#-- 关联父表 -->\n		<#if table.parentExists && table.parentTableFk == c.name && c.javaType != \"This\">\n			LEFT JOIN ${table.parent.name} b ON b.id = a.${table.parentTableFk}\n			<#elseif c.showType?? && c.showType == \"userselect\" && c.javaType != \"This\">\n		LEFT JOIN sys_user ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"officeselect\" && c.javaType != \"This\">\n		LEFT JOIN sys_office ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"areaselect\" && c.javaType != \"This\">\n		LEFT JOIN sys_area ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"treeselect\" && c.javaType != \"This\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"gridselect\" && c.javaType != \"This\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			</#if>\n		</#list>\n	</sql>\n\n\n\n	<select id=\"get\" resultType=\"${ClassName}\">\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		WHERE a.id = ${\"#\"}{id}\n	</select>\n\n	<select id=\"findList\" resultType=\"${ClassName}\">\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		<where>\n			<#if table.delFlagExists>a.del_flag = ${\"#\"}{DEL_FLAG_NORMAL}</#if>\n			${\"$\"}{dataScope}\n			<#list table.columnList as c>\n				<#if (c.isQuery?? && c.isQuery == \"1\") || (table.parentExists && table.parentTableFk == c.name) || c.javaFieldId == \'parent.id\' || c.javaFieldId == \'parentIds\'>\n					<#if c.queryType ?? && c.queryType == \'between\'>\n			<if test=\"begin${c.simpleJavaField?cap_first} != null and end${c.simpleJavaField?cap_first} != null <#if c.simpleJavaField != c.javaFieldId>and begin${c.javaFieldId?cap_first} != null and end${c.javaFieldId?cap_first} != null </#if>\">\n					<#else>\n			<if test=\"${c.simpleJavaField} != null<#if c.simpleJavaField != c.javaFieldId> and ${c.javaFieldId} != null</#if> and ${c.javaFieldId} != \'\'\">\n					</#if>\n					<#if c.queryType ?? && c.queryType == \'between\'>\n				AND a.${c.name} BETWEEN ${\"#\"}{begin${c.simpleJavaField?cap_first}} AND ${\"#\"}{end${c.simpleJavaField?cap_first}}\n					<#elseif c.queryType ?? && c.queryType == \'like\'>\n				AND a.${c.name} LIKE\n				    <if test=\"_databaseId == \'postgre\'\">\'%\'||${\"#\"}{${c.javaFieldId}}||\'%\'</if>\n					<if test=\"_databaseId == \'oracle\'\">\'%\'||${\"#\"}{${c.javaFieldId}}||\'%\'</if>\n					<if test=\"_databaseId == \'mssql\'\">\'%\'+${\"#\"}{${c.javaFieldId}}+\'%\'</if>\n					<if test=\"_databaseId == \'mysql\'\">concat(\'%\',${\"#\"}{${c.javaFieldId}},\'%\')</if>\n					<#elseif c.queryType ?? && c.queryType == \'left_like\'>\n				AND a.${c.name} LIKE\n				    <if test=\"_databaseId == \'postgre\'\">\'%\'||${\"#\"}{${c.javaFieldId}}</if>\n					<if test=\"_databaseId == \'oracle\'\">\'%\'||${\"#\"}{${c.javaFieldId}}</if>\n					<if test=\"_databaseId == \'mssql\'\">\'%\'+${\"#\"}{${c.javaFieldId}}</if>\n					<if test=\"_databaseId == \'mysql\'\">concat(\'%\',${\"#\"}{${c.javaFieldId}})</if>\n					<#elseif c.queryType ?? && c.queryType == \'right_like\'>\n				AND a.${c.name} LIKE\n				    <if test=\"_databaseId == \'postgre\'\">${\"#\"}{${c.javaFieldId}}||\'%\'</if>\n					<if test=\"_databaseId == \'oracle\'\">${\"#\"}{${c.javaFieldId}}||\'%\'</if>\n					<if test=\"_databaseId == \'mssql\'\">${\"#\"}{${c.javaFieldId}}+\'%\'</if>\n					<if test=\"_databaseId == \'mysql\'\">concat(${\"#\"}{${c.javaFieldId}},\'%\')</if>\n					<#else>\n				AND a.${c.name} ${c.queryType} ${\"#\"}{${c.javaFieldId}}\n					</#if>\n			</if>\n				</#if>\n			</#list>\n		</where>\n		<#list table.columnList as c>\n			<#if c.name == \'sort\'>\n		ORDER BY a.sort ASC\n			</#if>\n		</#list>\n	</select>\n\n	<select id=\"findAllList\" resultType=\"${ClassName}\">\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		<where>\n			<#if table.delFlagExists>a.del_flag = ${\"#\"}{DEL_FLAG_NORMAL}</#if>\n			${\"$\"}{dataScope}\n		</where>\n		<#list table.columnList as c>\n			<#if c.name == \'sort\'>\n		ORDER BY a.sort ASC\n			</#if>\n		</#list>\n	</select>\n\n	<select id=\"getChildren\" parameterType=\"String\" resultMap=\"${className}Result\">\n        select * from ${table.name} where parent_id = ${\"#\"}{id} ORDER BY sort\n    </select>\n\n	<select id=\"findByParentIdsLike\" resultType=\"${ClassName}\">\n		SELECT\n			a.id,\n			a.parent_id AS \"parent.id\",\n			a.parent_ids\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		<where>\n			<#if table.delFlagExists>a.del_flag = ${\"#\"}{DEL_FLAG_NORMAL}</#if>\n			AND a.parent_ids LIKE ${\"#\"}{parentIds}\n		</where>\n		<#list table.columnList as c>\n			<#if c.name == \'sort\'>\n		ORDER BY a.sort ASC\n			</#if>\n		</#list>\n	</select>\n\n	<insert id=\"insert\">\n		INSERT INTO ${table.name}(\n		<#assign insertField>\n			<#list table.columnList as c>\n				<#if c.isInsert?? && c.isInsert == \"1\">\n				<#if !(table.genIdType ==\"2\" && c.name==\'id\') >\n			   ${c.name},\n				</#if>\n				</#if>\n			</#list>\n		</#assign>\n${insertField?substring(0, insertField?last_index_of(\",\"))}\n		) VALUES (\n		<#assign insertJavaField>\n			<#list table.columnList as c>\n				<#if c.isInsert?? && c.isInsert == \"1\">\n				<#if !(table.genIdType ==\"2\" && c.name==\'id\') >\n			  <#if c.showType == \"umeditor\">\n			${\"#\"}{${c.javaFieldId}, jdbcType=BLOB},\n			    <#else>\n			${\"#\"}{${c.javaFieldId}},\n				</#if>\n				</#if>\n				</#if>\n			</#list>\n		</#assign>\n${insertJavaField?substring(0, insertJavaField?last_index_of(\",\"))}\n		)\n	</insert>\n\n	<update id=\"update\">\n		UPDATE ${table.name} SET\n			<#assign updateField>\n				<#list table.columnList as c>\n					<#if c.isEdit?? && c.isEdit == \"1\">\n			    <#if c.showType == \"umeditor\">\n			${c.name} = ${\"#\"}{${c.javaFieldId}, jdbcType=BLOB},\n			    <#else>\n			${c.name} = ${\"#\"}{${c.javaFieldId}},\n				</#if>\n					</#if>\n				</#list>\n			</#assign>\n${updateField?substring(0, updateField?last_index_of(\",\"))}\n		WHERE id = ${\"#\"}{id}\n	</update>\n\n	<update id=\"updateParentIds\">\n		UPDATE ${table.name} SET\n			parent_id = ${\"#\"}{parent.id},\n			parent_ids = ${\"#\"}{parentIds}\n		WHERE id = ${\"#\"}{id}\n	</update>\n\n	<!--物理删除-->\n	<update id=\"delete\">\n		DELETE FROM ${table.name}\n	<#if table.parentExists>\n		<#list table.columnList as c>\n			<#if table.parentTableFk == c.name>\n		<choose>\n			<when test=\"id !=null and id != \'\'\">\n				WHERE id = ${\"#\"}{id}\n			</when>\n			<otherwise>\n				WHERE ${table.parentTableFk} = ${\"#\"}{${c.javaFieldId}}\n			</otherwise>\n		</choose>\n			</#if>\n		</#list>\n	<#else>\n		WHERE id = ${\"#\"}{id} OR parent_ids LIKE\n		<if test=\"_databaseId == \'postgre\'\">\'%,\'||${\"#\"}{id}||\',%\'</if>\n		<if test=\"_databaseId == \'oracle\'\">\'%,\'||${\"#\"}{id}||\',%\'</if>\n		<if test=\"_databaseId == \'mssql\'\">\'%,\'+${\"#\"}{id}+\',%\'</if>\n        <if test=\"_databaseId == \'mysql\'\">CONCAT(\'%,\', ${\"#\"}{id}, \',%\')</if>\n	</#if>\n	</update>\n\n	<!--逻辑删除-->\n	<update id=\"deleteByLogic\">\n		UPDATE ${table.name} SET\n			del_flag = ${\"#\"}{DEL_FLAG_DELETE}\n	<#if table.parentExists>\n		<#list table.columnList as c>\n			<#if table.parentTableFk == c.name>\n		<choose>\n			<when test=\"id !=null and id != \'\'\">\n				WHERE id = ${\"#\"}{id}\n			</when>\n			<otherwise>\n				WHERE ${table.parentTableFk} = ${\"#\"}{${c.javaFieldId}}\n			</otherwise>\n		</choose>\n			</#if>\n		</#list>\n	<#else>\n		WHERE id = ${\"#\"}{id} OR parent_ids LIKE\n		<if test=\"_databaseId == \'postgre\'\">\'%,\'||${\"#\"}{id}||\',%\'</if>\n		<if test=\"_databaseId == \'oracle\'\">\'%,\'||${\"#\"}{id}||\',%\'</if>\n		<if test=\"_databaseId == \'mssql\'\">\'%,\'+${\"#\"}{id}+\',%\'</if>\n        <if test=\"_databaseId == \'mysql\'\">CONCAT(\'%,\', ${\"#\"}{id}, \',%\')</if>\n	</#if>\n	</update>\n\n</mapper>]]>\n    </content>\n</template>', '', 0, 0, '0', NULL, '1', '2021-10-04 21:56:55', '1', '2021-10-04 21:56:55');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1445025691732045825', 'viewList.vue', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>viewList</name>\n    <filePath>src/views/${lastPackageName}/${moduleName}/${subModuleName}</filePath>\n    <fileName>${ClassName}List.vue</fileName>\n    <content><![CDATA[\n<template>\n    <div class=\"page\">\n      <el-form size=\"small\" :inline=\"true\" class=\"query-form\" ref=\"searchForm\" :model=\"searchForm\" @keyup.enter.native=\"refreshList()\" @submit.native.prevent>\n            <!-- 搜索框-->\n<#list table.columnList as c>\n            <#if c.isQuery?? && c.isQuery == \"1\">\n         <el-form-item prop=\"${c.javaFieldId}\">\n      <#if c.showType == \"input\" || c.showType == \"textarea\">\n                <el-input size=\"small\" v-model=\"searchForm.${c.javaFieldId}\" placeholder=\"${c.comments}\" clearable></el-input>\n      <#elseif c.showType == \"select\">\n                  <el-select v-model=\"searchForm.${c.javaFieldId}\" placeholder=\"请选择${c.comments}\" size=\"small\" style=\"width: 100%;\">\n                    <el-option\n                      v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\"\n                      :key=\"item.value\"\n                      :title=\"item.label\"\n                      :value=\"item.value\">\n                    </el-option>\n                  </el-select>\n        <#elseif c.showType == \"checkbox\">\n                <el-checkbox-group\n                        v-model=\"${c.javaFieldId}List\">\n                       <el-checkbox v-for=\"${c.javaFieldId} in $dictUtils.getDictList(\'${c.dictType}\')\" :title=\"${c.javaFieldId}.value\" :key=\"${c.javaFieldId}.value\">{{${c.javaFieldId}.label}}</el-checkbox>\n                    </el-checkbox-group>\n        <#elseif c.showType == \"radiobox\">\n                  <el-radio-group v-model=\"searchForm.${c.javaFieldId}\">\n                    <el-radio v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\" :title=\"item.value\" :key=\"item.value\">{{item.label}}</el-radio>\n                  </el-radio-group>\n            <#elseif c.showType == \"dateselect\" && c.queryType == \"between\">\n               <el-date-picker\n                    v-model=\"searchForm.${c.javaFieldId}\"\n                    type=\"daterange\"\n                    size=\"small\"\n                    align=\"right\"\n                    value-format=\"yyyy-MM-dd hh:mm:ss\"\n                    unlink-panels\n                    range-separator=\"至\"\n                    start-placeholder=\"开始日期\"\n                    end-placeholder=\"结束日期\">\n                 </el-date-picker>\n        <#elseif c.showType == \"dateselect\">\n                <el-date-picker\n                  v-model=\"searchForm.${c.javaFieldId}\"\n                  type=\"datetime\"\n                  size=\"small\"\n                  value-format=\"yyyy-MM-dd HH:mm:ss\"\n                  placeholder=\"请选择${c.comments}\">\n                </el-date-picker>\n        <#elseif c.showType == \"userselect\">\n            <user-select :limit=\'1\' size=\"small\" placeholder=\"请选择${c.comments}\" :value=\"searchForm.${c.javaFieldId}\" @getValue=\'(value) => {searchForm.${c.javaFieldId}=value}\'></user-select>\n        <#elseif c.showType == \"officeselect\">\n            <SelectTree\n                  ref=\"${c.javaFieldId}\"\n                  :props=\"{\n                      value: \'id\',             // ID字段名\n                      label: \'name\',         // 显示名称\n                      children: \'children\'    // 子级字段名\n                    }\"\n                  placeholder=\"请选择${c.comments}\"\n                  size=\"small\"\n                  :url=\"`/sys/office/treeData?type=2`\"\n                  :value=\"searchForm.${c.javaFieldId}\"\n                  :clearable=\"true\"\n                  :accordion=\"true\"\n                  @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"areaselect\">\n                <SelectTree\n                  ref=\"${c.javaFieldId}\"\n                  :props=\"{\n                      value: \'id\',             // ID字段名\n                      label: \'name\',         // 显示名称\n                      children: \'children\'    // 子级字段名\n                    }\"\n                  placeholder=\"请选择${c.comments}\"\n                  size=\"small\"\n                  :url=\"/sys/area/treeData\"\n                  :value=\"searchForm.${c.javaFieldId}\"\n                  :clearable=\"true\"\n                  :accordion=\"true\"\n                  @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"cityselect\">\n                  <CityPicker \n                    style=\"width:100%\"\n                    size=\"small\"\n                    placeholder=\"选择${c.comments}\"\n                    :value=\"searchForm.${c.javaFieldId}\"\n                    :clearable=\"true\" \n                    :accordion=\"true\"\n                    @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"treeselect\">\n            <SelectTree\n                  ref=\"${c.javaFieldId}\"\n                  :props=\"{\n                      value: \'id\',             // ID字段名\n                      label: \'name\',         // 显示名称\n                      children: \'children\'    // 子级字段名\n                    }\"\n                  placeholder=\"请选择${c.comments}\"\n                  size=\"small\"\n                  url=\"${c.dataUrl}/treeData\"\n                  :value=\"searchForm.${c.javaFieldId}\"\n                  :clearable=\"true\"\n                  :accordion=\"true\"\n                  @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"gridselect\">\n            <GridSelect\n                    title=\"选择${c.comments}\"\n                    placeholder=\"请选择${c.comments}\"\n                    labelName = \'${c.javaFieldName?split(\".\")[1]}\'\n                    labelValue = \'${c.javaFieldId?split(\".\")[1]}\'\n                    :value = \"searchForm.${c.javaFieldId}\"\n                    :limit=\"1\"\n                    size=\"small\"\n                    @getValue=\'(value) => {searchForm.${c.javaFieldId}=value}\'\n                    :columns=\"[\n            <#list c.fieldLabels?split(\"|\") as f>\n                    {\n                      prop: \'${c.fieldKeys?split(\"|\")[f_index]}\',\n                      label: \'${c.fieldLabels?split(\"|\")[f_index]}\'\n                    }<#if f_has_next>,</#if>\n            </#list>\n                    ]\"\n                    :searchs=\"[\n           <#list c.searchLabel?split(\"|\") as f>\n                    {\n                      prop: \'${c.searchKey?split(\"|\")[f_index]}\',\n                      label: \'${c.searchLabel?split(\"|\")[f_index]}\'\n                    }<#if f_has_next>,</#if>\n            </#list>\n                    ]\"\n                    dataListUrl=\"${c.dataUrl}/list\"\n                    entityBeanName=\"${c.dataUrl?split(\"/\")[c.dataUrl?split(\"/\")?size-1]}\"\n                    queryEntityUrl=\"${c.dataUrl}/queryById\">\n                  </GridSelect>\n        </#if>\n         </el-form-item>\n        </#if>\n    </#list>\n          <el-form-item>\n            <el-button type=\"primary\" @click=\"refreshList()\" size=\"small\" icon=\"el-icon-search\">查询</el-button>\n            <el-button @click=\"resetSearch()\" size=\"small\" icon=\"el-icon-refresh-right\">重置</el-button>\n          </el-form-item>\n      </el-form>\n    <div class=\"bg-white top\">\n    <vxe-toolbar :refresh=\"{query: refreshList}\" export print custom>\n      <template #buttons>\n          <el-row>\n            <el-button v-if=\"hasPermission(\'${permissionPrefix}:add\')\" type=\"primary\" size=\"small\" icon=\"el-icon-plus\" @click=\"add()\">新建</el-button>\n            <el-button v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" type=\"warning\" size=\"small\" icon=\"el-icon-edit-outline\" @click=\"edit()\"\n                 :disabled=\"$refs.${className} && $refs.${className}.getCheckboxRecords().length !== 1\" plain>修改</el-button>\n            <el-button v-if=\"hasPermission(\'${permissionPrefix}:del\')\" type=\"danger\"   size=\"small\" icon=\"el-icon-delete\" @click=\"del()\"\n                  :disabled=\"$refs.${className} && $refs.${className}.getCheckboxRecords().length === 0\" plain>删除</el-button>\n          </el-row>\n      </template>\n    </vxe-toolbar>\n    <div style=\"height: calc(100% - 50px);\">\n      <vxe-table\n        resizable\n        ref=\"${className}\"\n        border=\"inner\"\n        auto-resize\n        height=\"auto\"\n        row-id=\"id\"\n        size=\"small\"\n        show-header-overflow\n        show-overflow\n        highlight-hover-row\n        :print-config=\"{}\"\n        :export-config=\"{}\"\n        :tree-config=\"{}\"\n        :loading=\"loading\"\n        :checkbox-config=\"{checkStrictly: true}\"\n        :data=\"dataList\">\n      <vxe-table-column type=\"checkbox\" width=\"40px\"> </vxe-table-column>\n      <#assign cks=0/>\n      <#list table.columnList as c>\n        <#if c.isList?? && c.isList == \"1\">\n        <#assign cks= cks + 1/>\n        <#if cks == 1>\n                <#if c.showType == \"userselect\" || c.showType == \"officeselect\" || c.showType == \"areaselect\" || c.showType == \"treeselect\"|| c.showType == \"gridselect\">\n      <vxe-table-column\n        field=\"${c.javaFieldName}\"\n        align=\"left\"\n        tree-node \n        title=\"${c.comments}\">\n            <template slot-scope=\"scope\">\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\">{{scope.row.${c.javaFieldName}}}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\">{{scope.row.${c.javaFieldName}}}</el-link>\n              <span v-else>{{scope.row.${c.javaFieldName}}}</span>\n            </template>\n      </vxe-table-column>\n        <#elseif c.showType == \"select\">\n    <vxe-table-column\n        field=\"${c.javaFieldId}\"\n        align=\"left\"\n        tree-node \n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\"> {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\"> {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</el-link>\n              <span v-else>{{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</span>\n        </template>\n      </vxe-table-column>\n      <#elseif c.showType == \"checkbox\">\n    <vxe-table-column\n        field=\"${c.javaFieldId}\"\n        align=\"left\"\n        tree-node \n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\">{{scope.row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\">{{scope.row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}</el-link>\n              <span v-else>{{scope.row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}</span>\n        </template>\n      </vxe-table-column>\n      <#elseif c.showType == \"radiobox\">\n    <vxe-table-column\n        field=\"${c.javaFieldId}\"\n        align=\"left\"\n        tree-node \n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\">{{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\">{{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</el-link>\n              <span v-else> {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</span>\n        </template>\n      </vxe-table-column>\n        <#elseif c.showType == \"fileselect\">\n    <vxe-table-column\n        field=\"${c.javaFieldId}\"\n        align=\"left\"\n        tree-node \n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\" v-if=\"scope.row.${c.javaFieldId}\">\n            <a :href=\"item\" target=\"_blank\" :key=\"index\" v-for=\"(item, index) in scope.row.${c.javaFieldId}.split(\'|\')\">\n                {{decodeURIComponent(item.substring(item.lastIndexOf(\"/\")+1))}}\n            </a>\n        </template>\n      </vxe-table-column>\n              <#elseif c.showType == \"imageselect\">\n    <vxe-table-column\n        field=\"${c.javaFieldId}\"\n        align=\"left\"\n        tree-node \n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\" v-if=\"scope.row.${c.javaFieldId}\">\n          <el-image\n            style=\"height: 50px;width:50px;margin-right:10px;\"\n            :src=\"src\" v-for=\"(src, index) in scope.row.${c.javaFieldId}.split(\'|\')\" :key=\"index\"\n            :preview-src-list=\"scope.row.${c.javaFieldId}.split(\'|\')\">\n          </el-image>\n        </template>\n      </vxe-table-column>\n         <#elseif c.showType == \"wangeditor\" || c.showType == \"quilleditor\">\n      <vxe-table-column\n        field=\"${c.javaFieldId}\"\n        align=\"left\"\n        tree-node \n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n            <p v-html=\"$utils.unescapeHTML(scope.row.${c.javaFieldId})\"></p>\n        </template>\n      </vxe-table-column>\n      <#else>\n    <vxe-table-column\n        field=\"${c.javaFieldId}\"\n        align=\"left\"\n        tree-node \n        title=\"${c.comments}\">\n            <template slot-scope=\"scope\">\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\">{{scope.row.${c.javaFieldId}}}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\">{{scope.row.${c.javaFieldId}}}</el-link>\n              <span v-else>{{scope.row.${c.javaFieldId}}}</span>\n            </template>\n      </vxe-table-column>\n          </#if>\n        <#else>\n          <#if c.showType == \"userselect\" || c.showType == \"officeselect\" || c.showType == \"areaselect\" || c.showType == \"treeselect\"|| c.showType == \"gridselect\">\n      <vxe-table-column\n        field=\"${c.javaFieldName}\"\n        title=\"${c.comments}\">\n      </vxe-table-column>\n        <#elseif c.showType == \"select\">\n    <vxe-table-column\n        field=\"${c.javaFieldId}\"\n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n            {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}\n        </template>\n      </vxe-table-column>\n      <#elseif c.showType == \"checkbox\">\n    <vxe-table-column\n        field=\"${c.javaFieldId}\"\n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n            {{scope.row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}\n        </template>\n      </vxe-table-column>\n      <#elseif c.showType == \"radiobox\">\n    <vxe-table-column\n        field=\"${c.javaFieldId}\"\n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n            {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}\n        </template>\n      </vxe-table-column>\n        <#elseif c.showType == \"fileselect\">\n    <vxe-table-column\n        field=\"${c.javaFieldId}\"\n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\" v-if=\"scope.row.${c.javaFieldId}\">\n            <a :href=\"item\" target=\"_blank\" :key=\"index\" v-for=\"(item, index) in scope.row.${c.javaFieldId}.split(\'|\')\">\n                {{decodeURIComponent(item.substring(item.lastIndexOf(\"/\")+1))}}\n            </a>\n        </template>\n      </vxe-table-column>\n              <#elseif c.showType == \"imageselect\">\n    <vxe-table-column\n        field=\"${c.javaFieldId}\"\n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\" v-if=\"scope.row.${c.javaFieldId}\">\n          <el-image\n            style=\"height: 50px;width:50px;margin-right:10px;\"\n            :src=\"src\" v-for=\"(src, index) in scope.row.${c.javaFieldId}.split(\'|\')\" :key=\"index\"\n            :preview-src-list=\"scope.row.${c.javaFieldId}.split(\'|\')\">\n          </el-image>\n        </template>\n      </vxe-table-column>\n         <#elseif c.showType == \"wangeditor\" || c.showType == \"quilleditor\">\n      <vxe-table-column\n        field=\"${c.javaFieldId}\"\n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n            <p v-html=\"$utils.unescapeHTML(scope.row.${c.javaFieldId})\"></p>\n        </template>\n      </vxe-table-column>\n      <#else>\n    <vxe-table-column\n        field=\"${c.javaFieldId}\"\n        title=\"${c.comments}\">\n      </vxe-table-column>\n          </#if>\n      </#if>\n      </#if>\n      </#list>\n      <vxe-table-column title=\"操作\" width=\"300px\" fixed=\"right\" align=\"center\">\n        <template  slot-scope=\"scope\">\n          <el-button v-if=\"hasPermission(\'${permissionPrefix}:view\')\" type=\"text\" size=\"small\" icon=\"el-icon-view\" @click=\"view(scope.row.id)\">查看</el-button>\n          <el-button v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" type=\"text\" size=\"small\" icon=\"el-icon-edit\" @click=\"edit(scope.row.id)\">修改</el-button>\n          <el-button v-if=\"hasPermission(\'${permissionPrefix}:del\')\" type=\"text\" size=\"small\" icon=\"el-icon-delete\" @click=\"del(scope.row.id)\">删除</el-button>\n          <el-button v-if=\"hasPermission(\'${permissionPrefix}:add\')\" type=\"text\" size=\"small\" icon=\"el-icon-circle-plus-outline\" @click=\"addChild(scope.row.id, scope.row.name)\">添加下级${functionNameSimple}</el-button>\n        </template>\n      </vxe-table-column>\n    </vxe-table>\n    </div>\n    </div>\n        <!-- 弹窗, 新增 / 修改 -->\n    <${ClassName}Form  ref=\"${className}Form\" @refreshDataList=\"refreshList\"></${ClassName}Form>\n  </div>\n</template>\n\n<script>\n  import ${ClassName}Form from \'./${ClassName}Form\'\n  import XEUtils from \'xe-utils\'\n<#list table.importQueryList as c>\n  ${c}\n</#list>\n  export default {\n    data () {\n      return {\n        searchForm: {\n        <#list table.queryList as c>\n          <#if c.javaFieldId?contains(\".\")>\n          ${c.javaFieldId?split(\".\")[0]}: {\n            ${c.javaFieldId?split(\".\")[1]}: \'\'\n          }<#if c_has_next>,</#if>\n          <#else>\n          ${c.javaFieldId}: \'\'<#if c_has_next>,</#if>\n          </#if>\n    </#list>\n        },\n        dataList: [],\n        loading: false\n      }\n    },\n    components: {\n      <#list table.queryComponents as c>\n      ${c},\n    </#list>\n      ${ClassName}Form\n    },\n    activated () {\n      this.refreshList()\n    },\n\n    methods: {\n      // 获取数据列表\n      refreshList () {\n        this.loading = true\n        this.$http({\n          url: \'/${urlPrefix}/treeData\',\n          method: \'get\'\n        }).then(({data}) => {\n          this.dataList = data.treeData\n          this.handleSearch()\n          this.loading = false\n        })\n      },\n      handleSearch () {\n        let options = { children: \'children\' }\n        let searchProps = [<#list table.queryList as c>\'${c.javaFieldId}\'<#if c_has_next>, </#if></#list>]\n        this.dataList = XEUtils.searchTree(this.dataList, item => searchProps.every(key => XEUtils.toValueString(this.searchForm[key]).trim() === \'\' || XEUtils.toValueString(item[key]).indexOf(this.searchForm[key]) > -1), options)\n        this.$nextTick(() => {\n          this.$refs.${className}.setAllTreeExpand(true)\n        })\n      },\n      resetSearch () {\n        this.$refs.searchForm.resetFields()\n        this.refreshList()\n      },\n        // 新增下级\n      addChild (id, name) {\n        this.$refs.${className}Form.init(\'addChild\', {id: \'\', parent: {id: id, name: name}})\n      },\n       // 新增\n      add () {\n        this.$refs.${className}Form.init(\'add\', {id: \'\', parent: {id: \'\', name: \'\'}})\n      },\n      // 修改\n      edit (id) {\n        this.$refs.${className}Form.init(\'edit\', {id: id, parent: {id: \'\', name: \'\'}})\n      },\n      // 查看\n      view (id) {\n        this.$refs.${className}Form.init(\'view\', {id: id, parent: {id: \'\', name: \'\'}})\n      },\n      // 删除\n      del (id) {\n        let ids = id || this.$refs.${className}.getCheckboxRecords().map(item => {\n          return item.id\n        }).join(\',\')\n        this.$confirm(`确定删除所选项吗?`, \'提示\', {\n          confirmButtonText: \'确定\',\n          cancelButtonText: \'取消\',\n          type: \'warning\'\n        }).then(() => {\n          this.loading = true\n          this.$http({\n            url: \'/${urlPrefix}/delete\',\n            method: \'delete\',\n            params: {\'ids\': ids}\n          }).then(({data}) => {\n            if (data && data.success) {\n              this.$message.success(data.msg)\n              this.refreshList()\n            }\n            this.loading = false\n          })\n        })\n      }\n    }\n  }\n</script>\n]]>\n    </content>\n</template>', '', 0, 0, '0', NULL, '1', '2021-10-04 21:59:07', '1', '2021-10-04 21:59:07');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1445025692306665473', 'viewForm.vue', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>viewForm</name>\n    <filePath>src/views/${lastPackageName}/${moduleName}/${subModuleName}</filePath>\n    <fileName>${ClassName}Form.vue</fileName>\n    <content><![CDATA[\n<template>\n<div>\n  <el-dialog\n    :title=\"title\"\n    :close-on-click-modal=\"false\"\n     v-dialogDrag\n    :visible.sync=\"visible\">\n    <el-form size=\"small\" :model=\"inputForm\" ref=\"inputForm\" v-loading=\"loading\" :class=\"method===\'view\'?\'readonly\':\'\'\"  :disabled=\"method===\'view\'\"\n             label-width=\"120px\">\n      <el-row  :gutter=\"15\">\n<#list table.columnList as c>\n      <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n       <#if c.showType == \"wangeditor\" || c.showType == \"quilleditor\" || c.showType == \"fileselect\" || c.showType == \"imageselect\">\n        <el-col :span=\"24\">\n        <#else>\n        <el-col :span=\"12\">\n        </#if>\n            <el-form-item label=\"${c.comments}\" prop=\"${c.javaFieldId}\"\n                :rules=\"[\n                <#if c.isNull != \"1\">\n                  {required: true, message:\'${c.comments}不能为空\', trigger:\'blur\'}<#if c.validateType?? && c.validateType != \"\"><#if c.validateType != \"string\" ||  c.minLength?? && c.minLength != \"\">,</#if></#if>\n                </#if>\n                <#if c.minLength?? && c.minLength != \"\">\n                  { min: ${c.minLength}, message: \'最小长度是${c.minLength}个字符\', trigger: \'blur\' }<#if c.validateType?? && c.validateType != \"\"><#if c.validateType != \"string\">,</#if></#if>\n                </#if>\n                <#if c.validateType?? && c.validateType != \"\">\n                <#if c.validateType != \"string\">\n                  {validator: validator.${c.validateType}, trigger:\'blur\'}\n                 </#if>\n                 </#if>\n                 ]\">\n        <#if c.simpleJavaField == \'parent\'>\n                <SelectTree\n                      ref=\"${c.simpleJavaField}\"\n                      :props=\"{\n                          value: \'id\',             // ID字段名\n                          label: \'name\',         // 显示名称\n                          children: \'children\'    // 子级字段名\n                        }\"\n                      v-if=\"visible\"\n                      :url=\"`/${urlPrefix}/treeData?extId=${\"$\"}{inputForm.id}`\"\n                      :value=\"inputForm.${c.javaFieldId}\"\n                      :clearable=\"true\"\n                      :accordion=\"true\"\n                      @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"input\">\n              <el-input v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"请填写${c.comments}\" <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> <#if  c.minLength?? && c.minLength != \"\"> minlength=\"${c.minLength}\"</#if> <#if c.maxValue?? && c.maxValue != \"\"> max=\"${c.maxValue}\"</#if> <#if c.minValue?? && c.minValue != \"\"> min=\"${c.minValue}\"</#if> ></el-input>\n    <#elseif c.showType == \"textarea\">\n          <el-input type=\"textarea\" v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"请填写${c.comments}\" <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> <#if  c.minLength?? && c.minLength != \"\"> minlength=\"${c.minLength}\"</#if> <#if c.maxValue?? && c.maxValue != \"\"> max=\"${c.maxValue}\"</#if> <#if c.minValue?? && c.minValue != \"\"> min=\"${c.minValue}\"</#if> ></el-input>\n    <#elseif c.showType == \"select\">\n                <el-select v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"请选择\"  style=\"width: 100%;\">\n                          <el-option\n                            v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\"\n                            :key=\"item.value\"\n                            :label=\"item.label\"\n                            :value=\"item.value\">\n                          </el-option>\n                      </el-select>\n    <#elseif c.showType == \"checkbox\">\n                <el-checkbox-group\n                        v-model=\"${c.javaFieldId}List\">\n                       <el-checkbox v-for=\"${c.javaFieldId} in $dictUtils.getDictList(\'${c.dictType}\')\" :label=\"${c.javaFieldId}.value\" :key=\"${c.javaFieldId}.value\">{{${c.javaFieldId}.label}}</el-checkbox>\n                    </el-checkbox-group>\n    <#elseif c.showType == \"radiobox\">\n                    <el-radio-group v-model=\"inputForm.${c.javaFieldId}\">\n                        <el-radio v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\" :label=\"item.value\" :key=\"item.value\">{{item.label}}</el-radio>\n                    </el-radio-group>\n    <#elseif c.showType == \"dateselect\">\n                <el-date-picker\n                      v-model=\"inputForm.${c.javaFieldId}\"\n                      type=\"datetime\"\n                      style=\"width: 100%;\"\n                      value-format=\"yyyy-MM-dd HH:mm:ss\"\n                      placeholder=\"选择日期时间\">\n                    </el-date-picker>\n    <#elseif c.showType == \"userselect\">\n                <user-select :limit=\'1\' :value=\"inputForm.${c.javaFieldId}\" @getValue=\'(value) => {inputForm.${c.javaFieldId}=value}\'></user-select>\n    <#elseif c.showType == \"officeselect\">\n          <SelectTree\n                      ref=\"${c.simpleJavaField}\"\n                      :props=\"{\n                          value: \'id\',             // ID字段名\n                          label: \'name\',         // 显示名称\n                          children: \'children\'    // 子级字段名\n                        }\"\n\n                      url=\"/sys/office/treeData?type=2\"\n                      :value=\"inputForm.${c.javaFieldId}\"\n                      :clearable=\"true\"\n                      :accordion=\"true\"\n                      @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"areaselect\">\n              <SelectTree\n                      ref=\"${c.simpleJavaField}\"\n                      :props=\"{\n                          value: \'id\',             // ID字段名\n                          label: \'name\',         // 显示名称\n                          children: \'children\'    // 子级字段名\n                        }\"\n\n                      url=\"/sys/area/treeData\"\n                      :value=\"inputForm.${c.javaFieldId}\"\n                      :clearable=\"true\"\n                      :accordion=\"true\"\n                      @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"cityselect\">\n                      <CityPicker\n                         style=\"width:100%\"\n                        :value=\"inputForm.${c.javaFieldId}\"\n                        :clearable=\"true\"\n                        :accordion=\"true\"\n                        @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"fileselect\">\n              <el-upload ref=\"${c.javaFieldId}\"\n                    v-if=\"visible\"\n                    :action=\"`${\"$\"}{this.${\"$\"}http.BASE_URL}/sys/file/webupload/upload?uploadPath=/${moduleName}<#if subModuleName != \"\">/${subModuleName}</#if>/${className}`\"\n                    :headers=\"{token: ${\"$\"}cookie.get(\'token\')}\"\n                    :on-preview=\"(file, fileList) => {$window.location.href = (file.response && file.response.url) || file.url}\"\n                    :on-success=\"(response, file, fileList) => {\n                       inputForm.${c.javaFieldId} = fileList.map(item => (item.response && item.response.url) || item.url).join(\'|\')\n                    }\"\n                    :on-remove=\"(file, fileList) => {\n                      ${\"$\"}http.post(`/sys/file/webupload/deleteByUrl?url=${\"$\"}{(file.response && file.response.url) || file.url}`).then(({data}) => {\n                        ${\"$\"}message.success(data.msg)\n                      })\n                      inputForm.${c.javaFieldId} = fileList.map(item => item.url).join(\'|\')\n                    }\"\n                    :before-remove=\"(file, fileList) => {\n                      return ${\"$\"}confirm(`确定移除 ${\"$\"}{file.name}？`)\n                    }\"\n                    multiple\n                    :limit=\"5\"\n                    :on-exceed=\"(files, fileList) =>{\n                      $message.warning(`当前限制选择 5 个文件，本次选择了 ${\"$\"}{files.length} 个文件，共选择了 ${\"$\"}{files.length + fileList.length} 个文件`)\n                    }\"\n                    :file-list=\"${c.javaFieldId}Arra\">\n                    <el-button size=\"small\" type=\"primary\">点击上传</el-button>\n                    <div slot=\"tip\" class=\"el-upload__tip\">添加相关附件</div>\n                  </el-upload>\n<#elseif c.showType == \"imageselect\">\n              <el-upload ref=\"${c.javaFieldId}\"\n              v-if=\"visible\"\n              list-type=\"picture-card\"\n                    :action=\"`${\"$\"}{this.${\"$\"}http.BASE_URL}/sys/file/webupload/upload?uploadPath=/${moduleName}<#if subModuleName != \"\">/${subModuleName}</#if>/${className}`\"\n                    :headers=\"{token: ${\"$\"}cookie.get(\'token\')}\"\n                    :on-preview=\"(file, fileList) => {\n                        $alert(`<img style=\'width:100%\' src=\' ${\"$\"}{(file.response && file.response.url) || file.url}\'/>`,  {\n                          dangerouslyUseHTMLString: true,\n                          showConfirmButton: false,\n                          closeOnClickModal: true,\n                          customClass: \'showPic\'\n                        });\n                    }\"\n                    :on-success=\"(response, file, fileList) => {\n                       inputForm.${c.javaFieldId} = fileList.map(item => (item.response && item.response.url) || item.url).join(\'|\')\n                    }\"\n                    :on-remove=\"(file, fileList) => {\n                      ${\"$\"}http.post(`/sys/file/webupload/deleteByUrl?url=${\"$\"}{(file.response && file.response.url) || file.url}`).then(({data}) => {\n                        ${\"$\"}message.success(data.msg)\n                      })\n                      inputForm.${c.javaFieldId} = fileList.map(item => item.url).join(\'|\')\n                    }\"\n                    :before-remove=\"(file, fileList) => {\n                      return ${\"$\"}confirm(`确定移除 ${\"$\"}{file.name}？`)\n                    }\"\n                    multiple\n                    :limit=\"5\"\n                    :on-exceed=\"(files, fileList) =>{\n                      $message.warning(`当前限制选择 5 个文件，本次选择了 ${\"$\"}{files.length} 个文件，共选择了 ${\"$\"}{files.length + fileList.length} 个文件`)\n                    }\"\n                    :file-list=\"${c.javaFieldId}Arra\">\n                    <i class=\"el-icon-plus\"></i>\n                  </el-upload>\n    <#elseif c.showType == \"treeselect\">\n          <SelectTree\n                      ref=\"${c.simpleJavaField}\"\n                      :props=\"{\n                          value: \'id\',             // ID字段名\n                          label: \'name\',         // 显示名称\n                          children: \'children\'    // 子级字段名\n                        }\"\n                      url=\"${c.dataUrl}/treeData\"\n                      :value=\"inputForm.${c.javaFieldId}\"\n                      :clearable=\"true\"\n                      :accordion=\"true\"\n                      @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"gridselect\">\n          <GridSelect\n            title=\"选择${c.comments}\"\n            labelName = \'${c.javaFieldName?split(\".\")[1]}\'\n            labelValue = \'${c.javaFieldId?split(\".\")[1]}\'\n            :value = \"inputForm.${c.javaFieldId}\"\n            :limit=\"1\"\n            @getValue=\'(value) => {inputForm.${c.javaFieldId}=value}\'\n            :columns=\"[\n    <#list c.fieldLabels?split(\"|\") as f>\n            {\n              prop: \'${c.fieldKeys?split(\"|\")[f_index]}\',\n              label: \'${c.fieldLabels?split(\"|\")[f_index]}\'\n            }<#if f_has_next>,</#if>\n    </#list>\n            ]\"\n            :searchs=\"[\n   <#list c.searchLabel?split(\"|\") as f>\n            {\n              prop: \'${c.searchKey?split(\"|\")[f_index]}\',\n              label: \'${c.searchLabel?split(\"|\")[f_index]}\'\n            }<#if f_has_next>,</#if>\n    </#list>\n            ]\"\n            dataListUrl=\"${c.dataUrl}/list\"\n            entityBeanName=\"${c.dataUrl?split(\"/\")[c.dataUrl?split(\"/\")?size-1]}\"\n            queryEntityUrl=\"${c.dataUrl}/queryById\">\n          </GridSelect>\n    <#elseif c.showType == \"wangeditor\">\n            <WangEditor ref=\"${c.javaFieldId}Editor\" v-model=\"inputForm.${c.javaFieldId}\"/>\n    <#elseif c.showType == \"quilleditor\">\n            <vue-editor useCustomImageHandler @image-added=\"$utils.handleImageAdded\" v-model=\"inputForm.${c.javaFieldId}\"/>\n    </#if>\n           </el-form-item>\n        </el-col>\n      </#if>\n    </#list>\n        </el-row>\n    </el-form>\n    <span slot=\"footer\" class=\"dialog-footer\">\n      <el-button size=\"small\" @click=\"visible = false\">关闭</el-button>\n      <el-button size=\"small\" type=\"primary\" v-if=\"method != \'view\'\" @click=\"doSubmit()\" v-noMoreClick>确定</el-button>\n    </span>\n  </el-dialog>\n</div>\n</template>\n\n<script>\n<#list table.importFormList as c>\n  ${c}\n</#list>\n  export default {\n    data () {\n      return {\n        title: \'\',\n        method: \'\',\n        visible: false,\n        loading: false,\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"fileselect\" || c.showType == \"imageselect\">\n        ${c.javaFieldId}Arra: [],\n  </#if>\n  </#if>\n</#list>\n        inputForm: {\n          id: \'\'<#if (table.formList?size>0)>,</#if>\n        <#list table.formList as c>\n          <#if c.javaFieldId?contains(\".\")>\n          ${c.javaFieldId?split(\".\")[0]}: {\n            ${c.javaFieldId?split(\".\")[1]}: \'\'\n          }<#if c_has_next>,</#if>\n          <#else>\n          ${c.javaFieldId}: \'\'<#if c_has_next>,</#if>\n          </#if>\n    </#list>\n        }\n      }\n    },\n    components: {\n<#list table.formComponents as c>\n      ${c}<#if c_has_next>,</#if>\n</#list>\n    },\n    <#assign cks=0/>\n    <#list table.formList as c>\n     <#if c.showType == \"checkbox\">\n     <#assign cks= cks + 1/>\n    </#if>\n    </#list>\n    <#if cks gt 0>\n    computed: {\n    <#assign ckIndex=0/>\n    <#list table.formList as c>\n     <#if c.showType == \"checkbox\">\n     <#assign ckIndex= ckIndex + 1/>\n      ${c.javaFieldId}List: {\n        get: function () {\n          return this.inputForm.${c.javaFieldId} !== \'\' ? this.inputForm.${c.javaFieldId}.split(\',\') : []\n        },\n        set: function (val) {\n          this.inputForm.${c.javaFieldId} = val.join(\',\')\n        }\n      }<#if ckIndex lt cks >,</#if>\n    </#if>\n    </#list>\n    },\n    </#if>\n    methods: {\n      init (method, obj) {\n        this.method = method\n        this.inputForm.id = obj.id\n        if (method === \'add\') {\n          this.title = \'新建${functionNameSimple}\'\n        } else if (method === \'addChild\') {\n          this.title = \'添加下级${functionNameSimple}\'\n        } else if (method === \'edit\') {\n          this.title = \'修改${functionNameSimple}\'\n        } else if (method === \'view\') {\n          this.title = \'查看${functionNameSimple}\'\n        }\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"imageselect\" || c.showType == \"fileselect\">\n        this.${c.javaFieldId}Arra = []\n  </#if>\n  </#if>\n</#list>\n        this.visible = true\n        this.loading = false\n        this.$nextTick(() => {\n          this.$refs.inputForm.resetFields()\n          this.inputForm.parent.id = obj.parent.id\n          this.inputForm.parent.name = obj.parent.name\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"wangeditor\">\n          this.$refs.${c.javaFieldId}Editor.init(\'\')\n  </#if>\n  </#if>\n</#list>\n          if (method === \'edit\' || method === \'view\') { // 修改或者查看\n            this.loading = true\n            this.$http({\n              url: `/${urlPrefix}/queryById?id=${\"$\"}{this.inputForm.id}`,\n              method: \'get\'\n            }).then(({data}) => {\n              this.inputForm = this.recover(this.inputForm, data.${className})\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"wangeditor\">\n              this.$refs.${c.javaFieldId}Editor.init(this.inputForm.${c.javaFieldId})\n  </#if>\n  </#if>\n</#list>\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"fileselect\" || c.showType == \"imageselect\">\n              this.inputForm.${c.javaFieldId}.split(\'|\').forEach((item) => {\n                if (item.trim().length > 0) {\n                  this.${c.javaFieldId}Arra.push({name: decodeURIComponent(item.substring(item.lastIndexOf(\'/\') + 1)), url: item})\n                }\n              })\n  </#if>\n  </#if>\n</#list>\n              this.loading = false\n            })\n          }\n        })\n      },\n      // 表单提交\n      doSubmit () {\n        this.$refs[\'inputForm\'].validate((valid) => {\n          if (valid) {\n            this.loading = true\n            this.$http({\n              url: `/${urlPrefix}/save`,\n              method: \'post\',\n              data: this.inputForm\n            }).then(({data}) => {\n              this.loading = false\n              if (data && data.success) {\n                this.visible = false\n                this.$message.success(data.msg)\n                this.$emit(\'refreshDataList\')\n              }\n            })\n          }\n        })\n      }\n    }\n  }\n</script>\n\n  ]]>\n    </content>\n</template>', '', 0, 0, '0', NULL, '1', '2021-10-04 21:59:07', '1', '2021-10-04 21:59:07');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1445025692721901569', 'mapper.java', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<template>\r\n    <name>mapper</name>\r\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/mapper</filePath>\r\n    <fileName>${ClassName}Mapper.java</fileName>\r\n    <content><![CDATA[\r\n/**\r\n * Copyright © 2015-2020 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\r\n */\r\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper;\r\n\r\nimport org.springframework.stereotype.Repository;\r\nimport cn.weegoo.core.persistence.TreeMapper;\r\nimport org.apache.ibatis.annotations.Mapper;\r\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.entity.${ClassName};\r\n\r\n/**\r\n * ${functionName}MAPPER接口\r\n * @author ${functionAuthor}\r\n * @version ${functionVersion}\r\n */\r\n@Mapper\r\n@Repository\r\npublic interface ${ClassName}Mapper extends TreeMapper<${ClassName}> {\r\n	\r\n}]]>\r\n    </content>\r\n</template>', '', 0, 0, '0', NULL, '1', '2021-10-04 21:59:07', '1', '2021-10-04 21:59:07');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1445025692910645249', 'service.java', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>service</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/service</filePath>\n    <fileName>${ClassName}Service.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2015-2020 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service;\n\nimport java.util.List;\n\nimport org.springframework.stereotype.Service;\nimport org.springframework.transaction.annotation.Transactional;\n\nimport cn.weegoo.core.service.TreeService;\nimport cn.weegoo.common.utils.StringUtils;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.entity.${ClassName};\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper.${ClassName}Mapper;\n<#if table.dataSource?? && table.dataSource.enName?? && table.dataSource.enName!=\"\" && table.dataSource.enName!=\"master\">\nimport cn.weegoo.database.datasource.annotation.DS;\n</#if>\n\n/**\n * ${functionName}Service\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n<#if table.dataSource?? && table.dataSource.enName?? && table.dataSource.enName!=\"\" && table.dataSource.enName!=\"master\">\n@DS(\"${table.dataSource.enName}\")\n</#if>\n@Service\n@Transactional(readOnly = true)\npublic class ${ClassName}Service extends TreeService<${ClassName}Mapper, ${ClassName}> {\n\n	public ${ClassName} get(String id) {\n		return super.get(id);\n	}\n\n	public List<${ClassName}> findList(${ClassName} ${className}) {\n		if (StringUtils.isNotBlank(${className}.getParentIds())){\n			${className}.setParentIds(\",\"+${className}.getParentIds()+\",\");\n		}\n		return super.findList(${className});\n	}\n\n	@Transactional(readOnly = false)\n	public void save(${ClassName} ${className}) {\n		super.save(${className});\n	}\n\n	@Transactional(readOnly = false)\n	public void delete(${ClassName} ${className}) {\n		super.delete(${className});\n	}\n\n}]]>\n    </content>\n</template>', '', 0, 0, '0', NULL, '1', '2021-10-04 21:59:07', '1', '2021-10-04 21:59:07');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1445025693120360450', 'entity.java', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<template>\r\n    <name>entity</name>\r\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/entity</filePath>\r\n    <fileName>${ClassName}.java</fileName>\r\n    <content><![CDATA[\r\n/**\r\n * Copyright © 2015-2020 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\r\n */\r\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.entity;\r\n\r\n<#list table.importList as i>\r\nimport ${i};\r\n</#list>\r\nimport com.fasterxml.jackson.annotation.JsonIgnoreProperties;\r\n\r\nimport cn.weegoo.core.persistence.TreeEntity;\r\nimport lombok.Data;\r\n\r\n/**\r\n * ${functionName}Entity\r\n * @author ${functionAuthor}\r\n * @version ${functionVersion}\r\n */\r\n@Data \r\n@JsonIgnoreProperties(value={\"hibernateLazyInitializer\",\"handler\"})\r\npublic class ${ClassName} extends TreeEntity<${ClassName}> {\r\n	\r\n	private static final long serialVersionUID = 1L;\r\n	<#-- 生成字段属性 -->\r\n	<#list table.columnList as c>\r\n		<#-- 如果不是基类属性 -->\r\n		<#if c.isNotTreeBaseField>\r\n			<#list c.simpleAnnotationList as a>\r\n	@${a}\r\n			</#list>\r\n			<#-- 父类对象 -->\r\n			<#if table.parentExists && table.parentTableFk == c.name>\r\n	private ${table.parent.className?cap_first} ${c.simpleJavaField};		<#if c.comments??>// ${c.comments} 父类</#if>\r\n			<#-- 其它字段 -->\r\n			<#else>\r\n	private ${c.simpleJavaType} ${c.simpleJavaField};		<#if c.comments??>// ${c.comments}</#if>\r\n			</#if>\r\n		</#if>\r\n	</#list>\r\n	\r\n	<#-- 范围条件字段 -->\r\n	<#list table.columnList as c>\r\n		<#if c.isQuery?? && c.isQuery == \"1\" && c.queryType == \"between\">\r\n	private ${c.simpleJavaType} begin${c.simpleJavaField?cap_first};		<#if c.comments??>// 开始 ${c.comments}</#if>\r\n	private ${c.simpleJavaType} end${c.simpleJavaField?cap_first};		<#if c.comments??>// 结束 ${c.comments}</#if>\r\n		</#if>\r\n	</#list>\r\n	<#-- 子表列表字段 -->\r\n	<#list table.childList as c>\r\n	private List<${c.className?cap_first}> ${c.className?uncap_first}List = Lists.newArrayList();		// 子表列表\r\n	</#list>\r\n	\r\n	<#-- 构造方法 -->\r\n	public ${ClassName}() {\r\n		super();\r\n		<#if table.genIdType ==\"2\">\r\n		this.setIdType(IDTYPE_AUTO);\r\n		</#if>\r\n	}\r\n\r\n	public ${ClassName}(String id){\r\n		super(id);\r\n		<#if table.genIdType ==\"2\">\r\n		this.setIdType(IDTYPE_AUTO);\r\n		</#if>\r\n	}\r\n	<#list table.columnList as c>\r\n		<#if table.parentExists && table.parentTableFk == c.name>\r\n\r\n	public ${ClassName}(${table.parent.className?cap_first} ${c.simpleJavaField}){\r\n		this.${c.simpleJavaField} = ${c.simpleJavaField};\r\n	}\r\n		</#if>\r\n	</#list>\r\n\r\n	public  ${ClassName} getParent() {\r\n			return parent;\r\n	}\r\n	\r\n	@Override\r\n	public void setParent(${ClassName} parent) {\r\n		this.parent = parent;\r\n		\r\n	}\r\n	\r\n	<#-- JSON忽略了parent对象，添加此方法获取父类Id -->\r\n	<#list table.columnList as c>\r\n		<#if c.javaFieldId == \'parent.id\'>\r\n	public String getParentId() {\r\n		return parent != null && parent.getId() != null ? parent.getId() : \"0\";\r\n	}\r\n		</#if>\r\n	</#list>\r\n}]]>\r\n    </content>\r\n</template>', '', 0, 0, '0', NULL, '1', '2021-10-04 21:59:07', '1', '2021-10-04 21:59:07');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1445025693334269953', 'controller.java', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>controller</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/web</filePath>\n    <fileName>${ClassName}Controller.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2015-2020 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.web;\n\n<#list table.columnList as c>\n		<#-- 如果存在自定义java对象 -->\n		<#if c.tableName?? && c.tableName!=\"\">\nimport java.io.UnsupportedEncodingException;\nimport java.net.URLDecoder;\n		<#break>\n		</#if>\n</#list>\nimport java.util.List;\n\nimport org.apache.shiro.authz.annotation.Logical;\nimport org.apache.shiro.authz.annotation.RequiresPermissions;\nimport org.springframework.beans.factory.annotation.Autowired;\nimport org.springframework.ui.Model;\nimport org.springframework.web.bind.annotation.ModelAttribute;\nimport org.springframework.web.bind.annotation.RequestMapping;\nimport org.springframework.web.bind.annotation.GetMapping;\nimport org.springframework.web.bind.annotation.DeleteMapping;\nimport org.springframework.web.bind.annotation.PostMapping;\nimport org.springframework.web.bind.annotation.RequestParam;\nimport org.springframework.web.bind.annotation.RestController;\n\nimport com.google.common.collect.Lists;\nimport cn.weegoo.common.json.AjaxJson;\nimport cn.weegoo.core.web.BaseController;\nimport cn.weegoo.common.utils.StringUtils;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.entity.${ClassName};\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.${ClassName}Service;\n\n/**\n * ${functionName}Controller\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n@RestController\n@RequestMapping(value = \"/${urlPrefix}\")\npublic class ${ClassName}Controller extends BaseController {\n\n	@Autowired\n	private ${ClassName}Service ${className}Service;\n\n	@ModelAttribute\n	public ${ClassName} get(@RequestParam(required=false) String id) {\n		${ClassName} entity = null;\n		if (StringUtils.isNotBlank(id)){\n			entity = ${className}Service.get(id);\n		}\n		if (entity == null){\n			entity = new ${ClassName}();\n		}\n		return entity;\n	}\n\n\n	/**\n	 * ${functionNameSimple}树表数据\n	 */\n	@RequiresPermissions(\"${permissionPrefix}:list\")\n	@GetMapping(\"list\")\n	public AjaxJson list(${ClassName} ${className}) {\n		return AjaxJson.success().put(\"list\", ${className}Service.findList(${className}));\n	}\n\n	/**\n	 * 根据Id获取${functionNameSimple}数据\n	 */\n	@RequiresPermissions(value={\"${permissionPrefix}:view\",\"${permissionPrefix}:add\",\"${permissionPrefix}:edit\"},logical=Logical.OR)\n	@GetMapping(\"queryById\")\n	public AjaxJson queryById(${ClassName} ${className}) {\n		return AjaxJson.success().put(\"${className}\", ${className});\n	}\n\n	/**\n	 * 保存${functionNameSimple}\n	 */\n	@RequiresPermissions(value={\"${permissionPrefix}:add\",\"${permissionPrefix}:edit\"},logical=Logical.OR)\n	@PostMapping(\"save\")\n	public AjaxJson save(${ClassName} ${className}, Model model) throws Exception{\n		/**\n		 * 后台hibernate-validation插件校验\n		 */\n		String errMsg = beanValidator(${className});\n		if (StringUtils.isNotBlank(errMsg)){\n			return AjaxJson.error(errMsg);\n		}\n		//新增或编辑表单保存\n		${className}Service.save(${className});//保存\n		return AjaxJson.success(\"保存${functionNameSimple}成功\");\n	}\n\n	/**\n	 * 删除${functionNameSimple}\n	 */\n	@RequiresPermissions(\"${permissionPrefix}:del\")\n	@DeleteMapping(\"delete\")\n	public AjaxJson delete(String ids) {\n		String idArray[] =ids.split(\",\");\n		for(String id : idArray){\n			${className}Service.delete(new ${ClassName}(id));\n		}\n		return AjaxJson.success(\"删除${functionNameSimple}成功\");\n	}\n\n	/**\n     * 获取JSON树形数据。\n     * @param extId 排除的ID\n     * @return\n	*/\n	@RequiresPermissions(\"user\")\n	@GetMapping(\"treeData\")\n	public AjaxJson treeData(@RequestParam(required = false) String extId) {\n		List<${ClassName}> list = ${className}Service.findList(new ${ClassName}());\n		List rootTree =  ${className}Service.formatListToTree (new ${ClassName} (\"0\"),list, extId );\n		return AjaxJson.success().put(\"treeData\", rootTree);\n	}\n\n}]]>\n    </content>\n</template>', '', 0, 0, '0', NULL, '1', '2021-10-04 21:59:07', '1', '2021-10-04 21:59:07');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1445025693585928193', 'mapper.xml', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>mapper</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/mapper/xml</filePath>\n    <fileName>${ClassName}Mapper.xml</fileName>\n    <content><![CDATA[\n<?xml version=\"1.0\" encoding=\"UTF-8\" ?>\n<!DOCTYPE mapper PUBLIC \"-//mybatis.org//DTD Mapper 3.0//EN\" \"http://mybatis.org/dtd/mybatis-3-mapper.dtd\">\n<mapper namespace=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper.${ClassName}Mapper\">\n\n    <resultMap id=\"${className}Result\" type=\"${ClassName}\">\n   		<result property=\"id\" column=\"id\" />\n		<result property=\"name\" column=\"name\" />\n		<result property=\"sort\" column=\"sort\" />\n		<result property=\"parentIds\" column=\"parentIds\" />\n		<#list table.columnList as c>\n		<#if c.isNotTreeBaseField>\n		<result property=\"${c.simpleJavaField}\" column=\"${c.simpleJavaField}\" />\n		</#if>\n		</#list>\n    </resultMap>\n\n    <#-- 输出字段列 -->\n	<sql id=\"${className}Columns\">\n		<#assign columnField>\n			<#list table.columnList as c>\n		a.${c.name} AS \"${c.javaFieldId}\",\n			</#list>\n			<#list table.columnList as c>\n			<#-- 父表关联字段 -->\n				<#if table.parentExists && table.parentTableFk == c.name && c.javaType != \"This\">\n					<#list c.javaFieldAttrs as a>\n		b.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"userselect\" && c.javaType != \"This\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"officeselect\" && c.javaType != \"This\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"areaselect\" && c.javaType != \"This\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"treeselect\" && c.javaType != \"This\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"gridselect\" && c.javaType != \"This\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n				</#list>\n				</#if>\n\n			</#list>\n		</#assign>\n${columnField?substring(0, columnField?last_index_of(\",\"))}\n	</sql>\n\n\n\n\n	<#-- 输出字段关联表 -->\n	<sql id=\"${className}Joins\">\n\n		<#-- 关联系统表 -->\n		<#list table.columnList as c>\n		<#-- 关联父表 -->\n		<#if table.parentExists && table.parentTableFk == c.name && c.javaType != \"This\">\n			LEFT JOIN ${table.parent.name} b ON b.id = a.${table.parentTableFk}\n			<#elseif c.showType?? && c.showType == \"userselect\" && c.javaType != \"This\">\n		LEFT JOIN sys_user ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"officeselect\" && c.javaType != \"This\">\n		LEFT JOIN sys_office ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"areaselect\" && c.javaType != \"This\">\n		LEFT JOIN sys_area ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"treeselect\" && c.javaType != \"This\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"gridselect\" && c.javaType != \"This\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			</#if>\n		</#list>\n	</sql>\n\n\n\n	<select id=\"get\" resultType=\"${ClassName}\">\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		WHERE a.id = ${\"#\"}{id}\n	</select>\n\n	<select id=\"findList\" resultType=\"${ClassName}\">\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		<where>\n			<#if table.delFlagExists>a.del_flag = ${\"#\"}{DEL_FLAG_NORMAL}</#if>\n			${\"$\"}{dataScope}\n			<#list table.columnList as c>\n				<#if (c.isQuery?? && c.isQuery == \"1\") || (table.parentExists && table.parentTableFk == c.name) || c.javaFieldId == \'parent.id\' || c.javaFieldId == \'parentIds\'>\n					<#if c.queryType ?? && c.queryType == \'between\'>\n			<if test=\"begin${c.simpleJavaField?cap_first} != null and end${c.simpleJavaField?cap_first} != null <#if c.simpleJavaField != c.javaFieldId>and begin${c.javaFieldId?cap_first} != null and end${c.javaFieldId?cap_first} != null </#if>\">\n					<#else>\n			<if test=\"${c.simpleJavaField} != null<#if c.simpleJavaField != c.javaFieldId> and ${c.javaFieldId} != null</#if> and ${c.javaFieldId} != \'\'\">\n					</#if>\n					<#if c.queryType ?? && c.queryType == \'between\'>\n				AND a.${c.name} BETWEEN ${\"#\"}{begin${c.simpleJavaField?cap_first}} AND ${\"#\"}{end${c.simpleJavaField?cap_first}}\n					<#elseif c.queryType ?? && c.queryType == \'like\'>\n				AND a.${c.name} LIKE\n				    <if test=\"_databaseId == \'postgre\'\">\'%\'||${\"#\"}{${c.javaFieldId}}||\'%\'</if>\n					<if test=\"_databaseId == \'oracle\'\">\'%\'||${\"#\"}{${c.javaFieldId}}||\'%\'</if>\n					<if test=\"_databaseId == \'mssql\'\">\'%\'+${\"#\"}{${c.javaFieldId}}+\'%\'</if>\n					<if test=\"_databaseId == \'mysql\'\">concat(\'%\',${\"#\"}{${c.javaFieldId}},\'%\')</if>\n					<#elseif c.queryType ?? && c.queryType == \'left_like\'>\n				AND a.${c.name} LIKE\n				    <if test=\"_databaseId == \'postgre\'\">\'%\'||${\"#\"}{${c.javaFieldId}}</if>\n					<if test=\"_databaseId == \'oracle\'\">\'%\'||${\"#\"}{${c.javaFieldId}}</if>\n					<if test=\"_databaseId == \'mssql\'\">\'%\'+${\"#\"}{${c.javaFieldId}}</if>\n					<if test=\"_databaseId == \'mysql\'\">concat(\'%\',${\"#\"}{${c.javaFieldId}})</if>\n					<#elseif c.queryType ?? && c.queryType == \'right_like\'>\n				AND a.${c.name} LIKE\n				    <if test=\"_databaseId == \'postgre\'\">${\"#\"}{${c.javaFieldId}}||\'%\'</if>\n					<if test=\"_databaseId == \'oracle\'\">${\"#\"}{${c.javaFieldId}}||\'%\'</if>\n					<if test=\"_databaseId == \'mssql\'\">${\"#\"}{${c.javaFieldId}}+\'%\'</if>\n					<if test=\"_databaseId == \'mysql\'\">concat(${\"#\"}{${c.javaFieldId}},\'%\')</if>\n					<#else>\n				AND a.${c.name} ${c.queryType} ${\"#\"}{${c.javaFieldId}}\n					</#if>\n			</if>\n				</#if>\n			</#list>\n		</where>\n		<#list table.columnList as c>\n			<#if c.name == \'sort\'>\n		ORDER BY a.sort ASC\n			</#if>\n		</#list>\n	</select>\n\n	<select id=\"findAllList\" resultType=\"${ClassName}\">\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		<where>\n			<#if table.delFlagExists>a.del_flag = ${\"#\"}{DEL_FLAG_NORMAL}</#if>\n			${\"$\"}{dataScope}\n		</where>\n		<#list table.columnList as c>\n			<#if c.name == \'sort\'>\n		ORDER BY a.sort ASC\n			</#if>\n		</#list>\n	</select>\n\n	<select id=\"getChildren\" parameterType=\"String\" resultMap=\"${className}Result\">\n        select * from ${table.name} where parent_id = ${\"#\"}{id} ORDER BY sort\n    </select>\n\n	<select id=\"findByParentIdsLike\" resultType=\"${ClassName}\">\n		SELECT\n			a.id,\n			a.parent_id AS \"parent.id\",\n			a.parent_ids\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		<where>\n			<#if table.delFlagExists>a.del_flag = ${\"#\"}{DEL_FLAG_NORMAL}</#if>\n			AND a.parent_ids LIKE ${\"#\"}{parentIds}\n		</where>\n		<#list table.columnList as c>\n			<#if c.name == \'sort\'>\n		ORDER BY a.sort ASC\n			</#if>\n		</#list>\n	</select>\n\n	<insert id=\"insert\">\n		INSERT INTO ${table.name}(\n		<#assign insertField>\n			<#list table.columnList as c>\n				<#if c.isInsert?? && c.isInsert == \"1\">\n				<#if !(table.genIdType ==\"2\" && c.name==\'id\') >\n			   ${c.name},\n				</#if>\n				</#if>\n			</#list>\n		</#assign>\n${insertField?substring(0, insertField?last_index_of(\",\"))}\n		) VALUES (\n		<#assign insertJavaField>\n			<#list table.columnList as c>\n				<#if c.isInsert?? && c.isInsert == \"1\">\n				<#if !(table.genIdType ==\"2\" && c.name==\'id\') >\n			  <#if c.showType == \"umeditor\">\n			${\"#\"}{${c.javaFieldId}, jdbcType=BLOB},\n			    <#else>\n			${\"#\"}{${c.javaFieldId}},\n				</#if>\n				</#if>\n				</#if>\n			</#list>\n		</#assign>\n${insertJavaField?substring(0, insertJavaField?last_index_of(\",\"))}\n		)\n	</insert>\n\n	<update id=\"update\">\n		UPDATE ${table.name} SET\n			<#assign updateField>\n				<#list table.columnList as c>\n					<#if c.isEdit?? && c.isEdit == \"1\">\n			    <#if c.showType == \"umeditor\">\n			${c.name} = ${\"#\"}{${c.javaFieldId}, jdbcType=BLOB},\n			    <#else>\n			${c.name} = ${\"#\"}{${c.javaFieldId}},\n				</#if>\n					</#if>\n				</#list>\n			</#assign>\n${updateField?substring(0, updateField?last_index_of(\",\"))}\n		WHERE id = ${\"#\"}{id}\n	</update>\n\n	<update id=\"updateParentIds\">\n		UPDATE ${table.name} SET\n			parent_id = ${\"#\"}{parent.id},\n			parent_ids = ${\"#\"}{parentIds}\n		WHERE id = ${\"#\"}{id}\n	</update>\n\n	<!--物理删除-->\n	<update id=\"delete\">\n		DELETE FROM ${table.name}\n	<#if table.parentExists>\n		<#list table.columnList as c>\n			<#if table.parentTableFk == c.name>\n		<choose>\n			<when test=\"id !=null and id != \'\'\">\n				WHERE id = ${\"#\"}{id}\n			</when>\n			<otherwise>\n				WHERE ${table.parentTableFk} = ${\"#\"}{${c.javaFieldId}}\n			</otherwise>\n		</choose>\n			</#if>\n		</#list>\n	<#else>\n		WHERE id = ${\"#\"}{id} OR parent_ids LIKE\n		<if test=\"_databaseId == \'postgre\'\">\'%,\'||${\"#\"}{id}||\',%\'</if>\n		<if test=\"_databaseId == \'oracle\'\">\'%,\'||${\"#\"}{id}||\',%\'</if>\n		<if test=\"_databaseId == \'mssql\'\">\'%,\'+${\"#\"}{id}+\',%\'</if>\n        <if test=\"_databaseId == \'mysql\'\">CONCAT(\'%,\', ${\"#\"}{id}, \',%\')</if>\n	</#if>\n	</update>\n\n	<!--逻辑删除-->\n	<update id=\"deleteByLogic\">\n		UPDATE ${table.name} SET\n			del_flag = ${\"#\"}{DEL_FLAG_DELETE}\n	<#if table.parentExists>\n		<#list table.columnList as c>\n			<#if table.parentTableFk == c.name>\n		<choose>\n			<when test=\"id !=null and id != \'\'\">\n				WHERE id = ${\"#\"}{id}\n			</when>\n			<otherwise>\n				WHERE ${table.parentTableFk} = ${\"#\"}{${c.javaFieldId}}\n			</otherwise>\n		</choose>\n			</#if>\n		</#list>\n	<#else>\n		WHERE id = ${\"#\"}{id} OR parent_ids LIKE\n		<if test=\"_databaseId == \'postgre\'\">\'%,\'||${\"#\"}{id}||\',%\'</if>\n		<if test=\"_databaseId == \'oracle\'\">\'%,\'||${\"#\"}{id}||\',%\'</if>\n		<if test=\"_databaseId == \'mssql\'\">\'%,\'+${\"#\"}{id}+\',%\'</if>\n        <if test=\"_databaseId == \'mysql\'\">CONCAT(\'%,\', ${\"#\"}{id}, \',%\')</if>\n	</#if>\n	</update>\n\n</mapper>]]>\n    </content>\n</template>', '', 0, 0, '0', NULL, '1', '2021-10-04 21:59:07', '1', '2021-10-04 21:59:07');
