INSERT INTO `plugin_gencode_template_obj` VALUES ('1449403450998779906', 'controller.java', '1449403450692595713', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>controller</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/controller</filePath>\n    <fileName>${ClassName}Controller.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.controller;\n\nimport javax.validation.Valid;\nimport cn.weegoo.common.utils.ResponseUtil;\nimport cn.weegoo.core.query.QueryWrapperGenerator;\nimport cn.weegoo.aop.logging.annotation.ApiLog;\nimport io.swagger.annotations.Api;\nimport io.swagger.annotations.ApiOperation;\nimport org.springframework.http.ResponseEntity;\nimport org.springframework.beans.factory.annotation.Autowired;\nimport org.springframework.web.bind.annotation.*;\nimport com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;\nimport com.baomidou.mybatisplus.core.metadata.IPage;\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\n<#if !isNeedJoinTable>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${ClassName};\n</#if>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.${ClassName}Service;\n\n/**\n * ${functionName}Controller\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n@Api(tags =\"${functionName}\")\n@RestController\n@RequestMapping(value = \"/${urlPrefix}\")\npublic class ${ClassName}Controller {\n\n	@Autowired\n	private ${ClassName}Service ${className}Service;\n\n	/**\n	 * ${functionNameSimple}列表数据\n	 */\n	@ApiLog(\"查询${functionNameSimple}列表数据\")\n	@ApiOperation(value = \"查询${functionNameSimple}列表数据\")\n	@GetMapping(\"list\")\n	public ResponseEntity<#if isNeedJoinTable><IPage<${ClassName}DTO>><#else><IPage<${ClassName}>></#if> list(${ClassName}DTO ${className}DTO, Page<<#if isNeedJoinTable>${ClassName}DTO<#else>${ClassName}</#if>> page) throws Exception {\n		QueryWrapper queryWrapper = QueryWrapperGenerator.buildQueryCondition (${className}DTO, ${ClassName}DTO.class);\n				<#if isNeedJoinTable>\n		IPage<${ClassName}DTO> result = ${className}Service.findPage (page, queryWrapper);\n					<#else>\n		IPage<${ClassName}> result = ${className}Service.page (page, queryWrapper);\n					</#if>\n		return ResponseEntity.ok (result);\n	}\n\n\n	/**\n	 * 根据Id获取${functionNameSimple}数据\n	 */\n	@ApiLog(\"根据Id获取${functionNameSimple}数据\")\n	@ApiOperation(value = \"根据Id获取${functionNameSimple}数据\")\n	@GetMapping(\"queryById\")\n	public ResponseEntity<${ClassName}DTO> queryById(String id) {\n		return ResponseEntity.ok ( ${className}Service.findById ( id ) );\n	}\n\n	/**\n	 * 保存${functionNameSimple}\n	 */\n	@ApiLog(\"保存${functionNameSimple}\")\n	@ApiOperation(value = \"保存${functionNameSimple}\")\n	@PostMapping(\"save\")\n	public  ResponseEntity <String> save(@Valid @RequestBody ${ClassName}DTO ${className}DTO) {\n		//新增或编辑表单保存\n		${className}Service.saveOrUpdate (${className}DTO);\n		return ResponseUtil.newInstance ( ).add(\"businessTable\", \"${table.name}\").add(\"businessId\", ${className}DTO.getId()).ok (\"保存${functionNameSimple}成功\");\n	}\n\n\n	/**\n	 * 删除${functionNameSimple}\n	 */\n	@ApiLog(\"删除${functionNameSimple}\")\n	@ApiOperation(value = \"删除${functionNameSimple}\")\n	@DeleteMapping(\"delete\")\n	public ResponseEntity <String> delete(String ids) {\n		String idArray[] = ids.split(\",\");\n		for(String id: idArray){\n			${className}Service.removeById ( id );\n		}\n		return ResponseEntity.ok( \"删除${functionNameSimple}成功\" );\n	}\n\n}]]>\n    </content>\n</template>\n', '', 0, 10, '0', NULL, '1', '2021-10-16 23:54:46', '1', '2021-10-10 15:56:57');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1449403451057500161', 'service.java', '1449403450692595713', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>service</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/service</filePath>\n    <fileName>${ClassName}Service.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service;\n\nimport java.util.List;\nimport org.springframework.stereotype.Service;\nimport org.springframework.transaction.annotation.Transactional;\n<#if table.dataSource?? && table.dataSource.enName?? && table.dataSource.enName!=\"\" && table.dataSource.enName!=\"master\">\nimport com.baomidou.dynamic.datasource.annotation.DS;\n</#if>\nimport com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;\n<#if !isNeedJoinTable>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.mapstruct.${ClassName}Wrapper;\n</#if>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${ClassName};\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper.${ClassName}Mapper;\n\n/**\n * ${functionName}Service\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n<#if table.dataSource?? && table.dataSource.enName?? && table.dataSource.enName!=\"\" && table.dataSource.enName!=\"master\">\n@DS(\"${table.dataSource.enName}\")\n</#if>\n@Service\n@Transactional\npublic class ${ClassName}Service extends ServiceImpl<${ClassName}Mapper, ${ClassName}> {\n<#if isNeedJoinTable>\n\n	/**\n	 * 根据id查询\n	 * @param id\n	 * @return\n	 */\n	public ${ClassName}DTO findById(String id) {\n		return baseMapper.findById ( id );\n	}\n\n	/**\n	 * 查询列表\n	 * @param ${table.parentJavaField}\n	 * @return\n	 */\n	public List <${ClassName}DTO> findList(String ${table.parentJavaField}) {\n		return  baseMapper.findList (${table.parentJavaField});\n	}\n<#else >\n\n	/**\n	* 查询列表\n	* @param ${table.parentJavaField}\n	* @return\n	*/\n	public List <${ClassName}DTO> findList(String ${table.parentJavaField}) {\n		return super.lambdaQuery ().eq ( ${ClassName}::get${table.parentJavaField?cap_first}, ${table.parentJavaField} ).list ().stream ().map (${ClassName}Wrapper.INSTANCE::toDTO ).collect( Collectors.toList());\n	}\n</#if>\n\n}]]>\n    </content>\n</template>\n', '', 0, 15, '1', NULL, '1', '2021-10-10 22:47:40', '1', '2021-10-10 22:47:40');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1449403451091054593', 'mapper.xml', '1449403450692595713', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>mapper</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/mapper/xml</filePath>\n    <fileName>${ClassName}Mapper.xml</fileName>\n    <content><![CDATA[<?xml version=\"1.0\" encoding=\"UTF-8\" ?>\n<!DOCTYPE mapper PUBLIC \"-//mybatis.org//DTD Mapper 3.0//EN\" \"http://mybatis.org/dtd/mybatis-3-mapper.dtd\">\n<mapper namespace=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper.${ClassName}Mapper\">\n\n<#if isNeedJoinTable>\n    <#-- 输出字段列 -->\n	<sql id=\"${className}Columns\">\n		<#assign columnField>\n			<#list table.columnList as c>\n		a.${c.name} AS \"${c.javaFieldId}\",\n			</#list>\n			<#list table.columnList as c>\n			<#-- 父表关联字段 -->\n				<#if table.parentExists && table.parentTableFk == c.name>\n					<#list c.javaFieldAttrs as a>\n		b.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"userselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"officeselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"areaselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"treeselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"gridselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n				</#list>\n				</#if>\n\n			</#list>\n		</#assign>\n${columnField?substring(0, columnField?last_index_of(\",\"))}\n	</sql>\n\n	<#-- 输出字段关联表 -->\n	<sql id=\"${className}Joins\">\n\n		<#-- 关联系统表 -->\n		<#list table.columnList as c>\n		<#-- 关联父表 -->\n		<#if table.parentExists && table.parentTableFk == c.name>\n			LEFT JOIN ${table.parent.name} b ON b.id = a.${table.parentTableFk}\n			<#elseif c.isNotBasicUserField>\n		LEFT JOIN sys_user ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"officeselect\">\n		LEFT JOIN sys_office ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"areaselect\">\n		LEFT JOIN sys_area ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"treeselect\" && c.javaType != \"This\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"gridselect\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			</#if>\n		</#list>\n	</sql>\n\n\n	<select id=\"findById\" resultType=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO\">\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		WHERE a.id = ${\"#\"}{id} and a.del_flag = 0\n	</select>\n\n	<select id=\"findList\" resultType=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO\">\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		WHERE a.${table.parentTableFk} = ${\"#\"}{${table.parent.className}Id} and a.del_flag = 0\n	</select>\n</#if>\n\n</mapper>]]>\n    </content>\n</template>\n', '', 0, 20, '1', NULL, '1', '2021-10-16 23:54:46', '1', '2021-10-10 15:56:57');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1449403451128803330', 'service.java', '1449403450692595713', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>service</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/service</filePath>\n    <fileName>${ClassName}Service.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service;\n\nimport cn.weegoo.sys.constant.CommonConstants;\nimport org.springframework.stereotype.Service;\nimport org.springframework.transaction.annotation.Transactional;\nimport org.springframework.beans.factory.annotation.Autowired;\n<#if table.dataSource?? && table.dataSource.enName?? && table.dataSource.enName!=\"\" && table.dataSource.enName!=\"master\">\nimport com.baomidou.dynamic.datasource.annotation.DS;\n</#if>\nimport com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;\n<#if isNeedJoinTable>\nimport com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;\nimport com.baomidou.mybatisplus.core.metadata.IPage;\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\n</#if>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO;\n<#-- 子表列表 -->\n<#list table.childList as c>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${c.className?cap_first}DTO;\n</#list>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.mapstruct.${ClassName}Wrapper;\n<#list table.childList as c>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.mapstruct.${c.className?cap_first}Wrapper;\n</#list>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${ClassName};\n<#-- 子表列表 -->\n<#list table.childList as c>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${c.className?cap_first};\n</#list>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper.${ClassName}Mapper;\n\n/**\n * ${functionName}Service\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n<#if table.dataSource?? && table.dataSource.enName?? && table.dataSource.enName!=\"\" && table.dataSource.enName!=\"master\">\n@DS(\"${table.dataSource.enName}\")\n</#if>\n@Service\n@Transactional\npublic class ${ClassName}Service extends ServiceImpl<${ClassName}Mapper, ${ClassName}> {\n	<#-- 子表列表字段 -->\n<#list table.childList as c>\n	/**\n	* 子表service\n	*/\n	@Autowired\n	private ${c.className?cap_first}Service ${c.className?uncap_first}Service;\n</#list>\n<#if isNeedJoinTable>\n\n	/**\n	 * 根据id查询\n	 * @param id\n	 * @return\n	 */\n	public ${ClassName}DTO findById(String id) {\n		${ClassName}DTO ${className}DTO = baseMapper.findById ( id );\n		<#-- 子表列表 -->\n		<#list table.childList as c>\n		${className}DTO.set${c.className?cap_first}DTOList(${c.className?uncap_first}Service.findList(id));\n		</#list>\n		return ${className}DTO;\n	}\n\n	/**\n	 * 自定义分页检索\n	 * @param page\n	 * @param queryWrapper\n	 * @return\n	 */\n	public IPage <${ClassName}DTO> findPage(Page <${ClassName}DTO> page, QueryWrapper queryWrapper) {\n		queryWrapper.eq (\"a.del_flag\", 0 ); // 排除已经删除\n		return  baseMapper.findList (page, queryWrapper);\n	}\n\n	/**\n	* 保存或者更新\n	* @param  ${className}DTO\n	* @return\n	*/\n	public void saveOrUpdate(${ClassName}DTO ${className}DTO) {\n		${ClassName} ${className} =  ${ClassName}Wrapper.INSTANCE.toEntity ( ${className}DTO );\n		super.saveOrUpdate (${className});\n		${className}DTO.setId ( ${className}.getId () );\n	<#-- 子表列表 -->\n	<#list table.childList as c>\n		for (${c.className?cap_first}DTO ${c.className?uncap_first}DTO : ${className}DTO.get${c.className?cap_first}DTOList ()){\n			if ( CommonConstants.DELETED.equals ( ${c.className?uncap_first}DTO.getDelFlag()) ){\n				${c.className?uncap_first}Service.removeById ( ${c.className?uncap_first}DTO.getId () );\n			}else{\n				${c.className?cap_first} ${c.className?uncap_first} = ${c.className?cap_first}Wrapper.INSTANCE.toEntity ( ${c.className?uncap_first}DTO );\n				${c.className?uncap_first}.set${c.parentJavaField?cap_first} ( ${className}.getId () );\n				${c.className?uncap_first}Service.saveOrUpdate ( ${c.className?uncap_first} );\n			}\n		}\n	</#list>\n	}\n<#else>\n\n	/**\n	 * 根据id查询\n	 * @param id\n	 * @return\n	 */\n	public ${ClassName}DTO findById(String id) {\n		${ClassName}DTO ${className}DTO = ${ClassName}Wrapper.INSTANCE.toDTO ( super.getById ( id ) );\n<#-- 子表列表 -->\n	<#list table.childList as c>\n		${className}DTO.set${c.className?cap_first}DTOList(${c.className?uncap_first}Service.findList(id));\n	</#list>\n		return ${className}DTO;\n	}\n\n	/**\n	 * 保存或者更新\n	 * @param  ${className}DTO\n	 * @return\n	 */\n	public void saveOrUpdate(${ClassName}DTO ${className}DTO) {\n		${ClassName} ${className} =  ${ClassName}Wrapper.INSTANCE.toEntity ( ${className}DTO );\n		super.saveOrUpdate (${className});\n		${className}DTO.setId ( ${className}.getId () );\n<#-- 子表列表 -->\n	<#list table.childList as c>\n		for (${c.className?cap_first}DTO ${c.className?uncap_first}DTO : ${className}DTO.get${c.className?cap_first}DTOList ()){\n			if ( CommonConstants.DELETED.equals ( ${c.className?uncap_first}DTO.getDelFlag()) ){\n				${c.className?uncap_first}Service.removeById ( ${c.className?uncap_first}DTO.getId () );\n			}else{\n				${c.className?cap_first} ${c.className?uncap_first} = ${c.className?cap_first}Wrapper.INSTANCE.toEntity ( ${c.className?uncap_first}DTO );\n				${c.className?uncap_first}.set${c.parentJavaField?cap_first} ( ${className}.getId () );\n				${c.className?uncap_first}Service.saveOrUpdate ( ${c.className?uncap_first} );\n			}\n		}\n	</#list>\n	}\n</#if>\n\n	/**\n	 * 删除\n	 * @param  id\n	 * @return\