INSERT INTO `plugin_gencode_template_obj` VALUES ('1449403451351101441', 'mapper.xml', '1449403450692595713', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>mapper</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/mapper/xml</filePath>\n    <fileName>${ClassName}Mapper.xml</fileName>\n    <content><![CDATA[<?xml version=\"1.0\" encoding=\"UTF-8\" ?>\n<!DOCTYPE mapper PUBLIC \"-//mybatis.org//DTD Mapper 3.0//EN\" \"http://mybatis.org/dtd/mybatis-3-mapper.dtd\">\n<mapper namespace=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper.${ClassName}Mapper\">\n\n<#if isNeedJoinTable>\n    <#-- 输出字段列 -->\n	<sql id=\"${className}Columns\">\n		<#assign columnField>\n			<#list table.columnList as c>\n		a.${c.name} AS \"${c.javaFieldId}\",\n			</#list>\n			<#list table.columnList as c>\n			<#-- 父表关联字段 -->\n				<#if table.parentExists && table.parentTableFk == c.name>\n					<#list c.javaFieldAttrs as a>\n		b.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"userselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"officeselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"areaselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"treeselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"gridselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n				</#list>\n				</#if>\n\n			</#list>\n		</#assign>\n${columnField?substring(0, columnField?last_index_of(\",\"))}\n	</sql>\n\n	<#-- 输出字段关联表 -->\n	<sql id=\"${className}Joins\">\n\n		<#-- 关联系统表 -->\n		<#list table.columnList as c>\n		<#-- 关联父表 -->\n		<#if table.parentExists && table.parentTableFk == c.name>\n			LEFT JOIN ${table.parent.name} b ON b.id = a.${table.parentTableFk}\n			<#elseif c.isNotBasicUserField>\n		LEFT JOIN sys_user ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"officeselect\">\n		LEFT JOIN sys_office ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"areaselect\">\n		LEFT JOIN sys_area ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"treeselect\" && c.javaType != \"This\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"gridselect\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			</#if>\n		</#list>\n	</sql>\n\n\n\n	<select id=\"findById\" resultType=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO\">\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		WHERE a.id = ${\"#\"}{id} and a.del_flag = 0\n	</select>\n\n	<select id=\"findList\" resultType=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO\" >\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n	    ${\"$\"}{ew.customSqlSegment}\n	</select>\n</#if>\n\n</mapper>]]>\n    </content>\n</template>\n', '', 0, 40, '0', NULL, '1', '2021-10-16 23:54:46', '1', '2021-10-10 15:56:57');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1449403451397238786', 'wrapper.java', '1449403450692595713', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>service</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/service/mapstruct</filePath>\n    <fileName>${ClassName}Wrapper.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.mapstruct;\n\n\nimport cn.weegoo.core.mapstruct.EntityWrapper;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${ClassName};\nimport org.mapstruct.Mapper;\n<#if isNeedWrapper>\nimport org.mapstruct.Mapping;\nimport org.mapstruct.Mappings;\n</#if>\nimport org.mapstruct.ReportingPolicy;\nimport org.mapstruct.factory.Mappers;\n\n/**\n *  ${ClassName}Wrapper\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n@Mapper(componentModel = \"spring\", unmappedTargetPolicy = ReportingPolicy.IGNORE, uses = {} )\npublic interface ${ClassName}Wrapper extends EntityWrapper<${ClassName}DTO, ${ClassName}> {\n\n    ${ClassName}Wrapper INSTANCE = Mappers.getMapper(${ClassName}Wrapper.class);\n    <#if isNeedWrapper>\n     @Mappings({\n     	<#list table.columnList as c>\n		<#-- 如果不是基类属性 -->\n		    <#if c.isNotBaseField>\n     		    <#if table.parentExists && table.parentTableFk == c.name>\n            @Mapping(source = \"${c.javaFieldId}\", target = \"${c.simpleJavaFieldId}\"),\n			<#-- 其它字段 -->\n			    <#elseif c.isNotBasicJavaType>\n            @Mapping(source = \"${c.javaFieldId}\", target = \"${c.simpleJavaFieldId}\"),\n	             </#if>\n	        </#if>\n	    </#list>\n            @Mapping(source = \"createBy.id\", target = \"createBy\"),\n            @Mapping (source = \"updateBy.id\", target = \"updateBy\")})\n    ${ClassName} toEntity(${ClassName}DTO dto);\n\n\n    @Mappings({\n     	<#list table.columnList as c>\n		<#-- 如果不是基类属性 -->\n		    <#if c.isNotBaseField>\n     		    <#if table.parentExists && table.parentTableFk == c.name>\n            @Mapping(source = \"${c.simpleJavaFieldId}\", target = \"${c.javaFieldId}\"),\n			<#-- 其它字段 -->\n			    <#elseif c.isNotBasicJavaType>\n            @Mapping(source = \"${c.simpleJavaFieldId}\", target = \"${c.javaFieldId}\"),\n	             </#if>\n	        </#if>\n	    </#list>\n            @Mapping (source = \"createBy\", target = \"createBy.id\"),\n            @Mapping (source = \"updateBy\", target = \"updateBy.id\")})\n    ${ClassName}DTO toDTO(${ClassName} entity);\n    </#if>\n}\n]]>\n    </content>\n</template>\n', '', 0, 50, '0', NULL, '1', '2021-10-10 18:34:11', '1', '2021-10-10 18:34:11');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1449403451422404610', 'viewForm.vue', '1449403450692595713', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>viewForm</name>\n    <filePath>src/views/modules/${moduleName}/${subModuleName}</filePath>\n    <fileName>${ClassName}Form.vue</fileName>\n    <content><![CDATA[\n<template>\n  <el-dialog\n    :title=\"title\"\n    :close-on-click-modal=\"false\"\n     v-dialogDrag\n    :visible.sync=\"visible\">\n    <el-form size=\"small\" :model=\"inputForm\" ref=\"inputForm\" :class=\"method===\'view\'?\'readonly\':\'\'\"  :disabled=\"method===\'view\'\" @keyup.enter.native=\"doSubmit()\"\n             label-width=\"120px\">\n      <el-row  :gutter=\"15\">\n<#list table.columnList as c>\n      <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField)>\n       <#if c.showType == \"wangeditor\" || c.showType == \"quilleditor\" || c.showType == \"fileselect\" || c.showType == \"imageselect\">\n        <el-col :span=\"24\">\n        <#else>\n        <el-col :span=\"12\">\n        </#if>\n            <el-form-item label=\"${c.comments}\" prop=\"${c.javaFieldId}\"\n                :rules=\"[\n                <#if c.isNull != \"1\">\n                  {required: true, message:\'${c.comments}不能为空\', trigger:\'blur\'}<#if c.validateType?? && c.validateType != \"\"><#if c.validateType != \"string\" ||  c.minLength?? && c.minLength != \"\">,</#if></#if>\n                </#if>\n                <#if c.minLength?? && c.minLength != \"\">\n                  { min: ${c.minLength}, message: \'最小长度是${c.minLength}个字符\', trigger: \'blur\' }<#if c.validateType?? && c.validateType != \"\"><#if c.validateType != \"string\">,</#if></#if>\n                </#if>\n                <#if c.validateType?? && c.validateType != \"\">\n                <#if c.validateType != \"string\">\n                  {validator: validator.${c.validateType}, trigger:\'blur\'}\n                 </#if>\n                 </#if>\n                 ]\">\n    <#if c.showType == \"input\">\n              <el-input v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"请填写${c.comments}\" <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> <#if  c.minLength?? && c.minLength != \"\"> minlength=\"${c.minLength}\"</#if> <#if c.maxValue?? && c.maxValue != \"\"> max=\"${c.maxValue}\"</#if> <#if c.minValue?? && c.minValue != \"\"> min=\"${c.minValue}\"</#if> ></el-input>\n    <#elseif c.showType == \"inputnumber\">\n          <el-input-number v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"请填写${c.comments}\" <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> <#if  c.minLength?? && c.minLength != \"\"> minlength=\"${c.minLength}\"</#if> <#if c.maxValue?? && c.maxValue != \"\"> max=\"${c.maxValue}\"</#if> <#if c.minValue?? && c.minValue != \"\"> min=\"${c.minValue}\"</#if> style=\"width: 100%;\"></el-input-number>\n    <#elseif c.showType == \"textarea\">\n          <el-input type=\"textarea\" v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"请填写${c.comments}\" <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> <#if  c.minLength?? && c.minLength != \"\"> minlength=\"${c.minLength}\"</#if> <#if c.maxValue?? && c.maxValue != \"\"> max=\"${c.maxValue}\"</#if> <#if c.minValue?? && c.minValue != \"\"> min=\"${c.minValue}\"</#if> ></el-input>\n    <#elseif c.showType == \"select\">\n                <el-select v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"请选择\"  style=\"width: 100%;\">\n                          <el-option\n                            v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\"\n                            :key=\"item.value\"\n                            :label=\"item.label\"\n                            :value=\"item.value\">\n                          </el-option>\n                      </el-select>\n    <#elseif c.showType == \"checkbox\">\n                <el-checkbox-group\n                        v-model=\"${c.javaFieldId}List\">\n                       <el-checkbox v-for=\"${c.javaFieldId} in $dictUtils.getDictList(\'${c.dictType}\')\" :label=\"${c.javaFieldId}.value\" :key=\"${c.javaFieldId}.value\">{{${c.javaFieldId}.label}}</el-checkbox>\n                    </el-checkbox-group>\n    <#elseif c.showType == \"radiobox\">\n                    <el-radio-group v-model=\"inputForm.${c.javaFieldId}\">\n                        <el-radio v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\" :label=\"item.value\" :key=\"item.value\">{{item.label}}</el-radio>\n                    </el-radio-group>\n    <#elseif c.showType == \"dateselect\">\n                <el-date-picker\n                      style=\"width: 100%;\"\n                      v-model=\"inputForm.${c.javaFieldId}\"\n                      type=\"datetime\"\n                      value-format=\"yyyy-MM-dd HH:mm:ss\"\n                      placeholder=\"选择日期时间\">\n                    </el-date-picker>\n    <#elseif c.showType == \"userselect\">\n                <user-select :limit=\'1\' :value=\"inputForm.${c.javaFieldId}\" @getValue=\'(value) => {inputForm.${c.javaFieldId}=value}\'></user-select>\n    <#elseif c.showType == \"officeselect\">\n          <SelectTree\n                      ref=\"${c.simpleJavaField}\"\n                      :props=\"{\n                          value: \'id\',             // ID字段名\n                          label: \'name\',         // 显示名称\n                          children: \'children\'    // 子级字段名\n                        }\"\n\n                      url=\"/sys/office/treeData?type=2\"\n                      :value=\"inputForm.${c.javaFieldId}\"\n                      :clearable=\"true\"\n                      :accordion=\"true\"\n                      @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"areaselect\">\n              <SelectTree\n                      ref=\"${c.simpleJavaField}\"\n                      :props=\"{\n                          value: \'id\',             // ID字段名\n                          label: \'name\',         // 显示名称\n                          children: \'children\'    // 子级字段名\n                        }\"\n\n                      url=\"/sys/area/treeData\"\n                      :value=\"inputForm.${c.javaFieldId}\"\n                      :clearable=\"true\"\n                      :accordion=\"true\"\n                      @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"cityselect\">\n                      <CityPicker\n                         style=\"width:100%\"\n                        :value=\"inputForm.${c.javaFieldId}\"\n                        :clearable=\"true\"\n                        :accordion=\"true\"\n                        @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"fileselect\">\n              <el-upload ref=\"${c.javaFieldId}\"\n                    v-if=\"visible\"\n                    :action=\"`${\"$\"}{this.${\"$\"}http.BASE_URL}/sys/file/webupload/upload?uploadPath=/${moduleName}<#if subModuleName != \"\">/${subModuleName}</#if>/${className}`\"\n                    :headers=\"{token: ${\"$\"}cookie.get(\'token\')}\"\n                    :on-preview=\"(file, fileList) => {$window.location.href = (file.response && file.response.url) || file.url}\"\n                    :on-success=\"(response, file, fileList) => {\n                       inputForm.${c.javaFieldId} = fileList.map(item => (item.response && item.response.url) || item.url).join(\'|\')\n                    }\"\n                    :on-remove=\"(file, fileList) => {\n                      ${\"$\"}http.delete(`/sys/file/webupload/deleteByUrl?url=${\"$\"}{(file.response && file.response.url) || file.url}`).then(({data}) => {\n                        ${\"$\"}message.success(data)\n                      })\n                      inputForm.${c.javaFieldId} = fileList.map(item => item.url).join(\'|\')\n                    }\"\n                    :before-remove=\"(file, fileList) => {\n                      return ${\"$\"}confirm(`确定移除 ${\"$\"}{file.name}？`)\n                    }\"\n                    multiple\n                    :limit=\"5\"\n                    :on-exceed=\"(files, fileList) =>{\n                      $message.warning(`当前限制选择 5 个文件，本次选择了 ${\"$\"}{files.length} 个文件，共选择了 ${\"$\"}{files.length + fileList.length} 个文件`)\n                    }\"\n                    :file-list=\"${c.javaFieldId}Arra\">\n                    <el-button size=\"small\" type=\"primary\">点击上传</el-button>\n                    <div slot=\"tip\" class=\"el-upload__tip\">添加相关附件</div>\n                  </el-upload>\n<#elseif c.showType == \"imageselect\">\n              <el-upload ref=\"${c.javaFieldId}\"\n              v-if=\"visible\"\n              list-type=\"picture-card\"\n                    :action=\"`${\"$\"}{this.${\"$\"}http.BASE_URL}/sys/file/webupload/upload?uploadPath=/${moduleName}<#if subModuleName != \"\">/${subModuleName}</#if>/${className}`\"\n                    :headers=\"{token: ${\"$\"}cookie.get(\'token\')}\"\n                    :on-preview=\"(file, fileList) => {\n                        $alert(`<img style=\'width:100%\' src=\' ${\"$\"}{(file.response && file.response.url) || file.url}\'/>`,  {\n                          dangerouslyUseHTMLString: true,\n                          showConfirmButton: false,\n                          closeOnClickModal: true,\n                          customClass: \'showPic\'\n                        });\n                    }\"\n                    :on-success=\"(response, file, fileList) => {\n                       inputForm.${c.javaFieldId} = fileList.map(item => (item.response && item.response.url) || item.url).join(\'|\')\n                    }\"\n                    :on-remove=\"(file, fileList) => {\n                      ${\"$\"}http.delete(`/sys/file/webupload/deleteByUrl?url=${\"$\"}{(file.response && file.response.url) || file.url}`).then(({data}) => {\n                        ${\"$\"}message.success(data)\n                      })\n                      inputForm.${c.javaFieldId} = fileList.map(item => item.url).join(\'|\')\n                    }\"\n                    :before-remove=\"(file, fileList) => {\n                      return ${\"$\"}confirm(`确定移除 ${\"$\"}{file.name}？`)\n                    }\"\n                    multiple\n                    :limit=\"5\"\n                    :on-exceed=\"(files, fileList) =>{\n                      $message.warning(`当前限制选择 5 个文件，本次选择了 ${\"$\"}{files.length} 个文件，共选择了 ${\"$\"}{files.length + fileList.length} 个文件`)\n                    }\"\n                    :file-list=\"${c.javaFieldId}Arra\">\n                    <i class=\"el-icon-plus\"></i>\n                  </el-upload>\n    <#elseif c.showType == \"treeselect\">\n          <SelectTree\n                      ref=\"${c.simpleJavaField}\"\n                      :props=\"{\n                          value: \'id\',             // ID字段名\n                          label: \'name\',         // 显示名称\n                          children: \'children\'    // 子级字段名\n                        }\"\n                      url=\"${c.dataUrl}/treeData\"\n                      :value=\"inputForm.${c.javaFieldId}\"\n                      :clearable=\"true\"\n                      :accordion=\"true\"\n                      @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"gridselect\">\n          <GridSelect\n            title=\"选择${c.comments}\"\n            labelName = \'${c.javaFieldName?split(\".\")[1]}\'\n            labelValue = \'${c.javaFieldId?split(\".\")[1]}\'\n            :value = \"inputForm.${c.javaFieldId}\"\n            :limit=\"1\"\n            @getValue=\'(value) => {inputForm.${c.javaFieldId}=value}\'\n            :columns=\"[\n    <#list c.fieldLabels?split(\"|\") as f>\n            {\n              prop: \'${c.fieldKeys?split(\"|\")[f_index]}\',\n              label: \'${c.fieldLabels?split(\"|\")[f_index]}\'\n            }<#if f_has_next>,</#if>\n    </#list>\n            ]\"\n            :searchs=\"[\n   <#list c.searchLabel?split(\"|\") as f>\n            {\n              prop: \'${c.searchKey?split(\"|\")[f_index]}\',\n              label: \'${c.searchLabel?split(\"|\")[f_index]}\'\n            }<#if f_has_next>,</#if>\n    </#list>\n            ]\"\n            dataListUrl=\"${c.dataUrl}/list\"\n            queryEntityUrl=\"${c.dataUrl}/queryById\">\n          </GridSelect>\n    <#elseif c.showType == \"wangeditor\">\n            <WangEditor ref=\"${c.javaFieldId}Editor\" v-model=\"inputForm.${c.javaFieldId}\"/>\n    <#elseif c.showType == \"quilleditor\">\n            <vue-editor useCustomImageHandler @image-added=\"$utils.handleImageAdded\" v-model=\"inputForm.${c.javaFieldId}\"/>\n    </#if>\n           </el-form-item>\n        </el-col>\n      </#if>\n    </#list>\n        </el-row>\n    </el-form>\n    <span slot=\"footer\" class=\"dialog-footer\">\n      <el-button size=\"small\" v-if=\"method === \'add\'\" type=\"primary\" @click=\"continueDoSubmit()\">继续添加</el-button>\n      <el-button size=\"small\" @click=\"visible = false\">关闭</el-button>\n      <el-button size=\"small\" v-if=\"method !== \'view\'\" type=\"primary\" @click=\"doSubmit()\">确定</el-button>\n    </span>\n  </el-dialog>\n</template>\n\n<script>\n<#list table.importFormList as c>\n  ${c}\n</#list>\n  export default {\n    data () {\n      return {\n        title: \'\',\n        method: \'\',\n        visible: false,\n        oldInputForm: \'\',\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"fileselect\" || c.showType == \"imageselect\">\n        ${c.javaFieldId}Arra: [],\n  </#if>\n  </#if>\n</#list>\n        inputForm: {\n          id: \'\'<#if (table.formList?size>0)>,</#if>\n        <#list table.formList as c>\n          <#if c.javaFieldId?contains(\".\")>\n          ${c.javaFieldId?split(\".\")[0]}: {\n            ${c.javaFieldId?split(\".\")[1]}: \'\'\n          }<#if c_has_next>,</#if>\n          <#else>\n          ${c.javaFieldId}: \'\'<#if c_has_next>,</#if>\n          </#if>\n    </#list>\n        }\n      }\n    },\n    components: {\n    <#list table.formComponents as c>\n      ${c}<#if c_has_next>,</#if>\n    </#list>\n    },\n    <#assign cks=0/>\n    <#list table.formList as c>\n     <#if c.showType == \"checkbox\">\n     <#assign cks= cks + 1/>\n    </#if>\n    </#list>\n    <#if cks gt 0>\n    computed: {\n    <#assign ckIndex=0/>\n    <#list table.formList as c>\n     <#if c.showType == \"checkbox\">\n     <#assign ckIndex= ckIndex + 1/>\n      ${c.javaFieldId}List: {\n        get: function () {\n          return this.inputForm.${c.javaFieldId} !== \'\' ? this.inputForm.${c.javaFieldId}.split(\',\') : []\n        },\n        set: function (val) {\n          this.inputForm.${c.javaFieldId} = val.join(\',\')\n        }\n      }<#if ckIndex lt cks >,</#if>\n    </#if>\n    </#list>\n    },\n    </#if>\n    methods: {\n      init (method, obj) {\n        this.method = method\n        if (method === \'add\') {\n          this.title = `新建${functionNameSimple}`\n        } else if (method === \'edit\') {\n          this.title = \'修改${functionNameSimple}\'\n        } else if (method === \'view\') {\n          this.title = \'查看${functionNameSimple}\'\n        }\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"imageselect\" || c.showType == \"fileselect\">\n        this.${c.javaFieldId}Arra = []\n  </#if>\n  </#if>\n</#list>\n        this.visible = true\n        this.$nextTick(() => {\n          this.$refs.inputForm.resetFields()\n          this.inputForm.id = \'\'\n          this.oldInputForm = \'\'\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"wangeditor\">\n          this.$refs.${c.javaFieldId}Editor.init(\'\')\n  </#if>\n  </#if>\n</#list>\n          if (method === \'edit\' || method === \'view\') { // 修改或者查看\n            this.oldInputForm = obj\n            this.inputForm = JSON.parse(JSON.stringify(obj))\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"wangeditor\">\n            this.$refs.${c.javaFieldId}Editor.init(this.inputForm.${c.javaFieldId})\n  </#if>\n  </#if>\n</#list>\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\')>\n  <#if c.showType == \"fileselect\" || c.showType == \"imageselect\">\n            this.inputForm.${c.javaFieldId}.split(\'|\').forEach((item) => {\n              if (item.trim().length > 0) {\n                this.${c.javaFieldId}Arra.push({name: decodeURIComponent(item.substring(item.lastIndexOf(\'/\') + 1)), url: item})\n              }\n            })\n  </#if>\n  </#if>\n</#list>\n          }\n        })\n      },\n   // 表单提交\n      doSubmit () {\n        this.$refs[\'inputForm\'].validate((valid) => {\n          if (valid) {\n            this.$emit(\'addRow\', this.oldInputForm, JSON.parse(JSON.stringify(this.inputForm)))\n            this.visible = false\n          }\n        })\n      },\n      continueDoSubmit () {\n        this.$refs[\'inputForm\'].validate((valid) => {\n          if (valid) {\n            this.$emit(\'addRow\', this.oldInputForm, JSON.parse(JSON.stringify(this.inputForm)))\n            this.$refs[\'inputForm\'].resetFields()\n          }\n        })\n      }\n    }\n  }\n</script>\n\n  ]]>\n    </content>\n</template>\n', '', 0, 50, '1', NULL, '1', '2021-10-16 23:54:46', '1', '2021-10-10 15:56:57');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1449403451510484993', 'dto.java', '1449403450692595713', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>entity</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/service/dto</filePath>\n    <fileName>${ClassName}DTO.java</fileName>\n    <content><![CDATA[\n        /**\n        * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n        */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto;\n\n<#list table.importList as i>\nimport ${i};\n</#list>\n<#if (table.queryList?size > 0) >\nimport cn.weegoo.core.query.Query;\nimport cn.weegoo.core.query.QueryType;\n</#if>\nimport cn.weegoo.core.service.dto.BaseDTO;\nimport lombok.Data;\nimport lombok.EqualsAndHashCode;\n/**\n * ${functionName}Entity\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n@Data\n@EqualsAndHashCode(callSuper = false)\npublic class ${ClassName}DTO extends BaseDTO {\n\n	private static final long serialVersionUID = 1L;\n\n	<#-- 生成字段属性 -->\n	<#list table.columnList as c>\n		<#-- 如果不是基类属性 -->\n		<#if c.isNotBaseField>\n	        <#if c.comments??>\n	/**\n     * ${c.comments}\n     */\n            </#if>\n			<#list c.simpleAnnotationList as a>\n	@${a}\n			</#list>\n			<#-- 父类对象 -->\n			<#if table.parentExists && table.parentTableFk == c.name>\n                    <#if (c.isQuery?? && c.isQuery == \"1\" && c.queryType ?? && c.queryType != \"\" )>\n                        <#if isNeedJoinTable>\n	@Query(tableColumn = \"a.${c.name}\", javaField = \"${c.javaFieldId}\", type = QueryType.${c.queryType})\n                        <#else>\n    @Query(type = QueryType.${c.queryType})\n                        </#if>\n					</#if>\n	private ${table.parent.className?cap_first}DTO ${c.simpleJavaField};\n			<#-- 其它字段 -->\n			<#else>\n                    <#if (c.isQuery?? && c.isQuery == \"1\" && c.queryType ?? && c.queryType != \"\" )>\n                        <#if isNeedJoinTable>\n    @Query(tableColumn = \"a.${c.name}\", javaField = \"${c.javaFieldId}\", type = QueryType.${c.queryType})\n                        <#else>\n    @Query(type = QueryType.${c.queryType})\n                        </#if>\n                    </#if>\n	private ${c.simpleJavaType} ${c.simpleJavaField};\n			</#if>\n		</#if>\n	</#list>\n    <#-- 子表列表字段 -->\n    <#list table.childList as c>\n    /**\n     *子表列表\n     */\n	private List<${c.className?cap_first}DTO> ${c.className?uncap_first}DTOList = Lists.newArrayList();\n    </#list>\n\n}]]>\n    </content>\n</template>\n', '', 0, 60, '0', NULL, '1', '2021-10-10 18:34:11', '1', '2021-10-10 18:34:11');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1449403451531456514', 'entity.java', '1449403450692595713', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>entity</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/domain</filePath>\n    <fileName>${ClassName}.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain;\n\n<#list table.importEntityList as i>\nimport ${i};\n</#list>\n<#if table.idType ==\"2\">\nimport com.baomidou.mybatisplus.annotation.IdType;\nimport com.baomidou.mybatisplus.annotation.TableId;\n</#if>\nimport com.baomidou.mybatisplus.annotation.TableName;\nimport cn.weegoo.core.domain.BaseEntity;\n<#if  (!isNeedJoinTable && table.queryList?size > 0) >\nimport cn.weegoo.core.query.Query;\nimport cn.weegoo.core.query.QueryType;\n</#if>\nimport lombok.Data;\nimport lombok.EqualsAndHashCode;\n/**\n * ${functionName}Entity\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n@Data\n@EqualsAndHashCode(callSuper = false)\n@TableName(\"${table.name}\")\npublic class ${ClassName} extends BaseEntity {\n\n	private static final long serialVersionUID = 1L;\n	<#if table.idType ==\"2\">\n\n	@TableId(type = IdType.AUTO)\n	private Integer id;\n	</#if>\n\n	<#-- 生成字段属性 -->\n	<#list table.columnList as c>\n		<#-- 如果不是基类属性 -->\n		<#if c.isNotBaseField>\n			<#if c.comments??>\n	/**\n     * ${c.comments}\n     */\n            </#if>\n            <#if c.isBasicJavaType>\n<#if c.simpleJavaField != c.mapperJavaField>\n    @TableField(\"${c.name}\")\n</#if>\n	private ${c.simpleJavaType} ${c.simpleJavaField};\n	        <#else>\n<#if c.simpleJavaFieldId != c.mapperJavaField>\n    @TableField(\"${c.name}\")\n</#if>\n	private String ${c.simpleJavaFieldId};\n	         </#if>\n		</#if>\n	</#list>\n\n}]]>\n    </content>\n</template>\n', '', 0, 70, '0', NULL, '1', '2021-10-16 23:54:46', '1', '2021-10-10 15:56:57');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1449403451560816641', 'viewForm.vue', '1449403450692595713', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>viewForm</name>\n    <filePath>src/views/modules/${moduleName}/${subModuleName}</filePath>\n    <fileName>${ClassName}Form.vue</fileName>\n    <content><![CDATA[\n<template>\n<div>\n    <el-form size=\"small\" :model=\"inputForm\" ref=\"inputForm\" v-loading=\"loading\" :disabled=\"formReadOnly\" label-width=\"120px\">\n      <el-row  :gutter=\"15\">\n<#list table.columnList as c>\n      <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField)>\n        <el-col :span=\"24\">\n            <el-form-item label=\"${c.comments}\" prop=\"${c.javaFieldId}\"\n                :rules=\"[\n      <#if c.isNull != \"1\">\n                  {required: true, message:\'${c.comments}不能为空\', trigger:\'blur\'}<#if c.validateType?? && c.validateType != \"\"><#if c.validateType != \"string\" ||  c.minLength?? && c.minLength != \"\">,</#if></#if>\n                </#if>\n                <#if c.minLength?? && c.minLength != \"\">\n                  { min: ${c.minLength}, message: \'最小长度是${c.minLength}个字符\', trigger: \'blur\' }<#if c.validateType?? && c.validateType != \"\"><#if c.validateType != \"string\">,</#if></#if>\n                </#if>\n                <#if c.validateType?? && c.validateType != \"\">\n                <#if c.validateType != \"string\">\n                  {validator: validator.${c.validateType}, trigger:\'blur\'}\n                 </#if>\n                 </#if>\n                 ]\">\n    <#if c.showType == \"input\">\n              <el-input v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"请填写${c.comments}\" <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> <#if  c.minLength?? && c.minLength != \"\"> minlength=\"${c.minLength}\"</#if> <#if c.maxValue?? && c.maxValue != \"\"> max=\"${c.maxValue}\"</#if> <#if c.minValue?? && c.minValue != \"\"> min=\"${c.minValue}\"</#if> ></el-input>\n    <#elseif c.showType == \"inputnumber\">\n          <el-input-number v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"请填写${c.comments}\" <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> <#if  c.minLength?? && c.minLength != \"\"> minlength=\"${c.minLength}\"</#if> <#if c.maxValue?? && c.maxValue != \"\"> max=\"${c.maxValue}\"</#if> <#if c.minValue?? && c.minValue != \"\"> min=\"${c.minValue}\"</#if> style=\"width: 100%;\"></el-input-number>\n    <#elseif c.showType == \"textarea\">\n          <el-input type=\"textarea\" v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"请填写${c.comments}\" <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> <#if  c.minLength?? && c.minLength != \"\"> minlength=\"${c.minLength}\"</#if> <#if c.maxValue?? && c.maxValue != \"\"> max=\"${c.maxValue}\"</#if> <#if c.minValue?? && c.minValue != \"\"> min=\"${c.minValue}\"</#if> ></el-input>\n    <#elseif c.showType == \"select\">\n                <el-select v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"请选择\"  style=\"width: 100%;\">\n                          <el-option\n                            v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\"\n                            :key=\"item.value\"\n                            :label=\"item.label\"\n                            :value=\"item.value\">\n                          </el-option>\n                      </el-select>\n    <#elseif c.showType == \"checkbox\">\n                <el-checkbox-group\n                        v-model=\"${c.javaFieldId}List\">\n                       <el-checkbox v-for=\"${c.javaFieldId} in $dictUtils.getDictList(\'${c.dictType}\')\" :label=\"${c.javaFieldId}.value\" :key=\"${c.javaFieldId}.value\">{{${c.javaFieldId}.label}}</el-checkbox>\n                    </el-checkbox-group>\n    <#elseif c.showType == \"radiobox\">\n                    <el-radio-group v-model=\"inputForm.${c.javaFieldId}\">\n                        <el-radio v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\" :label=\"item.value\" :key=\"item.value\">{{item.label}}</el-radio>\n                    </el-radio-group>\n    <#elseif c.showType == \"dateselect\">\n                <el-date-picker\n                      style=\"width: 100%;\"\n                      v-model=\"inputForm.${c.javaFieldId}\"\n                      type=\"datetime\"\n                      value-format=\"yyyy-MM-dd HH:mm:ss\"\n                      placeholder=\"选择日期时间\">\n                    </el-date-picker>\n    <#elseif c.showType == \"userselect\">\n                <user-select :limit=\'1\' :value=\"inputForm.${c.javaFieldId}\" @getValue=\'(value) => {inputForm.${c.javaFieldId}=value}\'></user-select>\n    <#elseif c.showType == \"officeselect\">\n          <SelectTree\n                      ref=\"${c.simpleJavaField}\"\n                      :props=\"{\n                          value: \'id\',             // ID字段名\n                          label: \'name\',         // 显示名称\n                          children: \'children\'    // 子级字段名\n                        }\"\n\n                      url=\"/sys/office/treeData?type=2\"\n                      :value=\"inputForm.${c.javaFieldId}\"\n                      :clearable=\"true\"\n                      :accordion=\"true\"\n                      @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"areaselect\">\n              <SelectTree\n                      ref=\"${c.simpleJavaField}\"\n                      :props=\"{\n                          value: \'id\',             // ID字段名\n                          label: \'name\',         // 显示名称\n                          children: \'children\'    // 子级字段名\n                        }\"\n\n                      url=\"/sys/area/treeData\"\n                      :value=\"inputForm.${c.javaFieldId}\"\n                      :clearable=\"true\"\n                      :accordion=\"true\"\n                      @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"cityselect\">\n                      <CityPicker\n                         style=\"width:100%\"\n                        :value=\"inputForm.${c.javaFieldId}\"\n                        :clearable=\"true\"\n                        :accordion=\"true\"\n                        @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"fileselect\">\n              <file-upload v-model=\"inputForm.${c.javaFieldId}\" :limit=\"3\" tip=\"最多上传3个文件,单个文件不要超过10M\"></file-upload>\n<#elseif c.showType == \"imageselect\">\n              <image-upload v-model=\"inputForm.${c.javaFieldId}\" :limit=\"3\" tip=\"最多上传3个图片,单个图片不要超过10M\"></image-upload>\n    <#elseif c.showType == \"treeselect\">\n          <SelectTree\n                      ref=\"${c.simpleJavaField}\"\n                      :props=\"{\n                          value: \'id\',             // ID字段名\n                          label: \'name\',         // 显示名称\n                          children: \'children\'    // 子级字段名\n                        }\"\n\n                      url=\"${c.dataUrl}/treeData\"\n                      :value=\"inputForm.${c.javaFieldId}\"\n                      :clearable=\"true\"\n                      :accordion=\"true\"\n                      @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"gridselect\">\n          <GridSelect\n            title=\"选择${c.comments}\"\n            labelName = \'${c.javaFieldName?split(\".\")[1]}\'\n            labelValue = \'${c.javaFieldId?split(\".\")[1]}\'\n            :value = \"inputForm.${c.javaFieldId}\"\n            :limit=\"1\"\n            @getValue=\'(value) => {inputForm.${c.javaFieldId}=value}\'\n            :columns=\"[\n    <#list c.fieldLabels?split(\"|\") as f>\n            {\n              prop: \'${c.fieldKeys?split(\"|\")[f_index]}\',\n              label: \'${c.fieldLabels?split(\"|\")[f_index]}\'\n            }<#if f_has_next>,</#if>\n    </#list>\n            ]\"\n            :searchs=\"[\n   <#list c.searchLabel?split(\"|\") as f>\n            {\n              prop: \'${c.searchKey?split(\"|\")[f_index]}\',\n              label: \'${c.searchLabel?split(\"|\")[f_index]}\'\n            }<#if f_has_next>,</#if>\n    </#list>\n            ]\"\n            dataListUrl=\"${c.dataUrl}/list\"\n            queryEntityUrl=\"${c.dataUrl}/queryById\">\n          </GridSelect>\n    <#elseif c.showType == \"wangeditor\">\n            <WangEditor ref=\"${c.javaFieldId}Editor\" v-model=\"inputForm.${c.javaFieldId}\"/>\n    <#elseif c.showType == \"quilleditor\">\n            <vue-editor useCustomImageHandler @image-added=\"$utils.handleImageAdded\" v-model=\"inputForm.${c.javaFieldId}\"/>\n    </#if>\n           </el-form-item>\n        </el-col>\n      </#if>\n    </#list>\n     <#if (table.childList?size > 0) >\n      <#assign y=0 />\n    <el-col :span=\"24\">\n     <el-form-item label-width=\"0\">\n            <el-tabs v-model=\"${className?uncap_first}Tab\">\n        <#list table.childList as child>\n            <el-tab-pane label=\"${child.comments}\">\n                  <el-button size=\"small\" @click=\"add${child.className}Row\" type=\"primary\">新增</el-button>\n                  <el-table\n                  class=\"table\"\n                  size=\"small\"\n                  :data=\"inputForm.${child.className?uncap_first}DTOList.filter((item) => { return item.delFlag !== \'1\'})\"\n                  style=\"width: 100%\">\n                  <#assign childColumnCount = 0 />\n                <#list child.columnList as c>\n                    <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField || c.simpleJavaField == \'remarks\') && c.name != table.parentTableFk>\n                  <el-table-column\n                    prop=\"${c.javaFieldName}\"\n                    header-align=\"center\"\n                    align=\"center\"\n                    show-overflow-tooltip\n                    label=\"${c.comments}\">\n                      <template  #default=\"{row}\">\n                <#if c.showType == \"input\">\n                          <el-input v-model=\"row.${c.javaFieldId}\" placeholder=\"请填写${c.comments}\" <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> <#if  c.minLength?? && c.minLength != \"\"> minlength=\"${c.minLength}\"</#if> <#if c.maxValue?? && c.maxValue != \"\"> max=\"${c.maxValue}\"</#if> <#if c.minValue?? && c.minValue != \"\"> min=\"${c.minValue}\"</#if> ></el-input>\n                <#elseif c.showType == \"inputnumber\">\n                         <el-input-number v-model=\"row.${c.javaFieldId}\" placeholder=\"请填写${c.comments}\" <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> <#if  c.minLength?? && c.minLength != \"\"> minlength=\"${c.minLength}\"</#if> <#if c.maxValue?? && c.maxValue != \"\"> max=\"${c.maxValue}\"</#if> <#if c.minValue?? && c.minValue != \"\"> min=\"${c.minValue}\"</#if> style=\"width: 100%;\"></el-input-number>\n                <#elseif c.showType == \"textarea\">\n                         <el-input type=\"textarea\" v-model=\"row.${c.javaFieldId}\" placeholder=\"请填写${c.comments}\" <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> <#if  c.minLength?? && c.minLength != \"\"> minlength=\"${c.minLength}\"</#if> <#if c.maxValue?? && c.maxValue != \"\"> max=\"${c.maxValue}\"</#if> <#if c.minValue?? && c.minValue != \"\"> min=\"${c.minValue}\"</#if> ></el-input>\n                <#elseif c.showType == \"select\">\n                         <el-select v-model=\"row.${c.javaFieldId}\" placeholder=\"请选择\"  style=\"width: 100%;\">\n                              <el-option\n                                v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\"\n                                :key=\"item.value\"\n                                :label=\"item.label\"\n                                :value=\"item.value\">\n                              </el-option>\n                         </el-select>\n                <#elseif c.showType == \"checkbox\">\n                         <el-checkbox-group v-model=\"row.${c.javaFieldId}\">\n                                <el-checkbox v-for=\"${c.javaFieldId} in $dictUtils.getDictList(\'${c.dictType}\')\" :label=\"${c.javaFieldId}.value\" :key=\"${c.javaFieldId}.value\">{{${c.javaFieldId}.label}}</el-checkbox>\n                         </el-checkbox-group>\n                <#elseif c.showType == \"radiobox\">\n                         <el-radio-group v-model=\"row.${c.javaFieldId}\">\n                                <el-radio v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\" :label=\"item.value\" :key=\"item.value\">{{item.label}}</el-radio>\n                         </el-radio-group>\n                <#elseif c.showType == \"dateselect\">\n                            <el-date-picker\n                              style=\"width: 100%;\"\n                              v-model=\"row.${c.javaFieldId}\"\n                              type=\"datetime\"\n                              value-format=\"yyyy-MM-dd HH:mm:ss\"\n                              placeholder=\"选择日期时间\">\n                            </el-date-picker>\n                <#elseif c.showType == \"userselect\">\n                         <user-select :limit=\'1\' :value=\"row.${c.javaFieldId}\" @getValue=\'(value) => {row.${c.javaFieldId}=value}\'></user-select>\n                <#elseif c.showType == \"officeselect\">\n                         <SelectTree\n                          :props=\"{\n                              value: \'id\',             // ID字段名\n                              label: \'name\',         // 显示名称\n                              children: \'children\'    // 子级字段名\n                            }\"\n    \n                          url=\"/sys/office/treeData?type=2\"\n                          :value=\"row.${c.javaFieldId}\"\n                          :clearable=\"true\"\n                          :accordion=\"true\"\n                          @getValue=\"(value) => {row.${c.javaFieldId}=value}\"/>\n                <#elseif c.showType == \"areaselect\">\n                         <SelectTree\n                          ref=\"${c.simpleJavaField}\"\n                          :props=\"{\n                              value: \'id\',             // ID字段名\n                              label: \'name\',         // 显示名称\n                              children: \'children\'    // 子级字段名\n                            }\"\n    \n                          url=\"/sys/area/treeData\"\n                          :value=\"row.${c.javaFieldId}\"\n                          :clearable=\"true\"\n                          :accordion=\"true\"\n                          @getValue=\"(value) => {row.${c.javaFieldId}=value}\"/>\n                <#elseif c.showType == \"cityselect\">\n                      <CityPicker\n                         style=\"width:100%\"\n                        :value=\"row.${c.javaFieldId}\"\n                        :clearable=\"true\"\n                        :accordion=\"true\"\n                        @getValue=\"(value) => {row.${c.javaFieldId}=value}\"/>          \n                <#elseif c.showType == \"fileselect\">\n                         <file-upload v-model=\"row.${c.javaFieldId}\" :limit=\"3\" tip=\"最多上传3个文件,单个文件不要超过10M\"></file-upload>\n                <#elseif c.showType == \"imageselect\">\n                         <image-upload v-model=\"row.${c.javaFieldId}\" :limit=\"3\" tip=\"最多上传3个图片,单个图片不要超过10M\"></image-upload>\n                <#elseif c.showType == \"treeselect\">\n                        <SelectTree\n                          ref=\"${c.simpleJavaField}\"\n                          :props=\"{\n                              value: \'id\',             // ID字段名\n                              label: \'name\',         // 显示名称\n                              children: \'children\'    // 子级字段名\n                            }\"\n    \n                          url=\"${c.dataUrl}/treeData\"\n                          :value=\"row.${c.javaFieldId}\"\n                          :clearable=\"true\"\n                          :accordion=\"true\"\n                          @getValue=\"(value) => {row.${c.javaFieldId}=value}\"/>\n                <#elseif c.showType == \"gridselect\">\n                         <GridSelect\n                            title=\"选择${c.comments}\"\n                            labelName = \'${c.javaFieldName?split(\".\")[1]}\'\n                            labelValue = \'${c.javaFieldId?split(\".\")[1]}\'\n                            :value = \"row.${c.javaFieldId}\"\n                            :limit=\"1\"\n                            @getValue=\'(value) => {row.${c.javaFieldId}=value}\'\n                            :columns=\"[\n                    <#list c.fieldLabels?split(\"|\") as f>\n                            {\n                              prop: \'${c.fieldKeys?split(\"|\")[f_index]}\',\n                              label: \'${c.fieldLabels?split(\"|\")[f_index]}\'\n                            }<#if f_has_next>,</#if>\n                    </#list>\n                            ]\"\n                            :searchs=\"[\n                   <#list c.searchLabel?split(\"|\") as f>\n                            {\n                              prop: \'${c.searchKey?split(\"|\")[f_index]}\',\n                              label: \'${c.searchLabel?split(\"|\")[f_index]}\'\n                            }<#if f_has_next>,</#if>\n                    </#list>\n                            ]\"\n                            dataListUrl=\"${c.dataUrl}/list\"\n                            queryEntityUrl=\"${c.dataUrl}/queryById\">\n                          </GridSelect>\n                <#elseif c.showType == \"wangeditor\">\n                        <WangEditor  v-model=\"row.${c.javaFieldId}\"/>\n                <#elseif c.showType == \"quilleditor\">\n                        <vue-editor useCustomImageHandler @image-added=\"$utils.handleImageAdded\" v-model=\"row.${c.javaFieldId}\"/>\n                </#if>\n                      </template>\n                  </el-table-column>\n                    </#if>\n                </#list>\n                  <el-table-column\n                    fixed=\"right\"\n                    label=\"操作\"\n                    width=\"150\">\n                    <template slot-scope=\"scope\">\n                      <el-button @click=\"view${child.className}Row(scope.row)\" type=\"text\" size=\"small\">查看</el-button>\n                      <el-button  @click=\"edit${child.className}Row(scope.row)\" type=\"text\" size=\"small\">编辑</el-button>\n                      <el-button  @click=\"del${child.className}Row(scope.row)\" type=\"text\" size=\"small\">删除</el-button>\n                    </template>\n                  </el-table-column>\n                </el-table>\n              </el-tab-pane>\n         </#list>\n            </el-tabs>\n           </el-form-item>\n          </el-col>\n     </#if>\n        </el-row>\n    </el-form>\n</div>\n</template>\n\n<script>\n<#list table.importFormList as c>\n  ${c}\n</#list>\n  import ${className}Service from \'@/api/${moduleName}<#if subModuleName != \"\">/${subModuleName}</#if>/${className}Service\'\n  export default {\n    data () {\n      return {\n        title: \'\',\n        method: \'\',\n        visible: false,\n        loading: false,\n          <#if (table.childList?size > 0)>\n        ${className?uncap_first}Tab: \'0\n          </#if>\n        inputForm: {\n          id: \'\'<#if (table.formList?size>0)>,</#if>\n<#list table.childList as child>\n          ${child.className?uncap_first}DTOList: [],\n</#list>\n        <#list table.formList as c>\n          <#if c.javaFieldId?contains(\".\")>\n          ${c.javaFieldId?split(\".\")[0]}: {\n            ${c.javaFieldId?split(\".\")[1]}: \'\'\n          }<#if c_has_next>,</#if>\n          <#else>\n          ${c.javaFieldId}: \'\'<#if c_has_next>,</#if>\n          </#if>\n    </#list>\n        }\n      }\n    },\n    props: {\n      businessId: {\n        type: String,\n        default: \'\'\n      },\n      formReadOnly: {\n        type: Boolean,\n        default: false\n      }\n    },\n    components: {\n<#list table.formComponents as c>\n      ${c}<#if c_has_next>,</#if>\n</#list>\n    },\n    watch: {\n      \'businessId\': {\n        handler (newVal) {\n          if (this.businessId) {\n            this.init(this.businessId)\n          } else {\n            this.$nextTick(() => {\n              this.$refs.inputForm.resetFields()\n            })\n          }\n        },\n        immediate: true,\n        deep: false\n      }\n    },\n    <#assign cks=0/>\n    <#list table.formList as c>\n     <#if c.showType == \"checkbox\">\n     <#assign cks= cks + 1/>\n    </#if>\n    </#list>\n    <#if cks gt 0>\n    computed: {\n    <#assign ckIndex=0/>\n    <#list table.formList as c>\n     <#if c.showType == \"checkbox\">\n     <#assign ckIndex= ckIndex + 1/>\n      ${c.javaFieldId}List: {\n       get: function () {\n         return this.inputForm.${c.javaFieldId} !== \'\' ? this.inputForm.${c.javaFieldId}.split(\',\') : []\n       },\n       set: function (val) {\n         this.inputForm.${c.javaFieldId} = val.join(\',\')\n       }\n     }<#if ckIndex lt cks >,</#if>\n    </#if>\n    </#list>\n    },\n    </#if>\n    methods: {\n      init (id) {\n        if (id) {\n<#if (table.childList?size > 0)>\n          this.${className?uncap_first}Tab = \'0\'\n</#if>\n<#list table.childList as child>\n          this.inputForm.${child.className?uncap_first}DTOList = []\n</#list>\n          this.loading = true\n          this.inputForm.id = id\n          this.$nextTick(() => {\n            this.$refs.inputForm.resetFields()\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField)>\n  <#if c.showType == \"wangeditor\">\n            this.$refs.${c.javaFieldId}Editor.init(\'\')\n  </#if>\n  </#if>\n</#list>\n            ${className}Service.queryById(this.inputForm.id).then(({data}) => {\n              this.inputForm = this.recover(this.inputForm, data)\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField)>\n  <#if c.showType == \"wangeditor\">\n              this.$refs.${c.javaFieldId}Editor.init(this.inputForm.${c.javaFieldId})\n  </#if>\n  </#if>\n</#list>\n              this.loading = false\n            })\n          })\n        }\n      },\n<#list table.childList as child>\n      save${child.className}Row (child) {\n        if (child[0] === \'\') {\n          this.inputForm.${child.className?uncap_first}DTOList.push(child[1])\n        } else {\n          this.inputForm.${child.className?uncap_first}DTOList.forEach((item, index) => {\n            if (item === child[0]) {\n              this.inputForm.${child.className?uncap_first}DTOList.splice(index, 1, child[1])\n            }\n          })\n        }\n      },\n      add${child.className}Row () {\n        this.inputForm.${child.className?uncap_first}DTOList.push({\n<#list child.formList as c>\n<#if c.javaFieldId?contains(\".\")>\n          ${c.javaFieldId?split(\".\")[0]}: {\n            ${c.javaFieldId?split(\".\")[1]}: \'\'\n          }<#if c_has_next>,</#if>\n<#else>\n          ${c.javaFieldId}: \'\'<#if c_has_next>,</#if>\n</#if>\n</#list>\n        })\n      },\n      del${child.className}Row (child) {\n        this.inputForm.${child.className?uncap_first}DTOList.forEach((item, index) => {\n          if (item === child && item.id === \'\') {\n            this.inputForm.${child.className?uncap_first}DTOList.splice(index, 1)\n          } else if (item === child) {\n            item.delFlag = \'1\'\n            this.inputForm.${child.className?uncap_first}DTOList.splice(index, 1, item)\n          }\n        })\n      },\n</#list>\n      // 表单提交\n      saveForm (callback) {\n        this.$refs[\'inputForm\'].validate((valid) => {\n          if (valid) {\n            this.loading = true\n            ${className}Service.save(this.inputForm).then(({data}) => {\n              callback(data.businessTable, data.businessId, this.inputForm)\n              this.loading = false\n            }).catch(() => {\n              this.loading = false\n            })\n          }\n        })\n      }\n    }\n  }\n</script>\n\n  ]]>\n    </content>\n</template>\n', '', 0, 80, '0', NULL, '1', '2021-10-16 23:54:46', '1', '2021-10-10 15:56:57');
