pe.row.${c.javaFieldId}, \'-\') }}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\"> {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</el-link>\n              <span v-else>{{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</span>\n        <#else>\n              {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}\n        </#if>\n        </template>\n      </vxe-column>\n      <#elseif c.showType == \"checkbox\">\n    <vxe-column\n        field=\"${c.javaFieldId}\"\n        sortable\n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n       <#if firstListField>\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\">{{scope.row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\">{{scope.row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}</el-link>\n              <span v-else>{{scope.row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}</span>\n        <#else>\n              {{scope.row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}\n        </#if>\n        </template>\n      </vxe-column>\n      <#elseif c.showType == \"radiobox\">\n    <vxe-column\n        field=\"${c.javaFieldId}\"\n        sortable\n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n       <#if firstListField>\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\">{{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\">{{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</el-link>\n              <span v-else> {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</span>\n        <#else>\n             {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}\n        </#if>\n        </template>\n      </vxe-column>\n		  <#elseif c.showType == \"fileselect\">\n    <vxe-column\n		  field=\"${c.javaFieldId}\"\n		  sortable\n		  title=\"${c.comments}\">\n		  <template #default=\"{ row }\">\n				<template v-if=\"row.${c.javaFieldId}\">\n				    <el-link type=\"primary\" class=\"m-r-10\" :href=\"item\" target=\"_blank\" :key=\"index\" v-for=\"(item, index) in row.${c.javaFieldId}.split(\',\')\"> {{decodeURIComponent(item.substring(item.lastIndexOf(\"&name=\")+6))}}</el-link>\n				</template>\n		  </template>\n		</vxe-column>\n				  <#elseif c.showType == \"imageselect\">\n    <vxe-column\n		  field=\"${c.javaFieldId}\"\n		  sortable\n		  title=\"${c.comments}\">\n		  <template  #default=\"{ row }\">\n		    <template v-if=\"row.${c.javaFieldId}\">\n				  <el-image\n				    style=\"height: 50px;width:50px;margin-right:10px;\"\n				    :src=\"src\" v-for=\"(src, index) in row.${c.javaFieldId}.split(\',\')\" :key=\"index\"\n				    :preview-src-list=\"row.${c.javaFieldId}.split(\',\')\">\n				  </el-image>\n		     </template>\n		  </template>\n		</vxe-column>\n        <#elseif c.showType == \"wangeditor\" || c.showType == \"quilleditor\">\n      <vxe-column\n        field=\"${c.javaFieldId}\"\n        sortable\n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n            <p v-html=\"$utils.unescapeHTML(scope.row.${c.javaFieldId})\"></p>\n        </template>\n      </vxe-column>\n      <#else>\n    <vxe-column\n        field=\"${c.javaFieldId}\"\n        sortable\n        title=\"${c.comments}\">\n        <#if firstListField>\n            <template slot-scope=\"scope\">\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\">{{scope.row.${c.javaFieldId}}}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\">{{scope.row.${c.javaFieldId}}}</el-link>\n              <span v-else>{{scope.row.${c.javaFieldId}}}</span>\n            </template>\n        </#if>\n      </vxe-column>\n          </#if>\n        <#assign firstListField = false>\n      </#if>\n      </#list>\n      <vxe-column\n        fixed=\"right\"\n        align=\"center\"\n        width=\"200\"\n        title=\"操作\">\n        <template  slot-scope=\"scope\">\n          <el-button v-if=\"hasPermission(\'${permissionPrefix}:view\')\" type=\"text\" icon=\"el-icon-view\" size=\"small\" @click=\"view(scope.row.id)\">查看</el-button>\n          <el-button v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" type=\"text\" icon=\"el-icon-edit\" size=\"small\" @click=\"edit(scope.row.id)\">修改</el-button>\n          <el-button v-if=\"hasPermission(\'${permissionPrefix}:del\')\" type=\"text\"  icon=\"el-icon-delete\" size=\"small\" @click=\"del(scope.row.id)\">删除</el-button>\n        </template>\n      </vxe-column>\n    </vxe-table>\n    <vxe-pager\n      background\n      size=\"small\"\n      :current-page=\"tablePage.currentPage\"\n      :page-size=\"tablePage.pageSize\"\n      :total=\"tablePage.total\"\n      :page-sizes=\"[10, 20, 100, 1000, {label: \'全量数据\', value: 1000000}]\"\n      :layouts=\"[\'PrevPage\', \'JumpNumber\', \'NextPage\', \'FullJump\', \'Sizes\', \'Total\']\"\n      @page-change=\"currentChangeHandle\">\n    </vxe-pager>\n    </div>\n    </div>\n        <!-- 弹窗, 新增 / 修改 -->\n    <${ClassName}Form  ref=\"${className}Form\" @refreshDataList=\"refreshList\"></${ClassName}Form>\n  </div>\n</template>\n\n<script>\n  import ${ClassName}Form from \'./${ClassName}Form\'\n  import ${className}Service from \'@/api/${moduleName}<#if subModuleName != \"\">/${subModuleName}</#if>/${className}Service\'\n<#list table.importQueryList as c>\n  ${c}\n</#list>\n  export default {\n    data () {\n      return {\n        searchForm: {\n        <#list table.queryList as c>\n          <#if c.javaFieldId?contains(\".\")>\n          ${c.javaFieldId?split(\".\")[0]}: {\n            ${c.javaFieldId?split(\".\")[1]}: \'\'\n          }<#if c_has_next>,</#if>\n          <#else>\n          ${c.javaFieldId}: \'\'<#if c_has_next>,</#if>\n          </#if>\n    </#list>\n        },\n        dataList: [],\n        tablePage: {\n          total: 0,\n          currentPage: 1,\n          pageSize: 10,\n          orders: <#if isNeedJoinTable>[{ column: \'a.create_date\', asc: false }]<#else>[{ column: \'create_date\', asc: false }]</#if>\n        },\n        loading: false\n      }\n    },\n    components: {\n      <#list table.queryComponents as c>\n      ${c},\n    </#list>\n      ${ClassName}Form\n    },\n    activated () {\n      this.refreshList()\n    },\n    <#assign cks=0/>\n    <#list table.columnList as c>\n    <#if c.isQuery?? && c.isQuery == \"1\">\n     <#if c.showType == \"checkbox\">\n     <#assign cks= cks + 1/>\n    </#if>\n    </#if>\n    </#list>\n    <#if cks gt 0>\n    computed: {\n    <#assign ckIndex=0/>\n    <#list table.columnList as c>\n    <#if c.isQuery?? && c.isQuery == \"1\">\n     <#if c.showType == \"checkbox\">\n     <#assign ckIndex= ckIndex + 1/>\n      ${c.javaFieldId}List: {\n        get: function () {\n          return this.searchForm.${c.javaFieldId} !== \'\' ? this.searchForm.${c.javaFieldId}.split(\',\') : []\n        },\n        set: function (val) {\n          this.searchForm.${c.javaFieldId} = val.join(\',\')\n        }\n      }<#if ckIndex lt cks >,</#if>\n    </#if>\n    </#if>\n    </#list>\n    },\n    </#if>\n    methods: {\n      // 获取数据列表\n      refreshList () {\n        this.loading = true\n        ${className}Service.list({\n          \'current\': this.tablePage.currentPage,\n          \'size\': this.tablePage.pageSize,\n          \'orders\': this.tablePage.orders,\n            <#if (table.queryBetwinDate?size > 0)>\n            <#list table.queryBetwinDate as c>\n          begin${c.javaFieldId?cap_first}: this.searchForm.${c.javaFieldId}[0],\n          end${c.javaFieldId?cap_first}: this.searchForm.${c.javaFieldId}[1],\n            </#list>\n          ...this.lodash.omit(this.searchForm, <#list table.queryBetwinDate as c>\'${c.javaFieldId}\'<#if c_has_next>, </#if></#list>)\n            <#else>\n          ...this.searchForm\n            </#if>\n        }).then(({data}) => {\n          this.dataList = data.records\n          this.tablePage.total = data.total\n          this.loading = false\n        })\n      },\n      // 当前页\n      currentChangeHandle ({ currentPage, pageSize }) {\n        this.tablePage.currentPage = currentPage\n        this.tablePage.pageSize = pageSize\n        this.refreshList()\n      },\n      // 排序\n      sortChangeHandle (obj) {\n        this.tablePage.orders = []\n        if (obj.order != null) {\n          this.tablePage.orders = [{ column: obj.column.sortBy || this.$utils.toLine(obj.property), asc: obj.order === \'asc\' }]\n        } else {\n          this.tablePage.orders = <#if isNeedJoinTable>[{ column: \'a.create_date\', asc: false }]<#else>[{ column: \'create_date\', asc: false }]</#if>\n        }\n        this.refreshList()\n      },\n      // 新增\n      add () {\n        this.$refs.${className}Form.init(\'add\', \'\')\n      },\n      // 修改\n      edit (id) {\n        id = id || this.$refs.${className}Table.getCheckboxRecords().map(item => {\n          return item.id\n        })[0]\n        this.$refs.${className}Form.init(\'edit\', id)\n      },\n      // 查看\n      view (id) {\n        this.$refs.${className}Form.init(\'view\', id)\n      },\n      // 删除\n      del (id) {\n        let ids = id || this.$refs.${className}Table.getCheckboxRecords().map(item => {\n          return item.id\n        }).join(\',\')\n        this.$confirm(`确定删除所选项吗?`, \'提示\', {\n          confirmButtonText: \'确定\',\n          cancelButtonText: \'取消\',\n          type: \'warning\'\n        }).then(() => {\n          this.loading = true\n          ${className}Service.delete(ids).then(({data}) => {\n            this.$message.success(data)\n            this.refreshList()\n            this.loading = false\n          })\n        })\n      },\n      // 下载模板\n      downloadTpl () {\n        this.loading = true\n        ${className}Service.exportTemplate().then(({data}) => {\n    // 将二进制流文件写入excel表，以下为重要步骤\n          this.$utils.downloadExcel(data, \'请假表单导入模板\')\n          this.loading = false\n        }).catch(function (err) {\n          this.loading = false\n          if (err.response) {\n            console.log(err.response)\n          }\n        })\n      },\n      // 自定义服务端导入\n      importMethod ({ file }) {\n      // 处理表单\n        const formBody = new FormData()\n        formBody.append(\'file\', file)\n        this.loading = true\n        ${className}Service.importExcel(formBody).then(({data}) => {\n          this.$message.success({\n            dangerouslyUseHTMLString: true,\n            message: data\n          })\n          this.refreshList()\n        })\n      },\n      // 自定义服务端导出\n      exportMethod ({ options }) {\n      // 传给服务端的参数\n        const params = {\n          current: this.tablePage.currentPage,\n          size: this.tablePage.pageSize,\n          orders: this.tablePage.orders,\n          ...this.searchForm,\n          filename: options.filename,\n          sheetName: options.sheetName,\n          isHeader: options.isHeader,\n          original: options.original,\n          mode: options.mode,\n          selectIds: options.mode === \'selected\' ? options.data.map((item) => item.id) : [],\n          exportFields: options.columns.map((column) => column.property && column.property.split(\'.\')[0])\n        }\n        this.loading = true\n        return ${className}Service.exportExcel(params).then(({data}) => {\n      // 将二进制流文件写入excel表，以下为重要步骤\n          this.$utils.downloadExcel(data, options.filename)\n          this.loading = false\n        }).catch(function (err) {\n          if (err.response) {\n            console.log(err.response)\n          }\n        })\n      },\n      // 查看详情\n      detail ({row}) {\n        return new Promise(resolve => {\n          ${className}Service.queryById(row.id).then(({data}) => {\n            this.dataList.forEach((item, index) => {\n              if (item.id === row.id) {\n                        <#list table.childList as child>\n                item.${child.className?uncap_first}DTOList = data.${child.className?uncap_first}DTOList\n                        </#list>\n              }\n            })\n            resolve()\n          })\n        })\n      },\n      resetSearch () {\n        this.$refs.searchForm.resetFields()\n        this.refreshList()\n      }\n    }\n  }\n</script>\n]]>\n    </content>\n</template>\n', '', 0, 90, '0', NULL, '1', '2021-10-16 23:54:46', '1', '2021-10-10 15:56:57');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1449403451749560322', 'apiService.vue', '1449403450692595713', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>viewForm</name>\n    <filePath>src/api/${moduleName}/${subModuleName}</filePath>\n    <fileName>${className}Service.js</fileName>\n    <content><![CDATA[\nimport request from \'@/utils/httpRequest\'\n\nexport default {\n  save: function (inputForm) {\n    return request({\n      url: \'/${urlPrefix}/save\',\n      method: \'post\',\n      data: inputForm\n    })\n  },\n\n  delete: function (ids) {\n    return request({\n      url: \'/${urlPrefix}/delete\',\n      method: \'delete\',\n      params: {ids: ids}\n    })\n  },\n\n  queryById: function (id) {\n    return request({\n      url: \'/${urlPrefix}/queryById\',\n      method: \'get\',\n      params: {id: id}\n    })\n  },\n\n  list: function (params) {\n    return request({\n      url: \'/${urlPrefix}/list\',\n      method: \'get\',\n      params: params\n    })\n  },\n\n  exportTemplate: function () {\n    return request({\n      url: \'/${urlPrefix}/import/template\',\n      method: \'get\',\n      responseType: \'blob\'\n    })\n  },\n\n  exportExcel: function (params) {\n    return request({\n      url: \'/${urlPrefix}/export\',\n      method: \'get\',\n      params: params,\n      responseType: \'blob\'\n    })\n  },\n\n  importExcel: function (data) {\n    return request({\n      url: \'/${urlPrefix}/import\',\n      method: \'post\',\n      data: data\n    })\n  }\n}]]>\n    </content>\n</template>\n', '', 0, 100, '0', NULL, '1', '2021-10-10 20:57:42', '1', '2021-10-10 20:57:42');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1509174856569069569', 'controller.java', '1509174856489377794', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>controller</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/controller</filePath>\n    <fileName>${ClassName}Controller.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.controller;\n\nimport javax.servlet.http.HttpServletResponse;\nimport javax.validation.Valid;\nimport com.google.common.collect.Lists;\nimport cn.weegoo.core.excel.EasyExcelUtils;\nimport cn.weegoo.core.excel.ExcelOptions;\nimport cn.weegoo.core.excel.annotation.ExportMode;\nimport cn.weegoo.core.query.QueryWrapperGenerator;\nimport cn.weegoo.aop.logging.annotation.ApiLog;\nimport io.swagger.annotations.Api;\nimport io.swagger.annotations.ApiOperation;\nimport org.springframework.http.ResponseEntity;\nimport org.springframework.security.access.prepost.PreAuthorize;\nimport org.springframework.beans.factory.annotation.Autowired;\nimport org.springframework.web.bind.annotation.*;\nimport com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;\nimport com.baomidou.mybatisplus.core.metadata.IPage;\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\n<#if !isNeedJoinTable>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${ClassName};\n</#if>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.mapstruct.${ClassName}Wrapper;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.${ClassName}Service;\nimport org.springframework.web.multipart.MultipartFile;\n\nimport java.io.IOException;\nimport java.util.List;\n\n/**\n * ${functionName}Controller\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n\n@Api(tags =\"${functionName}\")\n@RestController\n@RequestMapping(value = \"/${urlPrefix}\")\npublic class ${ClassName}Controller {\n\n	@Autowired\n	private ${ClassName}Service ${className}Service;\n\n	@Autowired\n	private ${ClassName}Wrapper ${className}Wrapper;\n\n	/**\n	 * ${functionNameSimple}列表数据\n	 */\n	@ApiLog(\"查询${functionNameSimple}列表数据\")\n	@ApiOperation(value = \"查询${functionNameSimple}列表数据\")\n	@GetMapping(\"list\")\n	public ResponseEntity<#if isNeedJoinTable><IPage<${ClassName}DTO>><#else><IPage<${ClassName}>></#if> list(${ClassName}DTO ${className}DTO, Page<<#if isNeedJoinTable>${ClassName}DTO<#else>${ClassName}</#if>> page) throws Exception {\n		QueryWrapper queryWrapper = QueryWrapperGenerator.buildQueryCondition (${className}DTO, ${ClassName}DTO.class);\n				<#if isNeedJoinTable>\n		IPage<${ClassName}DTO> result = ${className}Service.findPage (page, queryWrapper);\n					<#else>\n		IPage<${ClassName}> result = ${className}Service.page (page, queryWrapper);\n					</#if>\n		return ResponseEntity.ok (result);\n	}\n\n\n	/**\n	 * 根据Id获取${functionNameSimple}数据\n	 */\n	@ApiLog(\"根据Id获取${functionNameSimple}数据\")\n	@ApiOperation(value = \"根据Id获取${functionNameSimple}数据\")\n	@GetMapping(\"queryById\")\n	public ResponseEntity<${ClassName}DTO> queryById(String id) {\n					<#if isNeedJoinTable>\n		return ResponseEntity.ok ( ${className}Service.findById ( id ) );\n					<#else >\n		return ResponseEntity.ok ( ${className}Wrapper.toDTO ( ${className}Service.getById ( id ) ) );\n					</#if>\n	}\n\n	/**\n	 * 保存${functionNameSimple}\n	 */\n	@ApiLog(\"保存${functionNameSimple}\")\n	@ApiOperation(value = \"保存${functionNameSimple}\")\n	@PostMapping(\"save\")\n	public  ResponseEntity <String> save(@Valid @RequestBody ${ClassName}DTO ${className}DTO) {\n		//新增或编辑表单保存\n		${className}Service.saveOrUpdate (${className}Wrapper.toEntity (${className}DTO));\n        return ResponseEntity.ok ( \"保存${functionNameSimple}成功\" );\n	}\n\n\n	/**\n	 * 删除${functionNameSimple}\n	 */\n	@ApiLog(\"删除${functionNameSimple}\")\n	@ApiOperation(value = \"删除${functionNameSimple}\")\n	@DeleteMapping(\"delete\")\n	public ResponseEntity <String> delete(String ids) {\n		String idArray[] = ids.split(\",\");\n        ${className}Service.removeByIds ( Lists.newArrayList ( idArray ) );\n		return ResponseEntity.ok( \"删除${functionNameSimple}成功\" );\n	}\n	\n	/**\n     * 导出${functionNameSimple}数据\n     *\n     * @param ${className}DTO\n     * @param page\n     * @param response\n     * @throws Exception\n     */\n    @ApiLog(\"导出${functionNameSimple}数据\")\n    @GetMapping(\"export\")\n    public void exportFile(${ClassName}DTO ${className}DTO, Page <<#if isNeedJoinTable>${ClassName}DTO<#else>${ClassName}</#if>> page, ExcelOptions options, HttpServletResponse response) throws Exception {\n        String fileName = options.getFilename ( );\n		QueryWrapper queryWrapper = QueryWrapperGenerator.buildQueryCondition (${className}DTO, ${ClassName}DTO.class);\n				<#if isNeedJoinTable>\n        if ( ExportMode.current.equals ( options.getMode ( ) ) ) { // 导出当前页数据\n            \n        } else if ( ExportMode.selected.equals ( options.getMode ( ) ) ) { // 导出选中数据\n            queryWrapper.in ( \"a.id\", options.getSelectIds () );\n        } else { // 导出全部数据\n            page.setSize ( -1 );\n            page.setCurrent ( 0 );\n        }\n        List<${ClassName}DTO> result = ${className}Service.findPage ( page, queryWrapper ).getRecords ( );\n					<#else>\n        if ( ExportMode.current.equals ( options.getMode ( ) ) ) { // 导出当前页数据\n            \n        } else if ( ExportMode.selected.equals ( options.getMode ( ) ) ) { // 导出选中数据\n            queryWrapper.in ( \"id\", options.getSelectIds () );\n        } else { // 导出全部数据\n            page.setSize ( -1 );\n            page.setCurrent ( 0 );\n        }\n        List < ${ClassName}> result = ${className}Service.page ( page, queryWrapper ).getRecords ( );\n					</#if>\n        EasyExcelUtils.newInstance ( ${className}Service, ${className}Wrapper ).exportExcel ( result,  options.getSheetName ( ), ${ClassName}DTO.class, fileName,options.getExportFields (), response );\n    }\n\n    /**\n     * 导入${functionNameSimple}数据\n     *\n     * @return\n     */\n    @PostMapping(\"import\")\n    public ResponseEntity importFile(MultipartFile file) throws IOException {\n        String result = EasyExcelUtils.newInstance ( ${className}Service, ${className}Wrapper ).importExcel ( file, ${ClassName}DTO.class );\n        return ResponseEntity.ok ( result );\n    }\n\n    /**\n     * 下载导入${functionNameSimple}数据模板\n     *\n     * @param response\n     * @return\n     */\n    @GetMapping(\"import/template\")\n    public void importFileTemplate(HttpServletResponse response) throws IOException {\n        String fileName = \"${functionNameSimple}数据导入模板.xlsx\";\n        List<${ClassName}DTO> list = Lists.newArrayList();\n        EasyExcelUtils.newInstance ( ${className}Service, ${className}Wrapper ).exportExcel ( list,  \"${functionNameSimple}数据\", ${ClassName}DTO.class, fileName, null, response );\n    }\n\n}]]>\n    </content>\n</template>\n', '', 0, 10, '0', NULL, '1', '2021-10-08 11:44:42', '1', '2021-10-08 11:44:42');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1509174856678121474', 'service.java', '1509174856489377794', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>service</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/service</filePath>\n    <fileName>${ClassName}Service.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service;\n\nimport org.springframework.stereotype.Service;\nimport org.springframework.transaction.annotation.Transactional;\n<#if table.dataSource?? && table.dataSource.enName?? && table.dataSource.enName!=\"\" && table.dataSource.enName!=\"master\">\nimport com.baomidou.dynamic.datasource.annotation.DS;\n</#if>\nimport com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;\n<#if isNeedJoinTable>\nimport com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;\nimport com.baomidou.mybatisplus.core.metadata.IPage;\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO;\n</#if>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${ClassName};\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper.${ClassName}Mapper;\n\n/**\n * ${functionName}Service\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n<#if table.dataSource?? && table.dataSource.enName?? && table.dataSource.enName!=\"\" && table.dataSource.enName!=\"master\">\n@DS(\"${table.dataSource.enName}\")\n</#if>\n@Service\n@Transactional\npublic class ${ClassName}Service extends ServiceImpl<${ClassName}Mapper, ${ClassName}> {\n<#if isNeedJoinTable>\n\n	/**\n	 * 根据id查询\n	 * @param id\n	 * @return\n	 */\n	public ${ClassName}DTO findById(String id) {\n		return baseMapper.findById ( id );\n	}\n\n	/**\n	 * 自定义分页检索\n	 * @param page\n	 * @param queryWrapper\n	 * @return\n	 */\n	public IPage <${ClassName}DTO> findPage(Page <${ClassName}DTO> page, QueryWrapper queryWrapper) {\n		queryWrapper.eq (\"a.del_flag\", 0 ); // 排除已经删除\n		return  baseMapper.findList (page, queryWrapper);\n	}\n</#if>\n\n}]]>\n    </content>\n</template>\n', '', 0, 20, '0', NULL, '1', '2021-10-08 11:46:42', '1', '2021-10-08 11:46:42');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1509174856724258817', 'mapper.java', '1509174856489377794', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>mapper</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/mapper</filePath>\n    <fileName>${ClassName}Mapper.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper;\n\nimport com.baomidou.mybatisplus.core.mapper.BaseMapper;\n<#if isNeedJoinTable>\nimport org.apache.ibatis.annotations.Param;\nimport com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;\nimport com.baomidou.mybatisplus.core.metadata.IPage;\nimport com.baomidou.mybatisplus.core.toolkit.Constants;\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO;\n</#if>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${ClassName};\n\n/**\n * ${functionName}MAPPER接口\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\npublic interface ${ClassName}Mapper extends BaseMapper<${ClassName}> {\n\n<#if isNeedJoinTable>\n    /**\n     * 根据id获取${functionName}\n     * @param id\n     * @return\n     */\n    ${ClassName}DTO findById(String id);\n\n    /**\n     * 获取${functionName}列表\n     *\n     * @param queryWrapper\n     * @return\n     */\n    IPage <${ClassName}DTO> findList(Page <${ClassName}DTO> page, @Param(Constants.WRAPPER) QueryWrapper queryWrapper);\n</#if>\n\n}]]>\n    </content>\n</template>\n', '', 0, 30, '0', NULL, '1', '2021-10-08 11:44:42', '1', '2021-10-08 11:44:42');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1509174856766201858', 'mapper.xml', '1509174856489377794', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>mapper</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/mapper/xml</filePath>\n    <fileName>${ClassName}Mapper.xml</fileName>\n    <content><![CDATA[<?xml version=\"1.0\" encoding=\"UTF-8\" ?>\n<!DOCTYPE mapper PUBLIC \"-//mybatis.org//DTD Mapper 3.0//EN\" \"http://mybatis.org/dtd/mybatis-3-mapper.dtd\">\n<mapper namespace=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper.${ClassName}Mapper\">\n\n<#if isNeedJoinTable>\n    <#-- 输出字段列 -->\n	<sql id=\"${className}Columns\">\n		<#assign columnField>\n			<#list table.columnList as c>\n		a.${c.name} AS \"${c.javaFieldId}\",\n			</#list>\n			<#list table.columnList as c>\n			<#-- 父表关联字段 -->\n				<#if table.parentExists && table.parentTableFk == c.name>\n					<#list c.javaFieldAttrs as a>\n		b.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"userselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"officeselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"areaselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"treeselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"gridselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n				</#list>\n				</#if>\n\n			</#list>\n		</#assign>\n${columnField?substring(0, columnField?last_index_of(\",\"))}\n	</sql>\n\n	<#-- 输出字段关联表 -->\n	<sql id=\"${className}Joins\">\n\n		<#-- 关联系统表 -->\n		<#list table.columnList as c>\n		<#-- 关联父表 -->\n		<#if table.parentExists && table.parentTableFk == c.name>\n			LEFT JOIN ${table.parent.name} b ON b.id = a.${table.parentTableFk}\n			<#elseif c.isNotBasicUserField>\n		LEFT JOIN sys_user ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"officeselect\">\n		LEFT JOIN sys_office ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"areaselect\">\n		LEFT JOIN sys_area ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"treeselect\" && c.javaType != \"This\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"gridselect\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			</#if>\n		</#list>\n	</sql>\n\n\n\n	<select id=\"findById\" resultType=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO\">\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		WHERE a.id = ${\"#\"}{id} and a.del_flag = 0\n	</select>\n\n	<select id=\"findList\" resultType=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO\">\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n	    ${\"$\"}{ew.customSqlSegment}\n	</select>\n</#if>\n\n</mapper>]]>\n    </content>\n</template>\n', '', 0, 40, '0', NULL, '1', '2021-10-08 11:44:56', '1', '2021-10-08 11:44:56');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1509174856808144897', 'dto.java', '1509174856489377794', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>dto</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/service/dto</filePath>\n    <fileName>${ClassName}DTO.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto;\n\n<#list table.importList as i>\nimport ${i};\n</#list>\n<#if (table.queryList?size > 0) >\nimport cn.weegoo.core.query.Query;\nimport cn.weegoo.core.query.QueryType;\n</#if>\nimport com.alibaba.excel.annotation.ExcelProperty;\n<#list table.importExcelList as i>\nimport ${i};\n</#list>\nimport cn.weegoo.core.service.dto.BaseDTO;\nimport lombok.Data;\nimport lombok.EqualsAndHashCode;\n/**\n * ${functionName}DTO\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n@Data\n@EqualsAndHashCode(callSuper = false)\npublic class ${ClassName}DTO extends BaseDTO {\n\n	private static final long serialVersionUID = 1L;\n\n	<#-- 生成字段属性 -->\n	<#list table.columnList as c>\n		<#-- 如果不是基类属性 -->\n		<#if c.isNotBaseField>\n	        <#if c.comments??>\n	        \n	/**\n     * ${c.comments}\n     */\n            </#if>\n			<#list c.simpleAnnotationList as a>\n	@${a}\n			</#list>\n			<#-- 父类对象 -->\n			<#if table.parentExists && table.parentTableFk == c.name>\n                    <#if (c.isQuery?? && c.isQuery == \"1\" && c.queryType ?? && c.queryType != \"\" )>\n                        <#if isNeedJoinTable>\n	@Query(tableColumn = \"a.${c.name}\", javaField = \"${c.javaFieldId}\", type = QueryType.${c.queryType})\n                        <#else>\n    @Query(type = QueryType.${c.queryType})\n                        </#if>\n					</#if>\n	@ExcelProperty(value = \"${c.comments}\", converter = ExcelFieldDTOConverter.class)\n	@ExcelFieldProperty(value = \"${c.javaFieldName}\", service =  \"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.${table.parent.className?cap_first}Service\", wrapper= \"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.mapstruct.${table.parent.className?cap_first}Wrapper\")\n	private ${table.parent.className?cap_first}DTO ${c.simpleJavaField};\n			<#-- 其它字段 -->\n			<#else>\n                    <#if (c.isQuery?? && c.isQuery == \"1\" && c.queryType ?? && c.queryType != \"\" )>\n                        <#if isNeedJoinTable>\n    @Query(tableColumn = \"a.${c.name}\", javaField = \"${c.javaFieldId}\", type = QueryType.${c.queryType})\n                        <#else>\n    @Query(type = QueryType.${c.queryType})\n                        </#if>\n                    </#if>\n    <#if c.showType?? && c.showType == \"userselect\">\n    @ExcelProperty(value = \"${c.comments}\", converter = ExcelUserDTOConverter.class) \n	<#elseif c.showType?? && c.showType == \"officeselect\">\n    @ExcelProperty(value = \"${c.comments}\", converter = ExcelOfficeDTOConverter.class) \n    <#elseif c.showType?? && c.showType == \"areaselect\">\n    @ExcelProperty(value = \"${c.comments}\", converter = ExcelAreaDTOConverter.class) \n    <#elseif c.showType?? && (c.showType == \"select\" || c.showType == \"radiobox\" || c.showType == \"checkbox\")>\n	@ExcelProperty(value = \"${c.comments}\", converter = ExcelDictDTOConverter.class)\n	@ExcelDictProperty(\"${c.dictType}\")\n    <#elseif c.showType?? && (c.showType == \"treeselect\"|| c.showType == \"gridselect\")>\n	@ExcelProperty(value = \"${c.comments}\", converter = ExcelFieldDTOConverter.class)\n	@ExcelFieldProperty(value = \"${c.javaFieldName}\", service =  \"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.${c.simpleJavaType?replace(\"DTO\",\"\")}Service\", wrapper= \"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.mapstruct.${c.simpleJavaType?replace(\"DTO\",\"\")}Wrapper\")\n    <#else>                    \n	@ExcelProperty(\"${c.comments}\") \n	</#if>                \n	private ${c.simpleJavaType} ${c.simpleJavaField};\n			</#if>\n		</#if>\n	</#list>\n    <#-- 子表列表字段 -->\n    <#list table.childList as c>\n    /**\n     *子表列表\n     */\n	private List<${c.className?cap_first}DTO> ${c.className?uncap_first}DTOList = Lists.newArrayList();\n    </#list>\n\n}]]>\n    </content>\n</template>\n', '', 0, 50, '0', NULL, '1', '2021-10-08 11:15:54', '1', '2021-10-08 11:15:54');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1509174856837505026', 'entity.java', '1509174856489377794', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>entity</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/domain</filePath>\n    <fileName>${ClassName}.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain;\n\n<#list table.importEntityList as i>\nimport ${i};\n</#list>\n<#if table.idType ==\"2\">\nimport com.baomidou.mybatisplus.annotation.IdType;\nimport com.baomidou.mybatisplus.annotation.TableId;\n</#if>\nimport com.baomidou.mybatisplus.annotation.TableName;\nimport cn.weegoo.core.domain.BaseEntity;\nimport lombok.Data;\nimport lombok.EqualsAndHashCode;\n/**\n * ${functionName}Entity\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n@Data\n@EqualsAndHashCode(callSuper = false)\n@TableName(\"${table.name}\")\npublic class ${ClassName} extends BaseEntity {\n\n	private static final long serialVersionUID = 1L;\n	<#if table.idType ==\"2\">\n\n	@TableId(type = IdType.AUTO)\n	private Integer id;\n	</#if>\n\n	<#-- 生成字段属性 -->\n	<#list table.columnList as c>\n		<#-- 如果不是基类属性 -->\n		<#if c.isNotBaseField>\n			<#if c.comments??>\n	/**\n     * ${c.comments}\n     */\n            </#if>\n            <#if c.isBasicJavaType>\n<#if c.simpleJavaField != c.mapperJavaField>\n    @TableField(\"${c.name}\")\n</#if>\n	private ${c.simpleJavaType} ${c.simpleJavaField};\n	        <#else>\n<#if c.simpleJavaFieldId != c.mapperJavaField>\n    @TableField(\"${c.name}\")\n</#if>\n	private String ${c.simpleJavaFieldId};\n	         </#if>\n		</#if>\n	</#list>\n\n}]]>\n    </content>\n</template>\n', '', 0, 60, '0', NULL, '1', '2021-10-08 11:44:42', '1', '2021-10-08 11:44:42');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1509174856896225282', 'wrapper.java', '1509174856489377794', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>service</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/service/mapstruct</filePath>\n    <fileName>${ClassName}Wrapper.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.mapstruct;\n\n\nimport cn.weegoo.core.mapstruct.EntityWrapper;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${ClassName};\nimport org.mapstruct.Mapper;\n<#if isNeedWrapper>\nimport org.mapstruct.Mapping;\nimport org.mapstruct.Mappings;\n</#if>\nimport org.mapstruct.ReportingPolicy;\nimport org.mapstruct.factory.Mappers;\n\n/**\n *  ${ClassName}Wrapper\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n@Mapper(componentModel = \"spring\", unmappedTargetPolicy = ReportingPolicy.IGNORE, uses = {} )\npublic interface ${ClassName}Wrapper extends EntityWrapper<${ClassName}DTO, ${ClassName}> {\n\n    ${ClassName}Wrapper INSTANCE = Mappers.getMapper(${ClassName}Wrapper.class);\n    <#if isNeedWrapper>\n     @Mappings({\n     	<#list table.columnList as c>\n		<#-- 如果不是基类属性 -->\n		    <#if c.isNotBaseField>\n     		    <#if table.parentExists && table.parentTableFk == c.name>\n            @Mapping(source = \"${c.javaFieldId}\", target = \"${c.simpleJavaFieldId}\"),\n			<#-- 其它字段 -->\n			    <#elseif c.isNotBasicJavaType>\n            @Mapping(source = \"${c.javaFieldId}\", target = \"${c.simpleJavaFieldId}\"),\n	             </#if>\n	        </#if>\n	    </#list>\n            @Mapping(source = \"createBy.id\", target = \"createBy\"),\n            @Mapping (source = \"updateBy.id\", target = \"updateBy\")})\n    ${ClassName} toEntity(${ClassName}DTO dto);\n\n\n    @Mappings({\n     	<#list table.columnList as c>\n		<#-- 如果不是基类属性 -->\n		    <#if c.isNotBaseField>\n     		    <#if table.parentExists && table.parentTableFk == c.name>\n            @Mapping(source = \"${c.simpleJavaFieldId}\", target = \"${c.javaFieldId}\"),\n			<#-- 其它字段 -->\n			    <#elseif c.isNotBasicJavaType>\n            @Mapping(source = \"${c.simpleJavaFieldId}\", target = \"${c.javaFieldId}\"),\n	             </#if>\n	        </#if>\n	    </#list>\n            @Mapping (source = \"createBy\", target = \"createBy.id\"),\n            @Mapping (source = \"updateBy\", target = \"updateBy.id\")})\n    ${ClassName}DTO toDTO(${ClassName} entity);\n    </#if>\n}\n]]>\n    </content>\n</template>\n', '', 0, 70, '0', NULL, '1', '2021-10-08 09:20:58', '1', '2021-10-08 09:20:58');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1509174856929779713', 'viewList.vue', '1509174856489377794', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>viewList</name>\n    <filePath>pages/${moduleName}/${subModuleName}</filePath>\n    <fileName>${ClassName}List.vue</fileName>\n    <content><![CDATA[\n<template>\n	<view>\n		<cu-custom bgColor=\"bg-blue\" :isBack=\"true\">\n			<block slot=\"backText\">返回</block>\n			<block slot=\"content\"> ${functionNameSimple}</block>\n		</cu-custom>\n		<view>\n			<view class=\"uni-container\">\n				<uni-table ref=\"table\" :loading=\"loading\" border stripe type=\"selection\" emptyText=\"暂无更多数据\" @selection-change=\"selectionChange\">\n					<uni-tr>\n					<#list table.columnList as c>\n					  <#if c.isList?? && c.isList == \"1\">\n						<uni-th  <#if c.isQuery?? && c.isQuery == \"1\">filter-type=\"search\" @filter-change=\"searchChangeHandle\"</#if>  <#if c.showType == \"userselect\" || c.showType == \"officeselect\" || c.showType == \"areaselect\" || c.showType == \"treeselect\"|| c.showType == \"gridselect\"> field=\"${c.javaFieldName}\" <#else> field=\"${c.javaFieldId}\"</#if> @sort-change=\"sortChangeHandle\" sortable align=\"center\">${c.comments}</uni-th>\n					  </#if>\n					</#list>	\n						<uni-th>操作</uni-th>\n					</uni-tr>\n					<uni-tr v-for=\"(row, index) in dataList\" :key=\"index\">\n						<#list table.columnList as c>\n						<#if c.isList?? && c.isList == \"1\">\n						  <#if c.showType == \"userselect\" || c.showType == \"officeselect\" || c.showType == \"areaselect\" || c.showType == \"treeselect\"|| c.showType == \"gridselect\">\n						<uni-td>{{ row.${c.simpleJavaField} && row.${c.javaFieldName} }}</uni-td>\n						  <#elseif c.showType == \"select\">\n						<uni-td>{{ $dictUtils.getDictLabel(\"${c.dictType}\", row.${c.javaFieldId}, \'-\') }}</uni-td>	\n						  <#elseif c.showType == \"checkbox\">\n						<uni-td>{{row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}</uni-td>	\n						  <#elseif c.showType == \"radiobox\">\n						<uni-td>{{ $dictUtils.getDictLabel(\"${c.dictType}\", row.${c.javaFieldId}, \'-\') }}</uni-td>\n						  <#elseif c.showType == \"fileselect\" || c.showType == \"imageselect\">\n                        <uni-td><image  :src=\"row.${c.javaFieldId}\"></image></uni-td>\n						  <#else>\n						<uni-td>{{row.${c.javaFieldId}}}</uni-td>\n						  </#if>\n						   </#if>\n						  </#list>	\n						<uni-td>\n							<view class=\"uni-group\">\n								<button class=\"uni-button\" size=\"mini\"  @click=\"edit(row.id)\" type=\"primary\">修改</button>\n								<button class=\"uni-button\" size=\"mini\"  @click=\"del(row.id)\" type=\"warn\">删除</button>\n							</view>\n						</uni-td>\n					</uni-tr>\n				</uni-table>\n				<view class=\"uni-pagination-box\">\n					<uni-pagination show-icon :page-size=\"tablePage.pageSize\" :current=\"tablePage.currentPage\" :total=\"tablePage.total\" @change=\"currentChangeHandle\" />\n				</view>\n			</view>\n		\n			<uni-fab\n				:pattern=\" {\n							color: \'#7A7E83\',\n							backgroundColor: \'#fff\',\n							selectedColor: \'#007AFF\',\n							buttonColor: \'#007AFF\'\n						}\"\n				horizontal=\"right\"\n				vertical=\"bottom\"\n				@fabClick=\"add\"\n			></uni-fab>\n		</view>\n		</view>\n</template>\n\n<script>\n  import uniFab from \'@/components/uni-fab/uni-fab.vue\';\n  import ${className}Service from \'@/api/${moduleName}<#if subModuleName != \"\">/${subModuleName}</#if>/${className}Service\'\n  export default {\n	onShow(option) {\n		this.$auth.checkLogin()\n	},\n	components:{\n		uniFab\n	},\n    data () {\n      return {\n        searchForm: {},\n        dataList: [], // 数据列表\n        tablePage: {\n          total: 0,\n          currentPage: 1,\n          pageSize: 10,\n          orders: []\n        },\n        loading: false\n      }\n    },\n	onLoad() {\n	  this.refreshList()\n	},\n    methods: {\n	  // 新增\n	  add (){\n	  	uni.navigateTo({\n	  	  url: \'/pages/${moduleName}/${subModuleName}/${ClassName}Form\'\n	  	})\n	  },\n	  // 修改\n	  edit (id) {\n	  	uni.navigateTo({\n	  	  url: \'/pages/${moduleName}/${subModuleName}/${ClassName}Form?id=\'+id\n	  	})\n	  },\n      // 删除\n	  del (id) {\n	  	uni.showModal({\n	  		title: \'提示\',\n	  		content: \'您确认要删除数据吗\',\n	  		showCancel: true,\n	  		success: (res) => {\n	  			if (res.confirm) {\n	  				${className}Service.delete(id).then(({data})=>{\n	  					uni.showToast({\n	  						title: data,\n	  						icon:\"success\"\n	  					})\n	  					this.refreshList()\n	  				})\n	  			}\n	  		}\n	  	});\n	  },\n	  /*获取数据列表 */\n	  refreshList() {\n	    this.loading = true\n	    ${className}Service.list({\n	  	  \'current\': this.tablePage.currentPage,\n	  	  \'size\': this.tablePage.pageSize,\n	  	  \'orders\': this.tablePage.orders,\n	  	  ...this.searchForm\n	  	}).then(({data}) => {\n	  	  this.dataList = data.records\n	  	  this.tablePage.total = data.total\n	  	  this.loading = false\n	  	})\n	  	\n	  },\n	  // 排序\n	  sortChangeHandle (column) {\n	  	this.tablePage.orders = []\n	  	if (column.order != null) {\n	  	  this.tablePage.orders.push({column: this.$utils.toLine(column.property), asc: column.order === \'ascending\'})\n	  	}\n	  	this.refreshList()\n	  },\n	// 检索\n	  searchChangeHandle (column) {\n		this.searchForm[column.property] = column.filter\n		this.refreshList()\n	  },\n	// 分页触发\n	  currentChangeHandle (e) {\n		this.tablePage.currentPage = e.current\n		this.refreshList()\n	  }\n    }\n  }\n</script>\n]]>\n    </content>\n</template>\n', '', 0, 80, '0', NULL, '1', '2021-10-08 11:46:42', '1', '2021-10-08 11:46:42');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1509174856980111361', 'viewForm.vue', '1509174856489377794', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>viewForm</name>\n    <filePath>pages/${moduleName}/${subModuleName}</filePath>\n    <fileName>${ClassName}Form.vue</fileName>\n    <content><![CDATA[\n<template>\n	<view>\n		<cu-custom bgColor=\"bg-blue\" backUrl=\"/pages/${moduleName}/${subModuleName}/${ClassName}List\" :isBack=\"true\">\n			<block slot=\"backText\">返回</block>\n			<block slot=\"content\">{{title}}</block>\n		</cu-custom>\n		<form @submit=\"formSubmit\" class=\"cu-list menu\">\n<#assign firstListField = true>\n<#list table.columnList as c>\n      <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField)>\n       <view class=\"cu-form-group <#if firstListField>margin-top</#if>\">\n		   <view class=\"title\">\n			 <#if c.isNull != \"1\"><text class=\"red-color \">* </text> </#if>${c.comments}  \n		   </view>\n    <#if c.showType == \"input\">\n		   <input placeholder=\'请填写${c.comments}\' <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> v-model=\"inputForm.${c.javaFieldId}\"></input>\n    <#elseif c.showType == \"inputnumber\">\n		   <input placeholder=\'请填写${c.comments}\'  type=\"number\" <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> v-model=\"inputForm.${c.javaFieldId}\"></input>\n    <#elseif c.showType == \"textarea\" || c.showType == \"wangeditor\" || c.showType == \"quilleditor\">\n           <textarea placeholder=\'请填写${c.comments}\' <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> v-model=\"inputForm.${c.javaFieldId}\"></textarea>\n    <#elseif c.showType == \"select\">\n		  <jp-picker v-model=\"inputForm.${c.javaFieldId}\" rangeKey=\"label\" rangeValue=\"value\" :range=\"$dictUtils.getDictList(\'${c.dictType}\')\">\n		  	<view class=\"picker\">\n		  		{{$dictUtils.getDictLabel(\'${c.dictType}\', inputForm.${c.javaFieldId} ,\'请选择\')}}\n		  	</view>\n		  </jp-picker>\n    <#elseif c.showType == \"checkbox\">\n		  <jp-checkbox-group2 v-model=\"inputForm.${c.javaFieldId}\">\n		  	<label v-for=\"(option, index) in $dictUtils.getDictList(\'${c.dictType}\')\">\n		  		<view>\n		  			{{option.label || option.value}}\n		  			<checkbox  class=\"blue\" :value=\"option.value\"  :class=\"(\',\'+inputForm.${c.javaFieldId}+\',\').indexOf(\',\'+option.value+\',\')>=0?\'checked\':\'\'\" :checked=\"(\',\'+inputForm.${c.javaFieldId}+\',\').indexOf(\',\'+option.value+\',\')>=0\" ></checkbox>\n		  		</view>\n		  	</label>\n		  </jp-checkbox-group2>	\n    <#elseif c.showType == \"radiobox\">\n		 <jp-radio-group  v-model=\"inputForm.${c.javaFieldId}\">\n			<label v-for=\"(option, index) in $dictUtils.getDictList(\'${c.dictType}\')\">\n				<view>\n					{{option.label}}\n					<radio class=\'blue radio\'  :value=\"option.value\"  :class=\"inputForm.${c.javaFieldId}==option.value?\'checked\':\'\'\" :checked=\"inputForm.${c.javaFieldId}==option.value?true:false\" ></radio>\n				</view>\n			</label>\n		 </jp-radio-group>\n    <#elseif c.showType == \"dateselect\">\n		<jp-datetime-picker v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"选择日期时间\"  mode=\"date\"></jp-datetime-picker>\n    <#elseif c.showType == \"userselect\">\n		<user-select v-model=\"inputForm.${c.javaFieldId}\"></user-select>\n    <#elseif c.showType == \"officeselect\">\n		<jp-office-select v-model=\"inputForm.${c.javaFieldId}\"></jp-office-select>\n    <#elseif c.showType == \"areaselect\">\n		<jp-area-select v-model=\"inputForm.${c.javaFieldId}\"></jp-area-select>\n    <#elseif c.showType == \"cityselect\">\n		<jp-area-select v-model=\"inputForm.${c.javaFieldId}\"></jp-area-select>\n    <#elseif c.showType == \"fileselect\" ||  c.showType == \"imageselect\">\n		<jp-image-upload2 v-model=\"inputForm.${c.javaFieldId}\"></jp-image-upload2>\n    <#elseif c.showType == \"treeselect\">\n\n    <#elseif c.showType == \"gridselect\">\n	\n    </#if>\n		</view>\n		<#assign firstListField = false>\n      </#if>\n	  </#list>\n	  \n			<view class=\"padding-xl\">\n				<button form-type=\"submit\" class=\"cu-btn block bg-blue margin-tb-sm lg\" >提交</button>\n			</view>\n		</form>\n	</view>\n</template>\n\n<script>\n  var  graceChecker = require(\"@/common/graceChecker.js\");\n  import ${className}Service from \'@/api/${moduleName}<#if subModuleName != \"\">/${subModuleName}</#if>/${className}Service\'\n  export default {\n    onShow() {\n      this.$auth.checkLogin()\n    },\n    data () {\n      return {\n        title: \'新建${functionNameSimple}\',\n        inputForm: {\n          id: \'\'<#if (table.formList?size>0)>,</#if>\n        <#list table.formList as c>\n          <#if c.javaFieldId?contains(\".\")>\n          ${c.javaFieldId?split(\".\")[0]}: {\n            ${c.javaFieldId?split(\".\")[1]}: \'\'\n          }<#if c_has_next>,</#if>\n          <#else>\n          ${c.javaFieldId}: \'\'<#if c_has_next>,</#if>\n          </#if>\n    </#list>\n        }\n      }\n    },\n	async onLoad(query) {\n		if(query&&query.id){\n			this.titile = \"修改${functionNameSimple}\";\n			let {data} = await ${className}Service.queryById(query.id)\n			this.inputForm = this.recover(this.inputForm, data)\n		}\n	},\n    methods: {\n      formSubmit: function(e) {\n      	//定义表单规则\n      	var rule = [\n			<#list table.validationList as c>\n			      <#if c.isNull != \"1\">\n			      {name:\"${c.javaFieldId}\", checkType : \"notnull\", checkRule:\"\",  errorMsg:\"${c.comments}不能为空\"}<#if c_has_next>,</#if>\n			     </#if>\n				<#if c.validateType?? && c.validateType != \"\">\n				<#if c.validateType != \"string\">\n			      {name:\"${c.javaFieldId}\", checkType : \"${c.validateType}\", checkRule:\"\",  errorMsg:\"\"}<#if c_has_next>,</#if>\n				 </#if>\n				 </#if>\n			</#list>	 \n      	];\n      	//进行表单检查\n      	var formData = this.inputForm;\n      	var checkRes = graceChecker.check(formData, rule);\n      	if(checkRes){\n      		uni.showLoading()\n      		this.${className}Service.save(this.inputForm).then(({data}) => {\n      			uni.showToast({title:data, icon:\"success\"});\n      			uni.navigateTo({\n      			  url: \'/pages/${moduleName}/${subModuleName}/${ClassName}List\'\n      			})\n      		}).catch((e)=>{\n      			\n      		})\n      		\n      	}else{\n      		uni.showToast({ title: graceChecker.error, icon: \"none\" });\n      	}\n      }\n    }\n  }\n</script>\n\n  ]]>\n    </content>\n</template>\n', '', 0, 90, '0', NULL, '1', '2021-10-08 11:46:42', '1', '2021-10-08 11:46:42');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1509174857022054402', 'apiService.vue', '1509174856489377794', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>ServiceJs</name>\n    <filePath>api/${moduleName}/${subModuleName}</filePath>\n    <fileName>${className}Service.js</fileName>\n    <content><![CDATA[\nimport request from \"../../../common/request\"\n\nexport default {\n  save: function (inputForm) {\n    return request({\n      url: \'/${urlPrefix}/save\',\n      method: \'post\',\n      data: inputForm\n    })\n  },\n\n  delete: function (ids) {\n    return request({\n      url: \'/${urlPrefix}/delete\',\n      method: \'delete\',\n      params: {ids: ids}\n    })\n  },\n\n  queryById: function (id) {\n    return request({\n      url: \'/${urlPrefix}/queryById\',\n      method: \'get\',\n      params: {id: id}\n    })\n  },\n\n  list: function (params) {\n    return request({\n      url: \'/${urlPrefix}/list\',\n      method: \'get\',\n      params: params\n    })\n  },\n\n  exportTemplate: function () {\n    return request({\n      url: \'/${urlPrefix}/import/template\',\n      method: \'get\',\n      responseType: \'blob\'\n    })\n  },\n\n  exportExcel: function (params) {\n    return request({\n      url: \'/${urlPrefix}/export\',\n      method: \'get\',\n      params: params,\n      responseType: \'blob\'\n    })\n  },\n\n  importExcel: function (data) {\n    return request({\n      url: \'/${urlPrefix}/import\',\n      method: \'post\',\n      data: data\n    })\n  }\n}]]>\n    </content>\n</template>\n', '', 0, 100, '0', NULL, '1', '2021-10-08 11:16:10', '1', '2021-10-08 11:16:10');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1509176957818904577', 'appController.java', '1509174856489377794', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>controller</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/controller/app</filePath>\n    <fileName>App${ClassName}Controller.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.controller.app;\n\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.controller.${ClassName}Controller;\nimport io.swagger.annotations.Api;\nimport org.springframework.web.bind.annotation.RequestMapping;\nimport org.springframework.web.bind.annotation.RestController;\n\n/**\n * App${functionName}Controller\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n\n@Api(tags =\"${functionName}\")\n@RestController\n@RequestMapping(value = \"/app/${urlPrefix}\")\npublic class App${ClassName}Controller extends ${ClassName}Controller {\n\n}]]>\n    </content>\n</template>\n', '', 0, 1, '0', NULL, '1', '2022-03-30 22:33:21', '1', '2022-03-30 22:33:21');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1555098546540126210', 'appController.java', '1555098546473017345', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>controller</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/controller/app</filePath>\n    <fileName>App${ClassName}Controller.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.controller.app;\n\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.controller.${ClassName}Controller;\nimport io.swagger.annotations.Api;\nimport org.springframework.web.bind.annotation.RequestMapping;\nimport org.springframework.web.bind.annotation.RestController;\n\n/**\n * App${functionName}Controller\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n\n@Api(tags =\"${functionName}\")\n@RestController\n@RequestMapping(value = \"/app/${urlPrefix}\")\npublic class App${ClassName}Controller extends ${ClassName}Controller {\n\n}]]>\n    </content>\n</template>\n', '', 0, 1, '0', NULL, '1', '2022-03-30 22:33:21', '1', '2022-03-30 22:33:21');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1555098546586263553', 'controller.java', '1555098546473017345', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>controller</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/controller</filePath>\n    <fileName>${ClassName}Controller.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.controller;\n\nimport javax.servlet.http.HttpServletResponse;\nimport javax.validation.Valid;\nimport com.google.common.collect.Lists;\nimport cn.weegoo.core.excel.EasyExcelUtils;\nimport cn.weegoo.core.excel.ExcelOptions;\nimport cn.weegoo.core.excel.annotation.ExportMode;\nimport cn.weegoo.core.query.QueryWrapperGenerator;\nimport cn.weegoo.aop.logging.annotation.ApiLog;\nimport io.swagger.annotations.Api;\nimport io.swagger.annotations.ApiOperation;\nimport org.springframework.http.ResponseEntity;\nimport org.springframework.security.access.prepost.PreAuthorize;\nimport org.springframework.beans.factory.annotation.Autowired;\nimport org.springframework.web.bind.annotation.*;\nimport com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;\nimport com.baomidou.mybatisplus.core.metadata.IPage;\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\n<#if !isNeedJoinTable>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${ClassName};\n</#if>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.mapstruct.${ClassName}Wrapper;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.${ClassName}Service;\nimport org.springframework.web.multipart.MultipartFile;\n\nimport java.io.IOException;\nimport java.util.List;\n\n/**\n * ${functionName}Controller\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n\n@Api(tags =\"${functionName}\")\n@RestController\n@RequestMapping(value = \"/${urlPrefix}\")\npublic class ${ClassName}Controller {\n\n	@Autowired\n	private ${ClassName}Service ${className}Service;\n\n	@Autowired\n	private ${ClassName}Wrapper ${className}Wrapper;\n\n	/**\n	 * ${functionNameSimple}列表数据\n	 */\n	@ApiLog(\"查询${functionNameSimple}列表数据\")\n	@ApiOperation(value = \"查询${functionNameSimple}列表数据\")\n	@GetMapping(\"list\")\n	public ResponseEntity<#if isNeedJoinTable><IPage<${ClassName}DTO>><#else><IPage<${ClassName}>></#if> list(${ClassName}DTO ${className}DTO, Page<<#if isNeedJoinTable>${ClassName}DTO<#else>${ClassName}</#if>> page) throws Exception {\n		QueryWrapper queryWrapper = QueryWrapperGenerator.buildQueryCondition (${className}DTO, ${ClassName}DTO.class);\n				<#if isNeedJoinTable>\n		IPage<${ClassName}DTO> result = ${className}Service.findPage (page, queryWrapper);\n					<#else>\n		IPage<${ClassName}> result = ${className}Service.page (page, queryWrapper);\n					</#if>\n		return ResponseEntity.ok (result);\n	}\n\n\n	/**\n	 * 根据Id获取${functionNameSimple}数据\n	 */\n	@ApiLog(\"根据Id获取${functionNameSimple}数据\")\n	@ApiOperation(value = \"根据Id获取${functionNameSimple}数据\")\n	@GetMapping(\"queryById\")\n	public ResponseEntity<${ClassName}DTO> queryById(String id) {\n					<#if isNeedJoinTable>\n		return ResponseEntity.ok ( ${className}Service.findById ( id ) );\n					<#else >\n		return ResponseEntity.ok ( ${className}Wrapper.toDTO ( ${className}Service.getById ( id ) ) );\n					</#if>\n	}\n\n	/**\n	 * 保存${functionNameSimple}\n	 */\n	@ApiLog(\"保存${functionNameSimple}\")\n	@ApiOperation(value = \"保存${functionNameSimple}\")\n	@PostMapping(\"save\")\n	public  ResponseEntity <String> save(@Valid @RequestBody ${ClassName}DTO ${className}DTO) {\n		//新增或编辑表单保存\n		${className}Service.saveOrUpdate (${className}Wrapper.toEntity (${className}DTO));\n        return ResponseEntity.ok ( \"保存${functionNameSimple}成功\" );\n	}\n\n\n	/**\n	 * 删除${functionNameSimple}\n	 */\n	@ApiLog(\"删除${functionNameSimple}\")\n	@ApiOperation(value = \"删除${functionNameSimple}\")\n	@DeleteMapping(\"delete\")\n	public ResponseEntity <String> delete(String ids) {\n		String idArray[] = ids.split(\",\");\n        ${className}Service.removeByIds ( Lists.newArrayList ( idArray ) );\n		return ResponseEntity.ok( \"删除${functionNameSimple}成功\" );\n	}\n	\n	/**\n     * 导出${functionNameSimple}数据\n     *\n     * @param ${className}DTO\n     * @param page\n     * @param response\n     * @throws Exception\n     */\n    @ApiLog(\"导出${functionNameSimple}数据\")\n    @GetMapping(\"export\")\n    public void exportFile(${ClassName}DTO ${className}DTO, Page <<#if isNeedJoinTable>${ClassName}DTO<#else>${ClassName}</#if>> page, ExcelOptions options, HttpServletResponse response) throws Exception {\n        String fileName = options.getFilename ( );\n		QueryWrapper queryWrapper = QueryWrapperGenerator.buildQueryCondition (${className}DTO, ${ClassName}DTO.class);\n				<#if isNeedJoinTable>\n        if ( ExportMode.current.equals ( options.getMode ( ) ) ) { // 导出当前页数据\n            \n        } else if ( ExportMode.selected.equals ( options.getMode ( ) ) ) { // 导出选中数据\n            queryWrapper.in ( \"a.id\", options.getSelectIds () );\n        } else { // 导出全部数据\n            page.setSize ( -1 );\n            page.setCurrent ( 0 );\n        }\n        List<${ClassName}DTO> result = ${className}Service.findPage ( page, queryWrapper ).getRecords ( );\n					<#else>\n        if ( ExportMode.current.equals ( options.getMode ( ) ) ) { // 导出当前页数据\n            \n        } else if ( ExportMode.selected.equals ( options.getMode ( ) ) ) { // 导出选中数据\n            queryWrapper.in ( \"id\", options.getSelectIds () );\n        } else { // 导出全部数据\n            page.setSize ( -1 );\n            page.setCurrent ( 0 );\n        }\n        List < ${ClassName}> result = ${className}Service.page ( page, queryWrapper ).getRecords ( );\n					</#if>\n        EasyExcelUtils.newInstance ( ${className}Service, ${className}Wrapper ).exportExcel ( result,  options.getSheetName ( ), ${ClassName}DTO.class, fileName,options.getExportFields (), response );\n    }\n\n    /**\n     * 导入${functionNameSimple}数据\n     *\n     * @return\n     */\n    @PostMapping(\"import\")\n    public ResponseEntity importFile(MultipartFile file) throws IOException {\n        String result = EasyExcelUtils.newInstance ( ${className}Service, ${className}Wrapper ).importExcel ( file, ${ClassName}DTO.class );\n        return ResponseEntity.ok ( result );\n    }\n\n    /**\n     * 下载导入${functionNameSimple}数据模板\n     *\n     * @param response\n     * @return\n     */\n    @GetMapping(\"import/template\")\n    public void importFileTemplate(HttpServletResponse response) throws IOException {\n        String fileName = \"${functionNameSimple}数据导入模板.xlsx\";\n        List<${ClassName}DTO> list = Lists.newArrayList();\n        EasyExcelUtils.newInstance ( ${className}Service, ${className}Wrapper ).exportExcel ( list,  \"${functionNameSimple}数据\", ${ClassName}DTO.class, fileName, null, response );\n    }\n\n}]]>\n    </content>\n</template>\n', '', 0, 10, '0', NULL, '1', '2021-10-08 11:44:42', '1', '2021-10-08 11:44:42');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1555098546619817985', 'service.java', '1555098546473017345', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>service</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/service</filePath>\n    <fileName>${ClassName}Service.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service;\n\nimport org.springframework.stereotype.Service;\nimport org.springframework.transaction.annotation.Transactional;\n<#if table.dataSource?? && table.dataSource.enName?? && table.dataSource.enName!=\"\" && table.dataSource.enName!=\"master\">\nimport com.baomidou.dynamic.datasource.annotation.DS;\n</#if>\nimport com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;\n<#if isNeedJoinTable>\nimport com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;\nimport com.baomidou.mybatisplus.core.metadata.IPage;\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO;\n</#if>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${ClassName};\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper.${ClassName}Mapper;\n\n/**\n * ${functionName}Service\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n<#if table.dataSource?? && table.dataSource.enName?? && table.dataSource.enName!=\"\" && table.dataSource.enName!=\"master\">\n@DS(\"${table.dataSource.enName}\")\n</#if>\n@Service\n@Transactional\npublic class ${ClassName}Service extends ServiceImpl<${ClassName}Mapper, ${ClassName}> {\n<#if isNeedJoinTable>\n\n	/**\n	 * 根据id查询\n	 * @param id\n	 * @return\n	 */\n	public ${ClassName}DTO findById(String id) {\n		return baseMapper.findById ( id );\n	}\n\n	/**\n	 * 自定义分页检索\n	 * @param page\n	 * @param queryWrapper\n	 * @return\n	 */\n	public IPage <${ClassName}DTO> findPage(Page <${ClassName}DTO> page, QueryWrapper queryWrapper) {\n		queryWrapper.eq (\"a.del_flag\", 0 ); // 排除已经删除\n		return  baseMapper.findList (page, queryWrapper);\n	}\n</#if>\n\n}]]>\n    </content>\n</template>\n', '', 0, 20, '0', NULL, '1', '2021-10-08 11:46:42', '1', '2021-10-08 11:46:42');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1555098546649178113', 'mapper.java', '1555098546473017345', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>mapper</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/mapper</filePath>\n    <fileName>${ClassName}Mapper.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper;\n\nimport com.baomidou.mybatisplus.core.mapper.BaseMapper;\n<#if isNeedJoinTable>\nimport org.apache.ibatis.annotations.Param;\nimport com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;\nimport com.baomidou.mybatisplus.core.metadata.IPage;\nimport com.baomidou.mybatisplus.core.toolkit.Constants;\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO;\n</#if>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${ClassName};\n\n/**\n * ${functionName}MAPPER接口\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\npublic interface ${ClassName}Mapper extends BaseMapper<${ClassName}> {\n\n<#if isNeedJoinTable>\n    /**\n     * 根据id获取${functionName}\n     * @param id\n     * @return\n     */\n    ${ClassName}DTO findById(String id);\n\n    /**\n     * 获取${functionName}列表\n     *\n     * @param queryWrapper\n     * @return\n     */\n    IPage <${ClassName}DTO> findList(Page <${ClassName}DTO> page, @Param(Constants.WRAPPER) QueryWrapper queryWrapper);\n</#if>\n\n}]]>\n    </content>\n</template>\n', '', 0, 30, '0', NULL, '1', '2021-10-08 11:44:42', '1', '2021-10-08 11:44:42');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1555098546695315457', 'mapper.xml', '1555098546473017345', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>mapper</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/mapper/xml</filePath>\n    <fileName>${ClassName}Mapper.xml</fileName>\n    <content><![CDATA[<?xml version=\"1.0\" encoding=\"UTF-8\" ?>\n<!DOCTYPE mapper PUBLIC \"-//mybatis.org//DTD Mapper 3.0//EN\" \"http://mybatis.org/dtd/mybatis-3-mapper.dtd\">\n<mapper namespace=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper.${ClassName}Mapper\">\n\n<#if isNeedJoinTable>\n    <#-- 输出字段列 -->\n	<sql id=\"${className}Columns\">\n		<#assign columnField>\n			<#list table.columnList as c>\n		a.${c.name} AS \"${c.javaFieldId}\",\n			</#list>\n			<#list table.columnList as c>\n			<#-- 父表关联字段 -->\n				<#if table.parentExists && table.parentTableFk == c.name>\n					<#list c.javaFieldAttrs as a>\n		b.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"userselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"officeselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"areaselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"treeselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"gridselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n				</#list>\n				</#if>\n\n			</#list>\n		</#assign>\n${columnField?substring(0, columnField?last_index_of(\",\"))}\n	</sql>\n\n	<#-- 输出字段关联表 -->\n	<sql id=\"${className}Joins\">\n\n		<#-- 关联系统表 -->\n		<#list table.columnList as c>\n		<#-- 关联父表 -->\n		<#if table.parentExists && table.parentTableFk == c.name>\n			LEFT JOIN ${table.parent.name} b ON b.id = a.${table.parentTableFk}\n			<#elseif c.isNotBasicUserField>\n		LEFT JOIN sys_user ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"officeselect\">\n		LEFT JOIN sys_office ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"areaselect\">\n		LEFT JOIN sys_area ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"treeselect\" && c.javaType != \"This\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"gridselect\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			</#if>\n		</#list>\n	</sql>\n\n\n\n	<select id=\"findById\" resultType=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO\">\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		WHERE a.id = ${\"#\"}{id} and a.del_flag = 0\n	</select>\n\n	<select id=\"findList\" resultType=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO\">\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n	    ${\"$\"}{ew.customSqlSegment}\n	</select>\n</#if>\n\n</mapper>]]>\n    </content>\n</template>\n', '', 0, 40, '0', NULL, '1', '2021-10-08 11:44:56', '1', '2021-10-08 11:44:56');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1555098546741452801', 'dto.java', '1555098546473017345', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>dto</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/service/dto</filePath>\n    <fileName>${ClassName}DTO.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto;\n\n<#list table.importList as i>\nimport ${i};\n</#list>\n<#if (table.queryList?size > 0) >\nimport cn.weegoo.core.query.Query;\nimport cn.weegoo.core.query.QueryType;\n</#if>\nimport com.alibaba.excel.annotation.ExcelProperty;\n<#list table.importExcelList as i>\nimport ${i};\n</#list>\nimport cn.weegoo.core.service.dto.BaseDTO;\nimport lombok.Data;\nimport lombok.EqualsAndHashCode;\n/**\n * ${functionName}DTO\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n@Data\n@EqualsAndHashCode(callSuper = false)\npublic class ${ClassName}DTO extends BaseDTO {\n\n	private static final long serialVersionUID = 1L;\n\n	<#-- 生成字段属性 -->\n	<#list table.columnList as c>\n		<#-- 如果不是基类属性 -->\n		<#if c.isNotBaseField>\n	        <#if c.comments??>\n	        \n	/**\n     * ${c.comments}\n     */\n            </#if>\n			<#list c.simpleAnnotationList as a>\n	@${a}\n			</#list>\n			<#-- 父类对象 -->\n			<#if table.parentExists && table.parentTableFk == c.name>\n                    <#if (c.isQuery?? && c.isQuery == \"1\" && c.queryType ?? && c.queryType != \"\" )>\n                        <#if isNeedJoinTable>\n	@Query(tableColumn = \"a.${c.name}\", javaField = \"${c.javaFieldId}\", type = QueryType.${c.queryType})\n                        <#else>\n    @Query(type = QueryType.${c.queryType})\n                        </#if>\n					</#if>\n	@ExcelProperty(value = \"${c.comments}\", converter = ExcelFieldDTOConverter.class)\n	@ExcelFieldProperty(value = \"${c.javaFieldName}\", service =  \"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.${table.parent.className?cap_first}Service\", wrapper= \"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.mapstruct.${table.parent.className?cap_first}Wrapper\")\n	private ${table.parent.className?cap_first}DTO ${c.simpleJavaField};\n			<#-- 其它字段 -->\n			<#else>\n                    <#if (c.isQuery?? && c.isQuery == \"1\" && c.queryType ?? && c.queryType != \"\" )>\n                        <#if isNeedJoinTable>\n    @Query(tableColumn = \"a.${c.name}\", javaField = \"${c.javaFieldId}\", type = QueryType.${c.queryType})\n                        <#else>\n    @Query(type = QueryType.${c.queryType})\n                        </#if>\n                    </#if>\n    <#if c.showType?? && c.showType == \"userselect\">\n    @ExcelProperty(value = \"${c.comments}\", converter = ExcelUserDTOConverter.class) \n	<#elseif c.showType?? && c.showType == \"officeselect\">\n    @ExcelProperty(value = \"${c.comments}\", converter = ExcelOfficeDTOConverter.class) \n    <#elseif c.showType?? && c.showType == \"areaselect\">\n    @ExcelProperty(value = \"${c.comments}\", converter = ExcelAreaDTOConverter.class) \n    <#elseif c.showType?? && (c.showType == \"select\" || c.showType == \"radiobox\" || c.showType == \"checkbox\")>\n	@ExcelProperty(value = \"${c.comments}\", converter = ExcelDictDTOConverter.class)\n	@ExcelDictProperty(\"${c.dictType}\")\n    <#elseif c.showType?? && (c.showType == \"treeselect\"|| c.showType == \"gridselect\")>\n	@ExcelProperty(value = \"${c.comments}\", converter = ExcelFieldDTOConverter.class)\n	@ExcelFieldProperty(value = \"${c.javaFieldName}\", service =  \"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.${c.simpleJavaType?replace(\"DTO\",\"\")}Service\", wrapper= \"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.mapstruct.${c.simpleJavaType?replace(\"DTO\",\"\")}Wrapper\")\n    <#else>                    \n	@ExcelProperty(\"${c.comments}\") \n	</#if>                \n	private ${c.simpleJavaType} ${c.simpleJavaField};\n			</#if>\n		</#if>\n	</#list>\n    <#-- 子表列表字段 -->\n    <#list table.childList as c>\n    /**\n     *子表列表\n     */\n	private List<${c.className?cap_first}DTO> ${c.className?uncap_first}DTOList = Lists.newArrayList();\n    </#list>\n\n}]]>\n    </content>\n</template>\n', '', 0, 50, '0', NULL, '1', '2021-10-08 11:15:54', '1', '2021-10-08 11:15:54');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1555098546766618625', 'entity.java', '1555098546473017345', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>entity</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/domain</filePath>\n    <fileName>${ClassName}.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain;\n\n<#list table.importEntityList as i>\nimport ${i};\n</#list>\n<#if table.idType ==\"2\">\nimport com.baomidou.mybatisplus.annotation.IdType;\nimport com.baomidou.mybatisplus.annotation.TableId;\n</#if>\nimport com.baomidou.mybatisplus.annotation.TableName;\nimport cn.weegoo.core.domain.BaseEntity;\nimport lombok.Data;\nimport lombok.EqualsAndHashCode;\n/**\n * ${functionName}Entity\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n@Data\n@EqualsAndHashCode(callSuper = false)\n@TableName(\"${table.name}\")\npublic class ${ClassName} extends BaseEntity {\n\n	private static final long serialVersionUID = 1L;\n	<#if table.idType ==\"2\">\n\n	@TableId(type = IdType.AUTO)\n	private Integer id;\n	</#if>\n\n	<#-- 生成字段属性 -->\n	<#list table.columnList as c>\n		<#-- 如果不是基类属性 -->\n		<#if c.isNotBaseField>\n			<#if c.comments??>\n	/**\n     * ${c.comments}\n     */\n            </#if>\n            <#if c.isBasicJavaType>\n<#if c.simpleJavaField != c.mapperJavaField>\n    @TableField(\"${c.name}\")\n</#if>\n	private ${c.simpleJavaType} ${c.simpleJavaField};\n	        <#else>\n<#if c.simpleJavaFieldId != c.mapperJavaField>\n    @TableField(\"${c.name}\")\n</#if>\n	private String ${c.simpleJavaFieldId};\n	         </#if>\n		</#if>\n	</#list>\n\n}]]>\n    </content>\n</template>\n', '', 0, 60, '0', NULL, '1', '2021-10-08 11:44:42', '1', '2021-10-08 11:44:42');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1555098546808561665', 'wrapper.java', '1555098546473017345', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>service</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/service/mapstruct</filePath>\n    <fileName>${ClassName}Wrapper.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.mapstruct;\n\n\nimport cn.weegoo.core.mapstruct.EntityWrapper;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${ClassName};\nimport org.mapstruct.Mapper;\n<#if isNeedWrapper>\nimport org.mapstruct.Mapping;\nimport org.mapstruct.Mappings;\n</#if>\nimport org.mapstruct.ReportingPolicy;\nimport org.mapstruct.factory.Mappers;\n\n/**\n *  ${ClassName}Wrapper\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n@Mapper(componentModel = \"spring\", unmappedTargetPolicy = ReportingPolicy.IGNORE, uses = {} )\npublic interface ${ClassName}Wrapper extends EntityWrapper<${ClassName}DTO, ${ClassName}> {\n\n    ${ClassName}Wrapper INSTANCE = Mappers.getMapper(${ClassName}Wrapper.class);\n    <#if isNeedWrapper>\n     @Mappings({\n     	<#list table.columnList as c>\n		<#-- 如果不是基类属性 -->\n		    <#if c.isNotBaseField>\n     		    <#if table.parentExists && table.parentTableFk == c.name>\n            @Mapping(source = \"${c.javaFieldId}\", target = \"${c.simpleJavaFieldId}\"),\n			<#-- 其它字段 -->\n			    <#elseif c.isNotBasicJavaType>\n            @Mapping(source = \"${c.javaFieldId}\", target = \"${c.simpleJavaFieldId}\"),\n	             </#if>\n	        </#if>\n	    </#list>\n            @Mapping(source = \"createBy.id\", target = \"createBy\"),\n            @Mapping (source = \"updateBy.id\", target = \"updateBy\")})\n    ${ClassName} toEntity(${ClassName}DTO dto);\n\n\n    @Mappings({\n     	<#list table.columnList as c>\n		<#-- 如果不是基类属性 -->\n		    <#if c.isNotBaseField>\n     		    <#if table.parentExists && table.parentTableFk == c.name>\n            @Mapping(source = \"${c.simpleJavaFieldId}\", target = \"${c.javaFieldId}\"),\n			<#-- 其它字段 -->\n			    <#elseif c.isNotBasicJavaType>\n            @Mapping(source = \"${c.simpleJavaFieldId}\", target = \"${c.javaFieldId}\"),\n	             </#if>\n	        </#if>\n	    </#list>\n            @Mapping (source = \"createBy\", target = \"createBy.id\"),\n            @Mapping (source = \"updateBy\", target = \"updateBy.id\")})\n    ${ClassName}DTO toDTO(${ClassName} entity);\n    </#if>\n}\n]]>\n    </content>\n</template>\n', '', 0, 70, '0', NULL, '1', '2021-10-08 09:20:58', '1', '2021-10-08 09:20:58');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1555098546846310401', 'viewList.vue', '1555098546473017345', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>viewList</name>\n    <filePath>pages/${moduleName}/${subModuleName}</filePath>\n    <fileName>${ClassName}List.vue</fileName>\n    <content><![CDATA[\n<template>\n	<view>\n		<cu-custom bgColor=\"bg-blue\" :isBack=\"true\">\n			<block slot=\"backText\">返回</block>\n			<block slot=\"content\"> ${functionNameSimple}</block>\n		</cu-custom>\n		<view  :style=\"[{top:CustomBar + \'px\'}]\">\n		<view class=\"cu-bar search\">\n			<view class=\"search-form bg-white round\">\n				<text class=\"cuIcon-search\"></text>\n				<input type=\"text\"  placeholder=\"搜索\" v-model=\"curWord\" confirm-type=\"search\" @input=\"inputWord\"></input>\n			</view>\n		</view>\n		<mescroll-body ref=\"mescrollRef\" @init=\"mescrollInit\" @down=\"downCallback\" :up=\"upOption\" @up=\"upCallback\">\n				<view class=\"cu-item\" :class=\"modalName==\'move-box-\'+ index?\'move-cur\':\'\'\" v-for=\"(row, index) in dataList\" :key=\"index\"\n				 @touchstart=\"ListTouchStart\" @touchmove=\"ListTouchMove\"  @touchend=\"ListTouchEnd\" :data-target=\"\'move-box-\' + index\">\n			 		<view class=\"cu-bar bg-white solid-bottom\">\n            			<view class=\"action\">\n            				<text class=\"cuIcon-titles text-orange \"></text># {{index + 1}}\n            			</view>\n            			<view class=\"action\">\n            					<button class=\"uni-button\" size=\"mini\"  @click=\"edit(row.id)\" type=\"primary\">修改</button>\n            					<button class=\"uni-button\" size=\"mini\"  @click=\"del(row.id)\" type=\"warn\">删除</button>\n            			</view>\n            		</view>\n	                <view class=\"cu-card dynamic no-card\">\n			        <view class=\"cu-item shadow\">\n				    <view class=\"cu-list menu-avatar comment solids-top\">\n					<view class=\"cu-item\">\n					<view class=\"content\">\n						<#list table.columnList as c>\n						<#if c.isList?? && c.isList == \"1\">\n						<view class=\" margin-top-sm  text-sm\">\n								<view class=\"flex\">\n								<view>	${c.comments}:</view>\n								<view class=\"flex-sub\">\n						  <#if c.showType == \"userselect\" || c.showType == \"officeselect\" || c.showType == \"areaselect\" || c.showType == \"treeselect\"|| c.showType == \"gridselect\">\n					    	     {{ row.${c.simpleJavaField} && row.${c.javaFieldName} }}\n						  <#elseif c.showType == \"select\">\n					            {{ $dictUtils.getDictLabel(\"${c.dictType}\", row.${c.javaFieldId}, \'-\') }}\n						  <#elseif c.showType == \"checkbox\">\n						        {{row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}\n						  <#elseif c.showType == \"radiobox\">\n						        {{ $dictUtils.getDictLabel(\"${c.dictType}\", row.${c.javaFieldId}, \'-\') }}\n						  <#elseif c.showType == \"fileselect\" || c.showType == \"imageselect\">\n                                <image  :src=\"row.${c.javaFieldId}\"></image>\n						  <#else>\n						        {{row.${c.javaFieldId}}}\n						  </#if>\n						  	    </view>\n						  	    </view>\n						</view>\n						   </#if>\n						  </#list>	\n				    </view>\n				  	</view>\n				  	</view>\n				  	</view>\n				  	</view>\n				</view>\n		</mescroll-body>\n			<uni-fab\n				:pattern=\" {\n							color: \'#7A7E83\',\n							backgroundColor: \'#fff\',\n							selectedColor: \'#007AFF\',\n							buttonColor: \'#007AFF\'\n						}\"\n				horizontal=\"right\"\n				vertical=\"bottom\"\n				@fabClick=\"add\"\n			></uni-fab>\n		</view>\n		</view>\n</template>\n\n<script>\n  import uniFab from \'@/components/uni-fab/uni-fab.vue\';\n  import ${className}Service from \'@/api/${moduleName}<#if subModuleName != \"\">/${subModuleName}</#if>/${className}Service\'\n  import MescrollMixin from \"@/components/mescroll-uni/mescroll-mixins.js\";\n  import MescrollMoreItemMixin from \"@/components/mescroll-uni/mixins/mescroll-more-item.js\";\n  export default {\n  	mixins: [MescrollMixin,MescrollMoreItemMixin], // 使用mixin (在main.js注册全局组件)\n	onShow(option) {\n		this.$auth.checkLogin()\n	},\n	components:{\n		uniFab\n	},\n    data () {\n      return {\n        searchForm: {},\n        curWord:\"\" ,//当前搜索关键词\n        dataList: [], // 数据列表\n        modalName: null,\n        tablePage: {\n          total: 0,\n          currentPage: 1,\n          pageSize: 10,\n          orders: []\n        },\n        loading: false\n      }\n    },\n    methods: {\n	  // 新增\n	  add (){\n	  	uni.navigateTo({\n	  	  url: \'/pages/${moduleName}/${subModuleName}/${ClassName}Form\'\n	  	})\n	  },\n	  // 修改\n	  edit (id) {\n	  	uni.navigateTo({\n	  	  url: \'/pages/${moduleName}/${subModuleName}/${ClassName}Form?id=\'+id\n	  	})\n	  },\n      // 删除\n	  del (id) {\n	  	uni.showModal({\n	  		title: \'提示\',\n	  		content: \'您确认要删除数据吗\',\n	  		showCancel: true,\n	  		success: (res) => {\n	  			if (res.confirm) {\n	  				${className}Service.delete(id).then(({data})=>{\n	  					uni.showToast({\n	  						title: data,\n	  						icon:\"success\"\n	  					})\n	  					this.doSearch()\n	  				})\n	  			}\n	  		}\n	  	});\n	  },\n	  /*获取数据列表 */\n	  upCallback(page) {\n	    this.loading = true\n	    ${className}Service.list({\n	  	  current: page.num,\n	  	  size: page.size,\n	  	  ...this.searchForm\n	  	}).then(({data}) => {\n	    let curPageData = data.records\n	    this.mescroll.endBySize(curPageData.length,  data.total);\n	    //如果是第一页需手动制空列表\n	    if(page.num == 1) \n	  	  this.dataList = [];\n	  	  //追加新数据\n	  	  this.dataList=this.dataList.concat(curPageData);\n	  	}).catch(e=>{\n			//联网失败, 结束加载\n	  	  this.mescroll.endErr();\n		})\n	  	\n	  },\n		// 输入监听\n	  inputWord(e){\n	    // this.curWord = e.detail.value // 已使用v-model,无需再次赋值\n	    // 节流,避免输入过快多次请求\n	    this.searchTimer && clearTimeout(this.searchTimer)\n	    this.searchTimer = setTimeout(()=>{\n	  	  this.doSearch(this.curWord)\n	    },300)\n	  },\n	  // 搜索\n	  doSearch(word){\n	    this.curWord = word\n	    this.dataList = []; // 先清空列表,显示加载进度\n	    this.mescroll.resetUpScroll();\n	  },\n	  // ListTouch触摸开始\n	  ListTouchStart(e) {\n	    this.listTouchStart = e.touches[0].pageX\n	  },\n    	\n	  // ListTouch计算方向\n	  ListTouchMove(e) {\n	    this.listTouchDirection = e.touches[0].pageX - this.listTouchStart > -60? \'right\' : \'left\'\n	  },\n    	\n	  // ListTouch计算滚动\n	  ListTouchEnd(e) {\n	    if (this.listTouchDirection == \'left\') {\n	  	  this.modalName = e.currentTarget.dataset.target\n	    } else {\n	  	  this.modalName = null\n	    }\n	    this.listTouchDirection = null\n	  }\n    }\n  }\n</script>\n\n<style>\n	.ellipsis-description {\n		  font-size: 12px;\n		  line-height: $line-height-base;\n		  display: -webkit-box;/*作为弹性伸缩盒子模型显示*/\n		  -webkit-line-clamp: 1; /*显示的行数；如果要设置2行加...则设置为2*/\n		  overflow: hidden; /*超出的文本隐藏*/\n		  text-overflow: ellipsis; /* 溢出用省略号*/\n		   -webkit-box-orient: vertical;/*伸缩盒子的子元素排列：从上到下*/\n	}\n	\n	.cu-bar .search-form{\n		background-color: white;\n	}\n</style>\n]]>\n    </content>\n</template>\n', '', 0, 80, '0', NULL, '1', '2021-10-08 11:46:42', '1', '2021-10-08 11:46:42');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1555098546884059137', 'viewForm.vue', '1555098546473017345', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>viewForm</name>\n    <filePath>pages/${moduleName}/${subModuleName}</filePath>\n    <fileName>${ClassName}Form.vue</fileName>\n    <content><![CDATA[\n<template>\n	<view>\n		<cu-custom bgColor=\"bg-blue\" backUrl=\"/pages/${moduleName}/${subModuleName}/${ClassName}List\" :isBack=\"true\">\n			<block slot=\"backText\">返回</block>\n			<block slot=\"content\">{{title}}</block>\n		</cu-custom>\n		<form @submit=\"formSubmit\" class=\"cu-list menu\">\n<#assign firstListField = true>\n<#list table.columnList as c>\n      <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField)>\n       <view class=\"cu-form-group <#if firstListField>margin-top</#if>\">\n		   <view class=\"title\">\n			 <#if c.isNull != \"1\"><text class=\"red-color \">* </text> </#if>${c.comments}  \n		   </view>\n    <#if c.showType == \"input\">\n		   <input placeholder=\'请填写${c.comments}\' <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> v-model=\"inputForm.${c.javaFieldId}\"></input>\n    <#elseif c.showType == \"inputnumber\">\n		   <input placeholder=\'请填写${c.comments}\'  type=\"number\" <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> v-model=\"inputForm.${c.javaFieldId}\"></input>\n    <#elseif c.showType == \"textarea\" || c.showType == \"wangeditor\" || c.showType == \"quilleditor\">\n           <textarea placeholder=\'请填写${c.comments}\' <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> v-model=\"inputForm.${c.javaFieldId}\"></textarea>\n    <#elseif c.showType == \"select\">\n		  <jp-picker v-model=\"inputForm.${c.javaFieldId}\" rangeKey=\"label\" rangeValue=\"value\" :range=\"$dictUtils.getDictList(\'${c.dictType}\')\">\n		  	<view class=\"picker\">\n		  		{{$dictUtils.getDictLabel(\'${c.dictType}\', inputForm.${c.javaFieldId} ,\'请选择\')}}\n		  	</view>\n		  </jp-picker>\n    <#elseif c.showType == \"checkbox\">\n		  <jp-checkbox-group2 v-model=\"inputForm.${c.javaFieldId}\">\n		  	<label v-for=\"(option, index) in $dictUtils.getDictList(\'${c.dictType}\')\">\n		  		<view>\n		  			{{option.label || option.value}}\n		  			<checkbox  class=\"blue\" :value=\"option.value\"  :class=\"(\',\'+inputForm.${c.javaFieldId}+\',\').indexOf(\',\'+option.value+\',\')>=0?\'checked\':\'\'\" :checked=\"(\',\'+inputForm.${c.javaFieldId}+\',\').indexOf(\',\'+option.value+\',\')>=0\" ></checkbox>\n		  		</view>\n		  	</label>\n		  </jp-checkbox-group2>	\n    <#elseif c.showType == \"radiobox\">\n		 <jp-radio-group  v-model=\"inputForm.${c.javaFieldId}\">\n			<label v-for=\"(option, index) in $dictUtils.getDictList(\'${c.dictType}\')\">\n				<view>\n					{{option.label}}\n					<radio class=\'blue radio\'  :value=\"option.value\"  :class=\"inputForm.${c.javaFieldId}==option.value?\'checked\':\'\'\" :checked=\"inputForm.${c.javaFieldId}==option.value?true:false\" ></radio>\n				</view>\n			</label>\n		 </jp-radio-group>\n    <#elseif c.showType == \"dateselect\">\n		<jp-datetime-picker v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"选择日期时间\"  mode=\"date\"></jp-datetime-picker>\n    <#elseif c.showType == \"userselect\">\n		<user-select v-model=\"inputForm.${c.javaFieldId}\"></user-select>\n    <#elseif c.showType == \"officeselect\">\n		<jp-office-select v-model=\"inputForm.${c.javaFieldId}\"></jp-office-select>\n    <#elseif c.showType == \"areaselect\">\n		<jp-area-select v-model=\"inputForm.${c.javaFieldId}\"></jp-area-select>\n    <#elseif c.showType == \"cityselect\">\n		<jp-area-select v-model=\"inputForm.${c.javaFieldId}\"></jp-area-select>\n    <#elseif c.showType == \"fileselect\" ||  c.showType == \"imageselect\">\n		<jp-image-upload2 v-model=\"inputForm.${c.javaFieldId}\"></jp-image-upload2>\n    <#elseif c.showType == \"treeselect\">\n\n    <#elseif c.showType == \"gridselect\">\n	\n    </#if>\n		</view>\n		<#assign firstListField = false>\n      </#if>\n	  </#list>\n	  \n			<view class=\"padding-xl\">\n				<button form-type=\"submit\" class=\"cu-btn block bg-blue margin-tb-sm lg\" >提交</button>\n			</view>\n		</form>\n	</view>\n</template>\n\n<script>\n  var  graceChecker = require(\"@/common/graceChecker.js\");\n  import ${className}Service from \'@/api/${moduleName}<#if subModuleName != \"\">/${subModuleName}</#if>/${className}Service\'\n  export default {\n    onShow() {\n      this.$auth.checkLogin()\n    },\n    data () {\n      return {\n        title: \'新建${functionNameSimple}\',\n        inputForm: {\n          id: \'\'<#if (table.formList?size>0)>,</#if>\n        <#list table.formList as c>\n          <#if c.javaFieldId?contains(\".\")>\n          ${c.javaFieldId?split(\".\")[0]}: {\n            ${c.javaFieldId?split(\".\")[1]}: \'\'\n          }<#if c_has_next>,</#if>\n          <#else>\n          ${c.javaFieldId}: \'\'<#if c_has_next>,</#if>\n          </#if>\n    </#list>\n        }\n      }\n    },\n	async onLoad(query) {\n		if(query&&query.id){\n			this.titile = \"修改${functionNameSimple}\";\n			let {data} = await ${className}Service.queryById(query.id)\n			this.inputForm = this.recover(this.inputForm, data)\n		}\n	},\n    methods: {\n      formSubmit: function(e) {\n      	//定义表单规则\n      	var rule = [\n			<#list table.validationList as c>\n			      <#if c.isNull != \"1\">\n			      {name:\"${c.javaFieldId}\", checkType : \"notnull\", checkRule:\"\",  errorMsg:\"${c.comments}不能为空\"}<#if c_has_next>,</#if>\n			     </#if>\n				<#if c.validateType?? && c.validateType != \"\">\n				<#if c.validateType != \"string\">\n			      {name:\"${c.javaFieldId}\", checkType : \"${c.validateType}\", checkRule:\"\",  errorMsg:\"\"}<#if c_has_next>,</#if>\n				 </#if>\n				 </#if>\n			</#list>	 \n      	];\n      	//进行表单检查\n      	var formData = this.inputForm;\n      	var checkRes = graceChecker.check(formData, rule);\n      	if(checkRes){\n      		uni.showLoading()\n      		${className}Service.save(this.inputForm).then(({data}) => {\n      			uni.showToast({title:data, icon:\"success\"});\n      			uni.navigateTo({\n      			  url: \'/pages/${moduleName}/${subModuleName}/${ClassName}List\'\n      			})\n      		}).catch((e)=>{\n      			\n      		})\n      		\n      	}else{\n      		uni.showToast({ title: graceChecker.error, icon: \"none\" });\n      	}\n      }\n    }\n  }\n</script>\n\n  ]]>\n    </content>\n</template>\n', '', 0, 90, '0', NULL, '1', '2021-10-08 11:46:42', '1', '2021-10-08 11:46:42');
INSERT INTO `plugin_gencode_template_obj` VALUES ('1555098546921807873', 'apiService.vue', '1555098546473017345', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>viewForm</name>\n    <filePath>api/${moduleName}/${subModuleName}</filePath>\n    <fileName>${className}Service.js</fileName>\n    <content><![CDATA[\nimport request from \"../../../common/request\"\n\nexport default {\n  save: function (inputForm) {\n    return request({\n      url: \'/${urlPrefix}/save\',\n      method: \'post\',\n      data: inputForm\n    })\n  },\n\n  delete: function (ids) {\n    return request({\n      url: \'/${urlPrefix}/delete\',\n      method: \'delete\',\n      params: {ids: ids}\n    })\n  },\n\n  queryById: function (id) {\n    return request({\n      url: \'/${urlPrefix}/queryById\',\n      method: \'get\',\n      params: {id: id}\n    })\n  },\n\n  list: function (params) {\n    return request({\n      url: \'/${urlPrefix}/list\',\n      method: \'get\',\n      params: params\n    })\n  },\n\n  exportTemplate: function () {\n    return request({\n      url: \'/${urlPrefix}/import/template\',\n      method: \'get\',\n      responseType: \'blob\'\n    })\n  },\n\n  exportExcel: function (params) {\n    return request({\n      url: \'/${urlPrefix}/export\',\n      method: \'get\',\n      params: params,\n      responseType: \'blob\'\n    })\n  },\n\n  importExcel: function (data) {\n    return request({\n      url: \'/${urlPrefix}/import\',\n      method: \'post\',\n      data: data\n    })\n  }\n}]]>\n    </content>\n</template>\n', '', 0, 100, '0', NULL, '1', '2021-10-08 11:16:10', '1', '2021-10-08 11:16:10');
INSERT INTO `plugin_gencode_template_obj` VALUES ('26fb3141eaa641abbe07b389b11566b0', 'service.java', '6b423144a2f24a0897f40c9a1bdb0c42', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>service</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/service</filePath>\n    <fileName>${ClassName}Service.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service;\n\nimport org.springframework.stereotype.Service;\nimport org.springframework.transaction.annotation.Transactional;\n<#if table.dataSource?? && table.dataSource.enName?? && table.dataSource.enName!=\"\" && table.dataSource.enName!=\"master\">\nimport com.baomidou.dynamic.datasource.annotation.DS;\n</#if>\nimport com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;\n<#if isNeedJoinTable>\nimport com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;\nimport com.baomidou.mybatisplus.core.metadata.IPage;\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO;\n</#if>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${ClassName};\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper.${ClassName}Mapper;\n\n/**\n * ${functionName}Service\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n<#if table.dataSource?? && table.dataSource.enName?? && table.dataSource.enName!=\"\" && table.dataSource.enName!=\"master\">\n@DS(\"${table.dataSource.enName}\")\n</#if>\n@Service\n@Transactional\npublic class ${ClassName}Service extends ServiceImpl<${ClassName}Mapper, ${ClassName}> {\n<#if isNeedJoinTable>\n\n	/**\n	 * 根据id查询\n	 * @param id\n	 * @return\n	 */\n	public ${ClassName}DTO findById(String id) {\n		return baseMapper.findById ( id );\n	}\n\n	/**\n	 * 自定义分页检索\n	 * @param page\n	 * @param queryWrapper\n	 * @return\n	 */\n	public IPage <${ClassName}DTO> findPage(Page <${ClassName}DTO> page, QueryWrapper queryWrapper) {\n		queryWrapper.eq (\"a.del_flag\", 0 ); // 排除已经删除\n		return  baseMapper.findList (page, queryWrapper);\n	}\n</#if>\n\n}]]>\n    </content>\n</template>\n', '', 0, 20, '1', NULL, NULL, NULL, '1', '2021-10-16 10:28:20');
INSERT INTO `plugin_gencode_template_obj` VALUES ('277692f915a945648f3ebd6cb54d0d0f', 'viewList.vue', '428393bb2287402aa3ebe7733c71d766', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>viewList</name>\n    <filePath>src/views/modules/${moduleName}/${subModuleName}</filePath>\n    <fileName>${ClassName}List.vue</fileName>\n    <content><![CDATA[\n<template>\n    <div class=\"page\">\n      <el-form size=\"small\" :inline=\"true\" class=\"query-form\" ref=\"searchForm\" :model=\"searchForm\" @keyup.enter.native=\"refreshList()\" @submit.native.prevent>\n            <!-- 搜索框-->\n<#list table.columnList as c>\n            <#if c.isQuery?? && c.isQuery == \"1\">\n         <el-form-item prop=\"${c.javaFieldId}\" label=\"${c.comments}\">\n      <#if c.showType == \"input\" || c.showType == \"textarea\">\n                <el-input size=\"small\" v-model=\"searchForm.${c.javaFieldId}\" placeholder=\"${c.comments}\" clearable></el-input>\n      <#elseif c.showType == \"select\">\n                  <el-select v-model=\"searchForm.${c.javaFieldId}\" placeholder=\"请选择${c.comments}\" size=\"small\" style=\"width: 100%;\">\n                    <el-option\n                      v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\"\n                      :key=\"item.value\"\n                      :label=\"item.label\"\n                      :value=\"item.value\">\n                    </el-option>\n                  </el-select>\n        <#elseif c.showType == \"checkbox\">\n                <el-checkbox-group\n                        v-model=\"${c.javaFieldId}List\">\n                       <el-checkbox v-for=\"${c.javaFieldId} in $dictUtils.getDictList(\'${c.dictType}\')\" :label=\"${c.javaFieldId}.value\" :key=\"${c.javaFieldId}.value\">{{${c.javaFieldId}.label}}</el-checkbox>\n                    </el-checkbox-group>\n        <#elseif c.showType == \"radiobox\">\n                  <el-radio-group v-model=\"searchForm.${c.javaFieldId}\">\n                    <el-radio v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\" :label=\"item.value\" :key=\"item.value\">{{item.label}}</el-radio>\n                  </el-radio-group>\n            <#elseif c.showType == \"dateselect\" &&(c.queryType == \"BETWEEN\" || c.queryType == \"NOTBETWEEN\")>\n               <el-date-picker\n                    v-model=\"searchForm.${c.javaFieldId}\"\n                    type=\"daterange\"\n                    size=\"small\"\n                    align=\"right\"\n                    value-format=\"yyyy-MM-dd hh:mm:ss\"\n                    unlink-panels\n                    range-separator=\"至\"\n                    start-placeholder=\"开始日期\"\n                    end-placeholder=\"结束日期\">\n                 </el-date-picker>\n        <#elseif c.showType == \"dateselect\">\n                <el-date-picker\n                  v-model=\"searchForm.${c.javaFieldId}\"\n                  type=\"datetime\"\n                  size=\"small\"\n                  value-format=\"yyyy-MM-dd HH:mm:ss\"\n                  placeholder=\"请选择${c.comments}\">\n                </el-date-picker>\n        <#elseif c.showType == \"userselect\">\n            <user-select :limit=\'1\' size=\"small\" placeholder=\"请选择${c.comments}\" :value=\"searchForm.${c.javaFieldId}\" @getValue=\'(value) => {searchForm.${c.javaFieldId}=value}\'></user-select>\n        <#elseif c.showType == \"officeselect\">\n            <SelectTree\n                  ref=\"${c.javaFieldId}\"\n                  :props=\"{\n                      value: \'id\',             // ID字段名\n                      label: \'name\',         // 显示名称\n                      children: \'children\'    // 子级字段名\n                    }\"\n                  placeholder=\"请选择${c.comments}\"\n                  size=\"small\"\n                  url=\"/sys/office/treeData?type=2\"\n                  :value=\"searchForm.${c.javaFieldId}\"\n                  :clearable=\"true\"\n                  :accordion=\"true\"\n                  @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"areaselect\">\n                <SelectTree\n                  ref=\"${c.javaFieldId}\"\n                  :props=\"{\n                      value: \'id\',             // ID字段名\n                      label: \'name\',         // 显示名称\n                      children: \'children\'    // 子级字段名\n                    }\"\n                  placeholder=\"请选择${c.comments}\"\n                  size=\"small\"\n                  url=\"/sys/area/treeData\"\n                  :value=\"searchForm.${c.javaFieldId}\"\n                  :clearable=\"true\"\n                  :accordion=\"true\"\n                  @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"cityselect\">\n                  <CityPicker\n                    style=\"width:100%\"\n                    size=\"small\"\n                    placeholder=\"选择${c.comments}\"\n                    :value=\"searchForm.${c.javaFieldId}\"\n                    :clearable=\"true\"\n                    :accordion=\"true\"\n                    @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"treeselect\">\n            <SelectTree\n                  ref=\"${c.javaFieldId}\"\n                  :props=\"{\n                      value: \'id\',             // ID字段名\n                      label: \'name\',         // 显示名称\n                      children: \'children\'    // 子级字段名\n                    }\"\n                  placeholder=\"请选择${c.comments}\"\n                  size=\"small\"\n                  url=\"${c.dataUrl}/treeData\"\n                  :value=\"searchForm.${c.javaFieldId}\"\n                  :clearable=\"true\"\n                  :accordion=\"true\"\n                  @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"gridselect\">\n            <GridSelect\n                    title=\"选择${c.comments}\"\n                    placeholder=\"请选择${c.comments}\"\n                    labelName = \'${c.javaFieldName?split(\".\")[1]}\'\n                    labelValue = \'${c.javaFieldId?split(\".\")[1]}\'\n                    :value = \"searchForm.${c.javaFieldId}\"\n                    :limit=\"1\"\n                    size=\"small\"\n                    @getValue=\'(value) => {searchForm.${c.javaFieldId}=value}\'\n                    :columns=\"[\n            <#list c.fieldLabels?split(\"|\") as f>\n                    {\n                      prop: \'${c.fieldKeys?split(\"|\")[f_index]}\',\n                      label: \'${c.fieldLabels?split(\"|\")[f_index]}\'\n                    }<#if f_has_next>,</#if>\n            </#list>\n                    ]\"\n                    :searchs=\"[\n           <#list c.searchLabel?split(\"|\") as f>\n                    {\n                      prop: \'${c.searchKey?split(\"|\")[f_index]}\',\n                      label: \'${c.searchLabel?split(\"|\")[f_index]}\'\n                    }<#if f_has_next>,</#if>\n            </#list>\n                    ]\"\n                    dataListUrl=\"${c.dataUrl}/list\"\n                    queryEntityUrl=\"${c.dataUrl}/queryById\">\n                  </GridSelect>\n        </#if>\n         </el-form-item>\n        </#if>\n    </#list>\n          <el-form-item>\n            <el-button type=\"primary\" @click=\"refreshList()\" size=\"small\" icon=\"el-icon-search\">查询</el-button>\n            <el-button @click=\"resetSearch()\" size=\"small\" icon=\"el-icon-refresh-right\">重置</el-button>\n          </el-form-item>\n      </el-form>\n    <div class=\"bg-white top\">\n    <vxe-toolbar :refresh=\"{query: refreshList}\" custom>\n    <!-- <vxe-toolbar :refresh=\"{query: refreshList}\" export print custom> -->\n      <template #buttons>\n            <el-button v-if=\"hasPermission(\'${permissionPrefix}:add\')\" type=\"primary\" size=\"small\" icon=\"el-icon-plus\" @click=\"add()\">新建</el-button>\n            <el-button v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" type=\"warning\" size=\"small\" icon=\"el-icon-edit-outline\" @click=\"edit()\"\n                 :disabled=\"$refs.${className}TreeTable && $refs.${className}TreeTable.getCheckboxRecords().length !== 1\" plain>修改</el-button>\n            <el-button v-if=\"hasPermission(\'${permissionPrefix}:del\')\" type=\"danger\"   size=\"small\" icon=\"el-icon-delete\" @click=\"del()\"\n                  :disabled=\"$refs.${className}TreeTable && $refs.${className}TreeTable.getCheckboxRecords().length === 0\" plain>删除</el-button>\n      </template>\n    </vxe-toolbar>\n    <div style=\"height: calc(100% - 50px);\">\n      <vxe-table\n        resizable\n        ref=\"${className}TreeTable\"\n        border=\"inner\"\n        auto-resize\n        height=\"auto\"\n        row-id=\"id\"\n        show-header-overflow\n        show-overflow\n        highlight-hover-row\n        :print-config=\"{}\"\n        :export-config=\"{}\"\n        :tree-config=\"{}\"\n        :loading=\"loading\"\n        :checkbox-config=\"{checkStrictly: true}\"\n        :data=\"dataList\">\n      <vxe-table-column type=\"checkbox\" width=\"40px\"> </vxe-table-column>\n      <#assign cks=0/>\n      <#list table.columnList as c>\n        <#if c.isList?? && c.isList == \"1\">\n        <#assign cks= cks + 1/>\n        <#if cks == 1>\n                <#if c.showType == \"userselect\" || c.showType == \"officeselect\" || c.showType == \"areaselect\" || c.showType == \"treeselect\"|| c.showType == \"gridselect\">\n      <vxe-table-column\n        field=\"${c.javaFieldName}\"\n        align=\"left\"\n        tree-node\n        title=\"${c.comments}\">\n            <template slot-scope=\"scope\">\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\">{{scope.row.${c.javaFieldName}}}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\">{{scope.row.${c.javaFieldName}}}</el-link>\n              <span v-else>{{scope.row.${c.javaFieldName}}}</span>\n            </template>\n      </vxe-table-column>\n        <#elseif c.showType == \"select\">\n    <vxe-table-column\n        field=\"${c.javaFieldId}\"\n        align=\"left\"\n        tree-node\n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\"> {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\"> {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</el-link>\n              <span v-else>{{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</span>\n        </template>\n      </vxe-table-column>\n      <#elseif c.showType == \"checkbox\">\n    <vxe-table-column\n        field=\"${c.javaFieldId}\"\n        align=\"left\"\n        tree-node\n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\">{{scope.row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\">{{scope.row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}</el-link>\n              <span v-else