             <#elseif c.showType == \"treeselect\">\n                        <SelectTree\n                          ref=\"${c.simpleJavaField}\"\n                          :props=\"{\n                              value: \'id\',             // ID字段名\n                              label: \'name\',         // 显示名称\n                              children: \'children\'    // 子级字段名\n                            }\"\n    \n                          url=\"${c.dataUrl}/treeData\"\n                          :value=\"row.${c.javaFieldId}\"\n                          :clearable=\"true\"\n                          :accordion=\"true\"\n                          @getValue=\"(value) => {row.${c.javaFieldId}=value}\"/>\n                <#elseif c.showType == \"gridselect\">\n                         <GridSelect\n                            title=\"选择${c.comments}\"\n                            labelName = \'${c.javaFieldName?split(\".\")[1]}\'\n                            labelValue = \'${c.javaFieldId?split(\".\")[1]}\'\n                            :value = \"row.${c.javaFieldId}\"\n                            :limit=\"1\"\n                            @getValue=\'(value) => {row.${c.javaFieldId}=value}\'\n                            :columns=\"[\n                    <#list c.fieldLabels?split(\"|\") as f>\n                            {\n                              prop: \'${c.fieldKeys?split(\"|\")[f_index]}\',\n                              label: \'${c.fieldLabels?split(\"|\")[f_index]}\'\n                            }<#if f_has_next>,</#if>\n                    </#list>\n                            ]\"\n                            :searchs=\"[\n                   <#list c.searchLabel?split(\"|\") as f>\n                            {\n                              prop: \'${c.searchKey?split(\"|\")[f_index]}\',\n                              label: \'${c.searchLabel?split(\"|\")[f_index]}\'\n                            }<#if f_has_next>,</#if>\n                    </#list>\n                            ]\"\n                            dataListUrl=\"${c.dataUrl}/list\"\n                            queryEntityUrl=\"${c.dataUrl}/queryById\">\n                          </GridSelect>\n                <#elseif c.showType == \"wangeditor\">\n                        <WangEditor  v-model=\"row.${c.javaFieldId}\"/>\n                <#elseif c.showType == \"quilleditor\">\n                        <vue-editor useCustomImageHandler @image-added=\"$utils.handleImageAdded\" v-model=\"row.${c.javaFieldId}\"/>\n                </#if>\n                      </template>\n                  </el-table-column>\n                    </#if>\n                </#list>\n                  <el-table-column\n                    fixed=\"right\"\n                    label=\"操作\"\n                    width=\"100\">\n                    <template #default=\"{row}\">\n                      <el-button  @click=\"del${child.className}Row(row)\" type=\"text\" size=\"small\">删除</el-button>\n                    </template>\n                  </el-table-column>\n                </el-table>\n              </el-tab-pane>\n         </#list>\n        </el-tabs>\n           </el-form-item>\n          </el-col>\n        </el-row>\n    </el-form>\n    <span slot=\"footer\" class=\"dialog-footer\">\n      <el-button size=\"small\" @click=\"visible = false\">关闭</el-button>\n      <el-button size=\"small\" type=\"primary\" v-if=\"method != \'view\'\" @click=\"doSubmit()\" v-noMoreClick>确定</el-button>\n    </span>\n  </el-dialog>\n</div>\n</template>\n\n<script>\n<#list table.importFormList as c>\n  ${c}\n</#list>\n  import ${className}Service from \'@/api/${moduleName}<#if subModuleName != \"\">/${subModuleName}</#if>/${className}Service\'\n  export default {\n    data () {\n      return {\n        title: \'\',\n        method: \'\',\n        visible: false,\n        loading: false,\n        ${className?uncap_first}Tab: \'0\',\n        inputForm: {\n          id: \'\'<#if (table.formList?size>0)>,</#if>\n<#list table.childList as child>\n          ${child.className?uncap_first}DTOList: [],\n</#list>\n        <#list table.formList as c>\n          <#if c.javaFieldId?contains(\".\")>\n          ${c.javaFieldId?split(\".\")[0]}: {\n            ${c.javaFieldId?split(\".\")[1]}: \'\'\n          }<#if c_has_next>,</#if>\n          <#else>\n          ${c.javaFieldId}: \'\'<#if c_has_next>,</#if>\n          </#if>\n    </#list>\n        }\n      }\n    },\n    components: {\n<#list table.formComponents as c>\n      ${c}<#if c_has_next>,</#if>\n</#list>\n    },\n    <#assign cks=0/>\n    <#list table.formList as c>\n     <#if c.showType == \"checkbox\">\n     <#assign cks= cks + 1/>\n    </#if>\n    </#list>\n    <#if cks gt 0>\n    computed: {\n    <#assign ckIndex=0/>\n    <#list table.formList as c>\n     <#if c.showType == \"checkbox\">\n     <#assign ckIndex= ckIndex + 1/>\n      ${c.javaFieldId}List: {\n        get: function () {\n          return this.inputForm.${c.javaFieldId} !== \'\' ? this.inputForm.${c.javaFieldId}.split(\',\') : []\n        },\n        set: function (val) {\n          this.inputForm.${c.javaFieldId} = val.join(\',\')\n        }\n      }<#if ckIndex lt cks >,</#if>\n    </#if>\n    </#list>\n    },\n    </#if>\n    methods: {\n      init (method, id) {\n        this.method = method\n        if (method === \'add\') {\n          this.title = `新建${functionNameSimple}`\n        } else if (method === \'edit\') {\n          this.title = \'修改${functionNameSimple}\'\n        } else if (method === \'view\') {\n          this.title = \'查看${functionNameSimple}\'\n        }\n        this.visible = true\n        this.loading = false\n        this.$nextTick(() => {\n          this.$refs.inputForm.resetFields()\n          this.inputForm.id = id\n          this.${className?uncap_first}Tab = \'0\'\n<#list table.childList as child>\n          this.inputForm.${child.className?uncap_first}DTOList = []\n</#list>\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField)>\n  <#if c.showType == \"wangeditor\">\n          this.$refs.${c.javaFieldId}Editor.init(\'\')\n  </#if>\n  </#if>\n</#list>\n          if (method === \'edit\' || method === \'view\') { // 修改或者查看\n            this.loading = true\n            ${className}Service.queryById(this.inputForm.id).then(({data}) => {\n              this.inputForm = this.recover(this.inputForm, data)\n<#list table.columnList as c>\n  <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField)>\n  <#if c.showType == \"wangeditor\">\n              this.$refs.${c.javaFieldId}Editor.init(this.inputForm.${c.javaFieldId})\n  </#if>\n  </#if>\n</#list>\n              this.loading = false\n            })\n          }\n        })\n      },\n<#list table.childList as child>\n      save${child.className}Row (child) {\n        if (child[0] === \'\') {\n          this.inputForm.${child.className?uncap_first}DTOList.push(child[1])\n        } else {\n          this.inputForm.${child.className?uncap_first}DTOList.forEach((item, index) => {\n            if (item === child[0]) {\n              this.inputForm.${child.className?uncap_first}DTOList.splice(index, 1, child[1])\n            }\n          })\n        }\n      },\n      add${child.className}Row () {\n        this.inputForm.${child.className?uncap_first}DTOList.push({\n<#list child.formList as c>\n<#if (c.isNotBaseField || c.simpleJavaField == \'remarks\') && c.name != child.parentTableFk>\n<#if c.javaFieldId?contains(\".\")>\n          ${c.javaFieldId?split(\".\")[0]}: {\n            ${c.javaFieldId?split(\".\")[1]}: \'\'\n          }<#if c_has_next>,</#if>\n<#else>\n          ${c.javaFieldId}: \'\'<#if c_has_next>,</#if>\n</#if>\n</#if>\n</#list>\n        })\n      },\n      del${child.className}Row (child) {\n        this.inputForm.${child.className?uncap_first}DTOList.forEach((item, index) => {\n          if (item === child && item.id === \'\') {\n            this.inputForm.${child.className?uncap_first}DTOList.splice(index, 1)\n          } else if (item === child) {\n            item.delFlag = \'1\'\n            this.inputForm.${child.className?uncap_first}DTOList.splice(index, 1, item)\n          }\n        })\n      },\n</#list>\n      // 表单提交\n      doSubmit () {\n        this.$refs[\'inputForm\'].validate((valid) => {\n          if (valid) {\n            this.loading = true\n            ${className}Service.save(this.inputForm).then(({data}) => {\n              this.visible = false\n              this.$message.success(data)\n              this.$emit(\'refreshDataList\')\n              this.loading = false\n            }).catch(() => {\n              this.loading = false\n            })\n          }\n        })\n      }\n    }\n  }\n</script>\n\n  ]]>\n    </content>\n</template>\n', '', 0, 80, '0', NULL, NULL, NULL, '1', '2021-10-10 15:56:57');
INSERT INTO `plugin_gencode_template_obj` VALUES ('4bd9d27a37be41c6ac0ded4d23395888', 'mapper.xml', '571bb51fe7a343eeae8e34deb5e78358', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>mapper</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/mapper/xml</filePath>\n    <fileName>${ClassName}Mapper.xml</fileName>\n    <content><![CDATA[<?xml version=\"1.0\" encoding=\"UTF-8\" ?>\n<!DOCTYPE mapper PUBLIC \"-//mybatis.org//DTD Mapper 3.0//EN\" \"http://mybatis.org/dtd/mybatis-3-mapper.dtd\">\n<mapper namespace=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper.${ClassName}Mapper\">\n\n<#if isNeedJoinTable>\n    <#-- 输出字段列 -->\n	<sql id=\"${className}Columns\">\n		<#assign columnField>\n			<#list table.columnList as c>\n		a.${c.name} AS \"${c.javaFieldId}\",\n			</#list>\n			<#list table.columnList as c>\n			<#-- 父表关联字段 -->\n				<#if table.parentExists && table.parentTableFk == c.name>\n					<#list c.javaFieldAttrs as a>\n		b.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"userselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"officeselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"areaselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"treeselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"gridselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n				</#list>\n				</#if>\n\n			</#list>\n		</#assign>\n${columnField?substring(0, columnField?last_index_of(\",\"))}\n	</sql>\n\n	<#-- 输出字段关联表 -->\n	<sql id=\"${className}Joins\">\n\n		<#-- 关联系统表 -->\n		<#list table.columnList as c>\n		<#-- 关联父表 -->\n		<#if table.parentExists && table.parentTableFk == c.name>\n			LEFT JOIN ${table.parent.name} b ON b.id = a.${table.parentTableFk}\n			<#elseif c.isNotBasicUserField>\n		LEFT JOIN sys_user ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"officeselect\">\n		LEFT JOIN sys_office ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"areaselect\">\n		LEFT JOIN sys_area ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"treeselect\" && c.javaType != \"This\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"gridselect\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			</#if>\n		</#list>\n	</sql>\n\n\n	<select id=\"findById\" resultType=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO\">\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		WHERE a.id = ${\"#\"}{id} and a.del_flag = 0\n	</select>\n\n	<select id=\"findList\" resultType=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO\">\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		WHERE a.${table.parentTableFk} = ${\"#\"}{${table.parent.className}Id} and a.del_flag = 0\n	</select>\n</#if>\n\n</mapper>]]>\n    </content>\n</template>\n', '', 0, 20, '1', NULL, NULL, NULL, '1', '2021-10-10 15:56:57');
INSERT INTO `plugin_gencode_template_obj` VALUES ('53cb4c90519c42a2a7583cd2601553e1', 'mapper.java', '428393bb2287402aa3ebe7733c71d766', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>mapper</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/mapper</filePath>\n    <fileName>${ClassName}Mapper.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper;\n\nimport cn.weegoo.core.domain.TreeMapper;\n<#if isNeedJoinTable>\nimport java.util.List;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO;\n</#if>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${ClassName};\n\n/**\n * ${functionName}MAPPER接口\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\npublic interface ${ClassName}Mapper extends TreeMapper<${ClassName}> {\n\n<#if isNeedJoinTable>\n    /**\n     * 根据id获取${functionName}\n     * @param id\n     * @return\n     */\n    ${ClassName}DTO findById(String id);\n\n    /**\n     * 获取${functionName}列表\n     *\n     * @return\n     */\n    List <${ClassName}DTO> findList();\n</#if>\n\n}]]>\n    </content>\n</template>\n', '', 0, 30, '0', NULL, NULL, NULL, '1', '2021-10-06 19:41:27');
INSERT INTO `plugin_gencode_template_obj` VALUES ('577d24f45e684ac3934887522254f7ff', 'service.java', '428393bb2287402aa3ebe7733c71d766', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>service</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/service</filePath>\n    <fileName>${ClassName}Service.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service;\n\nimport org.springframework.stereotype.Service;\nimport org.springframework.transaction.annotation.Transactional;\n		<#if isNeedJoinTable>\nimport java.util.List;\nimport cn.weegoo.core.service.TreeDTOService;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO;\n		<#else >\nimport cn.weegoo.core.service.TreeService;\n		</#if>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${ClassName};\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper.${ClassName}Mapper;\n<#if table.dataSource?? && table.dataSource.enName?? && table.dataSource.enName!=\"\" && table.dataSource.enName!=\"master\">\nimport com.baomidou.dynamic.datasource.annotation.DS;\n</#if>\n\n/**\n * ${functionName}Service\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n<#if table.dataSource?? && table.dataSource.enName?? && table.dataSource.enName!=\"\" && table.dataSource.enName!=\"master\">\n@DS(\"${table.dataSource.enName}\")\n</#if>\n@Service\n@Transactional\n<#if isNeedJoinTable>\npublic class ${ClassName}Service extends TreeDTOService<${ClassName}Mapper, ${ClassName}, ${ClassName}DTO> {\n<#else>\npublic class ${ClassName}Service extends TreeService<${ClassName}Mapper, ${ClassName}> {\n</#if>\n<#if isNeedJoinTable>\n\n	/**\n	 * 根据id查询\n	 * @param id\n	 * @return\n	 */\n	public ${ClassName}DTO findById(String id) {\n		return baseMapper.findById ( id );\n	}\n\n	/**\n	 * 查询全部数据\n	 * @return\n	 */\n	public List <${ClassName}DTO> listDTO() {\n		return  baseMapper.findList ();\n	}\n</#if>\n\n	public boolean saveOrUpdate(${ClassName} ${className}) {\n		return super.saveOrUpdate (${className});\n	}\n\n	public boolean removeWithChildrenById(String id) {\n		return super.removeWithChildrenById (id);\n	}\n\n}]]>\n    </content>\n</template>\n', '', 0, 20, '0', NULL, NULL, NULL, '1', '2021-10-06 19:41:27');
INSERT INTO `plugin_gencode_template_obj` VALUES ('6f0d1d117dfe4b0b8468f41a513a35f4', 'viewList.vue', '6b423144a2f24a0897f40c9a1bdb0c42', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>viewList</name>\n    <filePath>src/views/modules/${moduleName}/${subModuleName}</filePath>\n    <fileName>${ClassName}List.vue</fileName>\n    <content><![CDATA[\n<template>\n    <div class=\"jp-common-layout page\">\n     <div class=\"jp-common-layout-left\">\n        <div class=\"jp-common-title\">\n            <el-row :gutter=\"5\">\n                 <el-col :span=\"20\">\n                      <el-input placeholder=\"输入关键字进行过滤\" size=\"small\" v-model=\"filterText\"></el-input>\n                  </el-col>\n                   <el-col :span=\"4\">\n                       <el-button type=\"primary\" @click=\"addTreeNode\" size=\"small\" icon=\"el-icon-plus\" circle></el-button>\n                   </el-col>\n             </el-row>\n        </div>\n        <div class=\"jp-common-el-tree-scrollbar el-scrollbar\">\n         <div class=\"el-scrollbar__wrap\">\n            <div class=\"el-scrollbar__view\">\n              <el-tree\n                class=\"filter-tree\"\n                :data=\"${table.parent.className?uncap_first}TreeData\"\n                :props=\"{\n                      value: \'id\',             // ID字段名\n                      label: \'name\',         // 显示名称\n                      children: \'children\'    // 子级字段名\n                    }\"\n                default-expand-all\n                highlight-current\n                node-key=\"id\"\n                :filter-node-method=\"filterNode\"\n                :expand-on-click-node=\"false\"\n                @node-click=\"handleNodeClick\"\n                ref=\"${table.parent.className?uncap_first}Tree\">\n                   <span class=\"custom-tree-node\" slot-scope=\"{ node, data}\">\n                    <span>{{ node.label }}</span>\n                    <span>\n                        <el-button type=\"text\" class=\"tree-item-button\" icon=\"el-icon-plus\" @click=\"() => addChildTreeNode(data)\"></el-button>\n                        <el-button type=\"text\" class=\"tree-item-button\" icon=\"el-icon-edit-outline\" @click=\"() => editTreeNode(data)\"></el-button>\n                        <el-button type=\"text\" class=\"red tree-item-button\" icon=\"el-icon-delete\" @click=\"() => delTreeNode(data)\"></el-button>\n                    </span>\n                  </span>\n              </el-tree>\n            </div>\n          </div>\n         </div>\n     </div>\n     <div class=\"jp-common-layout-center jp-flex-main\">\n        <el-form size=\"small\" :inline=\"true\" class=\"query-form\" ref=\"searchForm\" :model=\"searchForm\" @keyup.enter.native=\"refreshList()\" @submit.native.prevent>\n            <!-- 搜索框-->\n<#list table.columnList as c>\n            <#if c.isQuery?? && c.isQuery == \"1\">\n         <el-form-item prop=\"${c.javaFieldId}\" label=\"${c.comments}\">\n		     	 <#if table.parentExists && table.parentTableFk == c.name>\n                 <SelectTree\n                  ref=\"${c.simpleJavaField}\"\n                  :props=\"{\n                      value: \'id\',             // ID字段名\n                      label: \'name\',         // 显示名称\n                      children: \'children\'    // 子级字段名\n                    }\"\n                  size=\"small\"\n                  url=\"/${childUrlPrefix}/${table.parent.className?uncap_first}/treeData\"\n                  :value=\"searchForm.${c.javaFieldId}\"\n                  :clearable=\"true\"\n                  :accordion=\"true\"\n                  @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n			<#elseif c.showType == \"input\" || c.showType == \"textarea\">\n                <el-input size=\"small\" v-model=\"searchForm.${c.javaFieldId}\" placeholder=\"${c.comments}\" clearable></el-input>\n      <#elseif c.showType == \"select\">\n               <el-select v-model=\"searchForm.${c.javaFieldId}\" placeholder=\"请选择${c.comments}\" size=\"small\" style=\"width: 100%;\">\n                <el-option\n                  v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\"\n                  :key=\"item.value\"\n                  :label=\"item.label\"\n                  :value=\"item.value\">\n                </el-option>\n               </el-select>\n        <#elseif c.showType == \"checkbox\">\n                <el-checkbox-group v-model=\"${c.javaFieldId}List\">\n                   <el-checkbox v-for=\"${c.javaFieldId} in $dictUtils.getDictList(\'${c.dictType}\')\" :label=\"${c.javaFieldId}.value\" :key=\"${c.javaFieldId}.value\">{{${c.javaFieldId}.label}}</el-checkbox>\n                </el-checkbox-group>\n        <#elseif c.showType == \"radiobox\">\n                 <el-radio-group v-model=\"searchForm.${c.javaFieldId}\">\n                <el-radio v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\" :label=\"item.value\" :key=\"item.value\">{{item.label}}</el-radio>\n              </el-radio-group>\n            <#elseif c.showType == \"dateselect\" &&(c.queryType == \"BETWEEN\" || c.queryType == \"NOTBETWEEN\")>\n               <el-date-picker\n                    v-model=\"searchForm.${c.javaFieldId}\"\n                    type=\"daterange\"\n                    size=\"small\"\n                    align=\"right\"\n                    value-format=\"yyyy-MM-dd hh:mm:ss\"\n                    unlink-panels\n                    range-separator=\"至\"\n                    start-placeholder=\"开始日期\"\n                    end-placeholder=\"结束日期\">\n                 </el-date-picker>\n        <#elseif c.showType == \"dateselect\">\n                <el-date-picker\n                  v-model=\"searchForm.${c.javaFieldId}\"\n                  type=\"datetime\"\n                  size=\"small\"\n                  value-format=\"yyyy-MM-dd HH:mm:ss\"\n                  placeholder=\"请选择${c.comments}\">\n                </el-date-picker>\n        <#elseif c.showType == \"userselect\">\n            <user-select :limit=\'1\' size=\"small\" placeholder=\"请选择${c.comments}\" :value=\"searchForm.${c.javaFieldId}\" @getValue=\'(value) => {searchForm.${c.javaFieldId}=value}\'></user-select>\n        <#elseif c.showType == \"officeselect\">\n            <SelectTree\n                  ref=\"${c.javaFieldId}\"\n                  :props=\"{\n                      value: \'id\',             // ID字段名\n                      label: \'name\',         // 显示名称\n                      children: \'children\'    // 子级字段名\n                    }\"\n                  placeholder=\"请选择${c.comments}\"\n                  size=\"small\"\n                  url=\"/sys/office/treeData?type=2\"\n                  :value=\"searchForm.${c.javaFieldId}\"\n                  :clearable=\"true\"\n                  :accordion=\"true\"\n                  @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"areaselect\">\n                <SelectTree\n                  ref=\"${c.javaFieldId}\"\n                  :props=\"{\n                      value: \'id\',             // ID字段名\n                      label: \'name\',         // 显示名称\n                      children: \'children\'    // 子级字段名\n                    }\"\n                  placeholder=\"请选择${c.comments}\"\n                  size=\"small\"\n                  url=\"/sys/area/treeData\"\n                  :value=\"searchForm.${c.javaFieldId}\"\n                  :clearable=\"true\"\n                  :accordion=\"true\"\n                  @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"cityselect\">\n                  <CityPicker\n                    style=\"width:100%\"\n                    size=\"small\"\n                    placeholder=\"选择${c.comments}\"\n                    :value=\"searchForm.${c.javaFieldId}\"\n                    :clearable=\"true\"\n                    :accordion=\"true\"\n                    @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"treeselect\">\n            <SelectTree\n                  ref=\"${c.javaFieldId}\"\n                  :props=\"{\n                      value: \'id\',             // ID字段名\n                      label: \'name\',         // 显示名称\n                      children: \'children\'    // 子级字段名\n                    }\"\n                  placeholder=\"请选择${c.comments}\"\n                  size=\"small\"\n                  url=\"${c.dataUrl}/treeData\"\n                  :value=\"searchForm.${c.javaFieldId}\"\n                  :clearable=\"true\"\n                  :accordion=\"true\"\n                  @getValue=\"(value) => {searchForm.${c.javaFieldId}=value}\"/>\n        <#elseif c.showType == \"gridselect\">\n            <GridSelect\n                    title=\"选择${c.comments}\"\n                    placeholder=\"请选择${c.comments}\"\n                    labelName = \'${c.javaFieldName?split(\".\")[1]}\'\n                    labelValue = \'${c.javaFieldId?split(\".\")[1]}\'\n                    :value = \"searchForm.${c.javaFieldId}\"\n                    :limit=\"1\"\n                    size=\"small\"\n                    @getValue=\'(value) => {searchForm.${c.javaFieldId}=value}\'\n                    :columns=\"[\n            <#list c.fieldLabels?split(\"|\") as f>\n                    {\n                      prop: \'${c.fieldKeys?split(\"|\")[f_index]}\',\n                      label: \'${c.fieldLabels?split(\"|\")[f_index]}\'\n                    }<#if f_has_next>,</#if>\n            </#list>\n                    ]\"\n                    :searchs=\"[\n           <#list c.searchLabel?split(\"|\") as f>\n                    {\n                      prop: \'${c.searchKey?split(\"|\")[f_index]}\',\n                      label: \'${c.searchLabel?split(\"|\")[f_index]}\'\n                    }<#if f_has_next>,</#if>\n            </#list>\n                    ]\"\n                    dataListUrl=\"${c.dataUrl}/list\"\n                    queryEntityUrl=\"${c.dataUrl}/queryById\">\n                  </GridSelect>\n        </#if>\n         </el-form-item>\n        </#if>\n    </#list>\n          <el-form-item>\n            <el-button type=\"primary\" @click=\"refreshList()\" size=\"small\" icon=\"el-icon-search\">查询</el-button>\n            <el-button @click=\"resetSearch()\" size=\"small\" icon=\"el-icon-refresh-right\">重置</el-button>\n          </el-form-item>\n      </el-form>\n\n     <div class=\"bg-white top\">\n        <!-- <vxe-toolbar :refresh=\"{query: refreshList}\" import export print custom> -->\n        <vxe-toolbar :refresh=\"{query: refreshList}\" custom>\n          <template #buttons>\n            <el-button v-if=\"hasPermission(\'${permissionPrefix}:add\')\" type=\"primary\" size=\"small\" icon=\"el-icon-plus\" @click=\"add()\">新建</el-button>\n            <el-button v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" type=\"warning\" size=\"small\" icon=\"el-icon-edit-outline\" @click=\"edit()\" :disabled=\"$refs.${className}Table && $refs.${className}Table.getCheckboxRecords().length !== 1\" plain>修改</el-button>\n            <el-button v-if=\"hasPermission(\'${permissionPrefix}:del\')\" type=\"danger\"   size=\"small\" icon=\"el-icon-delete\" @click=\"del()\" :disabled=\"$refs.${className}Table && $refs.${className}Table.getCheckboxRecords().length === 0\" plain>删除</el-button>\n          </template>\n          <!-- <template #tools>\n            <vxe-button\n    		  type=\"default\"\n    		  title=\"下载导入模板\"\n    		  v-if=\"hasPermission(\'${permissionPrefix}:import\')\"\n    		  class=\"el-icon-document m-r-12\"\n    		  @click=\"downloadTpl()\"\n    		  circle\n            >\n            </vxe-button>\n          </template> -->\n        </vxe-toolbar>\n        <div style=\"height: calc(100% - 80px);\">\n        <vxe-table\n            border=\"inner\"\n            auto-resize\n            resizable\n            height=\"auto\"\n            :loading=\"loading\"\n            ref=\"${className}Table\"\n            show-header-overflow\n            show-overflow\n            highlight-hover-row\n            :menu-config=\"{}\"\n            :print-config=\"{}\"\n            :import-config=\"{\n                importMethod: importMethod,\n                types: [\'csv\', \'xls\', \'xlsx\'],\n                remote: true,\n            }\"\n            :export-config=\"{\n                remote: true,\n                filename: `${functionNameSimple}数据${\"$\"}{moment(new Date()).format(\n            		\'YYYY-MM-DD\'\n                )}`,\n                sheetName: \'${functionNameSimple}数据\',\n                exportMethod: exportMethod,\n                types: [\'xlsx\'],\n                modes: [\'current\', \'selected\', \'all\'],\n            }\"\n            @sort-change=\"sortChangeHandle\"\n            :sort-config=\"{remote:true}\"\n            :data=\"dataList\"\n            :checkbox-config=\"{}\">\n            <vxe-column type=\"seq\" width=\"40\"></vxe-column>\n            <vxe-column type=\"checkbox\"  width=\"40px\"></vxe-column>\n      <#assign firstListField = true>\n      <#list table.columnList as c>\n        <#if c.isList?? && c.isList == \"1\">\n          <#if c.showType == \"userselect\" || c.showType == \"officeselect\" || c.showType == \"areaselect\" || c.showType == \"treeselect\"|| c.showType == \"gridselect\">\n      <vxe-column\n        field=\"${c.javaFieldName}\"\n        :sortable=\"false\"\n        title=\"${c.comments}\">\n        <#if firstListField>\n            <template slot-scope=\"scope\">\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\">{{scope.row.${c.simpleJavaField} && scope.row.${c.javaFieldName}}}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\">{{scope.row.${c.simpleJavaField} &&  scope.row.${c.javaFieldName}}}</el-link>\n              <span v-else>{{scope.row.${c.simpleJavaField} && scope.row.${c.javaFieldName}}}</span>\n            </template>\n        <#else>\n            <template slot-scope=\"scope\">\n                {{scope.row.${c.simpleJavaField} && scope.row.${c.javaFieldName}}}\n            </template>\n        </#if>\n      </vxe-column>\n        <#elseif c.showType == \"select\">\n    <vxe-column\n        field=\"${c.javaFieldId}\"\n        :sortable=\"false\"\n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n        <#if firstListField>\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\"> {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\"> {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</el-link>\n              <span v-else>{{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</span>\n        <#else>\n              {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}\n        </#if>\n        </template>\n      </vxe-column>\n      <#elseif c.showType == \"checkbox\">\n    <vxe-column\n        field=\"${c.javaFieldId}\"\n        :sortable=\"false\"\n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n       <#if firstListField>\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\">{{scope.row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\">{{scope.row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}</el-link>\n              <span v-else>{{scope.row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}</span>\n        <#else>\n              {{scope.row.${c.javaFieldId}.split(\",\").map( (item)=> { return $dictUtils.getDictLabel(\"${c.dictType}\", item, \'-\')}).join(\",\")  }}\n        </#if>\n        </template>\n      </vxe-column>\n      <#elseif c.showType == \"radiobox\">\n    <vxe-column\n        field=\"${c.javaFieldId}\"\n        :sortable=\"false\"\n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n       <#if firstListField>\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\">{{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\">{{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</el-link>\n              <span v-else> {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}</span>\n        <#else>\n             {{ $dictUtils.getDictLabel(\"${c.dictType}\", scope.row.${c.javaFieldId}, \'-\') }}\n        </#if>\n        </template>\n      </vxe-column>\n        <#elseif c.showType == \"fileselect\">\n    <vxe-column\n        field=\"${c.javaFieldId}\"\n        :sortable=\"false\"\n        title=\"${c.comments}\">\n          <template #default=\"{ row }\">\n				<template v-if=\"row.${c.javaFieldId}\">\n				    <el-link type=\"primary\" class=\"m-r-10\" :href=\"item\" target=\"_blank\" :key=\"index\" v-for=\"(item, index) in row.${c.javaFieldId}.split(\',\')\"> {{decodeURIComponent(item.substring(item.lastIndexOf(\"&name=\")+6))}}</el-link>\n				</template>\n		  </template>\n      </vxe-column>\n              <#elseif c.showType == \"imageselect\">\n    <vxe-column\n        field=\"${c.javaFieldId}\"\n        :sortable=\"false\"\n        title=\"${c.comments}\">\n        <template  #default=\"{ row }\">\n		    <template v-if=\"row.${c.javaFieldId}\">\n				  <el-image\n				    style=\"height: 50px;width:50px;margin-right:10px;\"\n				    :src=\"src\" v-for=\"(src, index) in row.${c.javaFieldId}.split(\',\')\" :key=\"index\"\n				    :preview-src-list=\"row.${c.javaFieldId}.split(\',\')\">\n				  </el-image>\n		     </template>\n		  </template>\n      </vxe-column>\n        <#elseif c.showType == \"wangeditor\" || c.showType == \"quilleditor\">\n      <vxe-column\n        field=\"${c.javaFieldId}\"\n        :sortable=\"false\"\n        title=\"${c.comments}\">\n        <template slot-scope=\"scope\">\n            <p v-html=\"$utils.unescapeHTML(scope.row.${c.javaFieldId})\"></p>\n        </template>\n      </vxe-column>\n      <#else>\n    <vxe-column\n        field=\"${c.javaFieldId}\"\n        :sortable=\"false\"\n        title=\"${c.comments}\">\n        <#if firstListField>\n            <template slot-scope=\"scope\">\n              <el-link  type=\"primary\" :underline=\"false\" v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" @click=\"edit(scope.row.id)\">{{scope.row.${c.javaFieldId}}}</el-link>\n              <el-link  type=\"primary\" :underline=\"false\" v-else-if=\"hasPermission(\'${permissionPrefix}:view\')\"  @click=\"view(scope.row.id)\">{{scope.row.${c.javaFieldId}}}</el-link>\n              <span v-else>{{scope.row.${c.javaFieldId}}}</span>\n            </template>\n        </#if>\n      </vxe-column>\n          </#if>\n        <#assign firstListField = false>\n      </#if>\n      </#list>\n      <vxe-column\n        fixed=\"right\"\n        align=\"center\"\n        width=\"200\"\n        title=\"操作\">\n        <template  slot-scope=\"scope\">\n          <el-button v-if=\"hasPermission(\'${permissionPrefix}:view\')\" type=\"text\" icon=\"el-icon-view\" size=\"small\" @click=\"view(scope.row.id)\">查看</el-button>\n          <el-button v-if=\"hasPermission(\'${permissionPrefix}:edit\')\" type=\"text\" icon=\"el-icon-edit\" size=\"small\" @click=\"edit(scope.row.id)\">修改</el-button>\n          <el-button v-if=\"hasPermission(\'${permissionPrefix}:del\')\" type=\"text\"  icon=\"el-icon-delete\" size=\"small\" @click=\"del(scope.row.id)\">删除</el-button>\n        </template>\n      </vxe-column>\n    </vxe-table>\n    <vxe-pager\n      background\n      size=\"small\"\n      :current-page=\"tablePage.currentPage\"\n      :page-size=\"tablePage.pageSize\"\n      :total=\"tablePage.total\"\n      :page-sizes=\"[10, 20, 100, 1000, {label: \'全量数据\', value: 1000000}]\"\n      :layouts=\"[\'PrevPage\', \'JumpNumber\', \'NextPage\', \'FullJump\', \'Sizes\', \'Total\']\"\n      @page-change=\"currentChangeHandle\">\n    </vxe-pager>\n   </div>\n        <!-- 弹窗, 新增 / 修改 -->\n    <${ClassName}Form  ref=\"${className}Form\" @refreshDataList=\"refreshList\"></${ClassName}Form>\n    <${table.parent.className?cap_first}Form  ref=\"${table.parent.className?uncap_first}Form\" @refreshTree=\"refreshTree\"></${table.parent.className?cap_first}Form>\n   </div>\n   </div>\n  </div>\n</template>\n\n<script>\n  import ${ClassName}Form from \'./${ClassName}Form\'\n  import ${table.parent.className?cap_first}Form from \'./${table.parent.className?cap_first}Form\'\n  import ${className}Service from \'@/api/${moduleName}<#if subModuleName != \"\">/${subModuleName}</#if>/${className}Service\'\n  import ${table.parent.className?uncap_first}Service from \'@/api/${moduleName}<#if subModuleName != \"\">/${subModuleName}</#if>/${table.parent.className?uncap_first}Service\'\n<#list table.importQueryList as c>\n  ${c}\n</#list>\n  export default {\n    data () {\n      return {\n        searchForm: {\n        <#list table.queryList as c>\n          <#if c.javaFieldId?contains(\".\")>\n          ${c.javaFieldId?split(\".\")[0]}: {\n            ${c.javaFieldId?split(\".\")[1]}: \'\'\n          }<#if c_has_next>,</#if>\n          <#else>\n          ${c.javaFieldId}: \'\'<#if c_has_next>,</#if>\n          </#if>\n		</#list>\n        },\n        filterText: \'\',\n        ${table.parent.className?uncap_first}TreeData: [],\n        select${table.parent.className?cap_first}Name: \'\',\n        dataList: [],\n        tablePage: {\n          total: 0,\n          currentPage: 1,\n          pageSize: 10,\n          orders: <#if isNeedJoinTable>[{ column: \'a.create_date\', asc: false }]<#else>[{ column: \'create_date\', asc: false }]</#if>\n        },\n        loading: false\n      }\n    },\n    components: {\n      <#list table.queryComponents as c>\n      ${c},\n    </#list>\n      ${ClassName}Form,\n      ${table.parent.className?cap_first}Form\n    },\n    activated () {\n      this.refreshTree()\n      this.refreshList()\n    },\n    computed: {\n        <#list table.columnList as c>\n        <#if c.isQuery?? && c.isQuery == \"1\">\n        <#if c.showType == \"checkbox\">\n        ${c.javaFieldId}List: {\n      get: function () {\n        return this.searchForm.${c.javaFieldId} !== \'\' ? this.searchForm.${c.javaFieldId}.split(\',\') : []\n      },\n      set: function (val) {\n        this.searchForm.${c.javaFieldId} = val.join(\',\')\n      }\n        }<#if c_has_next>,</#if>\n        </#if>\n        </#if>\n        </#list>\n    },\n    watch: {\n      filterText (val) {\n        this.$refs.${table.parent.className?uncap_first}Tree.filter(val)\n      }\n    },\n    methods: {\n      filterNode (value, data) {\n        if (!value) return true\n        return data.name.indexOf(value) !== -1\n      },\n      refreshTree () {\n        ${table.parent.className?uncap_first}Service.treeData().then(({data}) => {\n          this.${table.parent.className?uncap_first}TreeData = data\n        })\n      },\n      handleNodeClick (data) {\n        this.searchForm.${childSimpleJavaField}.id = data.id\n        this.refreshList()\n      },\n      addChildTreeNode (node) {\n        this.$refs.${table.parent.className?uncap_first}Form.init(\'addChild\', {id: \'\', parent: {id: node.id, name: node.name}})\n      },\n       // 新增\n      addTreeNode () {\n        this.$refs.${table.parent.className?uncap_first}Form.init(\'add\', {id: \'\', parent: {id: \'\', name: \'\'}})\n      },\n      // 修改\n      editTreeNode (data) {\n        this.$refs.${table.parent.className?uncap_first}Form.init(\'edit\', {id: data.id, parent: {id: \'\', name: \'\'}})\n      },\n      delTreeNode (data) {\n        this.$confirm(`确定删除所选项吗?`, \'提示\', {\n          confirmButtonText: \'确定\',\n          cancelButtonText: \'取消\',\n          type: \'warning\'\n        }).then(() => {\n          this.loading = true\n          ${table.parent.className?uncap_first}Service.delete(data.id).then(({data}) => {\n            this.$message.success(data)\n            this.loading = false\n            this.refreshTree()\n            this.refreshList()\n          })\n        })\n      },\n      // 获取数据列表\n      refreshList () {\n        this.loading = true\n        ${className}Service.list({\n          \'current\': this.tablePage.currentPage,\n          \'size\': this.tablePage.pageSize,\n          \'orders\': this.tablePage.orders,\n            <#if (table.queryBetwinDate?size > 0)>\n            <#list table.queryBetwinDate as c>\n          begin${c.javaFieldId?cap_first}: this.searchForm.${c.javaFieldId}[0],\n          end${c.javaFieldId?cap_first}: this.searchForm.${c.javaFieldId}[1],\n            </#list>\n          ...this.lodash.omit(this.searchForm, <#list table.queryBetwinDate as c>\'${c.javaFieldId}\'<#if c_has_next>, </#if></#list>)\n            <#else>\n          ...this.searchForm\n            </#if>\n        }).then(({data}) => {\n          this.dataList = data.records\n          this.tablePage.total = data.total\n          this.loading = false\n        })\n      },\n      // 当前页\n      currentChangeHandle ({ currentPage, pageSize }) {\n        this.tablePage.currentPage = currentPage\n        this.tablePage.pageSize = pageSize\n        this.refreshList()\n      },\n     // 排序\n      sortChangeHandle (obj) {\n        this.tablePage.orders = []\n        if (obj.order != null) {\n          this.tablePage.orders = [{ column: obj.column.sortBy || this.$utils.toLine(obj.property), asc: obj.order === \'asc\' }]\n        } else {\n          this.tablePage.orders = <#if isNeedJoinTable>[{ column: \'a.create_date\', asc: false }]<#else>[{ column: \'create_date\', asc: false }]</#if>\n        }\n        this.refreshList()\n      },\n      // 新增\n      add () {\n        this.$refs.${className}Form.init(\'add\', \'\')\n      },\n      // 修改\n      edit (id) {\n        id = id || this.$refs.${className}Table.getCheckboxRecords().map(item => {\n          return item.id\n        })[0]\n        this.$refs.${className}Form.init(\'edit\', id)\n      },\n      // 查看\n      view (id) {\n        this.$refs.${className}Form.init(\'view\', id)\n      },\n      // 删除\n      del (id) {\n        let ids = id || this.$refs.${className}Table.getCheckboxRecords().map(item => {\n          return item.id\n        }).join(\',\')\n        this.$confirm(`确定删除所选项吗?`, \'提示\', {\n          confirmButtonText: \'确定\',\n          cancelButtonText: \'取消\',\n          type: \'warning\'\n        }).then(() => {\n          this.loading = true\n          ${className}Service.delete(ids).then(({data}) => {\n            this.$message.success(data)\n            this.refreshList()\n            this.loading = false\n          })\n        })\n      },\n    // 下载模板\n      downloadTpl () {\n        this.loading = true\n        ${className}Service.exportTemplate().then(({data}) => {\n          // 将二进制流文件写入excel表，以下为重要步骤\n          this.$utils.downloadExcel(data, \'请假表单导入模板\')\n          this.loading = false\n        }).catch(function (err) {\n          this.loading = false\n          if (err.response) {\n            console.log(err.response)\n          }\n        })\n      },\n      // 自定义服务端导入\n      importMethod ({ file }) {\n      // 处理表单\n        const formBody = new FormData()\n        formBody.append(\'file\', file)\n        this.loading = true\n        ${className}Service.importExcel(formBody).then(({data}) => {\n          this.$message.success({\n            dangerouslyUseHTMLString: true,\n            message: data\n          })\n          this.refreshList()\n        })\n      },\n      // 自定义服务端导出\n      exportMethod ({ options }) {\n      // 传给服务端的参数\n        const params = {\n          current: this.tablePage.currentPage,\n          size: this.tablePage.pageSize,\n          orders: this.tablePage.orders,\n          ...this.searchForm,\n          filename: options.filename,\n          sheetName: options.sheetName,\n          isHeader: options.isHeader,\n          original: options.original,\n          mode: options.mode,\n          selectIds: options.mode === \'selected\' ? options.data.map((item) => item.id) : [],\n          exportFields: options.columns.map((column) => column.property && column.property.split(\'.\')[0])\n        }\n        this.loading = true\n        return ${className}Service.exportExcel(params).then(({data}) => {\n          // 将二进制流文件写入excel表，以下为重要步骤\n          this.$utils.downloadExcel(data, options.filename)\n          this.loading = false\n        }).catch(function (err) {\n          if (err.response) {\n            console.log(err.response)\n          }\n        })\n      },\n      resetSearch () {\n        this.$refs.searchForm.resetFields()\n        this.filterText = \'\'\n        this.$refs.${table.parent.className?uncap_first}Tree.setCurrentKey(null)\n        this.refreshList()\n      }\n    }\n  }\n</script>\n]]>\n    </content>\n</template>\n', '', 0, 90, '1', NULL, NULL, NULL, '1', '2021-10-16 10:28:20');
INSERT INTO `plugin_gencode_template_obj` VALUES ('77de75436b8f4e71bf5987f79088d3e2', 'entity.java', '428393bb2287402aa3ebe7733c71d766', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>entity</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/domain</filePath>\n    <fileName>${ClassName}.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain;\n\n<#list table.importEntityList as i>\nimport ${i};\n</#list>\n<#if table.idType ==\"2\">\nimport com.baomidou.mybatisplus.annotation.IdType;\nimport com.baomidou.mybatisplus.annotation.TableId;\n</#if>\nimport com.baomidou.mybatisplus.annotation.TableName;\nimport cn.weegoo.core.domain.TreeEntity;\nimport lombok.Data;\nimport lombok.EqualsAndHashCode;\n\n/**\n * ${functionName}Entity\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n@Data\n@EqualsAndHashCode(callSuper = false)\n@TableName(\"${table.name}\")\npublic class ${ClassName} extends TreeEntity<${ClassName}> {\n\n	private static final long serialVersionUID = 1L;\n\n		<#if table.idType ==\"2\">\n	@TableId(type = IdType.AUTO)\n	private Integer id;\n		</#if>\n	<#-- 生成字段属性 -->\n	<#list table.columnList as c>\n		<#-- 如果不是基类属性 -->\n		<#if c.isNotTreeBaseField>\n			<#if c.comments??>\n	/**\n     * ${c.comments}\n     */\n            </#if>\n            <#if c.isBasicJavaType>\n<#if c.simpleJavaField != c.mapperJavaField>\n    @TableField(\"${c.name}\")\n</#if>\n	private ${c.simpleJavaType} ${c.simpleJavaField};\n	        <#else>\n<#if c.simpleJavaFieldId != c.mapperJavaField>\n    @TableField(\"${c.name}\")\n</#if>\n	private String ${c.simpleJavaFieldId};\n	         </#if>\n		</#if>\n	</#list>\n\n}]]>\n    </content>\n</template>\n', '', 0, 70, '0', NULL, NULL, NULL, '1', '2021-10-06 19:41:27');
INSERT INTO `plugin_gencode_template_obj` VALUES ('78d4aac9ee3644d7a59b53e7187c688f', 'entity.java', '6b423144a2f24a0897f40c9a1bdb0c42', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>entity</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/domain</filePath>\n    <fileName>${ClassName}.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain;\n\n<#list table.importEntityList as i>\nimport ${i};\n</#list>\n<#if table.idType ==\"2\">\nimport com.baomidou.mybatisplus.annotation.IdType;\nimport com.baomidou.mybatisplus.annotation.TableId;\n</#if>\nimport com.baomidou.mybatisplus.annotation.TableName;\nimport cn.weegoo.core.domain.BaseEntity;\nimport lombok.Data;\nimport lombok.EqualsAndHashCode;\n/**\n * ${functionName}Entity\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n@Data\n@EqualsAndHashCode(callSuper = false)\n@TableName(\"${table.name}\")\npublic class ${ClassName} extends BaseEntity {\n\n	private static final long serialVersionUID = 1L;\n	<#if table.idType ==\"2\">\n\n	@TableId(type = IdType.AUTO)\n	private Integer id;\n	</#if>\n\n	<#-- 生成字段属性 -->\n	<#list table.columnList as c>\n		<#-- 如果不是基类属性 -->\n		<#if c.isNotBaseField>\n			<#if c.comments??>\n	/**\n     * ${c.comments}\n     */\n            </#if>\n            <#if c.isBasicJavaType>\n<#if c.simpleJavaField != c.mapperJavaField>\n    @TableField(\"${c.name}\")\n</#if>\n	private ${c.simpleJavaType} ${c.simpleJavaField};\n	        <#else>\n<#if c.simpleJavaFieldId != c.mapperJavaField>\n    @TableField(\"${c.name}\")\n</#if>\n	private String ${c.simpleJavaFieldId};\n	         </#if>\n		</#if>\n	</#list>\n\n}]]>\n    </content>\n</template>\n', '', 0, 70, '1', NULL, NULL, NULL, '1', '2021-10-16 10:28:21');
INSERT INTO `plugin_gencode_template_obj` VALUES ('7cb58993209f49f1a9d6a66c0d9a85fe', 'controller.java', '6b423144a2f24a0897f40c9a1bdb0c42', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>controller</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/controller</filePath>\n    <fileName>${ClassName}Controller.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.controller;\n\nimport javax.servlet.http.HttpServletResponse;\nimport javax.validation.Valid;\nimport com.google.common.collect.Lists;\nimport cn.weegoo.core.excel.EasyExcelUtils;\nimport cn.weegoo.core.excel.ExcelOptions;\nimport cn.weegoo.core.excel.annotation.ExportMode;\nimport cn.weegoo.core.query.QueryWrapperGenerator;\nimport cn.weegoo.aop.logging.annotation.ApiLog;\nimport io.swagger.annotations.Api;\nimport io.swagger.annotations.ApiOperation;\nimport org.springframework.http.ResponseEntity;\nimport org.springframework.security.access.prepost.PreAuthorize;\nimport org.springframework.beans.factory.annotation.Autowired;\nimport org.springframework.web.bind.annotation.*;\nimport com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;\nimport com.baomidou.mybatisplus.core.metadata.IPage;\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\n<#if !isNeedJoinTable>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${ClassName};\n</#if>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.mapstruct.${ClassName}Wrapper;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.${ClassName}Service;\nimport org.springframework.web.multipart.MultipartFile;\n\nimport java.io.IOException;\nimport java.util.List;\n\n/**\n * ${functionName}Controller\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n\n@Api(tags =\"${functionName}\")\n@RestController\n@RequestMapping(value = \"/${urlPrefix}\")\npublic class ${ClassName}Controller {\n\n	@Autowired\n	private ${ClassName}Service ${className}Service;\n\n	@Autowired\n	private ${ClassName}Wrapper ${className}Wrapper;\n\n	/**\n	 * ${functionNameSimple}列表数据\n	 */\n	@ApiLog(\"查询${functionNameSimple}列表数据\")\n	@ApiOperation(value = \"查询${functionNameSimple}列表数据\")\n	@PreAuthorize(\"hasAuthority(\'${permissionPrefix}:list\')\")\n	@GetMapping(\"list\")\n	public ResponseEntity<#if isNeedJoinTable><IPage<${ClassName}DTO>><#else><IPage<${ClassName}>></#if> list(${ClassName}DTO ${className}DTO, Page<<#if isNeedJoinTable>${ClassName}DTO<#else>${ClassName}</#if>> page) throws Exception {\n		QueryWrapper queryWrapper = QueryWrapperGenerator.buildQueryCondition (${className}DTO, ${ClassName}DTO.class);\n				<#if isNeedJoinTable>\n		IPage<${ClassName}DTO> result = ${className}Service.findPage (page, queryWrapper);\n					<#else>\n		IPage<${ClassName}> result = ${className}Service.page (page, queryWrapper);\n					</#if>\n		return ResponseEntity.ok (result);\n	}\n\n\n	/**\n	 * 根据Id获取${functionNameSimple}数据\n	 */\n	@ApiLog(\"根据Id获取${functionNameSimple}数据\")\n	@ApiOperation(value = \"根据Id获取${functionNameSimple}数据\")\n	@PreAuthorize(\"hasAnyAuthority(\'${permissionPrefix}:view\',\'${permissionPrefix}:add\',\'${permissionPrefix}:edit\')\")\n	@GetMapping(\"queryById\")\n	public ResponseEntity<${ClassName}DTO> queryById(String id) {\n					<#if isNeedJoinTable>\n		return ResponseEntity.ok ( ${className}Service.findById ( id ) );\n					<#else >\n		return ResponseEntity.ok ( ${className}Wrapper.toDTO ( ${className}Service.getById ( id ) ) );\n					</#if>\n	}\n\n	/**\n	 * 保存${functionNameSimple}\n	 */\n	@ApiLog(\"保存${functionNameSimple}\")\n	@ApiOperation(value = \"保存${functionNameSimple}\")\n	@PreAuthorize(\"hasAnyAuthority(\'${permissionPrefix}:add\',\'${permissionPrefix}:edit\')\")\n	@PostMapping(\"save\")\n	public  ResponseEntity <String> save(@Valid @RequestBody ${ClassName}DTO ${className}DTO) {\n		//新增或编辑表单保存\n		${className}Service.saveOrUpdate (${className}Wrapper.toEntity (${className}DTO));\n        return ResponseEntity.ok ( \"保存${functionNameSimple}成功\" );\n	}\n\n\n	/**\n	 * 删除${functionNameSimple}\n	 */\n	@ApiLog(\"删除${functionNameSimple}\")\n	@ApiOperation(value = \"删除${functionNameSimple}\")\n	@PreAuthorize(\"hasAuthority(\'${permissionPrefix}:del\')\")\n	@DeleteMapping(\"delete\")\n	public ResponseEntity <String> delete(String ids) {\n		String idArray[] = ids.split(\",\");\n        ${className}Service.removeByIds ( Lists.newArrayList ( idArray ) );\n		return ResponseEntity.ok( \"删除${functionNameSimple}成功\" );\n	}\n	\n	/**\n     * 导出${functionNameSimple}数据\n     *\n     * @param ${className}DTO\n     * @param page\n     * @param response\n     * @throws Exception\n     */\n    @ApiLog(\"导出${functionNameSimple}数据\")\n    @PreAuthorize(\"hasAnyAuthority(\'${permissionPrefix}:export\')\")\n    @GetMapping(\"export\")\n    public void exportFile(${ClassName}DTO ${className}DTO, Page <<#if isNeedJoinTable>${ClassName}DTO<#else>${ClassName}</#if>> page, ExcelOptions options, HttpServletResponse response) throws Exception {\n        String fileName = options.getFilename ( );\n		QueryWrapper queryWrapper = QueryWrapperGenerator.buildQueryCondition (${className}DTO, ${ClassName}DTO.class);\n				<#if isNeedJoinTable>\n        if ( ExportMode.current.equals ( options.getMode ( ) ) ) { // 导出当前页数据\n            \n        } else if ( ExportMode.selected.equals ( options.getMode ( ) ) ) { // 导出选中数据\n            queryWrapper.in ( \"a.id\", options.getSelectIds () );\n        } else { // 导出全部数据\n            page.setSize ( -1 );\n            page.setCurrent ( 0 );\n        }\n        List<${ClassName}DTO> result = ${className}Service.findPage ( page, queryWrapper ).getRecords ( );\n					<#else>\n        if ( ExportMode.current.equals ( options.getMode ( ) ) ) { // 导出当前页数据\n            \n        } else if ( ExportMode.selected.equals ( options.getMode ( ) ) ) { // 导出选中数据\n            queryWrapper.in ( \"id\", options.getSelectIds () );\n        } else { // 导出全部数据\n            page.setSize ( -1 );\n            page.setCurrent ( 0 );\n        }\n        List < ${ClassName}> result = ${className}Service.page ( page, queryWrapper ).getRecords ( );\n					</#if>\n        EasyExcelUtils.newInstance ( ${className}Service, ${className}Wrapper ).exportExcel ( result,  options.getSheetName ( ), ${ClassName}DTO.class, fileName,options.getExportFields (), response );\n    }\n\n    /**\n     * 导入${functionNameSimple}数据\n     *\n     * @return\n     */\n    @PreAuthorize(\"hasAnyAuthority(\'${permissionPrefix}:import\')\")\n    @PostMapping(\"import\")\n    public ResponseEntity importFile(MultipartFile file) throws IOException {\n        String result = EasyExcelUtils.newInstance ( ${className}Service, ${className}Wrapper ).importExcel ( file, ${ClassName}DTO.class );\n        return ResponseEntity.ok ( result );\n    }\n\n    /**\n     * 下载导入${functionNameSimple}数据模板\n     *\n     * @param response\n     * @return\n     */\n    @PreAuthorize (\"hasAnyAuthority(\'${permissionPrefix}:import\')\")\n    @GetMapping(\"import/template\")\n    public void importFileTemplate(HttpServletResponse response) throws IOException {\n        String fileName = \"${functionNameSimple}数据导入模板.xlsx\";\n        List<${ClassName}DTO> list = Lists.newArrayList();\n        EasyExcelUtils.newInstance ( ${className}Service, ${className}Wrapper ).exportExcel ( list,  \"${functionNameSimple}数据\", ${ClassName}DTO.class, fileName, null, response );\n    }\n\n}]]>\n    </content>\n</template>\n', '', 0, 0, '1', NULL, NULL, NULL, '1', '2021-10-16 10:28:21');
INSERT INTO `plugin_gencode_template_obj` VALUES ('7dad31522aa245c09b7290701e106411', 'mapper.java', '571bb51fe7a343eeae8e34deb5e78358', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>mapper</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/mapper</filePath>\n    <fileName>${ClassName}Mapper.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper;\n\n<#if isNeedJoinTable>;\nimport java.util.List;\n</#if>\nimport com.baomidou.mybatisplus.core.mapper.BaseMapper;\n<#if isNeedJoinTable>;\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO;\n</#if>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${ClassName};\n\n/**\n * ${functionName}MAPPER接口\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\npublic interface ${ClassName}Mapper extends BaseMapper<${ClassName}> {\n\n<#if isNeedJoinTable>\n    /**\n     * 根据id获取${functionName}\n     * @param id\n     * @return\n     */\n    ${ClassName}DTO findById(String id);\n\n    /**\n     * 获取${functionName}列表\n     *\n     * @param ${table.parent.className}Id\n     * @return\n     */\n    List <${ClassName}DTO> findList(String ${table.parent.className}Id);\n</#if>\n\n}]]>\n    </content>\n</template>\n', '', 0, 10, '1', NULL, NULL, NULL, '1', '2021-10-10 15:56:57');
INSERT INTO `plugin_gencode_template_obj` VALUES ('7f1c194ebf6946019924a3e2a515d435', 'service.java', '571bb51fe7a343eeae8e34deb5e78358', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>service</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/service</filePath>\n    <fileName>${ClassName}Service.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service;\n\nimport cn.weegoo.sys.constant.CommonConstants;\nimport org.springframework.stereotype.Service;\nimport org.springframework.transaction.annotation.Transactional;\nimport org.springframework.beans.factory.annotation.Autowired;\n<#if table.dataSource?? && table.dataSource.enName?? && table.dataSource.enName!=\"\" && table.dataSource.enName!=\"master\">\nimport com.baomidou.dynamic.datasource.annotation.DS;\n</#if>\nimport com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;\n<#if isNeedJoinTable>\nimport com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;\nimport com.baomidou.mybatisplus.core.metadata.IPage;\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\n</#if>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO;\n<#-- 子表列表 -->\n<#list table.childList as c>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${c.className?cap_first}DTO;\n</#list>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.mapstruct.${ClassName}Wrapper;\n<#list table.childList as c>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.mapstruct.${c.className?cap_first}Wrapper;\n</#list>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${ClassName};\n<#-- 子表列表 -->\n<#list table.childList as c>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain.${c.className?cap_first};\n</#list>\nimport ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper.${ClassName}Mapper;\n\n/**\n * ${functionName}Service\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n<#if table.dataSource?? && table.dataSource.enName?? && table.dataSource.enName!=\"\" && table.dataSource.enName!=\"master\">\n@DS(\"${table.dataSource.enName}\")\n</#if>\n@Service\n@Transactional\npublic class ${ClassName}Service extends ServiceImpl<${ClassName}Mapper, ${ClassName}> {\n	<#-- 子表列表字段 -->\n<#list table.childList as c>\n	/**\n	* 子表service\n	*/\n	@Autowired\n	private ${c.className?cap_first}Service ${c.className?uncap_first}Service;\n</#list>\n<#if isNeedJoinTable>\n\n	/**\n	 * 根据id查询\n	 * @param id\n	 * @return\n	 */\n	public ${ClassName}DTO findById(String id) {\n		${ClassName}DTO ${className}DTO = baseMapper.findById ( id );\n		<#-- 子表列表 -->\n		<#list table.childList as c>\n		${className}DTO.set${c.className?cap_first}DTOList(${c.className?uncap_first}Service.findList(id));\n		</#list>\n		return ${className}DTO;\n	}\n\n	/**\n	 * 自定义分页检索\n	 * @param page\n	 * @param queryWrapper\n	 * @return\n	 */\n	public IPage <${ClassName}DTO> findPage(Page <${ClassName}DTO> page, QueryWrapper queryWrapper) {\n		queryWrapper.eq (\"a.del_flag\", 0 ); // 排除已经删除\n		return  baseMapper.findList (page, queryWrapper);\n	}\n\n	/**\n	* 保存或者更新\n	* @param  ${className}DTO\n	* @return\n	*/\n	public void saveOrUpdate(${ClassName}DTO ${className}DTO) {\n		${ClassName} ${className} =  ${ClassName}Wrapper.INSTANCE.toEntity ( ${className}DTO );\n		super.saveOrUpdate (${className});\n	<#-- 子表列表 -->\n	<#list table.childList as c>\n		for (${c.className?cap_first}DTO ${c.className?uncap_first}DTO : ${className}DTO.get${c.className?cap_first}DTOList ()){\n			if ( CommonConstants.DELETED.equals ( ${c.className?uncap_first}DTO.getDelFlag()) ){\n				${c.className?uncap_first}Service.removeById ( ${c.className?uncap_first}DTO.getId () );\n			}else{\n				${c.className?cap_first} ${c.className?uncap_first} = ${c.className?cap_first}Wrapper.INSTANCE.toEntity ( ${c.className?uncap_first}DTO );\n				${c.className?uncap_first}.set${c.parentJavaField?cap_first} ( ${className}.getId () );\n				${c.className?uncap_first}Service.saveOrUpdate ( ${c.className?uncap_first} );\n			}\n		}\n	</#list>\n	}\n<#else>\n\n	/**\n	 * 根据id查询\n	 * @param id\n	 * @return\n	 */\n	public ${ClassName}DTO findById(String id) {\n		${ClassName}DTO ${className}DTO = ${ClassName}Wrapper.INSTANCE.toDTO ( super.getById ( id ) );\n<#-- 子表列表 -->\n	<#list table.childList as c>\n		${className}DTO.set${c.className?cap_first}DTOList(${c.className?uncap_first}Service.findList(id));\n	</#list>\n		return ${className}DTO;\n	}\n\n	/**\n	 * 保存或者更新\n	 * @param  ${className}DTO\n	 * @return\n	 */\n	public void saveOrUpdate(${ClassName}DTO ${className}DTO) {\n		${ClassName} ${className} =  ${ClassName}Wrapper.INSTANCE.toEntity ( ${className}DTO );\n		super.saveOrUpdate (${className});\n<#-- 子表列表 -->\n	<#list table.childList as c>\n		for (${c.className?cap_first}DTO ${c.className?uncap_first}DTO : ${className}DTO.get${c.className?cap_first}DTOList ()){\n			if ( CommonConstants.DELETED.equals ( ${c.className?uncap_first}DTO.getDelFlag()) ){\n				${c.className?uncap_first}Service.removeById ( ${c.className?uncap_first}DTO.getId () );\n			}else{\n				${c.className?cap_first} ${c.className?uncap_first} = ${c.className?cap_first}Wrapper.INSTANCE.toEntity ( ${c.className?uncap_first}DTO );\n				${c.className?uncap_first}.set${c.parentJavaField?cap_first} ( ${className}.getId () );\n				${c.className?uncap_first}Service.saveOrUpdate ( ${c.className?uncap_first} );\n			}\n		}\n	</#list>\n	}\n</#if>\n\n	/**\n	 * 删除\n	 * @param  id\n	 * @return\n	 */\n	public void removeById(String id) {\n		super.removeById ( id );\n	<#-- 子表列表 -->\n	<#list table.childList as c>\n		${c.className?uncap_first}Service.lambdaUpdate ().eq ( ${c.className?cap_first}::get${c.parentJavaField?cap_first}, id ).remove ();\n	</#list>\n	}\n\n}]]>\n    </content>\n</template>\n', '', 0, 20, '0', NULL, NULL, NULL, '1', '2021-10-10 15:56:57');
INSERT INTO `plugin_gencode_template_obj` VALUES ('822b55058f1847ac89c334d8a36a5151', 'entity.java', '571bb51fe7a343eeae8e34deb5e78358', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>entity</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/domain</filePath>\n    <fileName>${ClassName}.java</fileName>\n    <content><![CDATA[\n/**\n * Copyright © 2021-2025 <a href=\"http://www.weegoo.cn/\">weegoo</a> All rights reserved.\n */\npackage ${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.domain;\n\n<#list table.importEntityList as i>\nimport ${i};\n</#list>\n<#if table.idType ==\"2\">\nimport com.baomidou.mybatisplus.annotation.IdType;\nimport com.baomidou.mybatisplus.annotation.TableId;\n</#if>\nimport com.baomidou.mybatisplus.annotation.TableName;\nimport cn.weegoo.core.domain.BaseEntity;\n<#if  (!isNeedJoinTable && table.queryList?size > 0) >\nimport cn.weegoo.core.query.Query;\nimport cn.weegoo.core.query.QueryType;\n</#if>\nimport lombok.Data;\nimport lombok.EqualsAndHashCode;\n/**\n * ${functionName}Entity\n * @author ${functionAuthor}\n * @version ${functionVersion}\n */\n@Data\n@EqualsAndHashCode(callSuper = false)\n@TableName(\"${table.name}\")\npublic class ${ClassName} extends BaseEntity {\n\n	private static final long serialVersionUID = 1L;\n	<#if table.idType ==\"2\">\n\n	@TableId(type = IdType.AUTO)\n	private Integer id;\n	</#if>\n\n	<#-- 生成字段属性 -->\n	<#list table.columnList as c>\n		<#-- 如果不是基类属性 -->\n		<#if c.isNotBaseField>\n			<#if c.comments??>\n	/**\n     * ${c.comments}\n     */\n            </#if>\n            <#if c.isBasicJavaType>\n<#if c.simpleJavaField != c.mapperJavaField>\n    @TableField(\"${c.name}\")\n</#if>\n	private ${c.simpleJavaType} ${c.simpleJavaField};\n	        <#else>\n<#if c.simpleJavaFieldId != c.mapperJavaField>\n    @TableField(\"${c.name}\")\n</#if>\n	private String ${c.simpleJavaFieldId};\n	         </#if>\n		</#if>\n	</#list>\n\n}]]>\n    </content>\n</template>\n', '', 0, 70, '0', NULL, NULL, NULL, '1', '2021-10-10 15:56:57');
INSERT INTO `plugin_gencode_template_obj` VALUES ('82c3b57bf8e8467f9b5e71ab6b2c3c72', 'viewForm.vue', '6b423144a2f24a0897f40c9a1bdb0c42', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>viewForm</name>\n    <filePath>src/views/modules/${moduleName}/${subModuleName}</filePath>\n    <fileName>${ClassName}Form.vue</fileName>\n    <content><![CDATA[\n<template>\n<div>\n  <el-dialog\n    :title=\"title\"\n    :close-on-click-modal=\"false\"\n     v-dialogDrag\n    :visible.sync=\"visible\">\n    <el-form :model=\"inputForm\" size=\"small\" ref=\"inputForm\" v-loading=\"loading\" :class=\"method===\'view\'?\'readonly\':\'\'\"  :disabled=\"method===\'view\'\"\n             label-width=\"120px\">\n      <el-row  :gutter=\"15\">\n<#list table.columnList as c>\n      <#if c.isForm?? && c.isForm == \"1\" && (c.isNotBaseField)>\n       <#if c.showType == \"wangeditor\" || c.showType == \"quilleditor\" || c.showType == \"fileselect\" || c.showType == \"imageselect\">\n        <el-col :span=\"24\">\n        <#else>\n        <el-col :span=\"12\">\n        </#if>\n            <el-form-item label=\"${c.comments}\" prop=\"${c.javaFieldId}\"\n                :rules=\"[\n      <#if c.isNull != \"1\">\n                  {required: true, message:\'${c.comments}不能为空\', trigger:\'blur\'}<#if c.validateType?? && c.validateType != \"\"><#if c.validateType != \"string\" ||  c.minLength?? && c.minLength != \"\">,</#if></#if>\n                </#if>\n                <#if c.minLength?? && c.minLength != \"\">\n                  { min: ${c.minLength}, message: \'最小长度是${c.minLength}个字符\', trigger: \'blur\' }<#if c.validateType?? && c.validateType != \"\"><#if c.validateType != \"string\">,</#if></#if>\n                </#if>\n                <#if c.validateType?? && c.validateType != \"\">\n                <#if c.validateType != \"string\">\n                  {validator: validator.${c.validateType}, trigger:\'blur\'}\n                 </#if>\n                 </#if>\n                 ]\">\n    <#if table.parentExists && table.parentTableFk == c.name>\n          <SelectTree\n                      ref=\"${c.simpleJavaField}\"\n                      :props=\"{\n                          value: \'id\',             // ID字段名\n                          label: \'name\',         // 显示名称\n                          children: \'children\'    // 子级字段名\n                        }\"\n\n                      url=\"/${childUrlPrefix}/${table.parent.className?uncap_first}/treeData\"\n                      :value=\"inputForm.${c.javaFieldId}\"\n                      :clearable=\"true\"\n                      :accordion=\"true\"\n                      @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"input\">\n          <el-input v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"请填写${c.comments}\" <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> <#if  c.minLength?? && c.minLength != \"\"> minlength=\"${c.minLength}\"</#if> <#if c.maxValue?? && c.maxValue != \"\"> max=\"${c.maxValue}\"</#if> <#if c.minValue?? && c.minValue != \"\"> min=\"${c.minValue}\"</#if> ></el-input>\n    <#elseif c.showType == \"inputnumber\">\n          <el-input-number v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"请填写${c.comments}\" <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> <#if  c.minLength?? && c.minLength != \"\"> minlength=\"${c.minLength}\"</#if> <#if c.maxValue?? && c.maxValue != \"\"> max=\"${c.maxValue}\"</#if> <#if c.minValue?? && c.minValue != \"\"> min=\"${c.minValue}\"</#if> style=\"width: 100%;\"></el-input-number>\n    <#elseif c.showType == \"textarea\">\n          <el-input type=\"textarea\" v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"请填写${c.comments}\" <#if c.maxLength?? && c.maxLength != \"\"> maxlength=\"${c.maxLength}\"</#if> <#if  c.minLength?? && c.minLength != \"\"> minlength=\"${c.minLength}\"</#if> <#if c.maxValue?? && c.maxValue != \"\"> max=\"${c.maxValue}\"</#if> <#if c.minValue?? && c.minValue != \"\"> min=\"${c.minValue}\"</#if> ></el-input>\n    <#elseif c.showType == \"select\">\n                <el-select v-model=\"inputForm.${c.javaFieldId}\" placeholder=\"请选择\"  style=\"width: 100%;\">\n                          <el-option\n                            v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\"\n                            :key=\"item.value\"\n                            :label=\"item.label\"\n                            :value=\"item.value\">\n                          </el-option>\n                      </el-select>\n    <#elseif c.showType == \"checkbox\">\n                <el-checkbox-group\n                        v-model=\"${c.javaFieldId}List\">\n                       <el-checkbox v-for=\"${c.javaFieldId} in $dictUtils.getDictList(\'${c.dictType}\')\" :label=\"${c.javaFieldId}.value\" :key=\"${c.javaFieldId}.value\">{{${c.javaFieldId}.label}}</el-checkbox>\n                    </el-checkbox-group>\n    <#elseif c.showType == \"radiobox\">\n                    <el-radio-group v-model=\"inputForm.${c.javaFieldId}\">\n                        <el-radio v-for=\"item in $dictUtils.getDictList(\'${c.dictType}\')\" :label=\"item.value\" :key=\"item.value\">{{item.label}}</el-radio>\n                    </el-radio-group>\n    <#elseif c.showType == \"dateselect\">\n                <el-date-picker\n                      style=\"width: 100%;\"\n                      v-model=\"inputForm.${c.javaFieldId}\"\n                      type=\"datetime\"\n                      value-format=\"yyyy-MM-dd HH:mm:ss\"\n                      placeholder=\"选择日期时间\">\n                    </el-date-picker>\n    <#elseif c.showType == \"userselect\">\n                <user-select :limit=\'1\' :value=\"inputForm.${c.javaFieldId}\" @getValue=\'(value) => {inputForm.${c.javaFieldId}=value}\'></user-select>\n    <#elseif c.showType == \"officeselect\">\n          <SelectTree\n                      ref=\"${c.simpleJavaField}\"\n                      :props=\"{\n                          value: \'id\',             // ID字段名\n                          label: \'name\',         // 显示名称\n                          children: \'children\'    // 子级字段名\n                        }\"\n\n                      url=\"/sys/office/treeData?type=2\"\n                      :value=\"inputForm.${c.javaFieldId}\"\n                      :clearable=\"true\"\n                      :accordion=\"true\"\n                      @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"areaselect\">\n              <SelectTree\n                      ref=\"${c.simpleJavaField}\"\n                      :props=\"{\n                          value: \'id\',             // ID字段名\n                          label: \'name\',         // 显示名称\n                          children: \'children\'    // 子级字段名\n                        }\"\n\n                      url=\"/sys/area/treeData\"\n                      :value=\"inputForm.${c.javaFieldId}\"\n                      :clearable=\"true\"\n                      :accordion=\"true\"\n                      @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"cityselect\">\n                      <CityPicker\n                         style=\"width:100%\"\n                        :value=\"inputForm.${c.javaFieldId}\"\n                        :clearable=\"true\"\n                        :accordion=\"true\"\n                        @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"fileselect\">\n              <file-upload v-model=\"inputForm.${c.javaFieldId}\" :limit=\"3\" tip=\"最多上传3个文件,单个文件不要超过10M\"></file-upload>\n<#elseif c.showType == \"imageselect\">\n             <image-upload v-model=\"inputForm.${c.javaFieldId}\" :limit=\"3\" tip=\"最多上传3个图片,单个图片不要超过10M\"></image-upload>\n    <#elseif c.showType == \"treeselect\">\n          <SelectTree\n                      ref=\"${c.simpleJavaField}\"\n                      :props=\"{\n                          value: \'id\',             // ID字段名\n                          label: \'name\',         // 显示名称\n                          children: \'children\'    // 子级字段名\n                        }\"\n                      url=\"${c.dataUrl}/treeData\"\n                      :value=\"inputForm.${c.javaFieldId}\"\n                      :clearable=\"true\"\n                      :accordion=\"true\"\n                      @getValue=\"(value) => {inputForm.${c.javaFieldId}=value}\"/>\n    <#elseif c.showType == \"gridselect\">\n          <GridSelect\n            title=\"选择${c.comments}\"\n            labelName = \'${c.javaFieldName?split(\".\")[1]}\'\n            labelValue = \'${c.javaFieldId?split(\".\")[1]}\'\n            :value = \"inputForm.${c.javaFieldId}\"\n            :limit=\"1\"\n            @getValue=\'(value) => {inputForm.${c.javaFieldId}=value}\'\n            :columns=\"[\n    <#list c.fieldLabels?split(\"|\") as f>\n            {\n              prop: \'${c.fieldKeys?split(\"|\")[f_index]}\',\n              label: \'${c.fieldLabels?split(\"|\")[f_index]}\'\n            }<#if f_has_next>,</#if>\n    </#list>\n            ]\"\n            :searchs=\"[\n   <#list c.searchLabel?split(\"|\") as f>\n            {\n              prop: \'${c.searchKey?split(\"|\")[f_index]}\',\n              label: \'${c.searchLabel?split(\"|\")[f_index]}\'\n            }<#if f_has_next>,</#if>\n    </#list>\n            ]\"\n            dataListUrl=\"${c.dataUrl}/list\"\n            queryEntityUrl=\"${c.dataUrl}/queryById\">\n          </GridSelect>\n    <#elseif c.showType == \"wangeditor\">\n            <WangEditor ref=\"${c.javaFieldId}Editor\" v-model=\"inputForm.${c.javaFieldId}\"/>\n    <#elseif c.showType == \"quilleditor\">\n            <vue-editor useCustomImageHandler @image-added=\"$utils.handleImageAdded\" v-model=\"inputForm.${c.javaFieldId}\"/>\n    </#if>\n           </el-for