& c.isQuery == \"1\">\n     <#if c.showType == \"checkbox\">\n     <#assign ckIndex= ckIndex + 1/>\n      ${c.javaFieldId}List: {\n        get: function () {\n          return this.searchForm.${c.javaFieldId} !== \'\' ? this.searchForm.${c.javaFieldId}.split(\',\') : []\n        },\n        set: function (val) {\n          this.searchForm.${c.javaFieldId} = val.join(\',\')\n        }\n      }<#if ckIndex lt cks >,</#if>\n    </#if>\n    </#if>\n    </#list>\n    },\n    </#if>\n    methods: {\n      // 获取数据列表\n      refreshList () {\n        this.loading = true\n        ${className}Service.list({\n          \'current\': this.tablePage.currentPage,\n          \'size\': this.tablePage.pageSize,\n          \'orders\': this.tablePage.orders,\n            <#if (table.queryBetwinDate?size > 0)>\n            <#list table.queryBetwinDate as c>\n          begin${c.javaFieldId?cap_first}: this.searchForm.${c.javaFieldId}[0],\n          end${c.javaFieldId?cap_first}: this.searchForm.${c.javaFieldId}[1],\n            </#list>\n          ...this.lodash.omit(this.searchForm, <#list table.queryBetwinDate as c>\'${c.javaFieldId}\'<#if c_has_next>, </#if></#list>)\n            <#else>\n          ...this.searchForm\n            </#if>\n        }).then(({data}) => {\n          this.dataList = data.records\n          this.tablePage.total = data.total\n          this.loading = false\n        })\n      },\n      // 当前页\n      currentChangeHandle ({ currentPage, pageSize }) {\n        this.tablePage.currentPage = currentPage\n        this.tablePage.pageSize = pageSize\n        this.refreshList()\n      },\n      // 排序\n      sortChangeHandle (obj) {\n        this.tablePage.orders = []\n        if (obj.order != null) {\n          this.tablePage.orders = [{ column: obj.column.sortBy || this.$utils.toLine(obj.property), asc: obj.order === \'asc\' }]\n        } else {\n          this.tablePage.orders = <#if isNeedJoinTable>[{ column: \'a.create_date\', asc: false }]<#else>[{ column: \'create_date\', asc: false }]</#if>\n        }\n        this.refreshList()\n      },\n      // 新增\n      add () {\n        this.$refs.${className}Form.init(\'add\', \'\')\n      },\n      // 修改\n      edit (id) {\n        id = id || this.$refs.${className}Table.getCheckboxRecords().map(item => {\n          return item.id\n        })[0]\n        this.$refs.${className}Form.init(\'edit\', id)\n      },\n      // 查看\n      view (id) {\n        this.$refs.${className}Form.init(\'view\', id)\n      },\n      // 删除\n      del (id) {\n        let ids = id || this.$refs.${className}Table.getCheckboxRecords().map(item => {\n          return item.id\n        }).join(\',\')\n        this.$confirm(`确定删除所选项吗?`, \'提示\', {\n          confirmButtonText: \'确定\',\n          cancelButtonText: \'取消\',\n          type: \'warning\'\n        }).then(() => {\n          this.loading = true\n          ${className}Service.delete(ids).then(({data}) => {\n            this.$message.success(data)\n            this.refreshList()\n            this.loading = false\n          })\n        })\n      },\n      // 下载模板\n      downloadTpl () {\n        this.loading = true\n        ${className}Service.exportTemplate().then(({data}) => {\n          // 将二进制流文件写入excel表，以下为重要步骤\n          this.$utils.downloadExcel(data, \'请假表单导入模板\')\n          this.loading = false\n        }).catch(function (err) {\n          this.loading = false\n          if (err.response) {\n            console.log(err.response)\n          }\n        })\n      },\n      // 自定义服务端导入\n      importMethod ({ file }) {\n      // 处理表单\n        const formBody = new FormData()\n        formBody.append(\'file\', file)\n        this.loading = true\n        ${className}Service.importExcel(formBody).then(({data}) => {\n          this.$message.success({\n            dangerouslyUseHTMLString: true,\n            message: data\n          })\n          this.refreshList()\n        })\n      },\n      // 自定义服务端导出\n      exportMethod ({ options }) {\n      // 传给服务端的参数\n        const params = {\n          current: this.tablePage.currentPage,\n          size: this.tablePage.pageSize,\n          orders: this.tablePage.orders,\n          ...this.searchForm,\n          filename: options.filename,\n          sheetName: options.sheetName,\n          isHeader: options.isHeader,\n          original: options.original,\n          mode: options.mode,\n          selectIds: options.mode === \'selected\' ? options.data.map((item) => item.id) : [],\n          exportFields: options.columns.map((column) => column.property && column.property.split(\'.\')[0])\n        }\n        this.loading = true\n        return ${className}Service.exportExcel(params).then(({data}) => {\n          // 将二进制流文件写入excel表，以下为重要步骤\n          this.$utils.downloadExcel(data, options.filename)\n          this.loading = false\n        }).catch(function (err) {\n          if (err.response) {\n            console.log(err.response)\n          }\n        })\n      },\n      // 查看详情\n      detail ({row}) {\n        return new Promise(resolve => {\n          ${className}Service.queryById(row.id).then(({data}) => {\n            this.dataList.forEach((item, index) => {\n              if (item.id === row.id) {\n                        <#list table.childList as child>\n                item.${child.className?uncap_first}DTOList = data.${child.className?uncap_first}DTOList\n                        </#list>\n              }\n            })\n            resolve()\n          })\n        })\n      },\n      resetSearch () {\n        this.$refs.searchForm.resetFields()\n        this.refreshList()\n      }\n    }\n  }\n</script>\n]]>\n    </content>\n</template>\n', '', 0, 90, '0', NULL, NULL, NULL, '1', '2021-10-10 15:56:57');
INSERT INTO `plugin_gencode_template_obj` VALUES ('e5bea8a3e63b4a6e9f324ffaaa6c2930', 'mapper.xml', '6b423144a2f24a0897f40c9a1bdb0c42', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<template>\n    <name>mapper</name>\n    <filePath>src/main/java/${packageName}/${moduleName}/${subModuleName}/mapper/xml</filePath>\n    <fileName>${ClassName}Mapper.xml</fileName>\n    <content><![CDATA[<?xml version=\"1.0\" encoding=\"UTF-8\" ?>\n<!DOCTYPE mapper PUBLIC \"-//mybatis.org//DTD Mapper 3.0//EN\" \"http://mybatis.org/dtd/mybatis-3-mapper.dtd\">\n<mapper namespace=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.mapper.${ClassName}Mapper\">\n\n<#if isNeedJoinTable>\n    <#-- 输出字段列 -->\n	<sql id=\"${className}Columns\">\n		<#assign columnField>\n			<#list table.columnList as c>\n		a.${c.name} AS \"${c.javaFieldId}\",\n			</#list>\n			<#list table.columnList as c>\n			<#-- 父表关联字段 -->\n				<#if table.parentExists && table.parentTableFk == c.name>\n					<#list c.javaFieldAttrs as a>\n		b.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"userselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"officeselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"areaselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"treeselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n					</#list>\n				<#elseif c.showType?? && c.showType == \"gridselect\">\n					<#list c.javaFieldAttrs as a>\n		${c.simpleJavaField}.${a[1]} AS \"${c.simpleJavaField}.${a[0]}\",\n				</#list>\n				</#if>\n\n			</#list>\n		</#assign>\n${columnField?substring(0, columnField?last_index_of(\",\"))}\n	</sql>\n\n	<#-- 输出字段关联表 -->\n	<sql id=\"${className}Joins\">\n\n		<#-- 关联系统表 -->\n		<#list table.columnList as c>\n		<#-- 关联父表 -->\n		<#if table.parentExists && table.parentTableFk == c.name>\n			LEFT JOIN ${table.parent.name} b ON b.id = a.${table.parentTableFk}\n			<#elseif c.isNotBasicUserField>\n		LEFT JOIN sys_user ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"officeselect\">\n		LEFT JOIN sys_office ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"areaselect\">\n		LEFT JOIN sys_area ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"treeselect\" && c.javaType != \"This\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			<#elseif c.showType?? && c.showType == \"gridselect\">\n		LEFT JOIN ${c.tableName} ${c.simpleJavaField} ON ${c.simpleJavaField}.id = a.${c.name}\n			</#if>\n		</#list>\n	</sql>\n\n\n\n	<select id=\"findById\" resultType=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO\">\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n		WHERE a.id = ${\"#\"}{id} and a.del_flag = 0\n	</select>\n\n	<select id=\"findList\" resultType=\"${packageName}.${moduleName}<#if subModuleName != \"\">.${subModuleName}</#if>.service.dto.${ClassName}DTO\">\n		SELECT\n			<include refid=\"${className}Columns\"/>\n		FROM ${table.name} a\n		<include refid=\"${className}Joins\"/>\n	    ${\"$\"}{ew.customSqlSegment}\n	</select>\n</#if>\n\n</mapper>]]>\n    </content>\n</template>\n', '', 0, 40, '1', NULL, NULL, NULL, '1', '2021-10-16 10:28:21');
